INSERT INTO `comment` (`id`, `idUser`, `idTinTuc`, `NoiDung`, `created_at`, `updated_at`) VALUES
(2, 3, 3, 'Mong sao có đường to đẹp nối các Tỉnh với nhau sẽ giúp kinh tế phát triển rất nhanh.', '2020-06-24 19:41:45', '2020-06-24 19:41:45'),
(3, 1, 12, 'Mong là mau chóng có vaccine để cộng đồng không còn phải vật lộn với con virus thế kỷ này.', '2020-06-24 21:01:56', '2020-06-24 21:01:56'),
(4, 4, 21, 'Huân chương nào cũng cao quý và vinh quang cho người nhận .Cảm xúc dâng trào tự hào và kiêu hãnh , khi đứng nhận Huy chương Chiến Thắng, nó cũng thay mặt đất nước ghi nhận sự đóng góp to lớn trong chiến tranh bảo vệ Tổ Quốc của cá nhân hay tập thể.', '2020-06-26 10:52:28', '2020-06-26 10:52:28'),
(5, 1, 66, 'Barca có 2 thánh và 1 tướng. Thánh Cruyff, thánh Messi và tướng Pep, thảo nào cđv cũng có nhiều thánh tướng.\r\nVan Gaal làm sao bằng được thánh Cruyff. Tỷ số kỷ lục của Cruyff chung kết năm 94 đến giờ vẫn chưa ai phá được.', '2020-06-28 14:30:40', '2020-06-28 14:30:40'),
(6, 1, 72, 'Mỗi nhà mỗi cảnh, biết bao nhiêu là vừa, bao nhiêu là đủ ?! Hôm nay 26/9 cuối tháng rồi mà em có 15 triệu, có khi đầu tháng bố mẹ em ck cho 50 triệu trong khi khối bạn sv khác tự kiếm tiền tiêu hàng tháng chưa qua ngày 20 đã nhẵn túi... Nên cũng chả biết khuyên nhóc sao. Chưa làm ra tiền bố mẹ còn chu cấp thì cố gắng giảm gánh nặng của bố mẹ. Đã làm ra tiền, bố mẹ còn khó thì vừa phải thôi để tiền giúp đỡ bố mẹ. Còn tự chủ tài chính, bố mẹ khá giả thì cứ tận hưởng cuộc sống chạy đua công nghệ cho biết với đời!', '2020-06-28 14:59:25', '2020-06-28 14:59:25'),
(7, 5, 6, 'Lạc hậu đến buồn cười', '2020-06-30 13:03:12', '2020-06-30 13:03:12'),
(8, 5, 75, 'Tội phạm ở đâu cũng có. Tòa xử không công bằng ở đâu cũng có. Nhưng bất công do yếu tố chủng tộc thì đa số là ở Mỹ.', '2020-06-30 13:04:05', '2020-06-30 13:04:05');

