INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(1, 'Đầu xe container cháy ngùn ngụt', 'dau-xe-container-chay-ngun-ngut', 'TP HCM - Phát hiện lửa bốc lên khi xe đang chạy trên Xa lộ Hà Nội, tài xế nhanh trí tách thùng container ra khỏi đầu kéo để cứu hàng.', '<p style=\"text-align:center\"><img alt=\"Đầu xe bốc cháy ngùn ngụt. Ảnh: Đinh Vui.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/chay-xe-1-4773-1592987653.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=IZf_XoI7hOhIaqQMOvymPg\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Đầu xe bốc cháy ngùn ngụt. Ảnh:</span><em>&nbsp;Đinh Vui.</em></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:18px\">Trưa 24/6, tài xế Hoàng Văn Dũng (31 tuổi) lái xe container chở keo nhựa và màng co từ cảng Cát Lái đi Bình Dương. Khi cách cổng Khu công nghệ cao quận 9 khoảng 200 m, anh Dũng phát hiện đầu xe bốc cháy.</span></p>\r\n\r\n<p style=\"text-align: center;\"><iframe height=\"480\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/06/24/container-boc-chay-ngun-ngut-tren-xa-lo-ha-noi-1592987635.mp4\" width=\"680\"></iframe></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial\">Container bốc cháy giữa đường. Video:&nbsp;</span><em>Nguyễn Điệp.</em></span></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:18px\">Tài xế liền dừng xe tìm cách dập lửa nhưng bất thành. Anh nhanh chóng&nbsp;tháo thùng container 20&nbsp;feet khỏi đầu xe để cứu hàng. Ít phút sau, đám cháy đã bao trùm toàn bộ đầu</span><span style=\"font-size:18px\"><span style=\"font-family:arial\"> kéo,&nbsp;lửa và khói đen bốc lên ngùn ngụt.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Ba xe cứu hỏa cùng hàng chục cảnh sát chữa cháy đến dập tắt đám cháy sau 30 phút. Tuy nhiên, đầu xe bị hư hỏng, các giấy tờ cùng đồ dùng bên trong cháy rụi.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đầu xe container sau khi được dập tắt lửa. Ảnh: Đình Vui.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/chay-rui-5812-1592987654.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QBjQXH_n5SDjKySK2aFinQ\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Đầu xe container sau khi được dập tắt lửa. Ảnh:&nbsp;</span><em>Đình Vui.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Gần đây, nhiều vụ cháy xe liên tiếp xảy ra. Tối 20/6,&nbsp;xe khách 40 chỗ cháy&nbsp;&nbsp;ở lối lên Vành Đai Ba&nbsp;(gần công viên Yên Sở, Hà Nội). Trước đó hai ngày, xe&nbsp;giường nằm cháy&nbsp;rụi&nbsp;trên cao tốc Hà Nội - Lào Cai... Rất may các vụ hỏa hoạn&nbsp;không gây thương vong.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo chuyên gia kỹ thuật của các hãng xe, cảnh sát kỹ thuật hình sự, Cục Đăng kiểm, nhìn chung có&nbsp;5 lý do chính dẫn tới cháy xe&nbsp;gồm: hệ thống&nbsp;điện, hở ống xả,&nbsp;dầu nhớt xuống cấp, nhiên liệu,&nbsp;tác động&nbsp;bên ngoài. Trong đó, chập cháy hệ thống điện là nguyên nhân hàng đầu dẫn tới cháy xe vào mùa nóng.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Đinh Vui</strong></span></p>', 'dYZ4_1.jpg', 1, 22, 1, '2020-06-24 17:51:14', '2020-06-27 18:07:02'),
(2, 'TP HCM muốn thu 2.700 tỷ đồng phí hàng qua cảng mỗi năm', 'tp-hcm-muon-thu-2-700-ty-dong-phi-hang-qua-cang-moi-nam', 'Với sản lượng hơn 170 triệu tấn hàng mỗi năm, TP HCM kỳ vọng thu về 2.700 tỷ đồng từ phí sử dụng công trình, hạ tầng tại cảng biển đối với hàng hóa.', '<p><span style=\"font-size:18px\">Đây là kết quả của Tổ công tác liên ngành thành phố&nbsp;gửi UBND TP HCM sau chuyến học tập kinh nghiệm của Hải Phòng - nơi đầu tiên&nbsp;cả nước&nbsp;thu phí hạ tầng cảng biển. Số tiền thu được sẽ&nbsp;đầu tư&nbsp;hạ tầng giao thông&nbsp;kết nối cảng biển, kéo giảm&nbsp;ùn tắc&nbsp;và&nbsp;rút ngắn thời gian vận chuyển hàng hóa.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước đó, Văn phòng Thành ủy TP HCM thông báo kết luận của Bí thư Thành ủy Nguyễn Thiện Nhân giao UBND thành phố khẩn trương thành lập tổ công tác (Sở Giao thông Vận tải, Sở Tài chính, Cục Hải quan, sở ngành liên quan) nghiên cứu cách làm của Hải Phòng trong việc thu loại phí nói trên.</span></p>\r\n\r\n<p><img alt=\"Cát Lái hiện là&nbsp;cảng&nbsp;container quốc tế&nbsp;lớn&nbsp;và hiện đại&nbsp;nhất Việt Nam. Ảnh: Hữu Công\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/cat-lai1-8615-1405591198-15929-3831-4511-1592920460.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1a1vFab4oS5XnW6pRPHUUg\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Cát Lái&nbsp;hiện là&nbsp;cảng&nbsp;container quốc tế&nbsp;lớn&nbsp;và hiện đại&nbsp;nhất Việt Nam. Ảnh:&nbsp;</span><em>Hữu Công.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo tổ công tác, cảng Hải Phòng lớn&nbsp;nhất miền Bắc với 47 bến cảng, gần 100 cầu cảng kết nối với khoảng 260 km đường đô thị&nbsp;để chở hàng hóa tới&nbsp;các dịch vụ kho bãi (logistics). Hiện Hải Phòng thu&nbsp;phí sử dụng hạ tầng công trình, dịch vụ&nbsp;từ 16.000 đến 50.000 đồng&nbsp;cho một tấn&nbsp;hàng lỏng, hàng rời; từ 250.000 đồng đến 2,2 triệu đồng cho một container tùy loại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">TP HCM có vị trí địa lý cảng biển và sản lượng hàng hóa&nbsp;qua cảng biển tương đồng Hải Phòng. Các khu bến cảng nằm dọc theo sông từ phía Đông xuống Nam trải dài qua nhiều quận huyện&nbsp;(9,2, 4, 7, Nhà Bè)&nbsp;và nhiều tuyến đường với chiều dài hơn 80 km. Mỗi ngày có khoảng 26.000 lượt xe chở&nbsp;hàng hóa&nbsp;dùng&nbsp;các tuyến đường của thành phố để ra vào cảng&nbsp;khiến giao thông luôn&nbsp;quá tải.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vì vậy, để có vốn phát triển giao thông&nbsp;kết nối cảng biển trong điều kiện ngân sách hạn hẹp, Sở Giao thông Vận tải&nbsp;đánh giá việc thu phí sử dụng công trình, dịch vụ&nbsp;tại khu vực cửa khẩu cảng biển ở thành phố&nbsp;phù hợp về&nbsp;địa lý và cơ sở pháp lý. Với lượng hàng hóa năm 2019&nbsp;hơn 170 triệu tấn, nếu thu&nbsp;phí 16.000 đồng&nbsp;mỗi tấn như Hải Phòng, dự kiến mỗi năm TP HCM thu hơn 2.700 tỷ đồng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sở Giao thông Vận tải kiến nghị UBND thành phố đồng ý cho cơ quan này chủ trì xây dựng đề án thu phí nói trên, trình UBND thành phố&nbsp;xem xét vào cuối năm.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Hữu Công</strong></span></p>', 'qKSc_2.jpg', 1, 3, 1, '2020-06-24 19:03:36', '2020-06-24 20:28:17'),
(3, 'Tuyến vành đai nghìn tỷ đồng nối Hà Nội với Bắc Giang', 'tuyen-vanh-dai-nghin-ty-dong-noi-ha-noi-voi-bac-giang', 'Dài 35 km với tổng mức đầu tư dự kiến hơn 1.200 tỷ đồng, đường vành đai 4 nối thủ đô với tỉnh Bắc Giang sẽ hoàn thiện toàn tuyến vào cuối năm nay.', '<p><span style=\"font-size:18px\">Dự án đường vành đai 4 Hà Nội, đoạn&nbsp;qua địa phận tỉnh Bắc Giang dài hơn 35 km là một trong những&nbsp;đoạn đầu tiên được đầu tư xây dựng của vành đai này.&nbsp;Tổng mức đầu tư dự án&nbsp;1.230 tỷ đồng, từ&nbsp;nguồn vốn ngân sách của tỉnh Bắc Giang.</span><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-11-1592901464-1592913899.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2ov6bZ0JTbEyX2FbW-0ioQ\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:18px\">Điểm đầu tuyến đường hơn 35 km này nối&nbsp;từ cao tốc Bắc Giang - Hà Nội (khu vực thị trấn Nếnh, Việt Yên), điểm cuối là cầu Xuân Cẩm - Bắc Phú (thuộc xã Xuân Cẩm, Hiệp Hòa) nối với huyện Sóc Sơn (Hà Nội).</span><span style=\"font-size:11px\"><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-10-1592901464-1592913958.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9h9AzS5vJLItzti7aSUe_w\" style=\"width:100%\" /></span><span style=\"font-size:18px\">Tuyến đường xây dựng kết cấu bê tông&nbsp;theo tiêu chuẩn đường cấp III đồng bằng,&nbsp;chiều rộng&nbsp;12m.</span><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/vanh-dai-Bac-Giang-1592901574-1592913961.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=slg90BSn1q6kRXopkbHZJA\" style=\"width:100%\" /><span style=\"font-size:18px\">Do chưa hoàn thiện, tuyến đường&nbsp;thông xe từ 30/4 nhưng hiện chủ yếu&nbsp;phục vụ người dân các xã trong&nbsp;huyện Hiệp Hòa đi làm ruộng và buôn bán nhỏ lẻ.</span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-5-1592901461-1592913953.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Yy8tVvteteGgX_WOwSLTLA\" style=\"width:100%\" /><span style=\"font-size:18px\">Đến nay, tuyến đường đã hoàn thành khoảng 20 km trong tổng&nbsp;số 35 km, được cắm hệ thống biển báo. Tuyến đường này kết nối từ tỉnh Bắc Giang sang&nbsp;xã Bắc Phú, huyện Sóc Sơn (Hà Nội), tuy nhiên&nbsp;phía&nbsp;Sóc Sơn&nbsp;chưa được đầu tư xây dựng.</span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-12-1592901465-1592913857.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=K72KiynMCJQ70noeO43G8A\" style=\"width:100%\" /><span style=\"font-size:18px\">Đường vành đai 4 qua Bắc Giang có 3 cây cầu chính, trong đó cầu vượt đường sắt qua thị trấn Nếnh (huyện Việt Yên)&nbsp;đã hoàn thành.</span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/HUY-7916-1592913946.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vk7ZE_7LMDaQnozGHmuw7w\" style=\"width:100%\" /><span style=\"font-size:18px\"><span style=\"font-family:arial\">Cầu đường sắt qua thị trấn Nếnh rộng 12 m phục vụ hai làn ôtô và hai làn xe máy đi hai chiều.</span></span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-3-1592901460-1592913948.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eUbyzE2H77ED9nAXdEBBLQ\" style=\"width:100%\" /><span style=\"font-size:18px\"><span style=\"font-family:arial\">Trên tuyến đường còn có&nbsp;cầu Xuân Cẩm - Bắc Phú nối hai bờ sông Cầu thuộc huyện Sóc Sơn và Hiệp Hòa,&nbsp;tổng chiều dài 480 m, bề rộng 12 m, với tổng mức đầu tư 110 tỷ đồng. Cầu dự&nbsp;kiến hợp long vào đầu tháng 7.</span></span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-8-1592901463-1592913954.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ODo6OvW3rm0N6odvRqErsg\" style=\"width:100%\" /><span style=\"font-size:18px\">Nhóm công nhân đang&nbsp;sửa lại hệ thống điện trên công trình&nbsp;trong thời tiết&nbsp;khoảng 39 độ C.</span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-9-1592901463-1592913956.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cn7rdwhQlflrm_eVS4Qamw\" style=\"width:100%\" /><span style=\"font-size:18px\">Công nhân&nbsp;Hoàng Văn Quảng cho hay trong những ngày qua dù nắng&nbsp;nhưng vẫn phải ra&nbsp;công trường để&nbsp;đảm bảo tiến độ đến tháng 9 hoàn thiện và thông xe. Để tránh ánh nắng trực tiếp, kíp công nhân làm việc dưới cái nắng chói chang trên cầu đều trang bị kính đen.</span></p>\r\n\r\n<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/Vanh-dai-5-Bac-Giang-4-1592901461-1592913951.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=t4QSLFHlnnjVqc62IqUA1g\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài hai cầu trên, tuyến vành đai 4 qua Bắc Giang còn xây dựng một cầu vượt bắc qua cao tốc Hà Nội - Bắc Giang để sang khu công nghiệp Quang Châu (huyện Việt Yên). Cây cầu này dự kiến&nbsp;khánh thành vào cuối năm nay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi hoàn thiện, tuyến vành đai 4 qua Bắc Giang sẽ giúp kết nối các huyện Việt Yên, Hiệp Hòa của Bắc Giang với thủ đô.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Bá Đô - Giang Huy</strong></span></p>', 'n3Oz_3.jpg', 1, 13, 1, '2020-06-24 19:29:40', '2020-06-24 20:29:38'),
(4, 'Xây 9 đập ngăn mặn ở miền Tây', 'xay-9-dap-ngan-man-o-mien-tay', 'LONG AN - 9 đập ngăn mặn dọc theo sông Vàm Cỏ Tây với kinh phí xây dựng 5-10 tỷ đồng mỗi cái, nhằm bảo vệ 62.000 ha lúa, hoa màu và cây ăn quả.', '<p><span style=\"font-size:18px\">Ngày 23/6, ông Võ Kim Thuần, Chi cục trưởng Thủy lợi và Phát triển Nông thôn&nbsp;tỉnh&nbsp;Long An cho biết, 9 đập được xây dựng để ngăn mặn từ sông Vàm Cỏ Tây tràn vào các con kênh nội đồng. Hiện,&nbsp;5 cái&nbsp;dọc theo quốc lộ 62 (huyện Thạnh Hóa) đã được Bộ Nông nghiệp và Phát triển Nông thôn hỗ trợ đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong đó, hai đập&nbsp;Bà Hai Màng và&nbsp;Ông Nhượng&nbsp;lòng cống rộng khoảng 5 m dự kiến&nbsp;hoàn thành&nbsp;cuối năm nay. Ba cống khác sẽ&nbsp;hoàn thành&nbsp;cuối&nbsp;năm sau. Ngoài ra, tỉnh&nbsp;cũng đang&nbsp;đề xuất Bộ hỗ trợ kinh phí xây thêm bốn&nbsp;đập&nbsp;khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi hoàn thành, các cống đập này sẽ bảo vệ&nbsp;62.000 ha lúa, hoa màu và&nbsp;cây ăn quả vùng dự án Bắc Đông của hai&nbsp;tỉnh Long An và Tiền Giang.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Từ năm 2016 đến nay, các vị trí này đến mùa hạn mặn phải đóng đập tạm, sau đó tháo dỡ, tổng chi phí mỗi năm khoảng 4 tỷ đồng, nên cần được đầu tư kiên cố một lần để tiết kiệm chi phí\", ông Võ Kim Thuần nói.</span></p>\r\n\r\n<p><img alt=\"Cống đập bà Hai Màng đã hoàn thành 50%, sẽ đưa vào sử dụng cuối năm nay. Ảnh: Hoàng Nam\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/DSC5445-JPG-7652-1592892313.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LOBxFf7RUFtJGaHfko7_3w\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Cống đập bà Hai Màng đã hoàn thành 50%, sẽ đưa vào sử dụng cuối năm nay. Ảnh:&nbsp;</span><em>Hoàng Nam.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Ba hôm trước, Bộ Nông nghiệp và Phát triển Nông thôn cùng 13 tỉnh Đồng bằng sông Cửu Long tổng kết công tác phòng chống hạn mặn tại Long An. Mùa hạn mặn năm nay, nước mặn tiến sâu vào sông Vàm Cỏ Tây hơn 100 km, khiến&nbsp;hơn 2.700 ha lúa và 2.700 ha cây trồng bị thiệt hại, ảnh hưởng;&nbsp;3.500 hộ dân thiếu nước sinh hoạt.&nbsp;Tổng thiệt hại tại tỉnh Long An trên 55 tỷ đồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Toàn miền Tây ghi nhận 16.500 ha đất tôm lúa, gần 42.000 ha lúa đông xuân, 6.650 ha cây ăn quả, 1.200 ha rau màu cùng 8.700 ha diện tích nuôi trồng thủy sản bị thiệt hại. Khoảng 96.000 hộ&nbsp;thiếu nước, hàng nghìn điểm sạt lở tại các tỉnh Cà Mau, Tiền Giang, An Giang.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bộ Nông nghiệp và Phát triển Nông thôn nhận định hạn mặn những năm tiếp theo sẽ diễn biến khó lường, do vậy cần chủ động các phương án ứng phó. Các địa phương cần nghiên cứu chuyển đổi cơ cấu cây trồng thích ứng với điều kiện hạn mặn; xây các khu trữ nước ngọt ở vùng Đồng Tháp Mười và Tứ giác Long Xuyên; làm hệ thống đường ống, cầu máng chuyển nước cho các vùng ven biển; đầu tư&nbsp;các cống ngăn mặn ở&nbsp;cửa sông Cổ Chiên, Cung Hầu, Hàm Luông.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Hoàng Nam</strong></span></p>', 'u8zf_4.jpg', 1, 9, 2, '2020-06-24 19:48:34', '2020-06-24 20:33:53'),
(5, 'Trang trại rau hữu cơ rộng 60ha', 'trang-trai-rau-huu-co-rong-60ha', 'Mô hình sản xuất rau hữu cơ tại trang trại Hoa Viên cung cấp 1 tấn rau mỗi ngày cho người dân Hà Nội.', '<p style=\"text-align: center;\"><img alt=\"Trang trại rau hữu cơ Hoa Viên nằm ở xóm Dục, xã Yên Bình, huyện Thạch Thất, Hà Nội. Chị Trương Kim Hoa, chủ trang trại Hoa Viên đã cải tạo mảnh đất hoang thành mô hình trồng rau hữu cơ.&nbsp;Trang trại trồng trên 50 loại rau khác nhau ở 6 chủng loại: rau đặc sản, rau ăn lá, củ quả, rau gia vị, hoa quả, thảo mộc.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image001-1580979679-1763-1580980369.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Trang trại rau hữu cơ Hoa Viên nằm ở xóm Dục, xã Yên Bình, huyện Thạch Thất, Hà Nội. Chị Trương Kim Hoa, chủ trang trại Hoa Viên đã cải tạo mảnh đất hoang thành mô hình trồng rau hữu cơ.&nbsp;Trang trại trồng trên 50 loại rau khác nhau ở 6 chủng loại: rau đặc sản, rau ăn lá, củ quả, rau gia vị, hoa quả, thảo mộc.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Quy trình trồng rau hữu cơ được thực hiện nghiêm ngặt từ khâu chọn giống đến thu hoạch. Các công đoạn dọn cỏ, cải tạo đất đều làm thủ công. Toàn bộ cỏ dại, tàn dư thực vật sau thu hoạch được thu gom, ngâm ủ, cho trùn quế ăn. Khu vực xử lý tàn dư thực vật và làm phân hữu cơ bố trí riêng cách ly với khu trồng trọt, có diện tích hơn 2000m2.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image002-1580979854-7566-1580980369.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Quy trình trồng rau hữu cơ được thực hiện nghiêm ngặt từ khâu chọn giống đến thu hoạch. Các công đoạn dọn cỏ, cải tạo đất đều làm thủ công. Toàn bộ cỏ dại, tàn dư thực vật sau thu hoạch được thu gom, ngâm ủ, cho trùn quế ăn. Khu vực xử lý tàn dư thực vật và làm phân hữu cơ bố trí riêng cách ly với khu trồng trọt, có diện tích hơn 2000m2.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Tận dụng nguồn nước tinh khiết từ núi Vua Bà chảy quanh năm, chị Hoa dẫn 5km nước qua các bể lọc trên cao và bể con (tổng dung tích 2000m3), đồng thời sử dụng hệ thống tưới phun, tưới nhỏ giọt theo công nghệ tiên tiến trên 100% diện tích vườn.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image003-1580979863-9480-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Tận dụng nguồn nước tinh khiết từ núi Vua Bà chảy quanh năm, chị Hoa dẫn 5km nước qua các bể lọc trên cao và bể con (tổng dung tích 2000m3), đồng thời sử dụng hệ thống tưới phun, tưới nhỏ giọt theo công nghệ tiên tiến trên 100% diện tích vườn.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Đất rừng nguyên sinh dưới chân núi Ba Vì của vườn&nbsp;là một trong những lợi thế để trồng rau hữu cơ. Tuy nhiên, sau mỗi trận mưa, đất đá trôi dạt từ trên rừng xuống, nằm đè lên những luống rau. Vì vậy, công nhân ở đây giăng lưới quanh các thửa ruộng, đồng thời nhặt đá và cải tạo đất trồng mỗi ngày.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image004-1580979907-2417-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Đất rừng nguyên sinh dưới chân núi Ba Vì của vườn&nbsp;là một trong những lợi thế để trồng rau hữu cơ. Tuy nhiên, sau mỗi trận mưa, đất đá trôi dạt từ trên rừng xuống, nằm đè lên những luống rau. Vì vậy, công nhân ở đây giăng lưới quanh các thửa ruộng, đồng thời nhặt đá và cải tạo đất trồng mỗi ngày.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Công nhân tại trang trại&nbsp;tăng chất lượng, độ phì của đất bằng áp dụng biện pháp quản lý canh tác trồng trọt, trồng cây họ đậu để tận dụng khả năng cố định đạm. Bên cạnh đó, trang trại kết hợp sử dụng bón phân trùn quế do trang trại trực tiếp sản xuất và nước đậu tương ngâm ủ.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image005-1580979974-8106-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Công nhân tại trang trại&nbsp;tăng chất lượng, độ phì của đất bằng áp dụng biện pháp quản lý canh tác trồng trọt, trồng cây họ đậu để tận dụng khả năng cố định đạm. Bên cạnh đó, trang trại kết hợp sử dụng bón phân trùn quế do trang trại trực tiếp sản xuất và nước đậu tương ngâm ủ.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Chủ trang trại&nbsp;giữ lại hơn 50ha (chiếm hơn 83% diện tích sản xuất hữu cơ) làm môi trường sống cho các loài động vật, thực vật khác nhau trong kênh rạch, ao hồ và để cây cỏ mọc tự nhiên, phát triển&nbsp;cây trồng vùng đệm, rừng, vườn quả hỗn hợp.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image006-1580979988-4920-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Chủ trang trại&nbsp;giữ lại hơn 50ha (chiếm hơn 83% diện tích sản xuất hữu cơ) làm môi trường sống cho các loài động vật, thực vật khác nhau trong kênh rạch, ao hồ và để cây cỏ mọc tự nhiên, phát triển&nbsp;cây trồng vùng đệm, rừng, vườn quả hỗn hợp.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Rau được trồng theo hình thức luân canh, xen canh. Một số loại rau, củ, quả như su hào, bắp cải, dưa chuột... được trồng trong nhà màng với chế độ chăm sóc riêng.\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image007-1580980090-5645-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Rau được trồng theo hình thức luân canh, xen canh. Một số loại rau, củ, quả như su hào, bắp cải, dưa chuột... được trồng trong nhà màng với chế độ chăm sóc riêng.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Nông trại rau hữu cơ rộng 60ha - 7\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image008-1580980144-8551-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Tại trang trại, công nhân dành 30 phút mỗi ngày để bắt sâu, dùng đèn, bẫy bả sinh học và lợi dụng thiên địch để trừ sâu.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Nông trại rau hữu cơ rộng 60ha - 8\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image009-1580980314-7997-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Từ rừng, các loài chim, kỳ nhông, thằn lằn, cóc sà xuống các luống rau để ăn sâu, nếu sâu xuất hiện nhiều thì các tấm lưới có chức năng dồn chúng ra ngoài để thiên địch&nbsp;có chỗ về kiếm ăn.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Nông trại rau hữu cơ rộng 60ha - 9\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image010-1580980303-1412-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Trang trại sử dụng trên 100 lao động, với 90% là người dân địa phương. Những người ở xa sẽ ở tại các nhà chòi, nhà cấp 4 xung quanh các khu trồng rau.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"Nông trại rau hữu cơ rộng 60ha - 10\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image011-1580980293-7252-1580980370.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Rau được thu hái vào khoảng 4 đến 5&nbsp;giờ sáng hàng ngày và vận chuyển ngay cùng các loại cây giống đến nơi tiêu thụ trong nội thành Hà Nội.&nbsp;Mùa hè, trang trại đạt sản lượng 0,5 tấn/ngày, tăng lên 1 tấn/ngày vào mùa đông do tiêu thụ nhiều các loại củ, quả.</span></span></p>\r\n\r\n<p style=\"text-align: right;\"><strong><span style=\"font-size:18px\"><span style=\"font-family:arial,sans-serif\">Ngọc Ly</span></span></strong></p>', 'tsfT_5.jpg', 1, 20, 3, '2020-06-24 19:59:09', '2020-06-24 20:35:19'),
(6, 'Covid-19 len lỏi khắp chợ lớn nhất Mỹ Latinh', 'covid-19-len-loi-khap-cho-lon-nhat-my-latinh', 'MEXICO - Dù bị cảm, Martin Mateo không tin vào nCoV và vẫn tiếp tục đi bán cà chua tại chợ Central de Abasto, Mexico City, rồi qua đời vài ngày sau.', '<p><span style=\"font-size:18px\">Từ giữa tháng 4 đến giữa tháng 5, ít nhất 10 người bán cà chua tại Central de Abasto, chợ thực phẩm lớn nhất khu vực Mỹ Latinh tọa lạc ở thủ đô của Mexico, đã chết vì nCoV. Antonio Samano, em họ của Mateo, cùng một nhân viên gian hàng cạnh ông cũng tử vong trong&nbsp;đại dịch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Ở đây chúng tôi không tin Covid-19 là mối đe dọa. Nhưng khi chứng kiến từng người một ra đi, chúng tôi không còn nghi ngờ gì nữa\", Anastasio Ramon Alonso, một người bán cà chua lâu năm khác trong chợ, cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hôm 26/4, Hector Garcia, quản lý chợ Central de Abasto, thông báo&nbsp;nCoV được phát hiện trong khu chợ bán buôn rộng 3,3 km2. Thông tin này gây lo ngại bởi đây là nơi cung cấp thực phẩm cho 22/32 bang ở Mexico. Các siêu thị, nhà hàng và gia đình đều phụ thuộc vào 90.000 người làm việc trong chợ.</span><img alt=\"Các nhân viên y tế đi quanh chợ&nbsp;Central de Abasto tại Mexico City, Mexico, để đo thân nhiệt mọi người. Ảnh: Washington Post.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/R3KFNFVQ2YI6VGFVE6NGI6NB4Q-1283-1592988112.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=xXquCDEX-d7dA8LsBDSaVQ\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Các nhân viên y tế đi quanh chợ&nbsp;Central de Abasto tại Mexico City, Mexico, để đo thân nhiệt mọi người. Ảnh:&nbsp;</span><em>Washington Post.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Mateo đã bán hàng tại Central de Abasto hơn ba thập kỷ. Người đàn ông 50 tuổi bị bệnh cao huyết áp, một trong những yếu tố nguy hiểm đối với người nhiễm nCoV, và qua đời hôm 18/4.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều đồng nghiệp của Mateo cũng mắc các bệnh mạn tính, như David Hernandez, một người bị tiểu đường tử vong vì nCoV hồi giữa tháng 4. Phía bên kia gian hàng của Mateo, Isaac Pluma, một người khác mắc bệnh tiểu đường, vẫn đi làm dù bị cảm lạnh. Mỗi lần đi lên cầu thang, người đàn ông 46 tuổi này đều thở khò khè.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Ông chủ cố gắng thuyết phục Pluma về nghỉ, nhưng ông ấy không đồng ý vì cần tiền\", Enrique González, người làm cùng Pluma, kể lại. Pluma qua đời hôm 21/4.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những cái chết khiến Pedro Hernandez, một người thường sống trầm lặng trong chợ, trở nên hoảng hốt vì ông cũng bị tiểu đường. Hồi cuối tháng 4, Hernandez quyết định ngừng tới chỗ làm dù bề ngoài trông vẫn khỏe mạnh. Tuy nhiên, người đàn ông 58 tuổi&nbsp;qua đời vào đầu tháng 5.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Gần 3/4 số ca tử vong vì nCoV ở Mexico liên quan tới các bệnh lý nền, như cao huyết áp hay tiểu đường. Việc người dân tiêu thụ ngày càng nhiều thực phẩm giá rẻ chế biến sẵn và nước ngọt trong những thập kỷ gần đây, đặc biệt tại các khu dân cư nghèo, đã khiến tình trạng béo phì và các bệnh mạn tính khác tăng lên gấp bội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước cả khi Mexico báo cáo những ca nhiễm nCoV đầu tiên, các nhà dịch tễ học đã lo ngại về<strong>&nbsp;ảnh hưởng của đại dịch đối với một quốc gia đang gặp khủng hoảng dinh dưỡng</strong>. \"Chúng tôi biết sự hủy hoại sức khỏe suốt 30-40 năm qua sẽ phải trả giá\", Hugo Lopez-Gatell, quan chức phụ trách Covid-19 của Mexico, cho biết hồi cuối tháng 3.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi công bố dịch tại Central de Abasto, ban quản lý chợ và giới chức Mexico City đã hành động để kiềm chế nCoV. Họ cử 430 nhân viên y tế đến chợ để đo thân nhiệt và hỏi người dân tại đây về các triệu chứng, đồng thời tiến hành xét nghiệm và kêu gọi sử dụng khẩu trang cùng nước rửa tay diệt khuẩn. Tuy nhiên, những người bán hàng cho rằng động thái này quá muộn màng.</span></p>\r\n\r\n<p><img alt=\"Carlos Mateo, con trai Martin Mateo, đứng tại lối vào gian bán cà chua của gia đình tại chợ Central de&nbsp;Abasto. Ảnh: Washington Post.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/A6CBWBFQ2YI6VGFVE6NGI6NB4Q-4392-1592988112.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lXrmWO0QHc69opjNYqCh6Q\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Carlos Mateo, con trai Martin Mateo, đứng tại lối vào gian bán cà chua của gia đình tại chợ Central de&nbsp;Abasto. Ảnh:&nbsp;</span><em>Washington Post.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi bị rơi vào thế không phòng vệ\", Rafael Vergara, người quản lý một doanh nghiệp tại Central de Abasto, bày tỏ. Từ giữa tháng 4 tới nay, 12/30 nhân viên của Vergara đã bị ốm, khiến ông buộc phải đưa đội ngũ của mình đến các phòng khám tư nhân để xét nghiệm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, rất ít doanh nghiệp hành động tương tự.&nbsp;<strong>Chính phủ Mexico cũng không theo đuổi chiến lược xét nghiệm hàng loạt và truy vết tiếp xúc.</strong>&nbsp;Giới chức nước này giải thích rằng cách làm đó không thực tế đối với một quốc gia 128 triệu dân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Do không đi xét nghiệm, hầu hết người lao động tại chợ Central de Abasto không hay biết họ đã nhiễm virus. \"Nhiều người cảm thấy mệt, nhưng không chịu về nhà. Họ tiếp tục làm việc\", Vergara cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chính phủ Mexico đã phát động một chiến dịch cộng đồng lớn về Covid-19, với các buổi họp báo hàng đêm, nhưng vẫn không thể thuyết phục những người bán cà chua trong chợ, do&nbsp;<strong>người dân có xu hướng không tin tưởng vào chính quyền</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhà hoạt động Irene Tello Arista cho biết sự ngờ vực đó xuất phát từ các nguyên nhân sâu xa, chỉ ra rằng nhiều cư dân thậm chí thiếu những dịch vụ công cộng cơ bản, như nguồn nước an toàn. \"Họ nghĩ rằng nếu chính phủ chưa bao giờ ngó ngàng đến họ, thì cớ gì bây giờ lại quan tâm?\", Arista giải thích.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tin đồn lan khắp các quầy bán rau củ trong chợ Central de Abasto rằng bệnh viện là nơi nguy hiểm và các bác sĩ cố ý giết người. Dù vô lý, nhiều người bán hàng vẫn tin vào câu chuyện, bởi họ đã quen với hệ thống chăm sóc sức khỏe yếu kém.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Carlos Mateo, con trai của Martin Mateo, cho biết bố của anh và chú Samano ban đầu bị chẩn đoán nhầm là cảm lạnh và được đưa về nhà. Tuy nhiên, họ đều qua đời vào cùng hôm đó. Bản thân Carlos&nbsp;cũng nhiễm nCoV, nhưng may mắn bình phục. Trường hợp của Mateo cho thấy virus dễ dàng lây lan trong gia đình, cũng như giữa những người đồng nghiệp và bạn bè.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Mục tiêu bảo vệ những người lao động tại Central de Abasto chưa bao giờ dễ dàng</strong>. Khu chợ cung cấp 80% thực phẩm cho Mexico City, bao gồm 7.418 gian hàng, với khoảng 300.000 người mua và nhân viên giao hàng ghé mỗi ngày, nên không thể bị đóng cửa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vấn đề càng phức tạp hơn do cách tổ chức trong chợ rất rời rạc. Ban quản lý chợ chỉ thuê khoảng 1.000 người trong số 90.000 người lao động tại đây, chủ yếu là tạp vụ và nhân viên hành chính. Phần còn lại làm việc cho những chủ doanh nghiệp sở hữu hoặc cho thuê gian hàng</span>.</p>\r\n\r\n<p><img alt=\"Một nhân viên bán hàng tại chợ Central de Abasto. Ảnh: Washington Post.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/F5SVV3FQ24I6VGFVE6NGI6NB4Q-6931-1592988112.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=iEu38GGzTMjgHUVI21R6Jw\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Một nhân viên bán hàng tại chợ Central de Abasto. Ảnh:&nbsp;</span><em>Washington Post.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Một số ông chủ cố gắng hành động trách nhiệm bằng cách cho nhân viên nghỉ làm có lương. Tuy nhiên, không phải ai cũng được tận hưởng sự nghỉ ngơi xa xỉ này. \"Những người có tiền có thể ở nhà một, hai, thậm chí ba tháng. Nhưng những người kiếm sống từng ngày như chúng tôi không có nền tảng tài chính như vậy\", Jaime Garcia, người bán hàng 66 tuổi trong chợ, cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo Pedro Torres, chủ tịch một hiệp hội những người bán rau quả tại Central de Abasto, không ai rõ số người chết vì nCoV trong chợ, chỉ có tin đồn. \"Nhiều người nói có hàng trăm nạn nhân, số khác cho hay con số lên đến hàng nghìn, nhưng chúng tôi không có thống kê chính xác\", Torres cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các cuộc phỏng vấn với những người bán hàng trong chợ cho thấy ít nhất hàng chục người đã thiệt mạng. Israel Gonzalez, một người bán ớt xanh, nhận tin 9 người xung quanh khu vực anh bán hàng chết vì nCoV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Erik Cesario, một người bán ớt khác, biết đến 25 người. \"Dịch bệnh tấn công chúng tôi thật khủng khiếp\", Cesario nói. Nhiều ông chủ đã ở nhà, nhưng người làm thuê thì không thể vì khó khăn kinh tế.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Jorge Ochoa, quan chức y tế cấp cao của Mexico City, cho rằng các hành động của chính quyền thủ đô đã giúp kiểm soát nCoV tại khu chợ. Hơn 2.500 người được xét nghiệm, phát hiện 543 ca nhiễm. \"Chiến lược tại Central de Abasto là một trong những thành công lớn nhất của chúng tôi\", ông nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, những người lao động tại khu chợ cho rằng đại dịch được kiềm chế nhờ hàng chục chủ gian hàng quyết định đóng cửa suốt nhiều tuần, gần đây mới nối lại hoạt động.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Không ai tin những gì chính phủ nói, cho đến khi xuất hiện người chết, rồi sau đó mọi người lại kéo nhau rời đi\", người bán cà chua tên Jorge Amaro cho hay.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Ánh Ngọc</strong><span style=\"background-color:rgb(252, 250, 246); color:rgb(34, 34, 34); font-family:arial\">&nbsp;(Theo&nbsp;</span><em>Washington Post</em><span style=\"background-color:rgb(252, 250, 246); color:rgb(34, 34, 34); font-family:arial\">)</span></span></p>', 'p9HD_6.jpg', 1, 9, 5, '2020-06-24 20:17:02', '2020-06-24 20:37:22'),
(7, 'An Gia dự kiến dành trên 3.000 tỷ săn quỹ đất', 'an-gia-du-kien-danh-tren-3-000-ty-san-quy-dat', 'Chủ tịch An Gia nhận định sau dịch bệnh là \"thời điểm vàng\" để M&A mở rộng quỹ đất vì quá trình đàm phán giá diễn ra thuận lợi.', '<p><span style=\"font-size:18px\">Chia sẻ tại phiên họp thường niên mới đây, ông Nguyễn Bá Sáng - Chủ tịch HĐQT kiêm Tổng giám đốc Công ty cổ phần Đầu tư và phát triển Bất động sản An Gia (AGG) cho biết năm ngoái doanh nghiệp này đã mua quỹ đất có tổng giá trị 2.800 tỷ đồng của các dự án tại Bình Dương, Vũng Tàu và TP HCM.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công ty dự kiến năm nay&nbsp;chi thêm khoảng 3.000-5.000 tỷ đồng để mua quỹ đất và triển khai các dự án. Ngoài 33 ha&nbsp;quỹ đất tại TP HCM, công ty đang phát triển thêm 46 ha tại các tỉnh lân cận nhằm hình thành các khu nhà ở liên kế khép kín, hướng tới trở thành đơn vị phát triển đô thị trong 5 năm tới</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ông Sáng nhận định, sau dịch bệnh là thời điểm vàng để mở rộng quỹ đất vì quá trình đàm phán giá&nbsp;suôn sẻ hơn. Tiêu chí công ty săn quỹ đất là chấp nhận giá cao để đổi lại pháp lý tương đối hoàn chỉnh, có thể triển khai nhanh trong 6-9 tháng&nbsp;bán hàng nhằm hạn chế rủi ro.</span></p>\r\n\r\n<p><img alt=\"Ban lãnh đạo An Gia trả lời chất vấn của cổ đông tại phiên họp thường niên. Ảnh: Hà Đỗ.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/24/4eb735b6c06b3d35647a-5718-1593012413.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4Z9EHD8Vh1BbF7qK09PSKQ\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Ban lãnh đạo An Gia trả lời chất vấn của cổ đông tại phiên họp thường niên.&nbsp;</span><em>Ảnh: Hà Đỗ.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Dịch bệnh, theo ông Sáng, cũng giúp công ty có thêm thời gian xây dựng hệ thống vững chắc hơn, bù cho khoảng thời gian chạy đua phát triển dự án mà không chú trọng củng cố quản trị.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, tác động của dịch bệnh đến kết quả kinh doanh không hề nhỏ. Hoạt động bán hàng một số dự án đang chậm hơn bình thường buộc công ty điều chỉnh kế hoạch thận trọng hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công ty trình đại hội đồng cổ đông mục tiêu năm nay tiêu thụ 1.500 sản phẩm, có doanh số bán hàng 3.000 tỷ đồng và ghi nhận doanh thu thuần 2.400 tỷ đồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Ban đầu chúng tôi tính bán hết dự án Westgate (huyện Bình Chánh) và 400 căn ở Bình Dương, thu 8.000-9.000 tỷ đồng, nhưng thực tế đã giảm khoảng phân nửa\", ông Sáng nói và&nbsp;khẳng định việc thanh khoản chậm không dẫn đến thua lỗ. Công ty kỳ vọng lợi nhuận sau thuế phân bổ cho cổ đông công ty mẹ đạt 410 tỷ đồng, tăng 38% so với năm trước. Ước tính nửa đầu năm nay lợi nhuận khoảng 170 tỷ đồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm ngoái, công ty có doanh số bán hàng 6.600 tỷ đồng khi tiêu thụ hơn 2.700 sản phẩm, bao gồm 2 dự án mới tại TP HCM,&nbsp;Vũng Tàu và hàng tồn kho ở các dự án cũ. Doanh thu thuần đạt 385 tỷ đồng, lợi nhuận sau thuế hợp nhất của công ty mẹ tăng 12% lên xấp xỉ 330 tỷ đồng.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Phương Đông</strong></span></p>', 'HyjA_7.jpg', 1, 3, 10, '2020-06-24 20:22:59', '2020-06-24 20:22:59'),
(8, 'Judy Garland: Bi kịch của sự nổi tiếng', 'judy-garland-bi-kich-cua-su-noi-tieng', 'Minh tinh Judy Garland bị ép dùng chất kích thích từ tuổi thiếu niên để đáp ứng lịch làm việc dày đặc khi đóng \"The Wizard Of Oz\".', '<p><span style=\"font-size:18px\">Ngày 22/6 kỷ niệm 51 năm minh tinh&nbsp;qua đời. Bà ra đi vì sốc thuốc trong&nbsp;một căn nhà thuê tại London (Anh), hưởng dương 47 tuổi. Hơn bốn thập kỷ hoạt động, Garland&nbsp;được đánh giá là một những ngôi sao hàng đầu&nbsp;thế giới, trong cả điện ảnh và âm nhạc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, nhiều người hâm mộ cũng nhớ đến Judy Garland là một minh chứng cho mặt trái của ngành công nghiệp giải trí. Nhà báo&nbsp;Anne Helen Petersen, tác giả cuốn&nbsp;<em>Scandals of Classic Hollywood: Sex, Deviance and Drama from the Golden Age of American Cinema,&nbsp;</em>từng viết: \"Garland là nạn nhân đầu tiên của sự nổi tiếng bị phanh phui và chắc chắn không phải trường hợp cuối cùng\".</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Judy Garland lúc sinh thời. Ảnh: Evening Standard.\" src=\"https://i1-giaitri.vnecdn.net/2020/06/24/Judy-Garland-tre-1248-1592982261.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=D7R6KOfD37H5cNbt6g3fLw\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:arial; font-size:14px\">Judy Garland lúc sinh thời. Ảnh:&nbsp;</span><em>Evening Standard.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Judy Garland chào đời&nbsp;năm 1922 với&nbsp;tên khai sinh Frances Ethel Gumm. Suốt tuổi thơ bà&nbsp;chứng kiến cảnh&nbsp;cha mẹ cãi nhau. Ông Frank - cha bà</span><span style=\"font-size:18px\">&nbsp;</span><span style=\"font-size:18px\">-&nbsp;bị nhiều người dân địa phương đồn có sở thích tình dục bệnh hoạn với các nam thiếu niên. Gia đình sau đó&nbsp;phải chuyển đến California (Mỹ) sống để tránh dị nghị.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Judy Garland bị gia đình \"ép lớn\". Thay vì được sống như những đứa trẻ cùng tuổi, bà đối mặt&nbsp;gánh nặng kiếm tiền. Gia đình&nbsp;sở hữu một rạp hát nhỏ, tạo điều kiện cho Garland tiếp xúc với diễn xuất từ năm ba tuổi. Mẹ&nbsp;bà&nbsp;tìm mọi cách để các con gái&nbsp;dấn thân vào làng giải trí, ép chúng biểu diễn tại quán rượu, sòng bạc để gây ấn tượng với các ông bầu. Năm 13 tuổi, Garland&nbsp;ký hợp đồng với MGM, một trong những hãng phim lớn nhất bấy giờ.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Judy Garland (dưới) cùng hai chị gái trong nhóm hát The Garland Sisters. Ảnh: Instagram.\" src=\"https://i1-giaitri.vnecdn.net/2020/06/24/Judy-Garland-5727-1592982261.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eoN8xViowMZoXIhM5sLMsQ\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:arial; font-size:14px\">Judy Garland (dưới) cùng hai chị gái trong nhóm hát \"The Garland Sisters\". Ảnh:&nbsp;</span><em>Instagram</em><span style=\"font-family:arial; font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lịch làm việc dày đặc&nbsp;khiến bà bị mẹ và đoàn phim bắt</span><span style=\"font-size:18px\">&nbsp;</span><span style=\"font-size:18px\">sử dụng các loại thuốc an thần, kích thích. Năm 15 tuổi, Judy Garland bắt đầu bị phụ thuộc vào các loại thuốc và sử dụng chúng đến&nbsp;cuối đời. Theo&nbsp;</span><em style=\"font-size:18px\">Time</em><span style=\"font-size:18px\">, trong thời gian đóng và quảng bá&nbsp;</span><em style=\"font-size:18px\">The Wizard of Oz</em><span style=\"font-size:18px\">&nbsp;(1939), MGM cho Garland dùng&nbsp;amphetamines, một loại thuốc kích thích giúp bà luôn tỉnh táo trên phim trường. Buổi tối, họ ép&nbsp;bà uống thuốc để đi ngủ đúng giờ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giai đoạn này, Judy Garland cũng bị ám ảnh với cân nặng của bản thân sau một số bình luận khiếm nhã của các giám đốc đoàn phim. Họ luôn yêu cầu bà thực hiện&nbsp;chế độ ăn kiêng. Theo&nbsp;<em>Time</em>, khi quay cảnh uống một cốc sữa lắc, cô bé 15 tuổi lúc đó&nbsp;chỉ được phép giả vờ ngậm miệng vào ống hút, không được uống dù rất đói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>The Wizard of Oz</em>&nbsp;đưa&nbsp;Garland thành sao hạng A tại Hollywood. Bà&nbsp;có thêm&nbsp;nhiều phim ăn khách&nbsp;như&nbsp;<em>Meet Me in St. Louis</em>&nbsp;(1944)&nbsp;<em>Easter Parade</em>&nbsp;(1948). Nhưng thành công càng khiến&nbsp;diễn viên&nbsp;chìm sâu vào các vấn đề tâm lý, hậu quả của việc thường xuyên dùng các loại thuốc an thần, kích thích. Ở tuổi đôi mươi, bà bắt đầu nghiện rượu.</span></p>\r\n\r\n<div class=\"box_embed_video_parent embed_video_new\" id=\"video_parent_296023\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: optimizelegibility; position: relative; overflow: hidden; clear: both;\">\r\n<p><span style=\"font-size:18px\">Sức khỏe Judy Garland suy giảm rõ rệt, khiến bà thường vắng mặt tại phim trường. Các bác sĩ khuyên bà chỉ làm việc&nbsp;liên tiếp bốn hoặc năm buổi một tuần, tạo thêm các kỳ nghỉ&nbsp;giữa những đợt quay dài ngày. MGM nhiều lần đình chỉ Judy Garland, trước khi chấm dứt hợp đồng năm 1950, sau 15 năm bà cống hiến cho hãng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong sự nghiệp, Judy Garland cho biết thường xuyên bị quấy rối bởi các nhân vật quyền lực trong làng phim.&nbsp;Louis B. Mayer - người sáng lập hãng MGM - được mệnh danh là&nbsp;Harvey Weinstein&nbsp;của thế kỷ 20, dính nhiều bê bối về tình dục với các nữ diễn viên. Ông từng bị tố cáo sàm sỡ Judy Garland lúc tuổi thiếu niên. Chính Mayer đặt biệt danh cho bà là \"gù lưng\" và miệt thị&nbsp;cân nặng, ngoại hình diễn viên trước đoàn phim. Trong tự truyện, chồng thứ hai của Garland tiết lộ&nbsp;nhiều nam diễn viên từng thò tay dưới váy để trêu chọc bà khi đóng&nbsp;<em>The Wizard of Oz</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Công việc cũng ảnh hưởng lớn tới đời sống tình cảm của Judy Garland.</strong>&nbsp;Năm 18&nbsp;tuổi, bà phải lòng nhạc công&nbsp;David Rose và mang thai. Mối quan hệ bị gia đình và công ty của Garland phản đối, vì lúc đó Rose chưa hoàn tất vụ ly dị vợ cũ. Mẹ Garland ép bà đi phá thai và không cho hai người công khai tình cảm. Cặp sao cưới năm 1941 nhưng chia tay sau hai năm chung sống.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Judy Garland trải qua năm đời chồng và có ba con. Không cuộc hôn nhân nào của bà được nhận xét là êm ấm, hạnh phúc. Sau David Rose, những người chồng tiếp theo&nbsp;còn gây cho bà nhiều đau khổ hơn. Trong thời gian sống cùng chồng thứ hai, đạo diễn&nbsp;Vincent Minnelli, bà từng dùng mảnh thủy tinh cứa cổ tay tự tử nhưng không thành.&nbsp;Sidney Luft, chồng thứ ba,&nbsp;nghiện rượu và thường bạo hành mẹ con Garland. Bà cũng tố cáo&nbsp;Mark Herron đánh mình. Trong khi đó, Herron&nbsp;này khẳng định hai người có xô xát nhưng ông chỉ đánh Garland để tự vệ. Sau khi chia tay, Mark Herron công khai đồng tính và bắt đầu hẹn hò nam diễn viên khác.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Judy Garland cưới chồng thứ năm, Mike Deans, tháng 3/1969, ba tháng trước khi bà qua đời. Ảnh: Evening Standard.\" src=\"https://i1-giaitri.vnecdn.net/2020/06/24/Judy-Garland-cu-o-i-cho-ng-cuo-1215-8487-1592982261.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KGwVWXygyI6mh_sNPkz1CA\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:arial; font-size:14px\">Judy Garland cưới chồng thứ năm, Mike Deans, tháng 3/1969, ba tháng trước khi bà qua đời. Ảnh:&nbsp;</span><em>Evening Standard</em><span style=\"font-family:arial; font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mickey Deans - chồng cuối của Judy Garland - cũng là người đẩy bà tới tận cùng tuyệt vọng. Hai người quen khi Garland bỏ lại các con, đến London ca hát để kiếm tiền trả nợ. Deans lúc đó là một chủ hộp đêm, tìm mọi cách tiếp cận minh tinh vì nhìn thấy cơ hội kiếm tiền. Chồng ép Garland biểu diễn dù sức khỏe bà không ổn định. Minh tinh phải tăng tần suất sử dụng các loại thuốc để đảm bảo lịch hát. Bà qua đời chỉ ba tháng sau khi cưới Deans.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong cuốn sách&nbsp;<em>Not the End of the Rainbow</em>, Lorna Luft - con gái Judy Garland - cho biết: \"Khi đi chung xe với Deans trên đường tới&nbsp;đám tang mẹ tôi, ông&nbsp;ấy yêu cầu tài xế đến một nhà xuất bản để ký hợp đồng. Ông ta viết sách về giai đoạn họ sống bên nhau, với tựa&nbsp;đề&nbsp;<em>Weep No More, My Lady: The Best Selling Story of Judy Garland</em>. Ông ta chốt hợp đồng ít giờ trước đám tang mẹ tôi\".&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù có sự nghiệp thành công, kiếm được nhiều tiền, Judy Garland gặp khó khăn tài chính trong những năm cuối đời. Di sản của minh tinh lúc đó chỉ đạt giá trị khoảng 40.000 USD. Các con gái của bà sau này phải trả nợ cho mẹ với sự giúp đỡ của danh ca Frank Sinatra. Họ cho biết bà tiêu tốn tiền vì hào phóng với gia đình, họ hàng,&nbsp;đồng thời thường bị đồng nghiệp, công ty chủ quản lừa gạt khi ký hợp đồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hai con gái của bà là&nbsp;Liza Minnelli và&nbsp;Liza Luft đều theo nghiệp mẹ, thành công tại Hollywood. Con gái cả - Liza - từng đoạt tượng vàng Oscar với phim&nbsp;<em>Cabaret</em>&nbsp;(1972). Cô nói trên&nbsp;<em>Time</em>: \"Cuộc sống của mẹ giống một chiếc dây&nbsp;luôn bị kéo căng. Tôi chưa từng nghĩ bà tìm kiếm hạnh phúc. Mẹ luôn nghĩ hạnh phúc là khi mọi chuyện kết thúc\".</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Đạt Phan</strong></span></p>\r\n</div>', 'Ae8g_8.jpg', 1, 9, 17, '2020-06-24 20:47:04', '2020-06-24 20:47:04');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(9, 'Martial ghi hat-trick trong chiến thắng của Man Utd', 'martial-ghi-hat-trick-trong-chien-thang-cua-man-utd', 'Tiền đạo Anthony Martial toả sáng, giúp \"Quỷ đỏ\" vượt qua Sheffield United 3-0 ở vòng 31 Ngoại hạng Anh, tối 24/6.', '<p><span style=\"font-family:arial; font-size:18px\">Đây là chiến thắng quan trọng của Man Utd trước đối thủ trực tiếp trong cuộc đua&nbsp;top bốn. Nó giúp họ thu hẹp cách biệt với Chelsea còn hai điểm và nối dài chuỗi trận bất bại lên 13. Với Martial, anh là cầu thủ đầu tiên của Man Utd ghi hat-trick tại Ngoại hạng Anh kể từ khi cựu HLV Alex Ferguson giải nghệ. Tiền đạo người Pháp đã ghi 18 bàn trên mọi đấu trường ở mùa này - lần đầu tiên anh làm được điều đó.&nbsp;</span></p>\r\n\r\n<p><img alt=\"Martial là cầu thủ Man Utd đầu tiên ghi hat-trick ở Ngoại hạng Anh thời kỳ hậu Ferguson. Ảnh: Reuters.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/a-7300-1593024190.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=T8mnBSaaM8Op7GIfw_ED5A\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Martial là cầu thủ Man Utd đầu tiên ghi hat-trick ở Ngoại hạng Anh thời kỳ hậu Ferguson. Ảnh:&nbsp;</span><em>Reuters</em><span style=\"font-family:arial; font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Sheffield United không gây ra nhiều khó khăn cho Man Utd. Với một đội hình đang có phong độ cao, Ole Gunnar Solskjaer mạnh dạn sử dụng cả Paul Pogba lẫn Bruno Fernandes từ đầu. Ông cũng để tài năng trẻ Mason Greenwood đá chính ở cánh phải, thay vì Daniel James như mọi khi.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Đội chủ nhà mở tỷ số từ sớm. Cướp được bóng sau pha xử lý bất cẩn&nbsp;của Oliver Norwood, Marcus Rashford dốc bóng xuống sát đường biên rồi căng ngang cho Martial đệm cận thành. Trong một đợt phản công sau đó vài phút, Rashford&nbsp;phối hợp ở&nbsp;tốc độ cao với Martial nhưng lại đệm bóng ra ngoài khung thành trống.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Cầm bóng đến 68% trong nửa giờ thi đấu đầu tiên, những pha hãm thành của Man Utd tiềm ẩn nhiều nguy hiểm với 2 tiền vệ có khả năng kiến tạo tốt - Fernandes và Pogba. Ở một tình huống ban bật trung lộ, Pogba bấm bóng điệu nghệ để Rashford thoát xuống nhưng tiền đạo tuyển Anh lại sút trượt.</span></span></p>\r\n\r\n<p><img alt=\"Hàng thủ Sheffield United không thể đứng vững trước sức ép của Man Utd. Ảnh: AP.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/a-7040-1593027263.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5UXhTJDektVpH_zW2GWAIw\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Hàng thủ Sheffield United không thể đứng vững trước sức ép của Man Utd. Ảnh:&nbsp;</span><em>AP</em><span style=\"font-family:arial; font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối hiệp 1, Man Utd nhân đôi cách biệt. Sau một pha chuyển cánh tấn công từ trái sang phải,&nbsp;Aaron Wan-Bissaka vượt qua Enda Stevens rồi căng ngang. Bóng đi trúng vị trí Martial đang đứng và tiền đạo này nhẹ nhàng đệm bóng, ghi bàn thứ hai. Trước đó, hàng thủ đội khách đã bị rối loạn bởi những pha nhấp nhả trước vòng cấm của Fernandes và Pogba.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Man Utd chủ động đá chậm lại trong hiệp 2. Nhưng họ vẫn duy trì pressing tầm cao, khiến Sheffield United không thể phát triển bóng từ sân nhà như yêu cầu của HLV Chris Wilder. Đội khách buộc phải chơi bóng dài và dễ dàng bị Man Utd hóa&nbsp;giải.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ở khung thành bên kia, Greenwood bỗng trở thành một ngòi nổ nguy hiểm. Cầu thủ này di chuyển khá tự do và liên tục có vị trí tốt để dứt điểm. Sau một pha xoay trở khéo léo trong vòng cấm, Greenwood dứt điểm chân trái nhưng bị hậu vệ đội khách nhoài người cản phá. Không lâu sau, Greenwood bất ngờ có mặt ở cánh trái để phối hợp với Rashford trước khi tung cú sút chân phải vọt xà.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng dù nỗ lực, Greenwood cũng vô duyên như Rashford ở trận này. Và cầu thủ duy nhất của Man Utd có thể ghi bàn là Martial. Pha đánh gót điệu nghệ của Fernandes cùng tình huống phối hợp 1, 2 giữa Martial và Rashford xé toang hàng thủ Sheffield. Phá&nbsp;bẫy việt vị, Martial bấm bóng qua thủ thành Simon Moore, hoàn tất cú hat-trick.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vài phút sau bàn thắng này, Martial được thay ra. Solskjaer thực hiện cùng lúc 5 quyền thay người. Ông đưa James, Andreas Pereira, Juan Mata, Odion Ighalo và Scott McTominay vào sân, cho&nbsp;Pogba, Fernandes, Rashford và Greenwood ra nghỉ. Lúc này, tinh thần của đội khách đã xuống thấp nên Man Utd chỉ dạo chơi trong thời gian còn lại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối trận, Ighalo bỏ lỡ cơ hội ngon ăn sau pha tạt bóng chuẩn xác của Luke Shaw. Chân sút người Nigeria khống chế bóng lỗi và không thể dứt điểm. Tuy nhiên, tỷ số 3-0 đủ để Solskjaer và Martial cười tươi. Ở vòng tới, Man Utd sẽ làm khách của Brighton còn Sheffield United tiếp Tottenham.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Đội hình thi đấu</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Man Utd</em>:&nbsp;De Gea, Wan Bissaka, Lindelof, Maguire, Shaw, Pogba (McTominay 80\'), Matic, Greenwood (James 80\'), Bruno Fernandes (Pereira 80\'), Rashford (Mata 80\'), Martial (Ighalo 80\').</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Sheffield United</em>:&nbsp;Moore, Basham, Robinson, Jagielka, Baldock, Fleck, Norwood (Berge 46\'), Lundstram, Stevens, McGoldrick (Zivkovic 64\'), Mousset (McBurnie 46\').</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Quang Huy</strong></span></p>', 'Ai7y_1.jpg', 1, 3, 24, '2020-06-24 20:51:45', '2020-06-24 20:51:45'),
(10, 'Trốn trại giam bằng 6 lưỡi cưa', 'tron-trai-giam-bang-6-luoi-cua', 'Nguyễn Viết Huy, 34 tuổi, nhờ đàn em đưa 6 lưỡi cưa vào Trại tạm giam Công an Bình Thuận để cắt song sắt, cùng bạn tù tẩu thoát.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Ngày 23/6, Nguyễn Viết Huy - tức Huy \"Nấm Độc\", Nguyễn Văn Nưng (38 tuổi, ở huyện Tuy Phong) và&nbsp;Nguyễn Minh Hoàng (23 tuổi, ở&nbsp;Phan Thiết) bị TAND TP Phan Thiết đưa ra xét xử&nbsp;về tội&nbsp;</span><em>Trốn khỏi nơi giam giữ.</em></span></p>\r\n\r\n<p><img alt=\"Huy nấm độc và Nguyễn Văn Nưng sau khi bị bắt trở lại sau khi trốn trại, tháng 7/2019. Ảnh: Công an cung cấp.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/22/Huy-nam-doc-va-Nung-da-bi-bat-9546-6506-1592826984.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KLfIKEs5bk9vLXJmcd8j9w\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-family:arial; font-size:14px\">Huy (trái) và Nưng bị bắt trở lại sau khi trốn trại, tháng 7/2019. Ảnh:&nbsp;</span><em>Công an cung cấp.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo cáo trạng, tháng 6/2019, Huy và Nưng đang bị giam tại buồng số 5, dãy B, khu B thuộc Trại tạm giam Công an Bình Thuận. Cả hai lần lượt bị điều tra về hành vi&nbsp;<em>Tàng trữ trái phép chất ma túy&nbsp;</em>và&nbsp;<em>Giết người.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Huy và Nưng có ý định trốn trại nên bàn nhau thực hiện. Lợi dụng sơ hở trong công tác quản lý giam giữ, Huy đã nhiều lần thuê điện thoại di động từ Võ Ngọc Thiện (phạm nhân tự giác) để liên lạc ra bên ngoài.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Huy gọi cho đàn em Nguyễn Minh Hoàng, bảo mua lưỡi cưa sắt chuyển vào trại. Sau đó&nbsp;Hoàng nhét 6 lưỡi cưa&nbsp;trong cây chả và&nbsp;nem gói vào một&nbsp;bịch&nbsp;nylon, rồi nhờ người đàn ông&nbsp;nhà ở cạnh trại&nbsp;ném vô trại, nói là đồ ăn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thiện&nbsp;ra nhặt lấy, rồi chuyển vào buồng giam cho Huy và Nưng. Hai phạm nhân này dùng dây và vải quấn các lưỡi cưa lại tạo cán. Để cắt được song sắt cửa thông gió cao 3,25 m, Huy và Nưng đã móc chiếc&nbsp;mùng&nbsp;lên làm võng, rồi thay nhau ngồi lên thực hiện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dự định trong đêm 29 và rạng sáng 30/6/2019 sẽ cưa đứt, nên Huy gọi taxi&nbsp;từ&nbsp;TP HCM ra Phan Thiết chở vào Sài Gòn. Gã&nbsp;nhắn cho đàn em số tài xế và dặn không được tiết lộ việc trốn trại,&nbsp;đồng thời&nbsp;chuẩn bị quần áo, tiền để&nbsp;đón mình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">3h sáng, Nưng và Huy cắt đứt 6 vị trí trên ba thanh sắt, tạo thành lỗ trống hình chữ nhật trên cửa thông gió, cao 27 cm, rộng 17 cm. Cả hai&nbsp;chui ra, dùng&nbsp;chiếc mùng để vượt&nbsp;qua&nbsp;tường rào kẽm gai cao hơn 3 m,&nbsp;rồi&nbsp;lên mái nhà&nbsp;thoát ra đường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lúc này, Hoàng đã cho xe chờ&nbsp;sẵn, đưa đại ca&nbsp;và Nưng đi&nbsp;trốn.&nbsp;Vào TP HCM, Huy&nbsp;trả tiền taxi 2,7 triệu đồng và cho bạn tù&nbsp;500.000&nbsp;đồng để&nbsp;đi hướng khác.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bốn ngày sau, Hoàng bị bắt tại huyện Hóc Môn về hành vi&nbsp;<em>Tàng trữ ma túy trái phép</em>. Tiếp đó, Huy bị bắt tại nhà nghỉ ở TP Mỹ Tho, Tiền Giang. Còn Nưng sa lưới&nbsp;khi&nbsp;ở trong khu rẫy của&nbsp;người quen tại huyện Bảo Lâm,&nbsp;Lâm Đồng, sau 10 ngày lẩn trốn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo VKS, trong vụ vượt ngục này, Huy giữ vai trò cầm đầu. Hành vi của ba bị can thuộc trường hợp phạm tội có tổ chức quy định tại khoản 2 Điều 386 Bộ Luật Hình sự,&nbsp;được xác định rất nguy hiểm, xâm phạm đến chế độ giam giữ, gây trở ngại cho công tác điều tra...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Liên quan đến vụ án, ngày 2/8/2019,&nbsp;đại úy Lê Minh Sơn&nbsp;(35 tuổi, cán bộ Đội cảnh sát bảo vệ trại Trạm giam Công an Bình Thuận) đã bị VKSND tối cao khởi tố, bắt giam về hành vi&nbsp;<em>Lạm dụng chức vụ, quyền hạn, chiếm đoạt tài sản&nbsp;</em>theo Điều 355 Bộ luật Hình sự.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo cơ quan điều tra, tháng 4-6/2019, qua trung gian phạm nhân tự giác, đại úy Sơn đã cho&nbsp;Huy \"Nấm Độc\" và một số phạm nhân&nbsp;khác&nbsp;mướn điện thoại gọi ra ngoài. Vụ này được tách ra xét xử riêng.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Việt Quốc</strong></span></p>', 'PGtZ_1.jpg', 1, 1, 26, '2020-06-24 20:56:40', '2020-06-24 20:56:40'),
(11, 'So sánh học phí ngành Ngoại ngữ các đại học', 'so-sanh-hoc-phi-nganh-ngoai-ngu-cac-dai-hoc', 'Học phí ngành Ngoại ngữ thường 9,8 triệu đồng một năm với hệ đại trà, riêng chương trình chất lượng cao là 14-36 triệu đồng.', '<p><span style=\"font-size:18px\"><strong>1. Đại học Ngoại ngữ, Đại học Quốc gia Hà Nội</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường tuyển sinh các ngành gồm Ngôn ngữ Anh, Nga, Trung, Đức, Nhật, Hàn Quốc và Ả Rập chương trình chuẩn;&nbsp;Ngôn ngữ Anh, Pháp, Trung, Đức, Nhật, Hàn chương trình chất lượng cao.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường thu 280.000 đồng một tín chỉ với sinh viên các ngành ngôn ngữ nước ngoài chương trình chuẩn. Tổng số tín chỉ ngành này là 128 trong bốn năm, trung bình học phí một năm gần 9 triệu đồng. Với chương trình chất lượng cao, học phí một năm là 35 triệu đồng, tổng tín chỉ trong bốn năm là 152.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>2. Đại học Hà Nội</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đại học Hà Nội đào tạo 10&nbsp;ngành ngôn ngữ gồm: Anh, Nga, Pháp, Trung Quốc, Đức, Tây Ban Nha, Bồ Đào Nha, Italy, Nhật, Hàn Quốc. Trừ Ngôn ngữ Trung Quốc, Itay&nbsp;và Hàn Quốc có chương trình chất lượng cao, các ngành còn lại chỉ có chương trình chuẩn. Điểm trúng tuyển các ngành này năm 2019 dao động 25,88-33,85, môn ngoại ngữ nhân hệ số hai.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường thu học phí theo tín chỉ. Các học phần cơ sở ngành, chuyên ngành, thực tập và khóa luận của ngành đào tạo bằng tiếng Anh thu 650.000 đồng/tín chỉ. Các học phần còn lại 480.000 đồng/tín chỉ. Chương trình đào tạo chất lượng cao đối với các ngành ngôn ngữ Trung Quốc, Italy&nbsp;và Hàn Quốc 770.000 đồng/tín chỉ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>3. Đại học Ngoại thương</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bốn ngành Ngôn ngữ Anh, Pháp, Trung Quốc và Nhật chỉ tuyển sinh tại Đại học Ngoại thương trụ sở chính tại Hà Nội. Điểm trúng tuyển các ngành này năm 2019 từ 31,75 đến 34,3.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm học 2020-2021, với các chương trình đại trà, học phí là 18,5 triệu đồng. Các chương trình Chất lượng cao, Kinh doanh quốc tế theo mô hình tiên tiến của Nhật Bản, Kế toán - Kiếm toán định hướng nghề nghiệp ACCA, Logistics và Quản lý chuỗi cung ứng định hướng nghề nghiệp quốc tế 40 triệu đồng một năm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>4. Khoa Ngoại ngữ, Đại học Thái Nguyên</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khoa đào tạo bốn ngành ngôn ngữ gồm Anh, Nga, Pháp, Trung Quốc. Năm ngoái, điểm trúng tuyển của bốn ngành này lần lượt là 18,5, 13, 13 và 20,5. Trường thu học phí theo quy định của Chính phủ, mức 9,8 triệu đồng một năm, được quy đổi ra học phí tín chỉ theo từng chương trình đào tạo.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>5. Đại học Ngoại ngữ, Đại học Huế</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sáu ngành ngôn ngữ tại Đại học Ngoại ngữ, Đại học Huế là Anh, Nga, Pháp, Trung Quốc, Nhật, Hàn Quốc, tuyển khoảng 1.200 sinh viên. Điểm chuẩn năm 2019 các ngành này từ 15 đến 21,5, cao nhất là Ngôn ngữ Hàn Quốc, kế đó là Ngôn ngữ Trung Quốc - 21.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Học phí cho các ngành này 9,8 triệu đồng một năm. Riêng ngành Ngôn ngữ Nga, sinh viên được giảm 50% học phí.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Thí sinh tham dự kỳ thi THPT quốc gia 2019. Ảnh: Giang Huy\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/a796346dbd11594f0000-159297178-5737-2793-1592971993.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9r_vyn6EOVry36ugWLL8jQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-size:14px\"><span style=\"font-family:arial\">Thí sinh tham dự kỳ thi THPT quốc gia 2019. Ảnh:&nbsp;</span><em>Giang Huy</em></span></span></p>\r\n\r\n<p><strong style=\"font-size:18px\">6. Đại học Ngoại ngữ, Đại học Đà Nẵng</strong></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường đào tạo các ngành Ngôn ngữ Anh, Nga, Pháp, Trung Quốc, Nhật, Hàn Quốc, Thái Lan theo chương trình đại trà, trong đó&nbsp;ngôn ngữ Anh, Trung Quốc, Nhật và Hàn Quốc có thêm hệ chất lượng cao. Trừ Ngôn ngữ Anh tuyển 400 sinh viên, các ngành còn lại chủ yếu tuyển 40-80.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm ngoái, Ngôn ngữ Hàn Quốc lấy điểm cao nhất - 23,58, kế đó là Ngôn ngữ Trung Quốc 23,34, còn lại dao động 18,13-22,86.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường thu học phí 9,8 triệu đồng một năm với chương trình đại trà, 14 triệu đồng một năm chương trình chất lượng cao.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>7. Đại học Khoa học Xã hội và Nhân văn, Đại học Quốc gia TP HCM</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường tuyển sinh các ngành ngôn ngữ gồm Anh, Nga, Pháp, Trung Quốc, Đức, Tây Ban Nha, Italy, trong đó duy nhất Ngôn ngữ Anh đào tạo hệ chất lượng cao, tuyển tổng 825 sinh viên. Năm 2019, điểm trúng tuyển dao động&nbsp;mức 19-25,5.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Với chương trình đại trà, học phí là 184.000 đồng/tín chỉ. Tại chương trình chất lượng cao, học phí một năm là 36 triệu đồng, riêng hệ tài năng được miễn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>8. Đại học Sư phạm TP HCM</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sáu ngành ngôn ngữ được đào tạo tại Đại học Sư pham TP HCM gồm Anh, Nga, Pháp, Trung Quốc, Nhật, Hàn Quốc, chỉ tiêu từ 80 đến 200. Năm ngoái, Ngôn ngữ Anh lấy điểm chuẩn cao nhất - 23,25, kế đó là Hàn Quốc 22,75 điểm. Điểm trúng tuyển Ngôn ngữ Trung Quốc và Nhật là 22, còn lại 17,5 cho tất cả tổ hợp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường chưa thông báo học phí năm học 2020-2021 trên website. Năm ngoái, với các học phần khoa học xã hội, sinh viên phải nộp 263.000 đồng/tín chỉ. Các học phần khoa học tự nhiên, ngoại ngữ, giáo dục thể chất, giáo dục quốc phòng là 327.000 đồng/tín chỉ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>9. Đại học Cần Thơ</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường chỉ đào tạo hai ngành ngôn ngữ là Anh và Pháp, riêng Anh có hai chuyên ngành&nbsp;Phiên dịch, biên dịch tiếng Anh. Điểm chuẩn của hai ngành này năm 2019 là 22,5 và 17.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm nay, hệ đại trà học phí một năm là 9,8 triệu đồng, dự kiến năm 2021 tăng lên 10,8 triệu đồng. Với hệ chất lượng cao, các em học chương trình 4,5 năm nộp&nbsp;học phí 27 triệu đồng một năm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>10. Đại học Thăng Long (Hà Nội)</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường tuyển 855 sinh viên tại bốn ngành ngôn ngữ gồm Anh, Trung Quốc, Nhật và Hàn Quốc. Năm ngoái, điểm trúng tuyển ngành Ngôn ngữ Anh là 19,8, thấp nhất trong bốn ngành, còn Ngôn ngữ Trung Quốc cao nhất -&nbsp;21,6.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm nay, học phí ngành Ngôn ngữ Nhật, Hàn quốc là 24 triệu đồng, Ngôn ngữ Anh, Trung Quốc 23 triệu đồng.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Thanh Hằng</strong></span></p>', 'Bagn_1.jpg', 1, 4, 28, '2020-06-24 20:59:10', '2020-06-24 20:59:10'),
(12, 'Một viên thuốc hàng ngày ngăn được HIV', 'mot-vien-thuoc-hang-ngay-ngan-duoc-hiv', 'Điều trị bằng thuốc ARV đến mức ức chế virus dưới 200 bản sao trong một ml máu sẽ ngăn được HIV lây qua đường tình dục.', '<p><span style=\"font-size:18px\">Ông Hoàng Đình Cảnh, Phó Cục trưởng Cục phòng chống HIV/AIDS, Bộ Y tế, cho biết&nbsp; dịch HIV ở Việt Nam đã có những thay đổi trong những năm gần đây. Tỷ lệ nhiễm HIV trong người nghiện chích ma túy và phụ nữ mại dâm giảm, tỷ lệ lây nhiễm HIV trong nhóm nam quan hệ tình dục đồng giới (MSM) và chuyển giới nữ (TGW) vẫn ở mức cao. Tỷ lệ này đang gia tăng, nhất là ở các khu vực đô thị lớn, nguyên nhân chủ yếu là do hành vi tình dục không an toàn trong nhóm MSM và TGW.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việt Nam ghi nhận&nbsp;hơn 200.000&nbsp;người nhiễm HIV còn sống.&nbsp;Hiện chưa có vaccine&nbsp;ngừa HIV/AIDS, có các loại thuốc điều trị và dự phòng.&nbsp;Hơn 140.000 người đang điều trị&nbsp;thuốc kháng virus (ARV)&nbsp;ở khắp các tỉnh thành, trong đó có cả trẻ em. Thuốc đang được bảo hiểm y tế chi trả, gần 200 cơ sở điều trị cung cấp thuốc ARV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo ông Cảnh, Việt Nam là một số ít quốc gia trên thế giới triển khai thí điểm điều trị dự phòng trước phơi nhiễm HIV bằng thuốc kháng virus (PrEP). Chương trình thí điểm tiến hành&nbsp;từ năm 2017, đến nay mở rộng 26 tỉnh, thành phố. Bộ Y tế có kế hoạch mở rộng dịch vụ PrEP ra tất cả các tỉnh, thành phố trong cả nước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">PrEP (Pre-Exposure prophylaxis ) có nghĩa là điều trị dự phòng trước phơi nhiễm HIV. Một người có hành vi nguy cơ cao nhiễm HIV, nếu sử dụng thuốc kháng virus&nbsp;mỗi ngày, sẽ dự phòng, không bị nhiễm HIV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">PrEP là các thuốc có chứa Tenofovir. Ở Việt Nam, thuốc PrEP đang được các chương trình, dự án cấp miễn phí, kết hợp hai loại thuốc ARV là Tenofovir và Emtricitabine (TDF/FTC).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi dùng thuốc hàng ngày, nồng độ thuốc ARV trong máu có thể ngăn chặn không cho virus HIV xâm nhập và nếu chẳng may xâm nhập cũng không nhân lên trong cơ thể. Từ đó dự phòng được lây nhiễm HIV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bộ Y tế khuyến cáo&nbsp;dùng PrEP để dự phòng lây nhiễm HIV cho các nhóm: Nam có quan hệ tình dục đồng giới; Người chuyển giới nữ; Người bán dâm; Người tiêm chích ma túy; Bạn tình của người nhiễm HIV mà người nhiễm HIV đó chưa điều trị ARV hoặc điều trị ARV nhưng tải lượng HIV chưa đạt dưới ngưỡng phát hiện virus&nbsp;(hơn 200 bản sao trong một ml máu).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không phải ai có nguy cơ lây nhiễm HIV cũng dùng được PrEP. Những người không sử dụng được PrEP gồm: Người có HIV dương tính hoặc chưa xác định được; Người có dấu hiệu hoặc triệu chứng của nhiễm HIV cấp tính; Những người rối loạn chức năng thận; Người dị ứng với thuốc (Tenofovir và Emtricitabine); Người phơi nhiễm với HIV trong 72 giờ qua.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi một người&nbsp;có hành vi nguy cơ cao bị nhiễm HIV như quan hệ tình dục không an toàn (không sử dụng bao cao su) và tiêm chích không an toàn (sử dụng chung bơm kim tiêm), nên đến cơ sở cung cấp dịch vụ PrEP.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thuốc PrEP uống hàng ngày, mỗi ngày một viên. Có thể uống trước hoặc sau khi ăn. Các bác sĩ khuyến cáo nên uống vào một thời điểm nhất định để tạo thói quen uống thuốc đều đặn. Nếu quên liều, cần uống ngay khi nhớ ra. Tuy nhiên không được uống quá hai liều trong 24 giờ. PrEP không tương tác với đa số thuốc khác nên an toàn khi uống cùng với nhau.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hầu hết người dùng PrEP không gặp tác dụng phụ nào nghiêm trọng.&nbsp;Tuy nhiên, một số ít người có thể gặp phải một số tác dụng phụ như: buồn nôn, tiêu chảy, đau bụng, chóng mặt, đau đầu, chán ăn... Thông thường, các phản ứng phụ này chấm dứt sau một đến hai tuần. Nếu những biểu hiện này kéo dài và ảnh hưởng đến sinh hoạt, phải liên hệ bác sĩ để tham vấn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">PrEP là&nbsp;phương án dự phòng, được Tổ chức Y tế Thế giới khuyến cáo và nhiều quốc gia thực hiện từ nhiều năm nay. Phương pháp này không thay thế được vaccine HIV nhưng là cách đơn giản nhất, có thể giảm tới hơn 90% nguy cơ lây nhiễm HIV trong nhóm MSM.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vì vậy, Tổ chức Y tế Thế giới khuyến cáo PrEP nên được cung cấp cho những người nguy cơ nhiễm HIV cao, trong gói dự phòng HIV kết hợp.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Lê Nga</strong></span></p>', 'vYQD_1.jpg', 1, 6, 32, '2020-06-24 21:00:41', '2020-06-24 21:01:35'),
(13, 'Giới trẻ Hàn Quốc nghĩ \'sinh con là không cần thiết\'', 'gioi-tre-han-quoc-nghi-sinh-con-la-khong-can-thiet', 'Báo cáo của Tổng cục thống kê tại Hàn Quốc cho biết, 3 trong số 10 người trẻ được hỏi cho rằng việc kết hôn không nhất thiết phải sinh con.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Bản báo cáo dựa trên kết quả của hàng loạt các cuộc điều tra và nghiên cứu, chỉ khoảng 69,6% những người tham gia bỏ phiếu nói rằng đẻ con sau khi kết hôn là cần thiết. 30,4% không nghĩ như vậy.</span><img alt=\"Giới trẻ Hàn Quốc ngày càng tỏ ra sợ sinh con. Ảnh: Koreatimes.\" src=\"https://i1-giadinh.vnecdn.net/2020/06/24/Han-Quoc-9384-1592997333.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DgmjPtXfXNWFgTsdaCxfwg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-size:14px\"><span style=\"font-family:arial\">Giới trẻ Hàn Quốc ngày càng tỏ ra \"sợ\" sinh con. Ảnh:&nbsp;</span><em>Koreatimes.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hàn Quốc là một trong những quốc gia có tỷ lệ sinh thấp nhất thế giới. Tổng cục Thống kê&nbsp;(trực thuộc Bộ Kinh tế và Tài chính) dự báo dân số của nước này đạt đỉnh khoảng 52 triệu người vào năm 2028, và&nbsp;bắt đầu giảm xuống. Thống kê ước tính, dân số Hàn Quốc sẽ giảm xuống mức thấp nhất là 39,3 triệu dân - năm 2067. Tính đến cuối 2019, dân số Hàn Quốc là&nbsp;51,7 triệu người.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Báo cáo năm 2018 cũng cho thấy độ tuổi trung bình của phụ nữ sinh con lần đầu ở Hàn Quốc là 31,9 tuổi, tăng 0,3 tuổi so với một năm trước đó. Điều này cũng phản ánh động thái hướng tới sự giảm dân số. Ngoài ra, khảo sát theo giới tính cho thấy, 33,4% phụ nữ&nbsp;cho biết họ không nghĩ rằng có con là cần thiết, trong khi với nam giới, con số này là 27,4%.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thực tế ở Hàn Quốc cho thấy, một người có học vấn càng cao, người đó càng có khả năng không muốn&nbsp;sinh con. Trong một khảo sát, 36,2% người có trình độ Đại học trở lên cho biết họ không nghĩ rằng việc có con là cần thiết, trong khi 29,3% người có trình độ học vấn trung học có quan điểm như vậy. Báo cáo cũng đưa ra một thực trạng: Gánh nặng chi phí giáo dục tư nhân của trẻ (học thêm, các chương trình ngoại khóa... ) là lý do chính khiến tỷ lệ sinh giảm, do điều này \"làm gia tăng gánh nặng cho các phụ huynh\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại Hàn Quốc, năm 2019, 74,8% học sinh từ cấp ba trở xuống, tới tiểu học, có tham gia vào các chương trình giáo dục tư nhân, tăng 2% so với năm 2018. Trung bình, các bậc cha mẹ bỏ ra khoảng 321.000 won (tương đương 266 USD) cho&nbsp;giáo dục tư nhân cho mỗi trẻ, mỗi tháng. Nhiều cha mẹ \"thắt lưng buộc bụng\" để chi trả chi phí học hành của con cái. Đây được cho là một trong những sức ép lớn, dẫn đến việc nhiều người trẻ hình thành quan điểm không sinh con cái, kể cả sau khi đã kết hôn.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thùy Linh</strong><em>&nbsp;(Theo Koreantimes)</em></span></p>', 'R11U_1.jpg', 1, 1, 38, '2020-06-24 21:07:11', '2020-06-24 21:07:11'),
(14, 'Công viên bỏ hoang ở Huế lên báo Mỹ', 'cong-vien-bo-hoang-o-hue-len-bao-my', 'THỪA THIÊN - HUẾ - Insider chọn Hồ Thủy Tiên là một trong những công viên nước bỏ hoang có câu chuyện thú vị nhất và vẫn hút khách dù đóng cửa từ lâu.', '<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/22/weeatfish-1592823003.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TaktRBRd9zYpmnvkKfw04A\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Hồ Thủy Tiên</strong>&nbsp;ở Huế&nbsp;là một trong những công viên nước bỏ hoang nổi tiếng nhất thế giới. Rất nhiều khách du lịch bụi tới Việt Nam biến&nbsp;nó thành điểm check-in độc đáo.&nbsp;Ảnh:&nbsp;<em>Weeatfish</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công viên này tốn đến 70 tỉ đồng&nbsp;để xây dựng và mở cửa từ năm 2004 nhưng không thu hút được nhiều khách nên dừng hoạt động sau đó vài năm. Ngày nay, Hồ Thủy Tiên&nbsp;bị bao phủ bằng các bức tranh tường graffiti nhiều màu và cây cối mọc lên um tùm. Tuy vậy, công viên này lại trở thành&nbsp;điểm đến hấp dẫn khách tây và khách du lịch bụi Việt Nam&nbsp;thích mạo hiểm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/23/5b564bde51dfbe20008b4627-1592882411.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bxLh3gw3WQYe84MBNyZBKQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Công viên nước&nbsp;</span><strong>Safari Lagoon</strong><span style=\"font-family:arial\">&nbsp;xây dựng ngay trên nóc một trung tâm thương mại&nbsp;ở Pandan, Selangor, Malaysia. Công trình được quảng bá là công viên nước rộng nhất Đông Nam Á vào thời điểm mở cửa năm 1998. Tuy nhiên tới năm 2007, công viên đóng cửa và bị bỏ hoang đến nay. Safari Lagoon đóng vì&nbsp;hoạt động nhiều năm mà không có giấy phép và&nbsp;một nhân viên tử vong khi bị kẹt trong máy bơm áp suất cao. Ảnh:&nbsp;</span><em>neoseven07/youtube.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/22/1-1592824283.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WQ3o1gHJyuIQjmKlHtjMnA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Hồ Dolores</strong>&nbsp;là công viên nước tư nhân có&nbsp;từ đầu thập niên 1960 ở&nbsp;Newberry Spring, bang California, Mỹ. Người chủ xây công trình cho gia đình của ông&nbsp;và đặt theo tên vợ mình.&nbsp;Các máng trượt và những trò chơi mới&nbsp;dần được xây dựng thêm trong các năm sau và đến đầu thập niên 1970, Hồ Dolores nổi tiếng&nbsp;hơn và thu hút đông du khách. Tuy nhiên,&nbsp;tới năm&nbsp;1980 sức hút của nơi này giảm dần và công viên phải đóng cửa.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mở lại vào năm 1998 Hồ Dolores đổi tên thành Rock-A-Hoola với phong cách mới,&nbsp;nhưng nơi này cũng không tồn tại được lâu. Một nhân viên ở đây bị tai nạn dẫn tới tàn tật làm công viên&nbsp;hoạt động giảm sút. Lần cuối được sửa đổi của&nbsp;Rock-A-Hoola vào năm 2002 - 2004 rồi đổi tên&nbsp;Discovery Waterpark.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiện tại các hồ bơi trong công viên kín đặc hình graffiti&nbsp;tạo cảm hứng cho nhiều nhà làm phim. Năm 2012 đạo diễn phim \"Kilian Martin: Altered Route\" chọn nơi này làm bối cảnh, năm 2015 một clip giới thiệu xe Mini Cooper cũng quay tại đây.&nbsp;Ảnh:&nbsp;<em>Flickr.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/23/3038501686-94653a3e51-o-1592882090.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tCy7IChyYKS0pYSd4x130g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Các máng trượt nước&nbsp;</span><strong>Ebenezer Floppen Slopper&nbsp;</strong><span style=\"font-family:arial\">ở Oakhrook Terrace, bang Illinois, Mỹ, đón khách&nbsp;từ đầu thập niên 1980 và đóng cửa từ năm 1989. Công viên có nhiều máng trượt với độ dốc và địa hình nguy hiểm cho người chơi này&nbsp;nằm ở ngoại ô Chicago. Sau khi đóng cửa, toàn bộ khu vực tràn ngập lá khô, cây cối mọc che kín các lối đi. Ảnh:&nbsp;</span><em>Reddit.&nbsp;</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/23/Fun-park-Fyn-4-of-23-1592883211.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=c_XQdfsDxb4d7Qjmut8YJA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Fun Park Fyn</strong><span style=\"font-family:arial\">&nbsp;mở cửa vào những năm 1980 ở Aarup, Funen, Đan Mạch. Như nhiều công viên khác, nơi này phải đóng cửa do phá sản vào năm 2006. Các khu vui chơi sơn màu rực rỡ&nbsp;nhưng sau nhiều năm&nbsp;bị bỏ hoang làm nơi này càng thêm ảm đạm. Có người đồn&nbsp;Fun Park Fyn sẽ được cải tạo thành công viên chủ đề Hans Christian Andersen vì tác giả nổi tiếng&nbsp;sinh ra ở đây, nhưng đó là chuyện chưa từng có.&nbsp;Ảnh:&nbsp;</span><em>Claus Larsen.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/23/Aquatic-Paradise-The-Slides.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3EwAD68xhLWss87BpeIk9Q\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Công viên</span><strong>&nbsp;L\'Aquatic Paradis</strong><span style=\"font-family:arial\">&nbsp;ở Sitges, Tây Ban Nha, đón khách&nbsp;từ đầu thập niên 1990 nhưng sau 2 năm hoạt động,&nbsp;một tai nạn khủng khiếp&nbsp;xảy ra làm một trẻ em tử vong.&nbsp;Vụ việc đó kèm những khoản nợ lớn khiến công viên phải đóng cửa. Công viên bỏ hoang lại biến thành một tổ hợp văn hóa,&nbsp;là nơi tụ họp của các nghệ sĩ graffiti, dân chơi ván trượt. Ảnh:&nbsp;</span><em>Shanomag/Wiki.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/23/pinterest-1592885384.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=N9cXl2Dnztx-MtpSCeb_6Q\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Atlantis Marine&nbsp;</strong><span style=\"font-family:arial\">bắt đầu đón khách vào năm 1981 và kỳ vọng là điểm đến thay thế cho Gold Coast - nơi có những bãi biển đẹp nổi tiếng của Australia. Công viên&nbsp;gồm nhiều bể bơi, thuyền đạp vịt, và các show diễn cá heo hấp dẫn&nbsp;nhưng phải đóng cửa vào năm 1990 vì vấn đề tài chính. Các bức tượng trong Atlantis Marine&nbsp;hiện vẫn còn nhiều. Riêng&nbsp;tượng thần Neptune nhờ một đơn thỉnh cầu online&nbsp;đã được cải tạo vào năm 2015.&nbsp;Ảnh:&nbsp;</span><em>Pinterest.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-dulich.vnecdn.net/2020/06/23/Vineland-Station-1592887401.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3steNFPoQY3DYOMJL5mNTA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Công viên</span><strong>&nbsp;Wet n\'&nbsp;Wild</strong><span style=\"font-family:arial\">&nbsp;ở Vineland Station, Ontario, Canada, được xây từ thập niên 1960 bao gồm rất nhiều trò chơi giải trí như&nbsp;thuyền đụng, xe đụng, máng trượt, sân trượt ván, bãi cát... Từng là nơi vui chơi hấp dẫn,&nbsp;thu hút chủ yếu&nbsp;nhóm khách gia đình, Wet n\' Wild đóng cửa vào năm 2002 sau hơn 30 năm hoạt động. Sau đó các công trình ở đây thành nơi lý tưởng cho các họa sĩ graffiti thỏa sức sáng tạo.&nbsp;Ảnh:&nbsp;</span><em>Bruce Forsyth.&nbsp;</em></span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Khánh Trần</strong><span style=\"font-family:arial\">&nbsp;(theo&nbsp;</span><em>Insider</em><span style=\"font-family:arial\">)</span></span></p>', 'FSrr_1.jpg', 1, 0, 42, '2020-06-24 21:17:30', '2020-06-24 21:17:30'),
(15, 'Tiến sĩ nghèo theo đuổi ước mơ điều trị bệnh mất trí nhớ', 'tien-si-ngheo-theo-duoi-uoc-mo-dieu-tri-benh-mat-tri-nho', 'Từng có ý định bỏ học để gánh nợ cho gia đình, Võ Văn Giàu trở thành tiến sĩ với những nghiên cứu bậc nhất về bệnh Alzheimer tại Hàn Quốc.', '<p><span style=\"font-size:18px\">Hai tháng qua, TS Võ Văn Giàu (34 tuổi), nhận được lời mời tham dự nhiều diễn đàn khoa học nghiên cứu quốc tế về bệnh Alzheimer với vai trò là diễn giả. Nghiên cứu căn bệnh mất trí nhớ tại Đại học Gachon, Hàn Quốc, đến nay TS Giàu có nhiều công trình ứng dụng rộng rãi trong hỗ trợ sàng lọc và chẩn đoán bệnh tại nhiều bệnh viện lớn Hàn Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tại hội nghị quốc tế của Hiệp hội Alzheimer tổ chức vào tháng 7 sắp tới, tôi sẽ trình bày kết quả nghiên cứu thiết kế bản đồ gene của mình với hy vọng công trình có thể được ứng dụng tại nhiều quốc gia\", anh nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"TS Võ Văn Giàu tại phòng thí nghiệm. Ảnh: NVCC.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/19/TS-Giau-2766-1592560503.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dj5zrdlN8XloqhuaHvW1yg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-size:14px\"><span style=\"font-family:arial\">TS Võ Văn Giàu tại phòng thí nghiệm. Ảnh: NVCC.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sinh ra trong gia đình có 10 anh chị em&nbsp;ở vùng quê tỉnh Quảng Nam, bố mẹ anh chỉ có vài sào ruộng, rẫy khoai sắn để trang trải. Từ nhỏ đã bị ám ảnh những lần có người đến nhà siết nợ vì mùa màng thất thu. Thương bố mẹ làm lụng vất vả, vay mượn khắp nơi để có tiền cho 10 anh chị em ăn học, Giàu nhiều lần&nbsp;muốn nghỉ học để phụ giúp, gánh nợ cho gia đình. \"Nhưng nghe tôi nói câu đó xong, bố mẹ đều giận và kiên quyết không để ai trong nhà phải nghỉ học, chỉ có học mới có thể thoát nghèo được\", anh nhớ lại.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nghe lời bố mẹ, Giàu cố gắng thi đỗ vào trường THPT ở huyện. Không có điều kiện học thêm vì phải để giành tiền chữa bệnh cho bố, Giàu ở nhà vừa tự học, vừa phụ giúp ruộng nương. Những hôm được nghỉ, Giàu thường theo anh trai khi đó là sinh viên Đại học Y,&nbsp;đi tìm những cây cỏ, cây thuốc trong làng. Được anh trai giới thiệu những vị thuốc, Giàu không nghĩ chúng lại có công dụng \"thần kỳ\" trong điều trị bệnh đến vậy.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Có sẵn niềm đam mê với Sinh học và Hóa học, tốt nghiệp cấp ba, năm 2005, chàng trai quê Quảng Nam&nbsp;chọn ngành Công nghệ Sinh học hướng Y Sinh, trường Đại học Tôn Đức Thắng với mong muốn tìm hiểu, đi sâu nghiên cứu vai trò các loại cây thảo dược trị bệnh. Từ đây, con đường nghiên cứu khoa học dần mở ra khi anh có ba công trình nghiên cứu trong nước về sản phẩm tự nhiên có hoạt tính sinh học.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Đó là ba công trình tạo tiền đề phát triển các nghiên cứu của tôi theo hướng này cho đến hiện tại\", anh nói. Sau đó, anh trở thành giảng viên Đại học Công nghệ Thực phẩm HCM để tiếp nối lửa đam mê nghiên cứu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trở về quê trong một kỳ nghỉ, một người họ hàng xa hỏi anh về cách chữa trị và phục hồi bệnh mất trí nhớ bằng dược liệu. Nghiên cứu nhiều các loại thảo dược, lần đầu tiên anh&nbsp;bối rối trước câu hỏi chuyên môn này. Nhận thấy căn bệnh này tại Việt Nam phổ biến trong khi chưa có nhiều nghiên cứu về Alzheimer, chưa có phác đồ điều trị hiệu quả, dứt điểm, anh tìm hiểu kỹ hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những ý tưởng nghiên cứu về bệnh mất trí nhớ bắt đầu hình thành, trao đổi với một giáo sư người Mỹ gốc Hàn, anh nhận được học bổng toàn phần cho chương trình tiến sĩ Y sinh học tại Đại học Gachon, Hàn Quốc năm 2014.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những ý tưởng nghiên cứu về bệnh mất trí nhớ bắt đầu hình thành, trao đổi với một giáo sư người Mỹ gốc Hàn, anh nhận được thư chấp nhận cấp học bổng toàn phần cho chương trình tiến sĩ Y sinh học tại Đại học Gachon, Hàn Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhận được thông báo trúng học bổng, anh lo nhiều hơn mừng vì khi đó vừa lập gia đình,&nbsp;con trai mới 5 tháng tuổi và đi ở trọ. Nếu anh đi,&nbsp;vợ và con sẽ vất vả nhiều. Nhưng ở lại,&nbsp;những ý tưởng, dự án nghiên cứu mà anh tâm đắc nhất sẽ không có khả năng thực hiện. Anh Giàu vò đầu suy nghĩ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nếu em muốn, anh sẽ ở lại Việt Nam với con và em, chỉ cần nghiên cứu trong nước\", anh Giàu hỏi ý kiến vợ. \"Anh cứ yên tâm hoàn thành chương trình, mọi việc ở nhà có em lo\", vợ anh động viên và mong các dự án nghiên cứu của anh được thực hiện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lời động viên của vợ như tiếp sức mạnh, năm 2014, anh Giàu đặt chân sang xứ Hàn nghiên cứu, quyết tâm đi tìm lời giải căn bệnh Alzheimer. Để hoàn thành chương trình Tiến sĩ, anh phải học hơn 12 môn với 42 đơn vị học trình trong suốt ba năm bên cạnh hoàn thành các dự án nghiên cứu. Thời gian gọi điện về nhà lấp kín bằng những cuộc họp với các bác sĩ, chuyên gia để đánh giá và lên kế hoạch cho dự án. Chìm vào công việc nghiên cứu, anh lỡ hẹn những lần về thăm gia đình, không thể dự đám cưới của anh trai&nbsp;vì phải ở lại làm cho kịp tiến độ dự án.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vào buổi chiều tháng 6/2014, anh đã thuyết phục được hội đồng chuyên môn Hàn Quốc về tính khả thi của dự án thiết kế bản đồ gene giúp chẩn đoán và phát hiện sớm bệnh Alzheimer. Sau hai năm, anh khoanh vùng được 100 kiểu gene có liên quan đến Alzheimer từ 20.000 gene trong nhiễm sắc thể người.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Quá trình thiết kế bản đồ gene gặp khó khăn khi thu thập, phân tích và đối chiếu kiểu hình thực tế từng người bệnh. Vì đây là hướng nghiên cứu hệ thần kinh trung ương với cơ chế phức tạp, trực tiếp lên người bệnh, cần hợp tác với rất nhiều bệnh viện. \"Bên cạnh tìm đủ được số lượng mẫu, việc dùng các thuật toán và chứng minh con đường gây bệnh của từng gene tương ứng trên từng kiểu hình của bệnh nhân, để mô tả các con đường sinh bệnh liên quan là công việc tốn khá nhiều thời gian và công sức\", anh Giàu chia sẻ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Để khắc phục và tối ưu hóa bản đồ gene cho phù hợp với từng kiểu hình của bệnh nhân, TS Giàu sử dụng nền tảng giải trình tự gene thế hệ mới (NGS), kết hợp với số lượng mẫu đa dạng được thu thập, dành hết quỹ thời gian trong phòng thí nghiệm kể cả các ngày lễ tết. Một năm sau, TS Giàu hoàn thành dự án lập bản đồ 30 kiểu gene liên quan trực tiếp tới chẩn đoán bệnh Alzheimer.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kết quả này được cấp sáng chế tại Hàn Quốc và đang được ứng dụng phát triển thành bộ Kit trong phát hiện và sàng lọc ở một số bệnh viện Hàn Quốc. Với những thành quả nghiên cứu nổi bật, TS Giàu trở thành thành viên nhóm nghiên cứu bậc nhất về Alzheimer tại Hàn.&nbsp;Quỹ Nghiên cứu Quốc gia Hàn Quốc cũng cấp kinh phí hỗ trợ 100 triệu won (khoảng 2 tỷ đồng) trong ba năm, bắt đầu từ 2019, để nhóm phát triển các giải pháp chẩn đoán phân tử và ứng dụng liệu pháp gene điều trị bệnh mất trí nhớ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Song song với việc nghiên cứu ở&nbsp;nước ngoài, TS Giàu đã sáng lập, vận hành giữ vai trò&nbsp;Trưởng nhóm nghiên cứu công nghệ nano sinh học thuộc Trường Đại học Tôn Đức Thắng, kết nối những chương trình hợp tác của sinh viên với các chuyên gia nước ngoài. Nghiên cứu bước đầu&nbsp;xác định hợp chất quý trong cây thanh trà&nbsp;Việt Nam có tiềm năng làm thuốc điều trị bệnh Alzheimer là một trong những kết quả cho sự hợp tác này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 2019 anh giành giải thưởng Quả cầu vàng cho thanh niên nghiên cứu khoa học công nghệ.&nbsp;Đến bây giờ, ký ức về cái nghèo, bữa đói, bữa no vẫn ám ảnh. Anh bảo, trong nhà cả 10 anh chị em, ai cũng sợ cảnh nghèo khó. Vì vậy điều mà bố mẹ răn dạy là chỉ có cách học mới thoát ra được luôn được ghi nhớ mỗi khi gặp khó khăn. Có lẽ cũng vì vậy cả anh và&nbsp;các anh em trong nhà ai cũng học rất giỏi. Có người tốt nghiệp tiến sĩ Y khoa, nay đã là Giám đốc một bệnh viện lớn tại Hà Nội,&nbsp;người làm luật sư, kỹ sư...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cho rằng mình vẫn còn \"xanh\" để đứng nghiên cứu độc lập khi về nước, TS Giàu muốn tiếp tục theo các kiến thức chuyên sâu, sau đó mang những kết quả nghiên cứu và công nghệ học hỏi được để có thể ứng dụng ở quê hương, đặc biệt là những nghiên cứu về bệnh Alzheimer\", TS Giàu nói và luôn tâm niệm \"được ra đi, để được trở về\".&nbsp;</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Nguyễn Xuân</strong></span></p>', 'EI6h_1.jpg', 1, 1, 47, '2020-06-24 21:21:26', '2020-06-24 21:21:26'),
(16, 'Thung lũng Silicon \'dậy sóng\' vì chính sách visa của Trump', 'thung-lung-silicon-day-song-vi-chinh-sach-visa-cua-trump', 'Chính sách đóng băng visa H-1B ảnh hưởng trực tiếp đến các lập trình viên, khiến các \"đại gia\" công nghệ ở thung lũng Silicon phản đối kịch liệt.', '<p><span style=\"font-size:18px\">Ngày 22/6, Tổng thống Trump ký sắc lệnh ngừng cấp thẻ xanh ngoài lãnh thổ Mỹ tới ngày 31/12, đồng thời \"đóng băng\" nhiều thị thực lao động. Quyết định&nbsp;này&nbsp;sẽ ảnh hưởng trực tiếp đến những lao động tay nghề cao trong lĩnh vực công nghệ và gia đình họ, những người đang hưởng&nbsp;chính sách visa H-1B.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Quyết định của Trump ngay lập tức khiến thung lũng Silicon dậy sóng, bởi 3/4 thị thực H-1B của Mỹ được cấp chuyên gia và kỹ sư trong ngành công nghệ. Theo thống kê từ Cơ quan Nhập tịch và Di trú Mỹ (USCIS), 39% kỹ sư phần mềm, 27% lập trình viên máy tính và 28% công việc kỹ thuật điện của Mỹ là người nhập cư.</span></p>\r\n\r\n<div class=\"fig-picture\" style=\"margin: 0px; padding: 0px 0px 480px; box-sizing: border-box; text-rendering: optimizelegibility; width: 669.6px; float: left; display: table; justify-content: center; background: rgb(240, 238, 234); position: relative;\"><span style=\"font-size:18px\"><img alt=\"Các kỹ sư gốc Ấn sẽ là những người bị ảnh hưởng nhiều nhất bởi chính sách đóng băng visa H-1B của Tổng thống Trump. Ảnh:&nbsp;Uttam Ghosh.\" class=\"lazied lazy\" src=\"https://i1-sohoa.vnecdn.net/2020/06/24/Visa-H-1B-1231-1592990172.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mP5CPqi-E3zDxDpwiByWYg\" style=\"border:0px; box-sizing:border-box; font-size:0px; left:0px; line-height:0; margin:0px; max-height:700px; max-width:100%; padding:0px; position:absolute; text-rendering:optimizelegibility; top:0px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Kỹ sư tay nghề cao từ lâu đã là một phần không thể thiếu của Thung lũng Silicon, tuy nhiên, visa H-1B của những người làm việc tại Mỹ vừa bị Tổng thống Trump ngừng cấp/xét duyệt. Ảnh:&nbsp;<em>Uttam Ghosh.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các công ty công nghệ ở Thung lũng Silicon phần lớn tuyển dụng&nbsp;nhân tài nước ngoài qua chính sách visa H-1B. Mỗi năm&nbsp;có hàng chục nghìn tài năng nước ngoài hoặc sinh viên quốc tế vượt qua vòng xét thị thực này để đầu quân cho các công ty công nghệ ở đây. Visa H-1B từng được coi là nền tảng dự trữ tài năng công nghệ của nước Mỹ. Kỹ sư nước ngoài là một nửa của sự đổi mới ở Thung lũng Silicon. Dưới thời Obama, nước Mỹ đã đưa ra nhiều chính sách visa hấp dẫn để giữ chân các nhân tài công nghệ, nhưng&nbsp;Trump đang phá bỏ hết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giới chức Mỹ cho biết, chính sách thị thực mới sẽ khiến 525.000 lao động nước ngoài không có cơ hội đến Mỹ, ít nhất tới hết năm nay, tương đương với từng đó việc làm sẽ được dành cho người Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Câu hỏi đặt ra là, liệu những người Mỹ thất nghiệp có thể đảm nhận được những công việc này không?&nbsp;Câu trả lời&nbsp;là khó. Bởi phần lớn những người nhận thị thực H-1B là những lao động chất lượng cao, được đào tạo và làm việc trong ngành công nghệ cao, không thể thay thế được bởi những người Mỹ thất nghiệp thông thường.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Những người đứng đầu các công ty công nghệ lớn ở Thung lũng Silicon đồng loạt phản đối sắc lệnh mới này.&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">CEO Pichai của Google, người từng làm việc tại Mỹ theo diện visa H-1B, viết trên Twitter: \"Người nhập cư đã đóng góp vào thành công kinh tế của Mỹ, đưa Mỹ thành quốc gia dẫn đầu thế giới về công nghệ và cũng góp sức giúp Google được như hôm nay. Dù thất vọng bởi tuyên bố hôm nay, chúng tôi vẫn&nbsp;tiếp tục đứng cạnh những người nhập cư và nỗ lực tạo thêm cơ hội cho tất cả\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Amazon gọi chính sách đóng băng thị thực H-1B của Trump là \"thiển cận\".&nbsp;\"Đón nhận những&nbsp;tài năng sáng giá&nbsp;trên khắp thế giới là cách tốt nhất để Mỹ phục hồi kinh tế. Chúng tôi sẽ tiếp tục các chương trình hỗ trợ và bảo vệ quyền của người nhập cư\", đại diện công ty nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tim Cook, CEO của Apple, lên tiếng: \"Giống Apple, quốc gia nhập cư này luôn tìm thấy sức mạnh trong sự đa dạng. Chúng tôi hy&nbsp;vọng vào lời hứa lâu dài với&nbsp;\'giấc mơ Mỹ\'. Tôi thật sự thất vọng về tuyên bố này\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đại diện Facebook nói Tổng thống Trump chỉ lấy Covid-19 làm cái cớ cho chính sách hạn chế nhập cư. Thực tế, việc đẩy các tài năng có tay nghề cao ra khỏi Mỹ sẽ khiến việc phục hồi đất nước khó khăn hơn. Mỹ là quốc gia của những người nhập cư. Đất nước được hưởng lợi từ việc khuyến khích người tài từ khắp nơi trên thế giới về đây sinh sống và làm việc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đại diện của Microsoft, Tesla, Twitter, PayPal, Box... đều phản đối gay gắt sắc lệnh&nbsp;này&nbsp;và gọi đây là \"chính sách tồi tệ không thể chấp nhận được\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hùng Trần, Founder của Got It, một startup Việt Nam có trụ sở tại Thung lũng Silicon, nhận định: \"Với các công ty công nghệ, tài sản lớn nhất và vũ khí cạnh tranh quan trọng&nbsp;nhất là nhân tài. Hiện các công ty công nghệ vẫn trong chế độ làm việc từ xa nên điều này chưa ảnh hưởng nhiều. Nhưng khi mọi thứ quay trở lại bình thường mà không thu hút được người giỏi thì sẽ là thách thức rất lớn với các công ty\".&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo thống kê của USCIS, các công ty công nghệ và dịch vụ sẽ bị ảnh hưởng nặng&nbsp;nhất nếu chính sách visa H-1B thành hiện thực. Đầu tiên là Google. Trong năm 2019 công ty đã nộp 10.577 đơn xin visa H-1B. Tiếp đến là Amazon với 7.705 hồ sơ, IBM đứng vị trí thứ ba với 7.237 đơn, tiếp đến là Microsoft (6.014), Facebook (3.212) và Apple (1.708).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công dân từ Ấn Độ và Trung Quốc là những người bị ảnh hưởng lớn nhất. 74,55% visa H-1B trong năm 2019 được cấp cho người Ấn Độ, 11,5% là người Trung Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mặc dù chính sách thị thực của Trump khiến các \"đại gia\" công nghệ ở Thung lũng Silicon tức giận, họ không thể thay đổi được quyết định của Nhà Trắng. Các chuyên gia phân tích nhận định, chính sách visa với người nhập cư có thể được siết chặt hơn trong vài tháng tới khi cuộc bầu cử Tổng thống đang đến gần. \"Cơn ác mộng\" này có thể chỉ kết thúc vào tháng 11 khi cuộc tranh cử kết thúc.chính&nbsp;</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Khương Nha</strong></span></p>', 'f4Fg_1.jpg', 1, 1, 52, '2020-06-24 21:23:51', '2020-06-24 21:23:51');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(17, 'Những dấu hiệu cho biết điều hòa cần bảo dưỡng', 'nhung-dau-hieu-cho-biet-dieu-hoa-can-bao-duong', 'Kém mát, mùi lạ, có gió nhưng không mát là những biểu hiện cho thấy cần bảo dưỡng hệ thống điều hòa.', '<p><span style=\"font-size:18px\">Trong mùa nóng, hệ thống điều hòa&nbsp;rất dễ phát sinh các vấn đề. Dưới đây là những dấu hiệu cho thấy hệ thống điều hòa&nbsp;cần phải bảo dưỡng, theo lời khuyên từ các chuyên gia kỹ thuật:</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Xe đi&nbsp;trong ba&nbsp;năm trở lên và chưa từng&nbsp;bảo dưỡng&nbsp;hệ thống điều hòa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Quạt gió kêu to nhưng không thấy gió vào, chỉ đẩy hơi, hơi mát kém và có thể kèm theo mùi khó chịu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Xe phải nổ máy và lăn bánh 10 phút trở lên mới có hơi lạnh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Điều hoà lạnh, nhưng&nbsp;không sâu và làm mát kém so với thông thường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Xe dừng hoặc chờ đèn đỏ thì&nbsp;không có hơi mát, chỉ khi xe di chuyển mới có hơi lạnh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Điều hoà bật lên mát ngay nhưng chỉ một lúc sau lại là hơi nóng, nếu tắt điều hoà vài phút bật lên lại có hơi lạnh và hiện tượng này xảy ra thường xuyên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">-&nbsp;Thường xuyên có mùi lạ dù có lấy gió trong, hơi lạnh có độ ẩm cao hoặc quá khô.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những biểu trên có thể đến từ việc hao hụt ga, lọc gió điều hoà bị bẩn, quạt tản nhiệt cục nóng, lọc gió bẩn.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Đoàn Dũng</strong></span></p>', 'aRW4_1.jpg', 1, 4, 55, '2020-06-24 21:26:23', '2020-06-24 21:29:32'),
(18, 'Trump đánh giá thấp nỗi sợ Covid-19 của người ủng hộ', 'trump-danh-gia-thap-noi-so-covid-19-cua-nguoi-ung-ho', 'Lượng người dự mít tinh của Trump ở Tulsa cho thấy mối đe dọa từ Covid-19 có thể làm chùn bước nhiều người ủng hộ hơn ông nghĩ.', '<p><span style=\"font-size:18px\">Đứng sau sân khấu tại&nbsp;Trung tâm BOK ở Tulsa, Oklahoma hôm 20/6, Tổng thống Donald Trump cùng một số trợ lý nhìn trân trân vào những hàng ghế trống trong khán phòng hơn 19.000 chỗ ngồi.&nbsp;Chỉ khoảng 1/3 số ghế được lấp đầy, mặc dù đội ngũ chiến dịch tranh cử của Trump đã khoe rằng họ nhận được hơn một triệu lượt đăng ký mua vé.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/GettyImages-1251036879-9022-1592972456.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PFceHDugATfod2yQTd9dRA\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Người ủng hộ Trump ngồi giữa nhiều ghế bị bỏ trống tại cuộc mít tinh ở&nbsp;Tulsa hôm 20/6. Ảnh:&nbsp;</span><em>AFP</em><span style=\"font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi tất cả 50 bang Mỹ đã mở cửa trở lại, người Mỹ giờ phải tự đánh giá rủi ro, cân nhắc hoạt động nào đáng \"xông pha\" khi ca nhiễm nCoV vẫn tăng mạnh ở một số khu vực của đất nước.&nbsp;Số bệnh nhân nhập viện vì Covid-19 đang gia tăng ở 14 bang của Mỹ, đạt mức&nbsp;cao chưa từng thấy&nbsp;ở các bang Arizona, Arkansas, California, Bắc Carolina, Nam Carolina, Tennessee và Texas.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vào thời điểm nhiều người còn lo ngại về một số hoạt động bình thường như ăn uống tại nhà hàng, mua sắm tại cửa hàng tạp hóa hay đến salon làm đẹp, việc tham gia một cuộc mít tinh hàng chục nghìn người có vẻ nguy hiểm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đội ngũ của Trump đã nghĩ rằng \"những người ủng hộ ông có cách đánh giá khác về rủi ro và sẽ có đủ người lấp đầy trung tâm đó\", Matt Bennett, từ trung tâm nghiên cứu cánh tả Third Way tại Washington, nói. \"Nhưng rốt cục mọi chuyện không như vậy\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trump và chiến dịch của ông cho rằng lượng người tham gia mít tinh thấp là do lo lắng về nguy cơ bạo lực và truyền thông thổi phồng về độ nguy hiểm của Covid-19. Một số người dùng Tiktok và cả fan K-pop&nbsp;tuyên bố họ cố tình phá hoại sự kiện bằng cách hô hào đăng ký ảo, khiến cho chiến dịch của Trump tưởng nhầm rằng sẽ có rất đông người tham dự sự kiện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng sự thưa thớt tại cuộc mít tinh được tổ chức ở bang luôn nghiêng về đảng Cộng hòa như Oklahoma cho thấy người Mỹ thuộc mọi đảng phái&nbsp;vẫn thận trọng với Covid-19 khi giới chức nới phong tỏa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo một cuộc thăm dò tháng này của AP và Trung tâm nghiên cứu về vấn đề công cộng NORC, 80% đảng viên Dân chủ bày tỏ lo sợ bản thân hoặc ai đó trong gia đình nhiễm nCoV hơn đảng viên Cộng hòa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng tổng cộng, khoảng một nửa số đảng viên Cộng hòa nói rằng họ cũng phần nào lo lắng. 60%&nbsp;người Mỹ&nbsp;ủng hộ hạn chế tụ tập từ 10 người trở xuống.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thư ký báo chí Nhà Trắng Kayleigh McEnany hôm 22/6 khẳng định Tổng thống hài lòng với năng lượng của đám đông và nhấn mạnh hơn 7 triệu người đã theo dõi mít tinh qua kênh Fox News. Nhưng các cố vấn giấu tên cho biết Tổng thống thực chất rất tức giận về lượng người tham dự thấp.</span></p>\r\n\r\n<p><img alt=\"Trump tại cuộc mít tinh ở Tulsa hôm 20/6. Ảnh: AFP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/BB15QZ19-6171-1592972456.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0IUK5Tjwuv8mjgV6AEu5Sw\" style=\"width:100%\" /><span style=\"font-size:14px\">Trump tại cuộc mít tinh ở Tulsa hôm 20/6. Ảnh:&nbsp;</span><em>AFP</em><span style=\"font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chiến dịch của Trump đang nghiên cứu để rút ra bài học từ Tulsa. Các cố vấn của Tổng thống hối tiếc vì đã khoe về số lượng người đăng ký trước. Họ tin rằng người ủng hộ Trump không đến sự kiện do lo ngại về nguy cơ biểu tình và bạo lực trong khu vực, theo ba quan chức chiến dịch và quan chức Nhà Trắng giấu tên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng họ cũng thừa nhận có thể đã đánh giá quá cao sự sẵn lòng chấp nhận rủi ro của ngay cả những cử tri trung thành với Trump. Nguy cơ từ cuộc mít tinh hàng nghìn người, trong đó nhiều người không đeo khẩu trang và không tuân thủ giãn cánh xã hội, đã khiến nhiều cử tri chùn bước, đặc biệt là người cao tuổi và gia đình có trẻ nhỏ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các quan chức chiến dịch nhấn mạnh rằng các cuộc mít tinh vẫn sẽ là yếu tố chủ chốt trong chiến lược tái tranh cử của Tổng thống, nhưng thừa nhận có thể cần thay đổi&nbsp;ở một số bang. Họ đang thảo luận về việc tổ chức mít tinh ở những địa điểm nhỏ hơn hoặc ngoài trời hay ở những thành phố nhỏ ít người biểu tình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Họ có thể đưa ra thêm biện pháp để bảo vệ sức khỏe cho cả người tham dự và nhân viên. 8&nbsp;nhân viên chiến dịch và hai mật vụ chuẩn bị cho cuộc mít tinh đã dương tính với nCoV. Họ đang truy vết tiếp xúc của những người này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù vậy, các quan chức chiến dịch nhấn mạnh&nbsp;khả năng thu hút hàng nghìn người tới dự mít tinh giữa đại dịch của Trump là&nbsp;sự tương phản rõ rệt với ứng viên đảng Dân chủ Joe Biden.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Vẫn còn nhiều điều không chắc chắn về việc mở cửa trở lại. Đây là sự kiện đầu tiên sau vài tháng. Đám đông và sự nhiệt tình sẽ quay trở lại\", Dan Eberhart, nhà tài trợ chính của đảng Cộng hòa và là người ủng hộ Trump, nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều người tham dự mít tinh ở Tulsa nói rằng họ không lo ngại về nguy cơ lây nhiễm nCoV. Nhiều người nói Chúa sẽ quyết định số phận của họ, trong khi những người khác cho rằng truyền thông thổi phồng về nguy cơ lây nhiễm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng một số người thừa nhận họ lo ngại và đeo khẩu trang. \"Tôi muốn bảo vệ bố mẹ và ông bà\", Chris Rasmusser, 40 tuổi, nói. Anh đeo khẩu trang trong thời gian xếp hàng chờ vào trung tâm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trump và các đồng minh công kích Biden là \"trốn trong tầng hầm\" kể từ khi đại dịch khiến cuộc sống của người Mỹ đảo lộn hồi tháng ba. Biden chủ yếu thực hiện các cuộc họp trực tuyến với người ủng hộ và người gây quỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các cố vấn của Biden phản bác những lời chỉ trích, nói rằng hình thức vận động tranh cử truyền thống quá nguy hiểm vào lúc này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cử tri không quan tâm&nbsp;ông ấy quay video từ đâu\", người quản lý chiến dịch của Biden Jen O\'Malley Dillon nói trong cuộc phỏng vấn vào tháng trước. \"Điều họ quan tâm là ông ấy nói gì và chúng tôi kết nối với họ như thế nào\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Phương Vũ</strong>&nbsp;(Theo&nbsp;<em>AP</em>)</span></p>', 'G0FN_1.jpg', 1, 7, 6, '2020-06-24 23:00:24', '2020-06-24 23:10:55'),
(19, 'Thợ nail Việt ở California vui mừng ngày tái mở cửa', 'tho-nail-viet-o-california-vui-mung-ngay-tai-mo-cua', 'MỸ - Ngay khi Christina Dinh mở cửa lại tiệm nail, khách hàng với những bộ móng tay trầy xước ùa tới, ngồi vào ghế được xếp cách xa nhau.', '<p><span style=\"font-size:18px\">Tuy nhiên, đầu tiên, Dinh, 22 tuổi,&nbsp;phải đo&nbsp;thân nhiệt của họ. Bên trong tiệm&nbsp;Captivate Nail &amp; Spa chật hẹp ở thành phố Fullerton, bang California, Mỹ, cô yêu cầu từng khách ký vào tờ khai y tế về nguy cơ nhiễm Covid-19, liệt kê những dấu hiệu đáng ngờ về sức khỏe, đồng thời cam kết không khởi kiện tiệm nếu nhiễm nCoV. Dinh sau đó đề nghị&nbsp;mọi người rửa tay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đằng sau Dinh, mẹ cô, bà chủ&nbsp;Crystal Trang Luong, đang chăm sóc&nbsp;móng chân cho một nữ&nbsp;kế toán viên trẻ, người rất biết ơn các thợ nail vì đã giúp cô có thể khoe móng chân xinh kịp thời điểm diện những đôi sandal mùa hè.</span></p>\r\n\r\n<p><img alt=\"Bà chủ&nbsp;Crystal Trang Luong làm móng chân cho khách tại tiệm&nbsp;Captivate Nail &amp; Spa ở thành phố Fullerton, bang California hôm 19/6. Ảnh: LA Times\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/download-8025-1592895172.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MviGyBmlMjKmvgoBW5oWJQ\" style=\"width:616px\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Bà chủ&nbsp;Crystal Trang Luong làm móng chân cho khách tại tiệm&nbsp;Captivate Nail &amp; Spa ở thành phố Fullerton, bang California hôm 19/6. Ảnh:&nbsp;</span><em>LA Times</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Đó là hôm 19/6, ngày đầu tiên các tiệm nail ở bang California được phép tái mở sau gần 100 ngày ngừng hoạt động&nbsp;tránh Covid-19. Nhiều cửa tiệm trang bị tấm che mặt, bao chân, tấm chắn trên bàn, sắp xếp bàn ghế sao cho đảm bảo cả thợ nail lẫn khách hàng an toàn theo hướng dẫn từ Trung tâm Ngăn ngừa và&nbsp;Kiểm soát Dịch bệnh của bang và liên bang.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Khách yêu cầu như nào chúng tôi làm thế đó. Tôi không quan tâm quá nhiều tới giá cả, dù vẫn còn 3 tháng tiền nhà chưa trả và chưa có thu nhập để trả\", bà Luong, 48 tuổi, nói. \"Tôi chỉ nghĩ đến an toàn. Nếu chúng tôi không đảm bảo thì làm sao tất cả chúng tôi sống sót được?\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chỉ 5 tháng sau khi bà Luong dùng tiền tiết kiệm và vay mượn thêm chị dâu mua lại salon trên, Covid-19 bùng phát khiến bà phải đóng cửa tiệm hôm 17/3. Mắc kẹt ở nhà, bà lo lắng theo dõi tin tức, nghe ngóng xem bao giờ ngành nail mới được mở cửa lại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bà rất buồn khi nghe Thống đốc Gavin Newson hồi tháng 5 phát biểu rằng lây nhiễm nCoV trong cộng đồng xuất phát từ một tiệm nail. Tháng này, nhân viên của ông Newsom đã đính chính thông tin, cam kết thúc đẩy tái mở cửa ở giai đoạn 3, khi các dịch vụ cá nhân khác được nối lại hoạt động. California có 11.000 tiệm nail, trong đó 80% thuộc sở hữu của người gốc Việt.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Các bạn biết đó, tôi vui sướng đến ngây người. Các tiệm nail đã trở lại. Tôi không thể đợi thêm để báo với mẹ tôi tin này\", bà Joanne Bockian, 70 tuổi, người đến tiệm&nbsp;Captivate mỗi tháng một lần, nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bà đã đặt lịch làm móng để gây bất ngờ cho người mẹ sắp 96 tuổi của mình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Ở đây họ rất giỏi chiều khách, cư xử nhẹ nhàng và còn giúp bà ấy đi lại giày\",&nbsp;Bockian nói.</span></p>\r\n\r\n<p><img alt=\"Christina Dinh đo thân nhiệt cho khách tại&nbsp;tiệm&nbsp;Captivate Nail &amp; Spa. Ảnh: LA Times\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/download-1-4066-1592895173.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hJQC0ysMlalYie21DMZWzw\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Christina Dinh (phải) đo thân nhiệt cho khách tại&nbsp;tiệm&nbsp;Captivate Nail &amp; Spa. Ảnh:</span><em>&nbsp;LA Times</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi Dinh đang khử trùng một ghế spa, một phụ nữ cầm quả táo xuất hiện, đề nghị làm nail. Bà này không đeo khẩu trang hay điền vào tờ khai sức khỏe vì nói rằng mình \"làm việc cho chính quyền và họ không yêu cầu chúng tôi làm điều đó\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi rất xin lỗi thưa bà, chúng tôi không thể cung cấp dịch vụ cho bà\", Dinh nói. Người phụ nữ liền&nbsp;rời đi, 5 phút sau quay lại với khẩu trang trên mặt.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Điện thoại của Dinh không ngừng reo, khách hàng liên tục đặt chỗ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Bạn gặp mọi kiểu người ở đây\", Dinh, con út của bà Luong, một sinh viên điều dưỡng, nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cô từng chứng kiến mẹ mình chấp nhận làm những công việc ngắn hạn ở các tiệm nail tại&nbsp;Arizona và Bắc&nbsp;Carolina để nuôi gia đình. Bố cô là thợ cơ khí và gần đây đã đăng ký làm ca hàng ngày để kiếm thêm thu nhập. Cha mẹ cô từ Đà Lạt đến Mỹ định cư khi mới hơn 20 tuổi và quen biết nhau khi là hàng xóm ở quận Cam.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Gia đình bà Luong từng chia nhau một bánh xà bông để tắm gội, Dinh nhớ lại, \"cho đến khi bố tôi giới thiệu cho họ dầu gội và dầu xả\".&nbsp;\"Họ thậm chí không biết đó là gì\", Dinh kể. \"Và bây giờ, bà ấy đang điều hành cửa hàng riêng của mình\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sergio Martinez, một quản lý bán hàng của Auto Zone, và vợ anh, Diana, là khách hàng thường xuyên của tiệm Captivate. Họ cũng thích sự tận tâm của các thợ nail và không khí dễ chịu ở đây.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi được chào đón chân thành, như một gia đình. Chúng tôi cần đưa tiền boa nhiều hơn, suốt thời gian qua họ đã&nbsp;mất việc\",&nbsp;Martinez, 34 tuổi, nói.</span></p>\r\n\r\n<p><img alt=\"Diana Martinez vui mừng khi tiệm nail cô yêu thích mở cửa lại. Ảnh: LA Times\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/download-3-1590-1592895173.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=EhSnciuPVYSQ7ks_E7VAfA\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Diana Martinez vui mừng khi tiệm nail cô yêu thích mở cửa lại. Ảnh:&nbsp;</span><em>LA Times</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Martinez được làm sạch móng chân, trị liệu và mát xa bằng muối biển&nbsp;với giá 26 USD. \"Không giống như khi bạn tự làm sạch móng của mình, ở đây họ làm mọi thứ chỉn&nbsp;chu\", anh nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kathy Ong, nhân viên làm chân cho&nbsp;Martinez&nbsp;và là một thợ nail có 5 năm kinh nghiệm, cho hay cô \"đã đếm từng ngày đến khi salon mở lại vì ở nhà nghĩa là không có thu nhập\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một nghiên cứu mới được Liên đoàn Nail salon Lành mạnh Calfornia kết hợp với Trung tâm Lao động Đại học California tại Los Angles thực hiện cho thấy trong số hơn 700 nhân viên và chủ salon được khảo sát suốt thời gian bùng phát Covid-19, chỉ 6% chủ tiệm trả lương cho nhân viên. Hơn 90% nhân viên đã nộp đơn xin trợ cấp thất nghiệp và gần một nửa cần hỗ trợ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Brittney Robertson, một khách hàng, bày tỏ cảm thông với hoàn cảnh của các nhân viên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cả xã hội đang gặp khó khăn, đó là lý do&nbsp;nghề nail của các bạn được chào đón trở lại\", cô nói. Cô và Dinh vẫn giữ liên lạc khi chờ đợi tiệm nail mở cửa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi liên tục nhắn tin trên Instgram của họ: \'Còn bao nhiêu ngày nữa?\'\", Robertson, 29 tuổi, kể. \"Khi bạn đã quý mến họ, bạn sẽ không muốn đi tiệm khác nữa\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Anh Ngọc</strong>&nbsp;(Theo<em>&nbsp;LA Times</em>)</span></p>', 'a84B_1.jpg', 1, 5, 7, '2020-06-24 23:15:09', '2020-06-24 23:18:12'),
(20, 'Gia đình bị chia cắt vì lệnh ngừng cấp visa của Trump', 'gia-dinh-bi-chia-cat-vi-lenh-ngung-cap-visa-cua-trump', 'ẤN ĐỘ - Hồi đầu tháng ba, Poorva Dixit vội vã mua vé từ Mỹ về Ấn Độ sau khi biết người mẹ 72 tuổi ngã khỏi giường và đang nguy kịch.', '<p><span style=\"font-size:18px\">Cô quyết định để lại hai con nhỏ và chồng ở California vì Covid-19 đang lan rộng toàn cầu. Dixit và chồng cô đều là công dân Ấn Độ, đã sống ở Mỹ hơn 10 năm, còn các con của họ là công dân Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Là một nhân viên&nbsp;phát triển phần mềm với giấy phép làm việc tạm thời tại Mỹ, Dixit biết rằng để quay về Mỹ, cô sẽ phải đến lãnh sự quán ở Mumbai để được đóng dấu visa mới trong hộ chiếu, yêu cầu với một số người có visa Mỹ khi ra nước ngoài.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, hôm 16/3, một ngày trước cuộc hẹn đóng dấu visa, lãnh sự quán Mỹ đóng cửa do lệnh phong tỏa. 8 ngày sau, mẹ cô qua đời.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Vợ chồng Poorva Dixit và các con tại San Francisco, California, Mỹ. Ảnh: Reuters\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/download-4-1592987097-2578-1592987700.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cioogT6DZYwlyGW0EwAP5Q\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Vợ chồng Poorva Dixit và các con tại San Francisco, California, Mỹ. Ảnh:&nbsp;<em>Reuters</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sắc lệnh nhập cư mới&nbsp;được Tổng thống Donald Trump đưa ra hôm 22/6 cấm những người có visa làm việc tạm thời đến Mỹ có thể khiến Dixit mắc kẹt ở Ấn Độ, xa chồng con, đến ít nhất cuối năm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi đã mất mẹ và đang xa các con\", cô nói, cho hay đang ở với họ hàng tại ngoại ô Mumbai. \"Lúc này, tôi rất rối trí\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dixit chỉ là một trong gần 1.000 người Ấn Độ mắc kẹt trong tình cảnh tương tự và tham gia vào một nhóm chat kín trên ứng dụng Telegram. Nhiều người như cô đã sống và làm việc hợp pháp nhiều năm ở Mỹ nhưng đang ở Ấn Độ khi Trump ra sắc lệnh mới. Họ rất bối rối và lo lắng về khả năng quay lại Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sắc lệnh của Trump tạm thời đình chỉ nhập cảnh với những người đến Mỹ theo một số loại visa lao động, bao gồm&nbsp;H-1B, loại&nbsp;dành cho lao động có trình độ cao, thường là trong lĩnh vực công nghệ như Dixit và chồng cô. Lệnh cấm có hiệu lực từ ngày 24/6 cũng áp dụng với các visa L dùng cho việc chuyển giao quốc tế các nhân viên cấp cao, lao động mùa vụ, các thực tập&nbsp;sinh cũng như những thành viên gia đình của họ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Có một số trường hợp ngoại lệ với sắc lệnh này bao gồm những người làm việc trong ngành công nghiệp cung cấp thực phẩm và một số nhân viên y tế tham gia chống Covid-19. Tuy nhiên, trong khi vợ chồng, con cái của các công dân Mỹ được ngoại trừ thì số phận của cha mẹ những đứa trẻ là công dân Mỹ không rõ ra sao.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chồng của Dixit, anh Kaustubh, đang cố gắng vừa làm việc toàn thời gian vừa chăm sóc hai con gái 6 và 3 tuổi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dixit thường gọi điện cho các con, có khi vài tiếng một ngày, cố gắng gần gũi con bằng cách đọc sách và hát cho chúng nghe để chồng cô làm việc. Nhưng cô sợ rằng sự chia cắt này sẽ gây ra hậu quả tâm lý lâu dài, nhất là với con gái út khi cô bé ngày càng chán những cuộc điện thoại. Con gái lớn đã viết lên bức ảnh gia đình trên tủ lạnh rằng mình \"mãi mãi sống một cách buồn bã\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Vợ chồng Poorva Dixit và các con tại San Francisco, California, Mỹ. Ảnh: Reuters\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/download-5-4980-1592987700.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vaHphZd3roqndKW8oekp5A\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Vợ chồng Poorva Dixit và các con tại San Francisco, California, Mỹ. Ảnh:&nbsp;<em>Reuters</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhà Trắng cho hay biện pháp trên là cần thiết để dành cơ hội việc làm cho người Mỹ, khi hàng triệu người rơi vào cảnh thất nghiệp do Covid-19. Tuy nhiên, 6 người Ấn Độ trong nhóm chat trên Telegram, trong đó Dixit, cho hay họ vẫn làm công việc ở Mỹ suốt đại dịch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vinod Albuquerque, một tư vấn viên kinh doanh 41 tuổi, tiếp tục làm việc từ xa cho công ty ở Atlanta từ khi phải trở về thành phố&nbsp;Mangalore, Ấn Độ, gấp hồi tháng hai do bố bị đột quỵ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Anh để lại con trai 6 tuổi và người vợ dự kiến sinh con vào tháng 9 tới tại Mỹ. Anh cũng không thể giải quyết visa vì lãnh sự quán đã đóng cửa và hiện mắc kẹt tại Ấn Độ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Thật không công bằng\", Albuquerque nói. \"Chúng tôi hiểu có thể sắc lệnh này ảnh hưởng&nbsp;những người mới xin&nbsp;visa H1-B chưa&nbsp;từng đến Mỹ, nhưng những người như chúng tôi bị thiệt hại gấp đôi. Tôi vẫn đóng góp cho nền kinh tế và đang bị đánh thuế ở Mỹ\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Anh Ngọc</strong>&nbsp;(Theo&nbsp;<em>Reuters</em>)</span></p>', 'uCgs_1.jpg', 1, 0, 8, '2020-06-24 23:22:45', '2020-06-24 23:22:45'),
(21, 'Mẫu huân chương đắt nhất thế giới', 'mau-huan-chuong-dat-nhat-the-gioi', 'Quân đội Liên Xô chỉ trao 22 Huân chương Chiến thắng cho 17 người, và mỗi chiếc hiện nay có giá trị ước tính trên 20 triệu USD.', '<p><span style=\"font-size:18px\">Huân chương Chiến thắng&nbsp;là phần thưởng quân đội cao nhất&nbsp;được&nbsp;Liên Xô trao cho các nguyên soái, thống chế đã có những chiến tích vang dội làm thay đổi cục diện chiến trường. Trong lịch sử, chỉ có 12 chỉ huy Liên Xô và 5 chỉ huy ngoài Liên Xô được trao huân chương này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không chỉ mang giá trị to lớn về tinh thần, đây còn&nbsp;được coi là mẫu huân chương đắt giá và quý hiếm nhất trên thế giới, khi nó có thể được bán tại bất kỳ nhà đấu giá nào với mức giá khởi điểm trên 20 triệu USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tính đến năm 2017,&nbsp;Vua Michael I của Romania là người cuối cùng được nhận huân chương này còn sống. Sau khi ông qua đời vào năm đó,&nbsp;không ai rõ về số phận Huân chương Chiến thắng của ông. Hoàng gia Romania thông báo nó đang được lưu giữ trong tư gia của vua Michael I ở Versoix, Thụy Sĩ, nhưng có tin đồn ông đã bán huân chương này hồi thập niên 1980 với giá khoảng 4 triệu USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Huân chương Chiến thắng của Liên Xô. Ảnh: Bảo tàng Điện Kremlin.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/order-or-victory-8664-1592885266.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ARJDMj6Elx4XKwBjyf3yQA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Huân chương Chiến thắng của Liên Xô. Ảnh:&nbsp;<em>Bảo tàng Điện Kremlin</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Quyết định ban hành Huân chương Chiến thắng được Liên Xô đưa ra sau chiến dịch Stalingrad kết thúc vào tháng 2/1943, đánh dấu thắng lợi quan trọng đầu tiên của Hồng quân trong Chiến tranh Vệ quốc Vĩ đại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 1942 là giai đoạn cực kỳ khó khăn của Hồng quân, khi họ hứng chịu hàng loạt thất bại trước Đức quốc xã ở Mặt trận phía Nam. Lãnh đạo Iosef Stalin ban hành Chỉ thị số 227 vào ngày 28/7/1942 với khẩu hiệu \"Không lùi bước!\". Ông cũng hiểu rằng cần có những phần thưởng cao quý để trao cho những chỉ huy có thành tích nổi bật trên chiến trường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong giai đoạn 1942-1943, Stalin đã yêu cầu xây dựng nhiều huân chương và phần thưởng cho các sĩ quan quân đội, đặt theo tên những chỉ huy quân sự tài năng trong lịch sử Nga như Alexander Suvorov, Mikhail Kutuzov, đô đốc Fedor Ushakov và Pavel Nakhimov.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Huân chương Chiến thắng được xếp hàng đầu. Tháng 7/1943, khi trận đánh vành đai Kursk đang diễn ra, thiết kế sơ bộ của huân chương được đệ trình lên Stalin, trong đó hình chân dung của ông và lãnh đạo Vladimir Lenin được in ở mặt trước huân chương.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Stalin không chấp nhận thiết kế này. Đến tháng 10/1943, ông yêu cầu đặt hình tháp đồng hồ Spasskaya của Điện Kremlin lên mặt trước huân chương. Stalin phê duyệt thiết kế hoàn chỉnh của Huân chương Chiến thắng vào ngày 5/11/1943. Ba ngày sau, huân chương được ban hành và quá trình chế tạo bắt đầu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc chế tạo huân chương được giao cho nhà máy Đồng hồ và Đá quý Moskva.&nbsp;Liên Xô định chế tạo 30 huân chương, mỗi chiếc sử dụng 180 viên kim cương, 50 viên kim cương cắt đáy phẳng và 300 gram bạch kim. Quá trình chế tạo&nbsp;sử dụng tổng cộng 5.400 viên kim cương thường, 1.500 viên cắt đáy phẳng và 9 kg bạch kim.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Nguyên soái Zhukov với hai Huân chương Chiến thắng ở giữa ngực. Ảnh: TASS.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/order-or-victory-3-2374-1592885266.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5PYfYuRb6-f_VYuyLROeqQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nguyên soái Zhukov với hai Huân chương Chiến thắng ở giữa ngực. Ảnh:&nbsp;<em>TASS</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tổng cộng 22 Huân chương Chiến thắng được xuất xưởng, tất cả đều được chế tác thủ công, trong đó ba chiếc không được trao tặng mà được lưu giữ. Huân chương Chiến thắng nặng 78 gram,&nbsp;được làm từ bạch kim, phần chữ \"Pobeda\" (Chiến thắng) được làm từ vàng ròng, cùng với đó là 174 viên kim cương, 5 viên hồng ngọc với tổng khối lượng 25 carat.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các nhà chế tác ban đầu định dùng hồng ngọc tự nhiên, nhưng sau đó chuyển sang sử dụng hồng ngọc nhân tạo, bởi&nbsp;hồng ngọc tự nhiên có gam màu không đồng nhất, có thể khiến huân chương có vẻ lấm bẩn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các chi tiết như Điện Kremlin và cành tùng làm từ bạch kim ánh vàng, được khảm các viên kim cương nhỏ. Những chi tiết cố định phía sau như đinh và ốc được làm bằng bạc.&nbsp;Điểm độc đáo của huân chương là chúng không được khảm số thứ tự.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Huân chương Chiến thắng được trao lần đầu vào ngày 10/4/1944 cho tổng tư lệnh Stalin, nguyên soái Georgy Zhukov và Alexander Vaislevsky nhằm kỷ niệm chiến dịch giải phóng Ukraine. Cả ba người đều được trao Huân chương Chiến thắng lần hai vào năm 1945.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài các chỉ huy Liên Xô, phần thưởng này cũng được trao cho 5 người ngoại quốc. Thống chế Anh Bernard Montgomery và đại tướng Mỹ Dwight Eisenhower được trao thưởng tháng 6/1945 vì \"thành công đặc biệt trong các chiến dịch quy mô lớn dẫn đến chiến thắng của Liên Hợp Quốc trước Đức quốc xã\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vua Michael I của Romania được trao thưởng ngày 6/7/1954 vì quyết định bắt những người hợp tác với Đức quốc xã trong chính phủ Romania. Hành động này diễn ra ngày 23/8/1944, thời điểm phe Đồng minh chưa giành được chiến thắng quyết định trước Đức quốc xã.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nguyên soái Ba Lan Michal Rola-Zymierski được trao Huân chương Chiến thắng ngày 9/8/1945 vì những chiến dịch chống phát xít. Nguyên soái Nam Tư Josip Tito cũng được trao thưởng với lý do tương tự ngày 9/9/1945.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lãnh đạo Liên Xô Leonid Brezhnev được trao Huân chương Chiến thắng vào năm 1978. Tuy nhiên, phần thưởng này bị lãnh đạo Mikhail Gorbachev thu hồi vào năm 1989, bảy năm sau khi Brezhnev qua đời, với lý do ông không tham gia những chiến dịch quân sự mang tính quyết định trong Thế chiến II.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bảo tàng Điện Kremlin hiện lưu giữ bộ sưu tập Huân chương Chiến thắng lớn nhất thế giới với 8 chiếc, trong khi một trong ba huân chương không được trao tặng và huân chương của nguyên soái Rola-Zymierski hiện không rõ tung tích.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngày nay, hình ảnh Huân chương Chiến thắng và Huân chương Chiến tranh Vệ quốc cũng xuất hiện trong các lễ Duyệt binh Chiến thắng khi được treo trước Bảo tàng Lịch sử Quốc gia ở Quảng trường Đỏ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Biểu tượng Huân chương Chiến thắng (trái) và Huân chương Chiến tranh Vệ quốc trong lễ duyệt binh tại Moskva năm 2019. Ảnh: Điện Kremlin.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/order-or-victory-2-2254-1592885266.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n2JJ_0hSrvMWaogsYufXyA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Biểu tượng Huân chương Chiến thắng (trái) và Huân chương Chiến tranh Vệ quốc trong lễ duyệt binh tại Moskva năm 2019. Ảnh:&nbsp;<em>Điện Kremlin</em>.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Vũ Anh&nbsp;</strong>(Theo&nbsp;<em>RBTH</em>)</span></p>', 'T5TW_1.jpg', 1, 8, 9, '2020-06-24 23:27:31', '2020-06-24 23:27:31'),
(22, 'Mô hình second home mở cơ hội cho địa ốc nghỉ dưỡng', 'mo-hinh-second-home-mo-co-hoi-cho-dia-oc-nghi-duong', 'Các chuyên gia từ Savills, BCG Land nhận định dòng sản phẩm second home - ngôi nhà thứ hai có nhiều triển vọng bứt phá trên thị trường từ nay đến cuối năm.', '<p><span style=\"font-size:18px\">Tại tọa đàm trực tuyến \"Chìa khóa mở cửa ngành Bất động sản nghỉ dưỡng\" phát sóng&nbsp;sáng nay&nbsp;trên&nbsp;<em>VnExpress</em>, đại diện Savills và BCG Land đã phân tích, đánh giá xu hướng thị trường bất động sản nghỉ dưỡng hậu giãn cách xã hội.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Động lực từ du lịch nội địa</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đánh giá về bức tranh chung của thị trường, bà Đỗ Thị Thu Hằng, Giám đốc Bộ phận Nghiên cứu và Tư vấn Savills Hà Nội&nbsp;cho rằng các hoạt động kích cầu du lịch nội địa thể hiện&nbsp;những tín hiệu tích cực. Các địa điểm du lịch lớn như Đà Nẵng, Nha Trang, Quảng Ninh... thu hút đông đảo du khách. \"Một số nơi công suất đông, thậm chí trong tuần. Bởi đây là thời điểm mùa hè, nên rất thuận lợi cho các hoạt động du lịch khởi sắc trở lại\", bà Hằng nói.&nbsp;</span></p>\r\n\r\n<p><img alt=\"Bà Đỗ Thị Thu Hằng,&nbsp;Giám đốc Bộ phận Nghiên cứu và Tư vấn Savills Hà Nội\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/24/anh-2-2078-1592991536.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oA8wZ6dNWf935kOr6P1mNA\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Bà Đỗ Thị Thu Hằng,&nbsp;Giám đốc Bộ phận Nghiên cứu và Tư vấn Savills Hà Nội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo vị này, bên cạnh những địa điểm truyền thống, hoạt động du lịch sẽ trở nên sôi động hơn ở những địa phương mới nổi như Tuy Hòa (Phú Yên) hay Pleiku (Gia Lai). Hàng loạt chương trình ưu đãi của các hãng hàng không giúp người dân có thêm nhiều lựa chọn du lịch. \"Người dân đang thể hiện sự&nbsp;sẵn sàng hỗ trợ các địa phương khai phá tiềm năng du lịch, bằng cách tới những địa điểm chưa thực sự phát triển, tìm kiếm cảm giác hoang sơ\", bà Hằng nhận định.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sự hoàn thiện của hạ tầng giao thông&nbsp;tạo lực đẩy cho du lịch tại các địa phương. Đại diện Savills dẫn chứng, Quảng Ninh là một điển hình thành công tại miền Bắc. Các tuyến đường cao tốc rút ngắn quá trình di chuyển từ Hà Nội và các tỉnh thành lân cận về Quảng Ninh từ 3,5 giờ xuống 1 giờ. Nhờ sự phát triển du lịch, kinh tế địa phương này cũng có nhiều cơ hội bứt phá mạnh mẽ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Covid-19 thay đổi \"khẩu vị\" giới đầu tư</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo đại diện Savills, đại dịch khiến giới đầu tư bất động sản nghỉ dưỡng thay đổi quan điểm đầu tư nói chung. Nhiều chủ đầu tư bắt đầu chú trọng tới các dòng sản phẩm phù hợp trong những trường hợp tương tự Covid-19. \"Đó là nơi khách hàng của họ là các nhà đầu tư có thể mua và đến ở trong thời gian cách ly mà vẫn an toàn\", bà Hằng nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo vị này, các nhà đầu tư đang thể hiện sự thận trọng khi ra quyết định. \"Họ quan ngại diễn biến dịch có thực sự kiểm soát tốt không. Trong nước đang kiểm soát tốt nhưng quan trọng hơn là lượng khách quốc tế\", bà Hằng nhấn mạnh và cho rằng rất nhiều doanh nghiệp trong ngành này đang thực hiện cấu trúc lại hoạt động kinh doanh. \"Các chủ đầu tư đang dần mở cửa trở lại, nhưng họ sẽ phải tính toán xem cần củng cố, cải thiện ở những khía cạnh nào, quy trình kiểm soát, đảm bảo an toàn cho khách ra sao...\", đại diện Savills khẳng định.&nbsp;</span></p>\r\n\r\n<p><img alt=\"Bà Phạm Nguyễn Ngọc Thương, Phó tổng giám đốc BCG Land.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/24/anh-3-7433-1592991536.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nQgQW2AQvXjiszQeLyR2dg\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Bà Phạm Nguyễn Ngọc Thương, Phó tổng giám đốc BCG Land.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hầu hết các nhà đầu tư vào bất động sản nghỉ dưỡng đều là những nhà đầu tư lâu năm, với những kỳ vọng cao. Bản thân mỗi dự án muốn tạo sức hút với họ&nbsp;cần đáp ứng được nhiều tiêu chí. \"Pháp lý nhiều khi tốt như chủ đầu tư hoặc đơn vị điều hành không nhiều kinh nghiệm, thiếu năng lực, hoặc địa phương không hấp dẫn du khách... đều sẽ khiến các nhà đầu tư không chấp nhận các sản phẩm&nbsp;đó\", bà Hằng nhận định.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Để đáp ứng những tiêu chuẩn của nhà đầu tư, nhiều chủ đầu tư lựa chọn hợp tác với các đơn vị quốc tế. Theo bà Phạm Nguyễn Ngọc Thương, Phó tổng giám đốc BCG Land, dự án Malibu Hội An đơn vị này hợp tác với nhà vận hành 5 sao đến từ Mỹ.&nbsp;\"Chúng tôi đưa ra quyết định dựa trên những nghiên cứu chuyên sâu, thể hiện sự đầu tư nghiêm túc và bài bản về thị trường, sản phẩm, với chính sách chia sẻ lợi nhuận phù&nbsp;hợp cho các nhà đầu tư\", bà Thương cho biết.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chia sẻ về những khó khăn&nbsp;doanh nghiệp, Phó tổng giám đốc BCG Land cho hay doanh nghiệp gặp&nbsp;khó&nbsp;từ cuối 2019, khi niềm tin của nhà đầu tư bị suy giảm do một dự án ở Đà Nẵng không thực hiện được&nbsp;cam kết chi trả lợi nhuận với nhà đầu tư. Sang năm 2020, doanh nghiệp này tiếp tục chịu ảnh hưởng của Covid-19, khiến dự án chậm tiến độ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đại diện BCG Land bày tỏ cái nhìn tích cực khi cho rằng đây cũng&nbsp;là&nbsp;cơ hội cho doanh nghiệp \'lùi lại một bước\', nghiên cứu nhu cầu thị trường, đổi mới sản phẩm, tái cấu trúc doanh nghiệp. \"Các doanh nghiệp hoạt động với nguồn vốn mạnh mẽ, các dự án có pháp lý vững chắc sẽ tạo dựng niềm tin với các nhà đầu tư\", bà Thương nhận định.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Sức hút của \"ngôi nhà thứ hai\"</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo đánh giá của đại diện Savills, trên thế giới, một trong những sản phẩm lên ngôi giai đoạn&nbsp;Covid-19 là \"ngôi nhà thứ hai\" (second home). \"Trong&nbsp;giãn cách xã hội chúng ta thường làm việc, nghỉ ngơi tại nhà. Chúng ta cũng&nbsp;có thể lựa chọn tới&nbsp;các \'ngôi nhà thứ hai\' để vừa thực hiện cách ly xã hội, vừa chăm sóc sức khỏe, làm việc từ xa hoặc học trực tuyến...\", vị này cho biết.</span></p>\r\n\r\n<p><img alt=\"Phối cảnh nhà mẫu second home của NovaWorld Phan Thiết - giai đoạn một phát triển theo phong cách Mỹ.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/24/hinh-2-NVW-Phan-Thiet-1944-155-3755-1570-1593000597.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cSf6s08DVG2SzW0jppee5Q\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Phối cảnh nhà mẫu second home của NovaWorld Phan Thiết - giai đoạn một phát triển theo phong cách Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo bà Hằng, nhu cầu sở hữu dòng sản phẩm này đang có xu hướng tăng lên, do tính đa tác dụng của nó. \"Nhà đầu tư có thể cho thuê, kinh doanh và nghỉ ngơi trong thời gian bố trí được.&nbsp;Khi hoạt động du lịch phát triển tốt, lợi suất cho thuê hợp lý, second home sẽ&nbsp;là dòng sản phẩm hấp dẫn mà các nhà đầu tư nên hướng tới nếu muốn đa dạng gói đầu tư\", vị này đưa ra lời khuyên.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các dự án này thường tọa lạc&nbsp;tại nơi có cảnh quan đẹp, vị trí thuận lợi, tích hợp các&nbsp;tiện ích nội khu, tiện ích chăm sóc sức khỏe. Đơn cử, các&nbsp;sản phẩm second home của Novaland nằm&nbsp;tại vùng ven biển Phan Thiết, Bà Rịa - Vũng Tàu, cách TP HCM 1,5-2 giờ chạy xe theo các tuyến cao tốc.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bàn về xu hướng bất động sản thời gian tới, Phó tổng giám đốc BCG Land nhận định&nbsp;sau Covid-19, người dân quan tâm, chú trọng nhiều hơn đến sức khỏe. \"Khách hàng thay vì&nbsp;tới những nơi náo nhiệt và ồn ào lại hướng tới những địa điểm vui chơi giải trí lành mạnh, với&nbsp;những hoạt động chăm sóc sức khỏe\", bà Thương cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh đó, nhiều chủ đầu tư cũng chú trọng áp dụng các giải pháp công nghệ cho các dự án bất động sản. Các tiện ích&nbsp;dự án cũng đang hướng tới yếu tố riêng tư, an toàn. Theo đại diện Savills, thị trường đang có sự sàng lọc,&nbsp;đòi hỏi chủ đầu tư có năng lực,&nbsp;phát triển các dòng sản phẩm, tích hợp nhiều tiêu chí... để hoạt động kinh doanh thực sự thu hút khách hàng.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Hoài Phong</strong></span></p>', 'wZVn_1.jpg', 1, 2, 11, '2020-06-24 23:34:04', '2020-06-24 23:34:04'),
(23, 'Petrolimex giục PGBank sáp nhập HDBank', 'petrolimex-giuc-pgbank-sap-nhap-hdbank', 'Đại diện Petrolimex, cổ đông sở hữu 40% vốn PGBank, dọa sẽ thoái vốn nếu việc sáp nhập với HDBank không thực hiện trước ngày 31/8.', '<p><span style=\"font-size:18px\">Thông tin trên được ông Trần Ngọc Năm, thành viên Hội đồng quản trị PGBank, chia sẻ tại phiên họp thường niên sáng nay (24/6). Ông Năm đồng thời cũng là trưởng nhóm đại diện phần vốn góp của Petrolimex tại PGBank.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng ta không thể tiếp tục chờ đợi việc sáp nhập. Bao nhiêu tâm huyết của các cổ đông dồn vào ngân hàng. Là cổ đông lớn chúng tôi thậm chí còn áp lực hơn những cổ đông khác\", ông Năm nói và cho biết thêm, Petrolimex đang chịu áp lực phải thoái phần vốn góp tại PGBank do vượt quá tỷ lệ theo quy định.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu đến 31/8 việc hợp nhất PGBank và HDBank chưa thể hoàn tất, đại diện Petrolimex cho biết tập đoàn sẽ lên kế hoạch bán 40% cổ phần&nbsp;cho nhà đầu tư khác. Tuy nhiên, việc thực hiện, theo ông Năm, sẽ trên tinh thần thận trọng, tránh vi phạm các điều khoản trong hợp đồng sáp nhập với HDBank.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cái gì phải thoả&nbsp;thuận với HDBank thì thỏa thuận, cái gì PG Bank thực hiện được thì nên thực hiện. Hội đồng quản trị nên sớm đưa các&nbsp;nội dung này để làm việc với HDBank, phải bày tỏ quan điểm không thể chờ mãi được\", ông Năm nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo ông Nguyễn Quang Định, Chủ tịch PGBank, hiện tại ngân hàng này và HDBank vẫn&nbsp;trong quá trình thực hiện sáp nhập, tuy nhiên vướng mắc lớn nhất là Ngân hàng Nhà nước vẫn chưa thông qua đề án&nbsp;cuối cùng. Trước đó,&nbsp;cơ quan quản lý đã chấp thuận&nbsp;về mặt nguyên tắc từ tháng 9/2018.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài vấn đề sáp nhập, cổ tức cũng là một \"điểm nóng\" được quan tâm. Theo đại diện PGBank, việc quyết định tỷ lệ&nbsp;chia cổ tức 10% bằng cổ phếu đã được ngân hàng cân nhắc nhiều yếu tố, bao gồm cả những lo ngại về ảnh hưởng của dịch Covid-19 đến hoạt động.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Về kế hoạch kinh doanh, PGBank năm nay đặt mục tiêu tổng tài sản&nbsp;tăng 7,7% lên gần 34.000 tỷ đồng. Tổng dư nợ cho vay khách hàng tăng 6,6% với tổng huy động tăng 8,3%. Ngân hàng dự kiến thu hồi&nbsp;521 tỷ đồng&nbsp;nợ xấu, trong đó hơn&nbsp;200&nbsp;tỷ là nợ xấu nội bảng và&nbsp;268 tỷ đồng&nbsp;nợ xấu đã bán cho VAMC, còn lại&nbsp;là thu nợ đã xử lý dự phòng rủi ro.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tổng thu nhập hoạt động dự kiến hơn 1.000 tỷ đồng, giảm 13% so với 2019. Tuy nhiên, lợi nhuận dự kiến tăng 112% do giảm dự phòng rủi ro tín dụng. Tỷ lệ nợ xấu đến cuối năm mục tiêu đạt 2,9%, so với mức 3,16% cuối năm 2019.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 2019, tình hình nhân sự của PGBank phần nào được cải thiện sau thời gian dài bị ảnh hưởng bởi thông tin sáp nhập. Số lượng lao động bình quân là 1.579 người với mức lương trung bình gần 13,4 triệu đồng mỗi tháng. Tuy nhiên năm nay, PGBank dự kiến không tuyển thêm người,&nbsp;tập trung kiện toàn bộ máy và nâng cao năng suất.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài ra, các cổ đông cũng thông qua kế hoạch lưu ký và giao dịch cổ phiếu trên sàn UPCoM, uỷ quyền cho HĐQT lựa chọn thời điểm.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Minh Sơn</strong></span></p>', 'weS0_1.jpg', 1, 0, 12, '2020-06-25 00:49:15', '2020-06-25 00:49:15');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(24, 'Nhà bán lẻ đón đầu hoạt động kích cầu mua sắm trực tuyến', 'nha-ban-le-don-dau-hoat-dong-kich-cau-mua-sam-truc-tuyen', 'Các nhà bán hàng đang hưởng lợi nhờ hàng loạt hoạt động xúc tiến tiêu dùng sau Covid-19, kết hợp cùng các lễ hội mua sắm từ nền tảng thương mại điện tử.', '<p><span style=\"font-size:18px\">Chị Nhật Lệ - chủ doanh nghiệp Saigon Life, TPHCM hai tháng gần đây ưu tiên vốn để nhập đa dạng các dòng kem chống nắng, xịt khoáng dưỡng da. Đây là những sản phẩm hiện bán chạy tại cửa hàng online của chị từ tháng 5. Doanh số mặt hàng mỹ phẩm cũng tăng gấp 2 lần so với cùng kỳ năm ngoái nhờ đẩy mạnh bán hàng online trên nền tảng thương mại điện tử.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chị Lệ đánh giá nhu cầu mỹ phẩm vẫn tăng cao mỗi hè về, trùng thời điểm các gia đình, nhóm bạn trẻ đi du lịch. Tuy nhiên sức mua năm nay bật hẳn nhờ các hoạt động xúc tiến du lịch, thương mại diễn ra rầm rộ sau Covid-19. Trong đó có cả chương trình tại sàn mà chị đang đặt cửa hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Từ đầu tháng 5, đội ngũ của Lazada đã liên hệ với tôi để thông báo về chương trình khuyến mãi chủ lực vào giữa năm, cập nhật các hỗ trợ mới nhất đặc biệt là , đưa ra lời khuyên nên áp dụng lên sản phẩm nào\", chị Lệ chia sẻ về chính sách của sàn đã hỗ trợ chị bứt phá về doanh thu sau thời gian chịu ảnh hưởng bởi dịch bệnh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Cửa hàng của Saigon Life trên sàn thương mại điện tử Lazada.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/17/image001-6851-1592384364.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Nkqm_x2IBuQPmlZITJLNMw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cửa hàng của Saigon Life trên sàn thương mại điện tử Lazada.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đồng quan điểm trên, anh Trần Tín - chủ một cửa hàng phụ kiện dành cho phái nữ Lamdepdeal - nhìn nhận hoạt động kích cầu của các nền tảng thương mại điện tử giúp shop của anh vực dậy doanh số so với các tháng đầu năm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước sức mua cao, nhà bán lẻ nếu không chuẩn bị tốt nguồn cung sẽ dễ dẫn đến tình trạng \"thủng hàng\". Hiểu điều đó, từ đầu tháng 5 anh Tín đã tiến hành nhập hàng, đa số là các phụ kiện thời trang, làm đẹp phục vụ nhu cầu đón hè của phái nữ. \"Ưu đãi từ sàn kết hợp cùng chất lượng sản phẩm là cú hích kép khiến lượng giao dịch tăng vọt\", chủ cửa hàng online này chia sẻ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khảo sát gần đây của Criteo, thị trường bán lẻ trực tuyến tại khu vực Đông Nam Á đang phát triển mạnh so với cùng kỳ năm 2019. Doanh số trong tuần thứ 3 của tháng 5 tăng 106% so với cùng kỳ năm 2019.&nbsp;Sự gia tăng của mua sắm trực tuyến với nhiều&nbsp;sản phẩm khác nhau, từ chăm sóc sức khỏe sắc đẹp, trang phục đến điện tử tiêu dùng, thực phẩm và đồ uống đến máy sinh trắc học. Tốc độ phát triển của thương mại điện tử tại Việt Nam&nbsp;cũng đang tăng mạnh. 76% số người Việt được hỏi cho biết&nbsp;họ mua hàng trực tuyến nhiều hơn so với thông thường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chị Lệ và anh Tín là đại diện cho các nhà bán hàng đang tăng tốc kinh doanh trong giai đoạn cao điểm mua sắm, đón bắt xu hướng của thị trường. Năm nay họ không chỉ phục vụ cho nhu cầu sắm sửa vào hè, mà còn đón đầu nền kinh tế số tái khởi động sau dịch bệnh. Nhiều chương trình xúc tiến thương mại trực tuyến đang được thực hiện bởi các cơ quan chính quyền lẫn các đơn vị tư nhân. Đây không chỉ tạo ra cơ hội gia tăng lợi nhuận cho các nhà phân phối lớn, mà cả những nhà bán lẻ quy mô nhỏ và vừa cũng có thể tận dụng để bứt phá.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đơn cử vào đầu tháng 6 mới đây, Sở Công thương Hà Nội thực hiện chương trình khuyến mãi tập trung trong ba tháng 6, 7 và 11, thay vì chỉ diễn ra một lần vào tháng 11 như mọi năm. Mức khuyến mãi cho phép đạt đến 100%, áp dụng cho cả thương mại điện tử. Đây là một trong những chương trình nằm trong chuỗi sự kiện kích cầu nội địa của Hà Nội, nhằm tháo gỡ khó khăn cho các doanh nghiệp, giảm lượng hàng tồn kho...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sở Công Thương TP HCM cũng nhanh chóng triển khai chương trình kích cầu tiêu dùng kéo dài đến 30/7 mang tên \"60 ngày vàng khuyến mãi\". Theo đó, chương trình kêu gọi tất cả doanh nghiệp trên địa bàn thành phố tổ chức khuyến mãi với hạn mức tối đa 100%. Chương trình lập tức nhận sự hưởng ứng của các nhà bán lẻ như Nguyễn Kim, Thiên Hòa... Các chương trình áp dụng cho cả khách mua trực tiếp tại các điểm bán lẫn khách hàng trực tuyến.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ở cương vị doanh nghiệp tư nhân, sàn thường mại điện tử Lazada cũng sớm triển khai nhiều hoạt động thúc đẩy kinh tế số. Trong đó có kế hoạch triển khai lễ hội mua sắm giữa năm \"Sales hè rực rỡ\" diễn ra thường niên. Tuy nhiên thay vì gói gọn trong một ngày như năm ngoái, sàn này đã \"chơi lớn\" khi kéo dài tổng thời lượng ra 5 ngày, từ 18-22/6/2020. Nỗ lực này nhằm thu hút lượng lớn giao dịch từ người tiêu dùng, từ đó hỗ trợ các nhà bán hàng tối ưu lợi nhuận.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Lazada triển khai nhiều ưu đãi trong dịp này.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/17/image003-7639-1592384364.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m7AmJXfn2ONnLc4-Zhqafw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lazada triển khai nhiều ưu đãi kích cầu trong dịp này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cụ thể, sàn này khuyến mãi đến 90% cho sản phẩm đến từ hơn 1.000 thương hiệu uy tín thuộc nhiều ngành hàng như Samsung, Apple, Kiehl\'s, L\'Oréal, Giordano, Supersport, Rohto, Lock&amp;Lock, Lancome, Shiseido... Riêng trong ngày 18/6 sẽ diễn ra chương trình xả hàng chính hãng đến 50%, cùng nhiều sản phẩm đồng giá 1.000 đồng và 99.000 đồng trong các khung giờ 0h, 9h, 12h và 20h.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài các ưu đãi sản phẩm, điểm nổi bật nhất của lễ hội mua sắm giữa năm \"Sales hè rực rỡ\" là chính sách vận chuyển miễn phí không giới hạn cho toàn bộ đơn hàng trong khung giờ từ 12h đến 13h ngày 18/6. Liên tiếp nhiều ngày sau, từ 18-22/6, sàn này miễn phí vận chuyển cho mọi đơn hàng trị giá từ 149.000 đồng. Kể cả những đơn hàng giá trị nhỏ từ 49.000 đồng, Lazada cung cấp cho người dùng các mã giảm phí vận chuyển có thể dễ dàng thu thập trực tiếp trên ứng dụng hoặc thông qua các hoạt động giải trí, chơi game đoán giá, xem livestream...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh các chính sách ưu đãi phí vận chuyển áp dụng cho người mua, sàn thương mại điện tử này còn triển khai chương trình hỗ trợ cho nhà bán hàng mới. Từ 1/6 đến 31/10 các nhà bán hàng mới tham gia bán hàng trên sàn Lazada tại TP HCM và Hà Nội sẽ được hỗ trợ phí vận chuyển cho tối đa 10 đơn hàng, tối đa 9.900 đồng trên một đơn hàng và đã bao gồm thuế VAT.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Cùng với gói Kích cầu kinh tế hỗ trợ 45.000 doanh nghiệp vừa và nhỏ, các ưu đãi miễn phí giao hàng đã minh chứng những nỗ lực của Lazada trong việc đồng hành cùng các nhà bán hàng, doanh nghiệp Việt phục hồi kinh tế hậu Covid-19.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/17/image005-3282-1592384364.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=V4YxlZntr-yW0YFxUR4nyQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cùng với gói Kích cầu kinh tế hỗ trợ 45.000 doanh nghiệp vừa và nhỏ, các ưu đãi miễn phí giao hàng đã minh chứng những nỗ lực của Lazada trong việc đồng hành cùng các nhà bán hàng, doanh nghiệp Việt phục hồi kinh tế hậu Covid-19.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Triển khai hoạt động miễn phí giao hàng cũng đồng nghĩa phía sàn chấp nhận tăng thêm chi phí cho mỗi đơn hàng. Tuy nhiên đây là biện pháp thiết thực để khuyến khích các nhà bán hàng cung cấp hàng hóa, dịch vụ lên nền tảng số, giúp họ tiếp cận cơ hội kinh doanh mới nhiều tiềm năng và trợ lực cho nền kinh tế đang phục hồi sau Covid-19\", chị Vũ Ánh Tuyết - Chánh văn phòng Lazada Việt Nam cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Là một nhà bán hàng tham gia tích cực các hoạt động kích cầu trên sàn Lazada, anh Trịnh Trung Hậu - chủ doanh nghiệp sản phẩm gia dụng nhà bếp Bambam Store chia sẻ thêm, khi rào cản ‘phí giao hàng’ được gỡ bỏ thì khách hàng sẽ quyết định mua hàng rất nhanh, giúp tăng đáng kể tỷ lệ chuyển đổi đơn hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chưa kể, khách hàng cũng sẵn sàng mở rộng hầu bao hơn để sắm vài món nữa khi đã tiết kiệm được một khoản chi phí kha khá từ phí giao hàng. Đây thực sự là một tín hiệu đáng mừng cho những nhà bán hàng như chúng tôi\", ông Hậu nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo ông Steven Nguyễn - Giám đốc Cấp cao khu vực Đông Nam Á của Criteo, khi ngày càng có nhiều người tiêu dùng chuyển sang thương mại điện tử để mua sắm nhu yếu phẩm và các sản phẩm khác, thì các nhà bán lẻ cần tạo ra các chương trình giúp khách hàng xây dựng mối quan hệ lâu dài với kênh hoặc ứng dụng mua sắm trực tuyến. Việc này bao gồm cung cấp các sản phẩm có nhu cầu tiêu dùng cao hoặc các chương trình tri ân khách hàng thân thiết và tạo nhiều ưu đãi cho họ&nbsp;tham gia nhiều nhất.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tận dụng sức hút từ chiến dịch ưu đãi lớn trong năm của Lazada và chính sách ưu đãi cho người bán, các nhà bán lẻ có nhiều cơ hội để không chỉ vực dậy doanh thu sau giai đoạn thấp điểm, mà còn có nhiều cơ hội thiết lập những kỷ lục doanh số.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Bảo An</strong></span></p>', 'U1YT_1.jpg', 1, 3, 13, '2020-06-25 00:53:30', '2020-06-25 00:53:30'),
(25, '\'Mẹ quỷ\': Khi cái ác đội lốt người thân', 'me-quy-khi-cai-ac-doi-lot-nguoi-than', 'Trong phim kinh dị \"The Wretched\", thế lực tà ác len lỏi vào gia đình những người xấu số bằng cách đội lốt người mẹ.', '<p><span style=\"font-size:18px\"><em>(*) Bài viết tiết lộ một phần nội dung phim</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Mẹ</em>&nbsp;q<em>uỷ&nbsp;</em>do hai anh em Brett Pierce và Drew T. Pierce đạo diễn, mang dáng dấp của những phim kinh dị hạng B với ngân sách thấp, nhưng hấp dẫn nhờ nhiều tình tiết bất ngờ (plot twist) được xây dựng chắc tay. Pha trộn yếu tố tuổi thiếu niên và kinh dị siêu nhiên, tác phẩm có&nbsp;kịch bản được xây dựng tốt.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi Ben (John-Paul Howard) bắt đầu cuộc sống mới ở một thị trấn ven rừng, cậu nhận ra những dấu hiệu lạ từ căn nhà sát bên, nơi một gia đình đang đi nghỉ dưỡng. Những đứa con của đôi vợ chồng này dần biến mất và bị lãng quên hoàn toàn, còn người vợ&nbsp;(Azie Tesfai) hành xử kỳ lạ&nbsp;như bị một thế lực hắc ám chiếm lấy. Ngoài ra, nhiều ký tự lạ xuất hiện quanh căn nhà. Không thể thuyết phục bố mình (Jamison Jones) cùng những người xung quanh, Ben phải tự tìm hiểu về sinh vật bí ẩn này<em>&nbsp;</em>để cứu lấy những đứa trẻ và chính gia đình mình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Với những fan cứng của dòng kinh dị, yếu tố hù dọa trong&nbsp;<em>Mẹ quỷ</em>&nbsp;còn&nbsp;nhẹ, ít máu me hay những chiêu trò gây giật mình (jump scares). Thay vào đó, phim tập trung vào những tình tiết gợi sự căng thẳng, những cảnh quay dài gây&nbsp;hồi hộp. Đôi đạo diễn dành nhiều thời gian cho các đoạn ác quỷ \"vờn mồi\", từ từ xâm nhập ngôi nhà, cùng cuộc sống đời thường của Ben, trước khi đẩy cao trào ở phía sau.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Song song với tuyến về thế lực hắc ám muốn bắt cóc những đứa trẻ còn là câu chuyện về những cảm xúc tuổi mới lớn của Ben. Chuyện mẹ quỷ ác độc và nỗi lòng&nbsp;của Ben mang tính nhất quán về chủ đề: Cuộc sống mới khiến Ben phải xa mẹ ruột. Cậu dễ tức giận với nhân tình mới của bố mình và luôn đau đáu cảm giác mẹ mình sẽ bị thay thế. Cảm xúc này của Ben giống với nỗi sợ của những đứa trẻ hàng xóm khi mẹ quỷ đã chiếm lấy thân xác mẹ ruột của chúng. Ở nhiều cảnh, đạo diễn mô tả diễn biến từ góc nhìn của những đứa trẻ dần cảm nhận được người thân của chúng bị&nbsp;thế lực hắc ám chiếm hữu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Diễn xuất của nam diễn viên trẻ John-Paul Howard vừa vặn và hợp với nhân vật Ben. Ngoại hình ưa nhìn của cậu cũng là một điểm sáng của phim. Ảnh: IFC Films.\" src=\"https://i1-giaitri.vnecdn.net/2020/06/24/The-Wretched-Ben-2-9088-1592994315.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ryjMPIPTyWWu4KMMG37j2Q\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Diễn xuất của nam diễn viên trẻ John-Paul Howard vừa vặn và hợp với nhân vật Ben. Ngoại hình ưa nhìn của cậu cũng là một điểm sáng của phim. Ảnh:&nbsp;<em>IFC Films.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Qua cách con người dần khám phá về các sinh vật lạ, phim cũng gợi lại không khí của các tác phẩm ly&nbsp;kì, kinh dị của thập niên 1980 như&nbsp;<em>Close Encounters of the Third Kind (</em>Steven Spielberg)<em>&nbsp;</em>hay&nbsp;<em>Alien (</em>Ridley Scott). Phân đoạn Ben dùng ống nhòm để theo dõi những người hàng xóm đáng nghi gây liên tưởng&nbsp;đến tác phẩm kinh điển&nbsp;<em>Rear Window</em>&nbsp;của đạo diễn Alfred Hitchcock.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Điểm sáng của phim chính ở cú lật (plot twist) ở hồi ba, khiến toàn bộ nhận thức của nhân vật Ben về chuyện trước đó&nbsp;thay đổi. Ê-kíp phim đã&nbsp;xây dựng rất kỹ toàn bộ câu chuyện từ đầu để tình tiết bất ngờ này&nbsp;đạt được hiệu quả cao nhất. Mọi thứ đều được gieo vừa vặn và gặt đúng lúc. Tình tiết này giúp phim giữ được sự lôi cuốn, dù ở hồi kết, bối cảnh phim hơi đơn điệu còn yếu tố chiến đấu ở cao trào chỉ chưa đặc sắc.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tác phẩm không phải phim kinh dị của hãng lớn của Hollywood. Do đó, kỹ xảo trong phim chỉ ở mức chấp nhận được. Ở một số&nbsp;cảnh hù dọa. hành vi của ác quỷ còn&nbsp;khá dễ đoán.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ra mắt ở LHP Quốc tế Fantasia (Canada) vào tháng 7/2019,&nbsp;<em>Mẹ quỷ</em>&nbsp;nhận nhiều lời khen với 74% đánh giá tích cực trên<em>&nbsp;Rotten Tomatoes</em>. Ở Mỹ, phim khởi chiếu từ tháng 5 ở một số ít rạp còn mở, cũng như phát hành qua các dịch vụ trực tuyến. Ở Việt Nam, phim đang chiếu với nhãn C18 (không dành cho người dưới 18 tuổi).</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Luân Nguyễn</strong></span></p>', 'DUI7_1.jpg', 1, 0, 18, '2020-06-25 00:55:49', '2020-06-25 00:55:49'),
(26, 'Rhymastic ra MV đả kích lối sống vật chất', 'rhymastic-ra-mv-da-kich-loi-song-vat-chat', 'Rapper Rhymastic hóa thân vào bốn nhân vật trong video nhạc \"Giàu sang\" để phản ánh mặt trái của đồng tiền.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Ca khúc thuộc thể loại Electro Funk do ca sĩ sáng tác, hòa âm và đọc rap. Thông qua các tình huống: cầu thủ bị chơi xấu, trọng tài ngoảnh mặt làm ngơ vì nhận hối lộ, hooligan vướng vào tệ nạn&nbsp;đánh mất gia đình... Rhymastic thể hiện cái nhìn anh về chuyện đồng tiền chi phối cuộc sống.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/XQkfHFA2aP0\" width=\"560\"></iframe></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:arial\">MV \"Giàu sang\" - Rhymastic. Video:</span><em>&nbsp;Youtube.&nbsp;</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">MV đánh dấu sự trở lại của rapper sau&nbsp;cưới vợ đầu năm ngoái. Anh cho biết bà xã là hậu phương vững chắc để anh hoạt động âm nhạc,&nbsp;thêm động lực làm việc,&nbsp;chu toàn cuộc sống gia đình.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Rapper Rhymastic. Ảnh: Nhân vật cung cấp.\" src=\"https://i1-giaitri.vnecdn.net/2020/06/24/rymastic-9435-1592968618.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XFIPBX2Sa5J_qN3xMy0goA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Rapper&nbsp;Rhymastic. Ảnh:&nbsp;<em>Nhân vật cung cấp.&nbsp;</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Rhymastic tên thật Vũ Đức Thiện, sinh năm 1991, vào làng nhạc năm 2011, nhanh chóng tạo sự chú ý trong giới underground. Anh từng tốt nghiệp đại học kiến Trúc Hà Nội. Gần 10 năm vào nghề, rapper có các bản hit như:&nbsp;<em>Yêu 5, Treasure, Phán xét, Nến và hoa...&nbsp;</em></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Tâm Giao</strong></span></p>', 'DzWs_1.jpg', 1, 4, 19, '2020-06-25 14:56:33', '2020-06-25 15:00:26'),
(27, 'Gần 2.300 cây xanh \'nghe\' nhạc', 'gan-2-300-cay-xanh-nghe-nhac', 'TÂY BAN NHA - Nhóm tứ tấu UceLi biểu diễn trước 2.292 cây xanh, trước khi tặng chúng cho hàng nghìn nhân viên y tế ở tuyến đầu chống dịch.', '<p style=\"text-align: center;\"><span style=\"font-size:18px\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/i9BRbtIgipQ\" width=\"560\"></iframe></span></p>\r\n\r\n<div class=\"inner_caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: optimizelegibility;\">\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px\">Phần biểu diễn của nhóm UceLi. Video:<em>&nbsp;Youtube</em>.</span></p>\r\n</div>\r\n\r\n<p><span style=\"font-size:18px\">Theo&nbsp;<em>NPR</em>, nhà hát Opera&nbsp;The Gran Teatre del Liceu tại Barcelona mở lại hôm 22/6, tổ chức buổi diễn đặc biệt với \"khán giả\" là 2.292 cây xanh trong khán phòng. Nhà tổ chức và nghệ sĩ tin rằng âm nhạc có thể đem đến sự bình yên và cảm thông tới từng chậu cây. Số cây này sau đó sẽ được tặng tận tay các y bác sĩ nhằm tri ân công sức của họ thời gian qua.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Buổi biểu diễn trước 2.300 cây xanh của nhóm tứ tấu UceLi. Ảnh: AP.\" src=\"https://i1-giaitri.vnecdn.net/2020/06/24/bieu-dien-nhac-truoc-cay-xanh-3912-1592988295.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=l9kI-n0ANpL3sp1fBZrnFQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Buổi biểu diễn trước gần 2.300 cây xanh của nhóm tứ tấu UceLi. Ảnh:&nbsp;<em>AP.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bắt đầu buổi&nbsp;diễn, ban tổ chức phát thông báo yêu cầu \"khán giả\" giữ yên lặng, hạn chế rời chỗ ngồi và không sử dụng điện thoại. Nhóm tứ tấu UceLi bước ra sân khấu&nbsp;cúi chào. Họ biểu diễn bản<em>&nbsp;Crisantemi</em>&nbsp;của nhà soạn nhạc người Italy - Giacomo Puccini. Khi kết thúc, ban tổ chức dùng gió thổi vào cây tạo tiếng xào xạc, thay cho tiếng vỗ tay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chương trình&nbsp;cũng được truyền hình trực tiếp trên mạng xã hội và kêu gọi gây quỹ. Số tiền thu được sẽ được ủng hộ cho các tổ chức y tế.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhóm tứ tấu UceLi cho biết: \"Sau quãng thời gian kỳ lạ và đau thương, quản lý nhà hát cho chúng tôi cơ hội trở lại biểu diễn. Ý tưởng của buổi diễn gợi nhớ sự quan trọng của mối quan hệ giữa con người với tự nhiên\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tây Ban Nha nới lỏng lệnh phong tỏa xã hội từ cuối tuần trước, sau ba tháng. Đến ngày 24/6, nước này ghi nhận 293.832 ca nhiễm, 28.325 ca&nbsp;tử vong. Theo các nhà chức trách, rạp hát và phòng chiếu phim được phép mở cửa trở lại nhưng hạn chế số khách và áp dụng các quy định phòng dịch theo hướng dẫn.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Đạt Phan&nbsp;</strong><span style=\"font-family:arial\">(theo&nbsp;</span><em>NPR</em><span style=\"font-family:arial\">)</span></span></p>', 'mMMn_1.jpg', 1, 1, 19, '2020-06-25 15:15:07', '2020-06-25 15:15:07'),
(28, 'Chelsea - Man City và trận cầu có thể định đoạt ngôi vương', 'chelsea-man-city-va-tran-cau-co-the-dinh-doat-ngoi-vuong', 'Nếu không thắng chủ nhà Chelsea ở vòng 31 hôm nay, Man City sẽ phải chấp nhận nhường lại ngôi vương giải Ngoại hạng Anh cho Liverpool.', '<p><span style=\"font-size:18px\"><em>*Chelsea - Man City: 2h15 ngày 26/6, giờ Hà Nội.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đương kim vô địch Man City đang kém Liverpool 23 điểm. Họ buộc phải thắng Chelsea, để rút ngắn cách biệt xuống còn 20 điểm. Nếu họ hòa hoặc thua chủ sân Stamford Bridge, cách biệt giữa hai đội dẫn đầu sẽ lớn hơn 21 điểm.&nbsp;Man City chỉ có thể&nbsp;kiếm&nbsp;tối đa 21 điểm trong bảy trận còn lại, không đủ để ngăn Liverpool vô địch ngay đêm nay. Vòng tiếp theo, Man City đón vị khách Liverpool. Theo quy định ở&nbsp;trận đấu đầu tiên của đội vừa đăng quang Ngoại hạng Anh, đối thủ sẽ phải xếp hàng chào đón đương kim vô địch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Man City (trái) không có nhiều mục tiêu khi hành quân đến sân của Chelsea. Ảnh: Reuters.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/man-city-reuters-1593082733-15-5674-3336-1593083284.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZxlxEEdDkRc96drTLzDkNw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Man City (trái) không có nhiều mục tiêu khi hành quân đến sân của Chelsea. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Người mến mộ Man City sẽ không muốn thấy cảnh tượng đó. Nhưng người trong cuộc tỏ ra không quan tâm đến điều này.&nbsp;Chính HLV Jurgen Klopp cũng nói trước trận thắng Crystal Palace rằng: \"Tôi không quan tâm Liverpool sẽ vô địch ở đâu, và như thế nào\". Còn HLV Pep Guardiola cũng không phấn đấu đả bại Chelsea. Chiến lược gia Tây Ban Nha nói hôm 24/6, trong họp báo trước trận: \"Chelsea và Liverpool rất mạnh, nhưng trận gặp Newcastle ở tứ kết Cup FA ngày 28/6&nbsp;lại có ý nghĩa như trận chung kết\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Guardiola không giấu ý định cất giữ trụ cột cho trận gặp Newcastle ba ngày sau. Lịch đấu dày đặc cũng ảnh hưởng đến thể lực cầu thủ, khiến ông khó lòng tung đội hình mạnh nhất. Dù sao, đội hình hai của Man City cũng không kém đội chính nhiều. Chỉ có thủ môn Ederson, tiền vệ David Silva và Riyah Mahrez đá chính cả hai trận gần nhất. Nhưng Man City vẫn ghi tám bàn vào lưới&nbsp;Arsenal&nbsp;và&nbsp;Burnley, đồng thời không thủng lưới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Giroud (trái) có thể đá chính sau khi ghi bàn giúp Chelsea hạ Aston Villa. Ảnh: Reuters.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/giroud-1593082931-1593082953-8796-1593083284.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=21VHQ3YrGzxl34EqmKc3pQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Giroud (trái) có thể đá chính sau khi ghi bàn giúp Chelsea hạ Aston Villa. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Chelsea bị đánh giá dưới cơ Man City, nhưng họ sẽ vào trận với quyết tâm cao&nbsp;hơn</strong>. Thầy trò Frank Lampard đặt mục tiêu bảo vệ vị trí trong top bốn. Họ&nbsp;đang hơn Man Utd hai điểm, và chơi ít hơn một trận. Nhưng lịch đấu đầy giông bão sắp tới khiến Chelsea không thể chủ quan. Ngoài Man City, họ vẫn còn phải đụng Sheffield, Liverpool và Wolverhampton trong những trận còn lại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tiền vệ N\'Golo Kante trở lại giúp chủ nhà gia cố tuyến giữa. Trong trận thắng ngược Aston Villa vòng trước, những kép phụ như Christian Pulisic và Olivier Giroud tỏa sáng, cho thấy chiều sâu đội hình Chelsea. Lampard phải tính tới xoay vòng cầu thủ, khi họ cũng đấu tứ kết Cup FA với Leicester ngày 28/6.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lampard chào đón tiền vệ Jorginho trở lại sau án treo giò hai trận, trong khi Man City có thể vắng tiền đạo Sergio Aguero đến hết mùa. Guardiola có thể cho David Silva và Mahrez dự bị ở Stamford Bridge, nhưng Kevin De Bruyne và Raheem Sterling sẽ trở lại đá chính.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Aguero có thể vắng mặt đến hết mùa. Ảnh: PA.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/aguero-1593082843-4987-1593083285.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aZAHWZXRD9BRPnL56PckTQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Aguero có thể vắng mặt đến hết mùa. Ảnh:&nbsp;<em>PA</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chelsea vẫn đang là đội đả bại&nbsp;Man City nhiều nhất ở Ngoại hạng Anh, với 25 chiến thắng. Nhưng Man City thắng sáu trong bảy cuộc đối đầu gần nhất trên mọi đấu trường. Vào top bốn mùa này là thành công với Lampard. Cựu tiền vệ Chelsea có thể mơ về vị trí cao hơn mùa tới, khi đã bổ sung Timo Werner hay Hakim Ziyech. Trong tay Lampard là dàn cầu thủ cây nhà lá vườn, còn trẻ và nhiều khả năng tiến xa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bằng mọi giá thầy trò Lampard phải giành suất dự Champions League mùa tới. Vị trí thứ năm cũng có thể giúp họ hoàn thành mục tiêu, nếu Man City không&nbsp;kháng án&nbsp;thành công. Nhưng, Man Utd và Wolverhampton đều đang \"vào phom\" và có thể chen chân vào top bốn. Chelsea vẫn đang hoạt động tích cực để mang về Stamford Bridge thêm nhiều ngôi sao. Suất dự Champions League là điều kiện cần để chiêu dụng tài năng. Chiến thắng trước Man City sẽ là lời khẳng định đanh thép cho quyết tâm của thầy trò Lampard.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Man City dẫu sao cũng sẽ quyết thắng để không phải chúc mừng nhà vô địch trên sân Etihad. Klopp hiểu rằng phát biểu của Guardiola trước trận chỉ để khiến Chelsea chủ quan. \"Guardiola thực sự nói không coi trọng trận gặp Chelsea sao? Cái quái gì thế? Chắc ông ta&nbsp;đang chơi poker thôi\", HLV người Đức nói.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Xuân Bình</strong></span></p>', 'Jjth_1.jpg', 1, 3, 24, '2020-06-25 15:41:07', '2020-06-25 15:41:07'),
(29, 'Những kiểu mặc hot hè năm nay', 'nhung-kieu-mac-hot-he-nam-nay', 'Váy tay phồng và sandals tối giản duy trì sức hút sau ba năm \"làm mưa làm gió\" trên sàn diễn và trong đời thường.', '<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/1-1592902281.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2uUj9ttsOGZW5oWD0c3YDQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Trang phục họa tiết nhiệt đới&nbsp;được các sàn diễn lăng xê, tín đồ thời trang yêu thích. Bạn có thể mặc theo gợi ý của Versace Xuân Hè 2020 với áo khoác, quần shorts, dép tông, tank top đồng màu xanh lá.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/tay-phong-1592902284.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YaK6RFpnkMIPzROf7kA9zQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Phong cách hot trong mùa hè năm nay còn có váy tay bồng theo kiểu thập niên 1980 ngắn trên gối hoặc dài tới cổ chân, phối cùng sandals tối giản.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/ao-nhuom-1592902282.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TtRbcRW8nIqGjM1yeIZFkg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Áo sơ mi nhuộm loang màu</span><strong>&nbsp;</strong><span style=\"font-family:arial\">và quần baggy, sneakers cơ bản.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/LINEN-DRESS-1592902283.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pNQCqCld2iY2QSZ7WwAeMg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Đầm linen dáng&nbsp;maxi và hoa tai lệch bên được Jacquemus&nbsp;lăng xê trong show Xuân Hè 2020 trở thành kiểu mặc gây sốt trên mạng xã hội.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/khoe-noi-y-1592902335.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X4onD74ZtHr1YAMcFV2MFg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Mùa hè năm nay, blazer lên ngôi với kiểu mặc cùng&nbsp;áo lót.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/hoa-tiet-hoa-1592902283.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Bblm6B9wvl4BZZzoyOHMfQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Cách phối hot còn có áo và váy họa tiết hoa, đòi hỏi người mặc khéo léo, tinh tế để tránh rối mắt, rườm rà.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/dam-lua-1592902282.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_pdeicVnI8hifVRzFdsCxQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Váy lụa hai dây là món đồ luôn được yêu thích mỗi khi hè về. Kiểu dáng thiết kế lấy cảm hứng từ váy ngủ, đầm hai dây mỏng cùng sneakers tạo thoáng mát, gợi cảm cho người mặc.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/gigi-hadid-1592902283.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uNlqaaPGI2NL3sy8cFGvnA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Diện áo phông dài ngang đùi theo mốt giấu quần, đi kèm bốt là cách mặc được nhiều cô gái lựa chọn bởi nét phóng khoáng, dễ chịu trong ngày nóng.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/neon-1592902284.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eutR4RujVhGjHSrT2Yk0qQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Diện cả bộ màu neon từ trang phục tới phụ kiện là sự lựa chọn dành cho những tín đồ thời trang có cá tính mạnh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giaitri.vnecdn.net/2020/06/23/BOXER-SHORTS-1592902282.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VAW6GaQf9SjuwO8bNoMDeQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Quần đùi thể thao rộ lên trong năm nay nhận được sự hưởng ứng của nhiều cô gái.&nbsp;Bằng cách phối bra top, tank top, người mặc toát lên phong cách khỏe khoắn, năng động.</span></span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Sao&nbsp;Mai</strong><span style=\"font-family:arial\">&nbsp;(ảnh:&nbsp;</span><em>Pinterest, SDM, Gorunway</em><span style=\"font-family:arial\">)</span></span></p>', 'LGdL_1.jpg', 1, 1, 20, '2020-06-25 17:28:07', '2020-06-25 17:28:07'),
(30, 'Bố Djokovic: \'Lỗi tại Dimitrov\'', 'bo-djokovic-loi-tai-dimitrov', 'Ông Srdjan Djokovic cho rằng tay vợt Grigor Dimitrov mang nCoV đến Adria Tour và làm tổn hại đến hai nước Serbia và Croatia.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">\"Làm sao chuyện này xảy ra được?\", bố Djokovic nói trên&nbsp;</span><em>RTL&nbsp;</em><span style=\"font-family:arial\">hôm 24/6. \"Gã này nhiễm virus từ đâu đó rồi mới đến đây. Anh ta không xét nghiệm ở đây, mà ở chỗ khác. Tôi không nghĩ điều đó đúng đắn. Dimitrov đã gây thiệt hại cho Croatia và Serbia, trong đó có gia đình chúng tôi\".</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Ông Srdjan Djokovic (phải) bênh vực con trai. Ảnh: AP.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/srdjan-djokovic-not-fan-of-aus-4611-9988-1593049441.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ByXFvrYNKcYSm2yKMsKTwg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ông Srdjan Djokovic (phải) bênh vực con trai.&nbsp;<em>Ảnh: AP.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Grigor Dimitrov là tay vợt đầu tiên trong top 100 thông báo bản thân dương tính với nCoV, hôm 21/6. Ít ngày sau, Borna Coric, Viktor Troicki và Novak Djokovic lần lượt dương tính với virus. Đây đều là các tay vợt tham dự Adria Tour - chuỗi giải từ thiện của&nbsp;Djokovic ở vùng Balkan giữa mùa dịch.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi Djokovic bị cả thế giới&nbsp;lên án&nbsp;vì tổ chức giải mà không có biện pháp cách ly nào, cha anh lại đẩy trách nhiệm sang cho Dimitrov. Đại diện tay vợt Bulgaria sau đó phản pháo, khi nói rằng Dimitrov tuân thủ cách ly xã hội trước khi đến Belgrade, Serbia và Zadar, Croatia để dự hai chặng Adria Tour. Tay vợt này cũng không được yêu cầu xét nghiệm nCoV khi tham gia giải.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại chặng thứ hai&nbsp;Adria Tour, mỗi ngày có khoảng 4.000 khán giả vào sân, ngồi sát nhau và không tuân thủ giãn cách xã hội.&nbsp;Các tay vợt lớn, sau khi thi đấu, còn giao lưu với người hâm mộ bằng cách nhảy múa&nbsp;và hò hét cùng đám đông. Họ sau đó còn đi hộp đêm cùng nhau. Chặng thứ hai tại Zadar, Croatia bị hủy ngay trước thềm trận chung kết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Djokovic, sau khi nhiễm nCoV, xin lỗi công khai đến những người liên quan. Báo giới Serbia thì&nbsp;bênh vực anh&nbsp;khi cho rằng tay vợt số một&nbsp;thế giới tôn trọng tất cả quy định về sức khỏe của Serbia và Croatia, nơi hai chặng đầu tiên của Adria Tour được cấp phép&nbsp;tổ chức.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Nhân Đạt</strong>&nbsp;(theo&nbsp;<em>RTL</em>)</span></p>', 'uXNB_1.jpg', 1, 0, 25, '2020-06-25 17:38:12', '2020-06-25 17:38:12'),
(31, 'Vòng 6 V-League và sự khác biệt từ Công Phượng', 'vong-6-v-league-va-su-khac-biet-tu-cong-phuong', 'Tiền đạo Công Phượng ghi bàn và TP HCM lên ngôi đầu là điểm nhấn lớn nhất khi vòng 6 V-League khép lại hôm 24/6.', '<p><span style=\"font-size:18px\"><img alt=\"Trận thắng SLNA là trận thứ hai liên tiếp Công Phượng ghi bàn cho TP HCM. Ảnh: Nam Anh.\" src=\"https://i1-thethao.vnecdn.net/2020/06/25/CP-5647-1593063036.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TGAq0ZPEDt8aowRytyC-aw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trận thắng SLNA là trận thứ hai liên tiếp Công Phượng ghi bàn cho TP HCM. Ảnh:&nbsp;<em>Nam Anh</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không vội vã và cũng chẳng ồn ào, cái tên Công Phượng một lần nữa lại được nhắc đến ở vòng 6 V-League, vòng đấu chính thức tạo ra những \"cuộc chiến\" ở hai đầu bảng điểm, khiến V-League có một khởi đầu vô cùng lôi cuốn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">SLNA bất ngờ lên đỉnh bảng&nbsp;sau vòng 5 với thành tích không để thủng lưới, nhưng thất bại 1-3 ngay trên sân nhà dưới tay TP HCM đã trả đội này về với hiện thực phũ phàng: họ sẽ chẳng bao giờ biết được bản thân sẽ đi xa đến đâu nếu không tìm ra cách ghi bàn. Trong một ngày thi đấu không đạt phong độ tốt nhất, bất kỳ hàng phòng ngự vững chắc nào cũng có thể bị đổ sập nếu đối phương nhìn ra được những lỗ rò của nó. Điểm yếu của SLNA không phải là phòng thủ, mà là không có sự cân bằng giữa ba&nbsp;tuyến.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu đá với một đội tấn công ồ ạt như Hà Nội, SLNA chỉ cần làm tốt một việc, đó là tránh để thủng lưới và chờ đối thủ tự sụp đổ. Nhưng gặp TP HCM, SLNA như \"gà mắc tóc\" khi đội khách không vồ&nbsp;vập trong tấn công. Hai đối thủ có cùng quan điểm chơi bóng, thì đội&nbsp;nào sở hữu hàng tấn công chất lượng hơn, đội đó sẽ thắng trận. Những cầu thủ như Công Phượng chính là sự khác biệt, cũng là thứ mà SLNA đã thiếu&nbsp;từ khi Lê Công Vinh ra đi hồi 2014.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thất bại trước TP HCM là một ngã rẽ nguy hiểm cho SLNA. Số bàn thắng của họ hiện còn kém đội đang đứng chót bảng Nam Định. Trong khi bàn thắng khó mà tăng lên, bàn thua rồi sẽ còn thêm nhiều nữa trong một cuộc đua đường dài. Vì vậy, vị trí thứ hai&nbsp;của SLNA hiện nay không nói lên quá nhiều điều, nếu không cải thiện khâu ghi bàn, thì qua thời gian&nbsp;đội bóng xứ Nghệ cũng sẽ phải tụt dần xuống bên dưới. Họ cần tìm ngay một chân sút.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Công Phượng ghi bàn vào lưới SLNA và đưa TP HCM lên đỉnh bảng là câu chuyện thú vị nhất vòng 6</strong>. Tiền đạo này vốn không đủ chất lượng để vào năng khiếu của Nghệ An, nhưng hiện tại là người mà SLNA chỉ còn biết mơ ước. Nhưng SLNA chưa hẳn là đội \"khao khát\" Công Phượng nhất, mà ngược lại, đó lại là HAGL, đội bóng về nguyên tắc đang sở hữu tiền đạo người xứ Nghệ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thất bại trên sân Hòa Xuân của Đà Nẵng mới là trận thua thứ hai&nbsp;trong mùa của HAGL nhưng đây đã là trận thứ tư&nbsp;từ khi V-League trở lại, HAGL ghi không quá một&nbsp;bàn. Sau bốn&nbsp;vòng gần nhất, họ mới ghi ba&nbsp;bàn, trong khi thủng lưới đến bảy. Hiệu số của HAGL sau sáu vòng đấu hiện là 7-10, tức là chẳng khả quan hơn hiệu số của đội chót bảng Nam Định (5-11).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hàng thủ của HAGL chẳng có gì đáng nói, vẫn tệ như năm&nbsp;năm qua, nhưng số bàn thắng của họ lại đáng báo động. Hồi giai đoạn một mùa trước, một mình tiền đạo người Jamaica Chevaughn Walsh đã ghi đến bảy bàn và có năm pha kiến tạo cho HAGL. Sau sáu trận mùa này, Chevaughn Walsh ít nhiều thì cũng đang đóng góp với ba&nbsp;bàn thắng. Như vậy, phần còn lại của HAGL vẫn đang đối diện với những gì mà họ... đối diện suốt bao năm qua, đó là một lối chơi không rõ ràng, một quan điểm thi đấu luôn chờ đợi người khác \"để cho mình được chơi bóng\" thay vì phải chiến đấu cật lực để tìm cách đánh bại đối phương.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù HLV Lee Tae-hoon&nbsp;phủ nhận&nbsp;việc HAGL cần Công Phượng, thực tế cho thấy đội bóng phố núi chẳng có một tiền đạo nội nào xuất sắc. Sự kỳ vọng quá mức với Văn Toàn đang khiến anh đánh mất tự tin trong các pha bóng cận thành. Văn Toàn chỉ đá tròn vai khi dạt xa ngoài biên. Những gì mà Công Phượng đang làm ở TP HCM&nbsp;chính xác là điều mà HAGL cần lúc này. Đó là&nbsp;một cầu thủ có năng lực khuấy đảo hệ thống phòng thủ để Walsh tận dụng sức mạnh cá nhân, đưa bóng vào lưới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vấn đề bây giờ là các chi tiết trong thỏa thuận cho mượn Công Phượng mà HAGL đã làm việc với CLB TP HCM, bởi thời hạn cho mượn theo lý thuyết sẽ kết thúc vào cuối tháng 6, theo lịch trình của thị trường chuyển nhượng châu Âu, dựa trên hợp đồng đầu tiên giữa HAGL và Sint-Truiden. Rắc rối nằm&nbsp;ở chỗ, với những gì Công Phượng đang đem lại, TP HCM có lẽ sẽ muốn mua đứt luôn ngôi sao này hoặc tìm cách giữ Công Phượng càng lâu càng tốt nhằm phục vụ tham vọng vô địch mà họ đang đi đúng lộ trình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Với TP HCM hiện nay, Công Phượng không chỉ là một tay săn bàn. Đó còn là một biểu tượng đối trọng với&nbsp;dàn sao ở Hà Nội FC, trong một cuộc chiến cũng quan trọng không kém bên ngoài sân và trên truyền thông. Công Phượng vừa đính hôn, ghi hai&nbsp;bàn trong hai trận thắng liên tiếp của TP HCM. Trong khi Hà Nội vừa nhận trận thua trên sân nhà ở vòng 5, thì ngôi sao&nbsp;Quang Hải lại&nbsp;gặp rắc rối&nbsp;ở khía cạnh cá nhân. Dù đã cố gắng ra sân, chơi bóng với quyết tâm cao, ngoài hình ảnh lăn xả với chiếc&nbsp;băng quấn ngang đầu&nbsp;vì&nbsp;chấn thương thì màn trình diễn của Quang Hải trong tâm điểm dư luận lại khá mờ nhạt.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ở một vòng đấu mà có đến năm&nbsp;đội khách giành kết quả tốt, chiến thắng của Hà Nội trên Bình Dương không đem lại nhiều ấn tượng. Nhưng cái cách mà đội bóng Thủ đô cố gắng bảo vệ hình ảnh của Quang Hải cho thấy nhà ĐKVĐ đang đối diện với cuộc khủng hoảng nhỏ. Họ đang cố giải quyết vấn đề đó thật nhanh, trước khi nghĩ đến cuộc đua vô địch. Thay vì tìm cách hạ&nbsp;Bình Dương với tỷ số cao hơn, suốt cả trận, hễ có cơ hội, các đàn anh của Quang Hải sẽ tìm cách giúp cầu thủ này lấy lại cảm giác thi đấu. Ngay cả những ngoại binh như Rimario vẫn cố gắng nhường cơ hội ghi bàn cho Quang Hải. Nhưng cầu thủ đang ở trong \"tâm bão truyền thông\" không thể thắng được chính mình. Quang Hải cần thêm thời gian trong bối cảnh Hà Nội lại đang gặp áp lực lớn đến từ TP HCM.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Song Việt</strong></span></p>', 'h9jy_1.jpg', 1, 1, 24, '2020-06-25 17:40:44', '2020-06-25 17:40:44'),
(32, 'Khi ly hôn có được đòi tiền lương đã giao cho vợ?', 'khi-ly-hon-co-duoc-doi-tien-luong-da-giao-cho-vo-', 'Chồng tôi có mức lương rất cao, theo thỏa thuận, tôi sẽ quản lý tất cả để lo liệu cuộc sống gia đình. (Hoàng Linh)', '<p><span style=\"font-size:18px\">Anh ấy cũng ít khi để tâm tôi chi tiêu gì. Đầu tháng này, chồng tôi đơn phương ly hôn song lại yêu cầu trả lại một phần tiền lương anh ấy đã làm trong gần chục năm chung sống. Chồng tôi có căn cứ hợp pháp làm vậy không?</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Luật sư tư vấn</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo điều 33 Luật Hôn nhân và gia đình năm 2014, tài sản chung của vợ, chồng bao gồm:</span></p>\r\n\r\n<p><span style=\"font-size:18px\">1. Tài sản chung của vợ chồng gồm tài sản do vợ, chồng tạo ra, thu nhập do lao động, hoạt động sản xuất, kinh doanh, hoa lợi, lợi tức phát sinh từ tài sản riêng và thu nhập hợp pháp khác trong thời kỳ hôn nhân, trừ trường hợp được quy định tại khoản 1 điều 40 của Luật này (quy định về việc chia tài sản chung trong thời kỳ hôn nhân); tài sản mà vợ chồng được thừa kế chung hoặc được tặng cho chung và tài sản khác mà vợ chồng thỏa thuận là tài sản chung.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Quyền sử dụng đất mà vợ, chồng có được sau khi kết hôn là tài sản chung của vợ chồng, trừ trường hợp vợ hoặc chồng được thừa kế riêng, được tặng cho riêng hoặc có được thông qua giao dịch bằng tài sản riêng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">2. Tài sản chung của vợ chồng thuộc sở hữu chung hợp nhất, được dùng để bảo đảm nhu cầu của gia đình, thực hiện nghĩa vụ chung của vợ chồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">3. Trong trường hợp không có căn cứ để chứng minh tài sản mà vợ, chồng đang có tranh chấp là tài sản riêng của mỗi bên thì tài sản đó được coi là tài sản chung.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Căn cứ quy nói trên,&nbsp;tiền lương của chồng bạn được tạo lập trong thời kỳ hôn nhân, nên được xác định là tài sản chung của vợ, chồng. Khoản này không thể là tài sản riêng của chồng bạn nên anh ấy không có quyền đòi lại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hơn nữa, theo khoản 2 điều 29 Luật này,&nbsp;vợ, chồng có nghĩa vụ bảo đảm điều kiện để đáp ứng nhu cầu thiết yếu của gia đình. Do vậy, việc bạn sử dụng tiền lương của chồng để nuôi sống gia đình, đáp ứng các nhu cầu ăn, học của con cái là hoàn toàn có căn cứ và hợp pháp. Chồng bạn không có quyền yêu cầu hoàn trả.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Luật sư Đỗ Trọng Linh</strong><br />\r\n<em>Công ty luật Bảo An, Hà Nội</em></span></p>', 'GnGM_1.jpg', 1, 7, 27, '2020-06-25 17:44:45', '2020-06-25 17:44:45');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(33, 'Đòn trả thù của người vợ', 'don-tra-thu-cua-nguoi-vo', 'TRUNG QUỐC - Từ xa, tài xế taxi tưởng trên thảm cỏ trước mặt có người đàn ông đang nằm ngủ, nhưng tới gần mới biết đây là xác chết.', '<p style=\"text-align: right;\"><span style=\"font-size:18px\">8h42&nbsp;ngày 10/5/2009, tài xế gọi điện cho cảnh sát huyện Bân, tỉnh Thiểm Tây báo tin phát hiện thi thể bên đường, gần đó&nbsp;có chiếc hòm gỗ màu nâu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo kết quả khám nghiệm, nạn nhân là đàn ông,&nbsp;gần 30 tuổi, cao 1m72, mặc quần kaki sáng màu, áo phông dài tay có in các kí tự tiếng Anh. Nạn nhân đã chết khoảng bốn ngày trước, quanh cổ&nbsp;quấn đoạn dây vải&nbsp;được cắt từ ga trải giường. Phần ga trải giường còn lại được dùng để bọc bên ngoài thi thể cùng với chiếc khăn tắm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Áo của nạn nhân. Ảnh: CCTV.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/22/01-7795-1592821452.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=8G-CfFXFaR47KYpYmPMU7w\" style=\"height:100%; width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-size:14px\"><span style=\"font-family:arial\">Quần áo của nạn nhân. Ảnh:&nbsp;</span><em>CCTV.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nạn nhân nằm trên bãi cỏ cách rãnh thoát nước bên đường hơn một mét, mặt đất bên cạnh có vết đào bới bằng tay. Trên lề đường bên kia rãnh thoát nước có hòm gỗ màu nâu đặt trên cán hai cây lau nhà. Đây là loại hòm gỗ dùng để chứa đồ tương đối phổ biến ở địa phương, dài 90 cm, rộng 60 cm, cao 40 cm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cảnh sát xác định đây là vụ giết người vứt xác. Khăn tắm và ga trải giường cho thấy hiện trường&nbsp;ở trong&nbsp;căn nhà&nbsp;có phòng tắm và phòng ngủ. Hai cây lau nhà được dùng để khiêng hòm gỗ, cho thấy có ít nhất hai người tham gia. Xác vứt&nbsp;bên phải đường theo hướng từ trung tâm huyện đến thị trấn nên nghi phạm có khả năng cao đến từ trung tâm huyện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Người dân đi làm đồng qua vị trí này lúc 8h30 khẳng định khi đó bên đường chưa có hòm gỗ, như vậy là thời gian vứt xác diễn ra trong khoảng từ 8h30 đến 8h43. Các vết đào bới bằng tay cho thấy ban đầu hung thủ định đào hố chôn thi thể&nbsp; nhưng bỏ cuộc giữa chừng. Thời gian và địa điểm vứt xác cũng không được lựa chọn phù hợp. Cảnh sát suy đoán, hung thủ hành động tương đối vội vàng, không có tính toán kĩ lưỡng, tố chất tâm&nbsp;lý không tốt, sau ba hoặc bốn ngày giết người vẫn chưa hết hoang mang sợ hãi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trên người nạn nhân không có giấy tờ tùy thân, nhưng trong túi quần có danh thiếp của quán massage tại trung tâm huyện, rất có thể người này là khách hàng quen hoặc ít nhất cũng từng đến quán massage này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiệu massage mới khai trương được khoảng nửa năm, việc làm ăn bình thường, khách hàng không đông. Khi cảnh sát đề nghị gặp chủ quán, nhân viên cho biết đã năm ngày nay ông chủ và bà chủ đều không đến làm việc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đúng lúc này, việc đối chiếu vân tay và ADN cũng có kết quả, nạn nhân được xác định là Trình Á Phong, 28 tuổi, từng nhận án treo vì tội&nbsp;<em>Cướp tài sản</em>. Phong chính là chủ quán massage.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại nhà của vợ chồng Phong, cảnh sát phát hiện phòng ngủ chỉ có đệm mà không có ga trải giường. Trong thùng rác phòng vệ sinh có sợi dây vải được cắt từ ga trải giường, phù hợp với hoa văn trên sợi dây dùng để siết cổ nạn nhân. Trên bàn có lá thư ký tên Trình Á Phong, trong thư Phong nói Phong nảy sinh tình cảm với phụ nữ ở Quảng Đông nên đi tìm người này. Anh ta xin lỗi vợ vì đã thay lòng đổi dạ. Tuy nhiên cảnh sát dễ dàng nhận ra người viết là phụ nữ chứ không phải đàn ông.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đối tượng tình nghi số một là Vương Hội Ni - vợ Phong. Trong nhà của vợ chồng Phong dường như không có sẵn chiếc hòm gỗ như tại hiện trường nên cảnh sát tìm hỏi các cửa hàng xung quanh. Một cửa hàng xác nhận có bán chiếc hòm cùng loại vào sáng sớm ngày 10/5/2009. Người mua là cô gái trẻ cao khoảng 1m6, tóc dài, phù hợp với đặc điểm nhận dạng của Ni.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chiếc hòm được tài xế xe ba gác chở thuê về địa chỉ nhà vợ chồng Phong. Từ nhà tới địa điểm vứt xác, Ni lại thuê chiếc xe ba gác khác chở cả mình và chiếc hòm đi. Cũng chính tài xế xe ba gác thứ hai đã cùng Ni dùng cây lau nhà khiêng hòm lên xe và xuống xe. Như vậy là nhận định trước đó của cảnh sát không đúng, hung thủ chỉ có một người, tài xế xe ba gác hoàn toàn không biết sự việc, không phải là đồng phạm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Chiếc hòm gỗ tại hiện trường vứt xác. Ảnh: CCTV.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/22/02-8839-1592821452.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tbL4UMXR8EKVpplOh5GGiQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc hòm gỗ tại hiện trường vứt xác. Ảnh:&nbsp;<em>CCTV.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hai ngày sau khi cảnh sát nhận tin báo, Ni bị bắt khi đang làm tóc tại trung tâm huyện. Chị ta nhanh chóng nhận tội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Gần một năm trước, Ni gặp Phong trong một lần anh&nbsp;cùng bạn bè đến ăn ở quán cơm tại trung tâm huyện, nơi chị ta làm bưng bê. Thấy Ni trẻ trung, xinh xắn, Phong theo đuổi, thường xuyên mua quà tặng, ngày nào cũng chờ đến 22h lúc Ni tan ca để đưa về nhà. Chỉ một tháng từ khi quen nhau, hai người đã tổ chức đám cưới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi cưới, hai vợ chồng vay mượn bạn bè và người nhà mở quán massage. Thời gian đầu cuộc sống vợ chồng rất hạnh phúc, cho đến nửa năm sau Ni mới biết Phong còn giấu mình rất nhiều chuyện. Sau khi bị bắt vì tội cướp giật và phải nhận án treo, Phong thường xuyên tụ tập đánh bạc, ăn nhậu với bạn bè cả đêm không về. Việc làm ăn của quán massage còn chưa đi vào nề nếp, vẫn còn phải tiếp tục đầu tư, Phong lại thường xuyên bắt Ni đưa tiền đi đánh bạc, nếu không đưa sẽ đánh đập.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mâu thuẫn lên đến đỉnh điểm vào đêm ngày 5/5/2009, Phong uống rượu say về nhà đánh vợ, còn ép Ni sau đó phải \"tiếp khách\" để trừ nợ cho hắn. Không thể chấp nhận việc này, Ni chờ Phong ngủ say rồi cắt ga trải giường siết cổ trả thù.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Khang Diệp&nbsp;</strong>(Theo&nbsp;<em>CCTV</em>)</span></p>', 'CWsx_1.jpg', 1, 3, 26, '2020-06-25 17:56:54', '2020-06-25 17:57:32'),
(34, 'Lucky Luciano - trùm mafia bị hạ bệ vì quá tự tin', 'lucky-luciano-trum-mafia-bi-ha-be-vi-qua-tu-tin', 'MỸ - Từ tuổi thiếu niên, Lucky Luciano đã thể hiện bản chất tội phạm bằng việc mở đường dây bảo kê bạn học cùng trường.', '<p><span style=\"font-size:18px\">Sinh năm 1897, Charles \"Lucky\" Luciano di cư cùng gia đình từ Italy tới New York vào năm 10 tuổi. Vì không biết tiếng Anh, Luciano chật vật ở trường lớp và chuyển sang giao du với thành viên băng đảng. Trong thời gian này, Luciano mở đường dây bảo kê bạn cùng trường và sẵn sàng dùng vũ lực để ép con mồi trả tiền.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bỏ học vào năm 1914, Luciano làm văn thư cho công ty bán mũ với thù lao 7 USD mỗi tuần, công việc chân chính duy nhất trong đời ông trùm. Sau lần thắng bạc gần 250 USD, Luciano nghỉ việc ở công ty mũ và bắt đầu bước chân vào con đường tội phạm. Trong quá trình này, Luciano kết thân với hai thành viên băng đảng gốc Do Thái là Meyer Lansky và Bugsy Siegel, đồng minh quan trọng của Luciano sau này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 1916, Luciano bị bắt&nbsp;lần đầu tiên vì bán heroin. Chỉ sau 6 tháng ở trường giáo dưỡng, Luciano tiếp tục dấn sâu vào con đường tội phạm. Bốn năm sau, hắn trở thành tay súng bắn thuê cho Joe Masseria - trùm băng đảng tại vùng Lower Manhattan.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong thập niên 1920, chớp thời cơ Mỹ ban hành lệnh cấm rượu, Luciano tập trung buôn rượu lậu. Cùng với 5 tên trùm buôn rượu lậu khác, Luciano thống trị cả vùng bờ Đông nước Mỹ. Doanh thu từ hoạt động phi pháp của Luciano trong thời gian này ước tính lên tới 12 triệu USD mỗi năm, trong đó hắn&nbsp;đút túi bốn triệu USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối thập niên 1920, New York nổ ra cuộc chiến giành lãnh thổ đẫm máu giữa Joe Masseria và Salvatore Maranzano, một trùm mafia khác. Masseria và Maranzano thuộc thế hệ trùm mafia truyền thống bắt đầu sự nghiệp tội phạm tại Italy rồi qua Mỹ. Những kẻ này nghi kỵ người không cùng xuất xứ, đồng thời muốn giữ nguyên giá trị của \"thế giới mafia xưa\" như \"danh dự\", \"truyền thống\", và \"phẩm giá\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngược lại, Luciano từ nhỏ đã giao du với bạn bè tới từ nhiều nơi nên sẵn sàng bắt tay với người không cùng xuất xứ, miễn là có thể kiếm tiền. Hắn muốn thành lập tổ chức tội phạm quốc gia cho phép băng đảng Italy, Do Thái và Ireland cùng góp nguồn lực để xây dựng mạng lưới tội phạm khiến các bên cùng có lợi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Luciano biết rõ Masseria và Maranzano là hai hòn đá ngáng đường trên ước mơ của mình. Luciano nghĩ&nbsp;sự tham lam và bảo thủ&nbsp;của những trùm mafia truyền thống chính là điều khiến băng đảng Italy mãi nghèo, trong khi băng đảng Do Thái và Ireland phất lên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đầu năm 1931, Luciano lén hoạt động cho băng đảng đối địch khi cuộc chiến giành lãnh thổ ngày càng bất lợi cho phe mình. Sau khi đạt thỏa thuận ngầm với Maranzano, Luciano mời sếp trưởng Masseria tới dùng bữa tại một nhà hàng ở quận Brooklyn vào ngày 15/4/1931. Giữa ván bài, Luciano xin phép vào phòng vệ sinh. Ít giây sau, các tay súng do Luciano sắp xếp sẵn ập vào xả súng giết chết Masseria cùng cận vệ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Ảnh chụp chân dung của Luciano trong một lần bị bắt vào năm 1931. Ảnh: New York Police Department.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/1-3497-1592395284.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Xo7JvYEw53N0l8C4g7WdSg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ảnh chụp chân dung của Luciano trong một lần bị bắt vào năm 1931. Ảnh:&nbsp;<em>New York Police Department.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không còn kẻ đối địch, Maranzano phân các băng đảng Italy thành năm gia đình mafia, trong đó bản thân nắm chức cao nhất \"sếp của mọi sếp\". Ông trùm mới mang luật lệ mafia từ quê hương áp đặt lên mọi gia đình mafia tại New York. Maranzano còn kiềm chế hoạt động tội phạm của các tổ chức mafia cạnh tranh để làm lợi cho gia đình mình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bề ngoài, Luciano có vẻ chấp nhận những thay đổi trên nhưng trong lòng thầm kết luận rằng Maranzano thậm chí còn tham lam và bảo thủ hơn Masseria. Luciano ngấm ngầm tìm cơ hội loại bỏ Maranzano.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tháng 9/1931, do được gián điệp mật báo, Luciano hay tin mình trở thành mục tiêu hạ sát theo lệnh của Maranzano. Luciano quyết định ra tay trước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo thông tin gián điệp, Maranzano sắp bị Cục thuế vụ tới kiểm toán đột xuất nên đã yêu cầu cận vệ không mang súng tại văn phòng để tránh rắc rối. Chi tiết này khiến Luciano lập ra kế hoạch ám sát Maranzano. Để thực hiện kế hoạch, Luciano tuyển mộ bốn thành viên băng đảng gốc Do Thái với sự giúp đỡ của Meyer Lansky và Bugsy Siegel.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngày 10/9/1931, bốn&nbsp;sát thủ tới văn phòng của Maranzano dưới vỏ bọc nhân viên Cục thuế vụ. Những kẻ này có ngoại hình không giống người Italy nên hoàn toàn không bị Maranzano nghi ngờ. Bốn sát thủ sau đó tước vũ khí của đám cận vệ và sát hại Maranzano với sự chỉ điểm của gián điệp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau cái chết của Maranzano, Luciano trở thành ông trùm tội phạm quyền lực nhất tại Mỹ. Dù hoàn toàn có thể tự xưng là \"sếp của mọi sếp\", Luciano xóa bỏ danh hiệu này vì cho rằng đây là nguồn gốc sinh ra mâu thuẫn giữa các gia đình tội phạm và sợ trở thành mục tiêu của kẻ tham vọng khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối năm 1931, Luciano thành lập \"Ủy ban\" -&nbsp;cơ chế quản lý và giải quyết mâu thuẫn trong thế giới mafia. Mỗi gia đình mafia đều có đại diện trong \"Ủy ban\" với lá phiếu bình đẳng, mọi chuyện được xử lý thông qua biểu quyết. Thông qua \"Ủy ban\", Luciano muốn ngăn ngừa các cuộc chiến giành lãnh thổ trong tương lai.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bằng cách áp dụng tư tưởng làm việc kiểu doanh nghiệp vào tội phạm có tổ chức, Luciano đã đặt nền móng giúp mafia kiểm soát hoạt động đánh bạc, mại dâm, ma túy,... trong suốt thế kỷ 20. Luciano được cho là cha đẻ của tội phạm có tổ chức tại Mỹ, theo Claire White, quản lý chương trình giáo dục tại Bảo tàng Mafia (thành phố Las Vegas).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đầu những năm 1930, Luciano sống cuộc sống xa hoa tại New York. Nhưng những ngày tháng tốt đẹp ấy không kéo dài lâu vì Luciano đã lọt vào tầm ngắm của Thomas Dewey, công tố viên đặc biệt được giao trọng trách truy cứu tội phạm có tổ chức.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi thu thập chứng cứ, nhà chức trách bắt&nbsp;Luciano vào ngày 3/4/1936 vì vai trò đứng đầu trong mạng lưới mại dâm tại New York. Chứng cứ nhà chức trách nắm giữ hầu hết chỉ có tính chất gián tiếp, chủ yếu là lời khai của ba gái mại dâm. Tuy nhiên, với tâm thế tự tin thái quá, Luciano quyết định tự ra làm chứng trước tòa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong phần đối chất, công tố viên Dewey chỉ ra nhiều điểm mâu thuẫn trong lời khai khiến Luciano toát mồ hôi và chỉ biết đáp lời bằng những câu \"không biết\" hoặc \"không nhớ\" yếu ớt. Dựa vào hồ sơ thuế trong 1929-1935, Dewey chỉ ra thu nhập khai báo mỗi năm của Luciano chỉ vào khoảng 22.500 USD, trái ngược với lối sống xa hoa của ông trùm. Trước mặt bồi thẩm đoàn, Luciano lắp bắp và không giải thích được.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối cùng, Luciano và các đồng bị cáo bị kết án về tội<em>&nbsp;Cưỡng ép mại dâm</em>, lãnh án 30-50 năm tù. Thời gian đầu, Luciano vẫn cố vận hành mạng lưới tội phạm của gia đình mafia từ trong tù. Sau khi kháng cáo bị tòa tối cao liên bang bác bỏ vào năm 1938, Luciano để thuộc hạ lên thay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong Thế chiến II, Luciano bí mật thỏa thuận với chính phủ Mỹ. Theo đó, thuộc hạ của Luciano tại bến cảng New York sẽ đề phòng gián điệp từ Đức và Italy xâm nhập. Đổi lại, Luciano sẽ được giảm án khi chiến tranh kết thúc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tới nay, mức độ đóng góp của Luciano vào thắng lợi của cuộc chiến còn gây tranh cãi. Nhưng sau chiến Thế chiến II, Luciano được&nbsp;Thomas Dewey, khi ấy là thống đốc New York, giảm án với điều kiện phải bị trục xuất về Italy. Ngày 28/2/1946, Luciano đặt chân tới thành phố Naples, Italy.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau&nbsp;thời gian ngắn tại Italy, Luciano trốn tới thủ đô Havana, Cuba và cố gắng xây dựng mạng lưới tội phạm với sự trợ giúp của Meyer Lansky và Bugsy Siegel. Không được bao lâu, Luciano bị Cuba trục xuất về Italy vào năm 1947 dưới sức ép của chính phủ Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Lucky Luciano trong những năm đi đày tại Italy. Ảnh: Public Domain.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/2-6004-1592395284.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6xF0FP0J_5FHY0EscmNZEw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lucky Luciano trong thời gian tại Italy. Ảnh:&nbsp;<em>Public Domain.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Do bị nghi buôn ma túy, năm 1954, Luciano bị Cảnh sát Italy đặt dưới sự giám sát chặt chẽ trong hai năm. Ông trùm một thời phải tới trình diện cảnh sát mỗi tuần, ở nhà mỗi tối và không được rời Naples khi chưa được cho phép. Sức ảnh hưởng của Luciano với hoạt động mafia tại Mỹ cũng yếu dần. Năm 1957, Luciano chứng kiến gia đình mafia ở Mỹ của mình bị kẻ khác thâu tóm mà không thể can thiệp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối đời, Luciano muốn chia sẻ câu chuyện đời mình với công chúng. Tuy nhiên, ông trùm mafia một thời đã chết vì lên cơn đau tim vào ngày 26/1/1962, sau khi gặp mặt nhà làm phim. Chỉ khi ấy, thi thể của Luciano mới được cho phép quay về Mỹ để mai táng.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Quốc Đạt&nbsp;</strong>(Theo&nbsp;<em>Biography, The New York Times</em>)</span></p>', 'fIZJ_1.jpg', 1, 0, 26, '2020-06-26 04:28:24', '2020-06-26 04:28:24'),
(35, 'Cách diễn đạt \'can\'t put my finger on\'', 'cach-dien-dat-cant-put-my-finger-on', 'Theo cô Moon Nguyen, cụm \'can\'t put my finger on\' được dùng khi bạn không biết cụ thể vấn đề, không thể chỉ ra được cụ thể.', '<p style=\"text-align:center\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/YCdxnxiCJZY\" width=\"560\"></iframe></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Moon Nguyen</strong></span></p>', 'Lrbc_1.jpg', 1, 2, 29, '2020-06-26 04:45:00', '2020-06-26 04:53:24'),
(36, '10 bước chuẩn bị khi du học', '10-buoc-chuan-bi-khi-du-hoc', 'Ngoài chuẩn bị tài chính và lên lịch trình, du học sinh cần bảo vệ giấy tờ quan trọng, liên lạc trước với chủ nhà tương lai để du học an toàn.', '<p><span style=\"font-size:18px\"><em>EF Education</em>, một trong những tổ chức giáo dục quốc tế lớn, đưa ra lời khuyên giúp bạn du học thuận lợi.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>1. Chuẩn bị tài chính thật kỹ</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hãy chắc chắn là bạn đã tính toán kỹ&nbsp;các khoản chi phí phải trả trong thời gian du học. Chi phí này bao gồm cả những khoản có thể dự tính trước, như học phí, tiền sinh hoạt, tiền thuê nhà, tiền bảo hiểm...&nbsp;cho đến chi phí phát sinh, từ tiền khám bệnh khi ốm cho đến các thủ tục giấy tờ nếu có vấn đề về thị thực. Thường thì du học sinh sẽ phải trả nhiều tiền hơn đáng kể so với dự tính ban đầu.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bởi vậy&nbsp;hãy luôn chắc chắn bạn có khoản tiền đủ cho tối thiểu một vài tháng và tìm hiểu cách&nbsp;để gia đình gửi tiền sang hỗ trợ khi cần thiết. Việc gửi tiền quốc tế cũng sẽ yêu cầu một số thủ tục từ ngân hàng,&nbsp;du học sinh cùng gia đình nên chuẩn bị trước giấy tờ cần thiết.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>2. Chuẩn bị lịch trình</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mỗi du học sinh sẽ phải di chuyển bằng nhiều phương tiện, từ nhà đến sân bay, từ sân bay đến ký&nbsp;túc xá ở nước ngoài và ngược lại. Mỗi lượt như vậy đều cần tìm hiểu trước và ghi rõ lịch trình&nbsp;để tiết kiệm thời gian, công sức&nbsp;và chi phí.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đặc biệt hơn, học sinh cũng nên lưu ý các địa chỉ liên lạc quan trọng như công an, đại sứ quán hay văn phòng trường để đề phòng&nbsp;trường hợp khẩn cấp như lạc đường, hay gặp tai nạn trong khi di chuyển.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>3. Lựa chọn bảo hiểm</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tất cả du học sinh đều cần có bảo hiểm y tế khi du học. Nếu trường đại học của bạn hỗ trợ đăng ký bảo hiểm&nbsp;hay bạn đến một đất nước có bảo hiểm toàn dân, mọi chuyện sẽ vô cùng đơn giản. Trong trường hợp ngược lại, bạn phải tìm hiểu kỹ&nbsp;về bảo hiểm y tế trước khi xuất cảnh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những yếu tố cần cân nhắc khi lựa chọn bảo hiểm gồm có chất lượng (bảo hiểm sẽ chi trả cho trường hợp nào, thời gian trung bình là bao lâu, đánh giá của những người&nbsp;từng sử dụng bảo hiểm đó thế nào...) và chi phí (bảo hiểm sẽ thu bao nhiêu tiền một tháng/năm, yêu cầu đóng tiền trước hay&nbsp;theo tháng).</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>4. Bảo vệ giấy tờ quan trọng</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Du học sinh cần chuẩn bị đầy đủ tất cả giấy tờ quan trọng, ví dụ hộ chiếu, bảo hiểm, thẻ tín dụng và bản sao điện tử của tất cả giấy tờ này trước khi du học. Bản gốc và bản sao của giấy tờ nên được cất giữ ở những nơi khác nhau.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đồng thời, bạn&nbsp;nên chuẩn bị một bản sao giấy tờ gửi gia đình ở nhà trước, trong trường hợp có vấn đề phát sinh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Ảnh: Shutterstock\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/23/shutterstock-1317714005-8707-1592874810.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gpuXSBi9KsJBgpoKI2x1QQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ảnh:&nbsp;<em>Shutterstock</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>5. Nghiên cứu thật kỹ&nbsp;về nơi bạn sẽ đến học</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc nghiên cứu về nơi bạn sẽ đến học vừa là công việc tốn&nbsp;sức nhưng cũng là một trong những trải nghiệm thú vị nhất trong quá trình chuẩn bị trước khi đi học. Ngoài việc đọc trước một chút về lịch sử thành phố, về trường đại học sắp tới, bạn cũng có thể tìm hiểu một vài thứ thú vị&nbsp;hơn như quán cafe, nhà hàng gần trường đại học, hay hoạt động văn hóa, giải trí.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đừng quên tìm hiểu một chút về các từ đơn giản của ngôn ngữ bản địa ở nơi bạn đến. Việc bạn có thể chào, hay nói cảm ơn&nbsp;bằng ngôn ngữ mẹ đẻ của ai đó rất quan trọng trong quá trình kết bạn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>6. Chuẩn bị hành lý</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đầu tiên, bạn nên tìm hiểu trước kích thước, trọng lượng hành lý mà vé máy bay cho phép. Từ đó, bạn có thể suy nghĩ về việc bạn muốn mang gì khi du học.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Với những chuyến đi ngắn ngày, bạn chỉ nên mang gọn nhẹ đủ quần áo và đồ sinh hoạt cá nhân. Ngược lại, với chuyến đi dài ngày, bạn có thể cân nhắc mang những đồ đạc lưu niệm có ý nghĩa với bản thân, hay một vài quyển sách yêu thích.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước khi đóng hành lý, bạn cũng nên tham khảo vài cách giúp hành lý gọn gàng hơn, cách để đồ đạc không bị hỏng trong quá trình vận chuyển.&nbsp;Cuối cùng, trước khi đem ra sân bay, bạn nên đánh dấu hành lý bằng một&nbsp;bảng tên để dễ phân biệt.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>8. Chuẩn bị thuốc thang</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu bạn đang điều trị bệnh hay sử dụng kính áp tròng, nên chuẩn bị trước một lượng thuốc đầy đủ cho suốt chuyến du học của bạn. Trường hợp đi dài ngày, bạn cũng nên tìm hiểu cách để có thể mua thêm các loại thuốc hoặc sản phẩm y tế cần thiết ở đất nước sắp tới.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>9. Chuẩn bị thiết bị liên lạc</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Du học sinh cần tìm hiểu&nbsp;phương thức giúp liên lạc khi đến một đất nước mới. Một số nước sẽ cho phép bạn mua sim điện thoại du lịch ở sân bay, trong khi một số nước sẽ không cung cấp sim &nbsp;cho khách du lịch ngắn ngày.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một giải pháp có thể là sử dụng chế độ roaming điện thoại của bạn từ nhà mạng ở quê nhà. Tuy nhiên, đây là giải pháp tương đối tốn kém, chỉ nên sử dụng trong trường hợp khẩn cấp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Du học sinh cũng nên lưu ý ở một số đất nước sẽ không có wifi miễn phí&nbsp;nơi công cộng. Điều này khiến nhu cầu sử dụng điện thoại cá nhân lớn hơn rất nhiều so với một số đất nước khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>10. Liên lạc với chủ nhà tương lai</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường hợp bạn là du học sinh tuổi vị thành niên, nhiều khả năng trường sẽ xếp cho bạn ở cùng với một gia đình bản xứ (host). Khi đó, bạn&nbsp;nên liên lạc với gia đình host trước khi đi, qua email hay điện thoại, để giới thiệu bản thân&nbsp;cũng như tìm hiểu thêm về gia đình mới trong thời gian du học.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi đến nơi, bạn cũng nên mang theo món quà nhỏ từ quê nhà để tặng gia đình host. Món quả có thể không có nhiều ý nghĩa vật chất, nhưng sẽ thể hiện sự quan tâm của bạn, cũng như gây&nbsp;ấn tượng tốt với gia đình mới của mình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu ở ký&nbsp;túc, hay thuê nhà riêng, việc liên lạc sớm trước khi bay cũng giúp bạn hạn chế những vấn đề phát sinh và có nhiều thời gian hơn để xử lý thủ tục. Một món quà nhỏ cũng sẽ có tác dụng tương tự, để lại thiện cảm với những người xung quanh.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Phan Nghĩa&nbsp;</strong>(Theo<em>&nbsp;EF Education</em>)</span></p>', 'YxKK_1.jpg', 1, 1, 30, '2020-06-26 04:56:54', '2020-06-26 04:56:54'),
(37, 'Nữ sinh giành học bổng 6,4 tỷ đồng của đại học Mỹ', 'nu-sinh-gianh-hoc-bong-6-4-ty-dong-cua-dai-hoc-my', 'Đỗ Cao Minh Châu từng nghĩ \"du học chẳng để làm gì\", nhưng chuyến đi Hà Lan dự thi Olympic khoa học quốc tế trẻ năm 2017 đã khiến em thay đổi.', '<p><span style=\"font-size:18px\">Ngày cuối tuần giữa tháng 6, Minh Châu, lớp 12 Sinh trường THPT chuyên Hà Nội - Amsterdam, tự thưởng cho mình khoảng thời gian thư giãn sau những buổi học trên lớp. Vì giành học bổng du học, việc học tại trường và thi tốt nghiệp không còn quá áp lực với em.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong kỳ tuyển sinh tháng 4-5, Châu trúng tuyển sáu trường tại Mỹ và Đại học Aalto của Phần Lan. Trong đó, Đại học Notre Dame, xếp thứ 15 danh sách đại học quốc gia của Mỹ năm 2020 theo&nbsp;<em>US News &amp; World Report</em>, đề nghị cấp học phí và sinh hoạt phí trị giá hơn 6,4 tỷ đồng trong bốn năm. Còn tại bảng xếp hạng&nbsp;<em>QS&nbsp;</em>2021, Đại học Aalto xếp thứ 127 thế giới, là một trong những trường tốt nhất tại Phần Lan. Trường cũng trao học bổng 100% học phí cho Châu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhìn thành tích của cô gái chuyên Sinh, ít người biết rằng em từng nghĩ \"du học chẳng để làm gì\". Trong hai năm, từ việc thay đổi nhận thức, Châu dành thời gian hoàn thành các chứng chỉ cần thiết. \"Cho đến giờ khi nhìn lại hai năm đã qua, em vẫn nghĩ đó là một hành trình khó tin\", Châu nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Đỗ Cao Minh Châu trong buổi chụp kỷ yếu tốt nghiệp.&nbsp;Ảnh: Nhân vật cung cấp\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/20/IF0A4515-6564-1592626799.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=z2SvFTylxbZY4P1vXOIn1w\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đỗ Cao Minh Châu trong buổi chụp kỷ yếu tốt nghiệp.&nbsp;Ảnh:&nbsp;<em>Nhân vật cung cấp</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sinh ra và lớn lên tại Hà Nội, từ khi học khối THCS trường THPT chuyên Hà Nội - Amsterdam, Châu được gia đình gợi ý du học. Em không phản đối nhưng cũng chưa hoàn toàn ủng hộ và nghe theo kế hoạch của gia đình. \"Lúc đó em nghĩ tại sao phải du học? Du học cho mình cái gì? Em học trên lớp, thành tích tốt nhưng vẫn chưa tìm được câu trả lời\", cô gái kể.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đến khi vào lớp 10, nhờ đạt giải trong kỳ thi học sinh giỏi lớp 9 cấp thành phố, Minh Châu được vào vòng sơ loại để chọn 6 em, thành lập đội tuyển thi IJSO (Olympic Khoa học trẻ quốc tế) lần thứ 14, được tổ chức tại Hà Lan năm 2017. Châu không có nhiều hy vọng được chọn vì các bạn chuyên Toán, Lý, Hóa rất giỏi và luôn thống trị đội tuyển IJSO các năm, nhưng giấc mơ được chạm vào tuyết ở Hà Lan đã tạo động lực cho em đăng ký tham gia. Trải qua hai vòng thi, Châu may mắn là người cuối cùng được chọn trong 6 thành viên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong ba tháng ôn tập, Châu cùng các bạn phải học toàn bộ kiến thức Lý, Hóa, Sinh của bậc THPT. Đến giờ khi nhớ lại, Châu vẫn dùng từ \"kinh hoàng\" để diễn tả khoảng thời gian ôn luyện căng thẳng đó. Nữ sinh tự nhận tư chất không được như các bạn, giáo viên giảng một lần trên lớp Châu chưa thể hiểu ngay nên về nhà em phải xem lại. Thời gian ôn thi, ngày nào em cũng học đến 2h sáng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước khi bước vào kỳ thi chính thức, Châu phải trải qua ba lần thi thử. Lần đầu em xếp thứ 5/6, lần hai đứng chót và lần ba vươn lên vị trí thứ hai. \"Em đã rất căng thẳng khi xuất phát điểm ở cuối đội, nhưng điều khiến em tự hào nhất là sự chăm chỉ khả năng tự học và sức bền của bản thân\", Châu nhớ lại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngày bước chân đến Hà Lan để tham gia IJSO 2017, Châu rất lo lắng. Nếu bảo không hy vọng là nói dối nhưng nữ sinh chỉ dám nghĩ \"được giải là may lắm rồi\". Thế nên khi Châu giành huy chương bạc, cũng là thành tích tốt nhất của đoàn IJSO Việt Nam năm đó, các thầy cô đã đánh giá kết quả của em là \"cuộc lội ngược dòng ngoạn mục\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong hai tuần tại Hà Lan, Châu tham quan, dự nhiều hội thảo trao đổi, làm việc nhóm đồng thời trải nghiệm sự đa dạng văn hóa, gặp gỡ nhiều bạn bè trên thế giới. \"Khi giao lưu với thầy cô, bạn bè, được thoải mái trình bày quan điểm của mình và lắng nghe phản biện từ mọi người, em thấy rất hấp dẫn. Nhận thức của em về du học bắt đầu thay đổi\", Châu nói, khẳng định IJSO 2017 là cuộc thi giúp em mở ra một giấc mơ, hướng đi mới cho em.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Minh Châu (hàng dưới, thứ tư từ trái qua) cùng&nbsp;đội tuyển&nbsp;IJSO 2017.&nbsp;Ảnh: Nhân vật cung cấp\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/20/25299323-10215105139691251-514-3875-1407-1592626799.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=C_XyX2yqj5Ui2dfdb6Pjtw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Minh Châu (hàng dưới, thứ tư từ trái qua) cùng&nbsp;đội tuyển&nbsp;IJSO 2017.&nbsp;Ảnh:&nbsp;<em>Nhân vật cung cấp</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Châu bắt đầu tìm hiểu và ôn thi các chứng chỉ chuẩn hóa, tiếng Anh để hoàn thiện dần hồ sơ du học. Từ mùa hè năm 2018, nữ sinh tham gia một lớp ôn thi SAT trong ba tháng. Lần đầu thi, Châu chỉ đạt 1430/1600 điểm và tỏ ra tiếc nuối vì cho rằng chưa biết cách phân bổ thời gian. \"Em nghĩ mình có thể làm được nhiều điểm hơn, bài đọc không khó đến mức như thế\", Châu kể.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau lần đầu thi với kết quả không như ý, cô gái chuyên Sinh quyết định tự ôn để thi lại. Lần này, Châu luyện nhiều đề để cải thiện tốc độ, tập trung ôn lại những phần mình có thể mạnh và học thêm từ vựng. Thời gian nước rút, nữ sinh làm hai đề một ngày, mỗi đề 3,5 tiếng. Sau hai tháng nỗ lực, Châu đạt điểm gần tuyệt đối 1590/1600 trong lần thi SAT thứ hai, chỉ mất 10 điểm ở phần đọc hiểu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối năm 2018, Minh Châu học tập trung cùng đội tuyển ôn thi học sinh giỏi quốc gia môn Sinh. Em tiếp tục lên kế hoạch thi IELTS và SAT 2 song song với lịch học đội tuyển. Tự nhận vốn từ kém, bí quyết của em chỉ là luyện và học thật nhiều từ mới, làm đề trong sách Cambridge.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vì không học tại trung tâm, Châu không có giáo viên và bạn bè để luyện nói. Em chỉ đứng trước gương, tự nói với mình, ghi âm và sửa lỗi. Em học từ vựng và cụm từ theo chuyên đề, sau đó hướng đề bài theo nội dung các từ đã có để bài nói tăng tính học thuật. Sau sáu tháng tự học, Châu đạt IELTS 8.0, trong đó Reading điểm tuyệt đối 9.0, Listening 8.5, Speaking 7.5 và Writing 7.0. Kết quả thi SAT 2 cũng cực tốt với điểm tuyệt đối 800/800 cho cả ba môn Toán, Hóa, Sinh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù ôn thi chứng chỉ, cô gái chuyên Sinh cũng không lơ là việc học và vẫn dành thời gian tham gia hoạt động xã hội. Hai năm liên tiếp, Châu đều đạt giải nhì môn Sinh trong kỳ thi học sinh giỏi quốc gia. Tháng 5/2019, em cùng bạn bè thành lập&nbsp;<em>Healthy Everyday Oganization</em>&nbsp;(HEO), tổ chức phi lợi nhuận, tổ chức dự án nâng cao sức khỏe cá nhân, hiểu biết về dinh dưỡng cho học sinh bậc tiểu học và đầu THCS. Ngoài việc giữ vai trò chủ tịch, tự lên giáo án và đứng lớp, Châu cũng mời cố vấn là các thầy cô có chuyên môn, những người có sức ảnh hưởng và truyền cảm hứng đến các bạn nhỏ. Đến nay, ban tổ chức của HEO có 50 thành viên, tổ chức được 7 lớp học cho hơn 200 học sinh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi đã sở hữu thành tích học tập và ngoại khóa dày dặn, cô học trò chuyên Sinh đầu tư cho bài luận chính. Minh Châu kể về đam mê khoa học tự nhiên của bản thân và cách lĩnh vực này tác động lên tính cách của em. \"Khoa học tự nhiên vẫn được coi là lĩnh vực khô cứng, nhưng lại giúp em thay đổi nhiều về mặt cảm xúc, suy nghĩ và đưa em đến với nhiều cơ hội mới\", Châu nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi viết bài luận chính trong ba tháng, Minh Châu nhờ một người quen, đóng vai trò là cố vấn, chỉnh sửa cách hành văn, ngữ pháp giúp. Nữ sinh không gửi lần lượt hồ sơ cho từng trường mà hoàn thiện tất cả, cùng lúc gửi cho các trường vào cuối tháng 11/2019.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Minh Châu và cô giáo chủ nhiệm.&nbsp;Ảnh: Nhân vật cung cấp\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/20/Minh-Chau-2392-1592626799.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-dCNYTlzxOQtvNvAhCuXxg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Minh Châu và cô giáo chủ nhiệm.&nbsp;Ảnh:&nbsp;<em>Nhân vật cung cấp</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù hồ sơ khá đẹp với thành tích học tập, ngoại khóa dày dặn cùng khả năng chơi đàn bầu, Minh Châu vẫn lo lắng. Tại vòng nộp hồ sơ sớm cuối năm 2019, em đã không thành công khi bị ngôi trường đặt hy vọng cao nhất từ chối. Nhờ sự động viên của gia đình, em lấy lại cân bằng, vững tâm để bước vào kỳ thi học sinh giỏi quốc gia môn Sinh chỉ sau đó một tuần.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hai tháng tiếp theo vẫn trôi qua trong căng thẳng vì tỷ lệ đỗ ở vòng tuyển sinh thường sẽ thấp hơn tuyển sinh sớm. Nhưng rồi thư báo trúng tuyển đầu tiên gửi về trong sự hạnh phúc như vỡ òa của Minh Châu. 7 trường đồng ý trao học bổng cho em, trong đó có Đại học Notre Dame và Đại học Aalto. \"Khi nhận được kết quả trúng tuyển, em rất vui vì cố gắng của mình đã được đền đáp\", Châu kể. Hiện em chưa quyết định nhập học trường nào.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nguyễn Hải Thùy Linh, bạn cùng lớp và cùng đội tuyển Sinh học quốc gia với Minh Châu, đánh giá em là người cởi mở, thông minh và chăm chỉ. Trong quá trính sát cánh cùng nhau, cả hai thường giúp đỡ và chia sẻ những vui buồn trong học tập và cuộc sống. \"Với nỗ lực bền bỉ mà Châu đã bỏ ra, em nghĩ bạn hoàn toàn xứng đáng với thành tích đạt được. Châu có thế mạnh về sự tỉ mỉ, chăm chỉ, em nghĩ bạn phù hợp với ngành y hoặc nghiên cứu khoa học\", Linh nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thời gian tới, Châu sẽ thi chọn đội tuyển thi Olympic Sinh học, được tổ chức online vào tháng 8, sau đó sẽ cân nhắc các dự định tiếp theo. Từ những gì đã trải qua và thay đổi, cô gái sinh năm 2002 cho rằng mỗi người cần học cách đối mặt và vượt qua qua áp lực để vươn tới những cơ hội mới. \"Em nghĩ làm gì cũng phải có áp lực mới tiến lên được vì áp lực tạo ra động lực, giúp em làm được những việc tưởng chừng không thể và kiên trì theo đuổi mục tiêu\", Châu nói.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thanh Hằng</strong></span></p>', 'sN8w_1.jpg', 1, 3, 30, '2020-06-26 05:03:56', '2020-06-26 05:03:56'),
(38, 'Đường lây của bệnh bạch hầu', 'duong-lay-cua-benh-bach-hau', 'Bệnh bạch hầu lây truyền dễ dàng qua đường hô hấp, qua tiếp xúc trực tiếp với dịch tiết từ niêm mạc mũi, họng của bệnh nhân khi ho, hắt hơi.', '<p><span style=\"font-size:18px\">Đây là bệnh nhiễm khuẩn, nhiễm độc cấp tính do vi khuẩn bạch hầu gây nên, thuộc nhóm B trong Luật phòng chống bệnh truyền nhiễm. Bệnh thường gặp ở trẻ nhỏ, tuy nhiên cũng có thể gặp ở người lớn nếu không có miễn dịch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bác sĩ Huỳnh Minh Tuấn, Trưởng Khoa Kiểm soát nhiễm khuẩn, Bệnh viện Y dược TP HCM, cho biết phương thức lây truyền của vi khuẩn bạch hầu là lây truyền trực tiếp do hít phải chất tiết từ đường hô hấp của người bệnh thông qua các giọt nước nhỏ li ti phát ra từ đường hô hấp khi người bệnh ho, hắt hơi, nói chuyện. Bên cạnh đó, vi khuẩn trong chất tiết từ đường hô hấp của người bệnh có thể lây nhiễm và tồn tại trên bề mặt của các đồ vật xung quanh người bệnh từ vài ngày đến vài tuần như trong sữa, nước uống sống đến 20 ngày; trong tử thi sống được 2 tuần.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiếm gặp hơn, vi khuẩn có thể lây trực tiếp từ các sang thương trên da, là các nang dạng biểu bì, u mỡ, mụn cơm và nốt ruồi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nguồn lây truyền bệnh bạch hầu là người bệnh hoặc người lành mang mầm bệnh. Thời kỳ lây truyền thường là cuối thời kỳ ủ bệnh hoặc ngay khi khởi phát bệnh, có thể kéo dài từ hai đến bốn tuần. Trung bình, sau khi hít phải vi khuẩn 2-5 ngày, người&nbsp;sẽ phát bệnh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bất cứ người nào tiếp xúc với mầm bệnh đều có thể bị lây nhiễm, nhất là&nbsp;trẻ nhỏ khi hệ miễn dịch chưa hoàn chỉnh, người cao tuổi sức đề kháng yếu hoặc những người mắc các bệnh mạn tính hoặc đang phải sử dụng các thuốc ức chế miễn dịch, làm suy giảm sức đề kháng của cơ thể.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Môi trường dễ bùng phát dịch là cộng đồng có mật độ dân số cao, vệ sinh không đảm bảo.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Vi khuẩn&nbsp;Corynebacterium diphtheriae gây ra bệnh bạch hầu. Ảnh:CDC.\" src=\"https://i1-suckhoe.vnecdn.net/2020/06/26/Vi-khuan-trong-bai-3000-159283-3449-4373-1593137690.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FOFmDVtogvXqGk_eUz6WNA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Vi khuẩn&nbsp;Corynebacterium diphtheriae gây ra bệnh bạch hầu. Ảnh:<em>CDC.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước đây, bệnh bạch hầu lưu hành khá phổ biến. Từ khi vaccine phòng bạch hầu được đưa vào chương trình tiêm chủng mở rộng, bệnh đã được khống chế. Hàng năm chỉ ghi nhận một vài trường hợp lẻ tẻ do không được tiêm đầy đủ các mũi vaccine phòng bệnh, thường xảy ra ở các khu vực vùng sâu,&nbsp;nơi có tỷ lệ tiêm chủng thấp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một số trường hợp mắc bạch hầu tử vong có thể do không được tiêm chủng đầy đủ, đặc biệt trẻ em. Các trường hợp hiếm gặp có thể do cơ địa có khiếm khuyết trong hệ miễn dịch nên không tạo được khả năng miễn dịch mặc dù được tiêm phòng đầy đủ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài ra, người đã tiêm phòng bệnh bạch hầu hoặc từng mắc bệnh bạch hầu trong quá khứ vẫn có khả năng nhiễm lại do cơ thể không tạo ra miễn dịch hoặc khả năng miễn dịch giảm dần theo thời gian.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Để chủ động phòng chống bệnh bạch hầu, Cục Y tế dự phòng khuyến cáo người dân&nbsp;<strong>cần đưa trẻ đi tiêm chủng tiêm vaccine đầy đủ.</strong>&nbsp;Thường xuyên rửa tay bằng xà phòng; che miệng khi ho hoặc hắt hơi; giữ vệ sinh thân thể, mũi, họng hàng ngày; hạn chế tiếp xúc với người mắc bệnh hoặc nghi ngờ mắc bệnh. Đảm bảo nhà ở, nhà trẻ, lớp học thông thoáng, sạch sẽ và có đủ ánh sáng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi có dấu hiệu mắc bệnh hoặc nghi ngờ mắc bệnh bạch hầu phải được cách ly và đưa đến cơ sở y tế để được khám, điều trị kịp thời. Người dân trong ổ dịch cần thực hiện nghiêm túc việc uống thuốc phòng và tiêm vaccine phòng bệnh theo chỉ định và yêu cầu của cơ quan y tế.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Thùy An</strong></span></p>', 'vl1e_1.jpg', 1, 0, 33, '2020-06-26 08:36:34', '2020-06-26 08:36:34'),
(40, 'Thu Hoài được khen trẻ đẹp trong tiệc sinh nhật Hari Won', 'thu-hoai-duoc-khen-tre-dep-trong-tiec-sinh-nhat-hari-won', 'Thu Hoài được fan nhận xét trẻ trung trong loạt ảnh sinh nhật Hari Won, trong đó hình diện bikini dưới ống kính Trấn Thành nhận \"mưa\" lời khen.', '<p><span style=\"font-size:18px\">Vài ngày trước, Thu Hoài thu hút fan khi đăng ảnh diện bikini đỏ, tạo dáng trước biển. Hình ảnh do Trấn Thành - đàn em thân thiết của chị - ghi lại. Đa số fan nói ngưỡng mộ vóc dáng thon gọn của bà mẹ ba con. Khán giả nhận xét: \"Chị đang lão hóa ngược, càng ngày càng trẻ\", \"Gương mặt không&nbsp;dấu vết thời gian, không nghĩ Thu Hoài đã ngoài 40 tuổi\"...&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Hoa hậu Thu Hoài sexy&nbsp;qua ống kính Trấn Thành.\" src=\"https://i1-suckhoe.vnecdn.net/2020/06/26/image001-5139-1593137205.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=p9aA1OUxv3Dy2cyQAEEThg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hoa hậu Thu Hoài sexy&nbsp;qua ống kính Trấn Thành.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong loạt ảnh chụp cùng hội bạn thân nhỏ hơn mình gần 20 tuổi, Thu Hoài ghi điểm với gương mặt thon gọn, làn da căng mịn. Các đồng nghiệp đều nói rất khó đoán chính xác tuổi của Thu Hoài nếu gặp lần đầu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thu Hoài cho biết áy náy vì&nbsp;không thể trả lời hết những câu hỏi của fan trên trang cá nhân, đa phần mọi người đều muốn biết&nbsp;bí quyết giữ nhan sắc, vóc dáng của chị. \"Tôi&nbsp;may mắn hơn người khác khi làm chủ chuỗi mỹ viện, có điều kiện tiếp xúc với loạt&nbsp;công nghệ làm đẹp hiện đại. Bí quyết của tôi chỉ giản đơn như vậy\", chị nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bà mẹ ba con níu giữ thanh xuân nhờ Spider Web Lift - công nghệ căng chỉ mạng nhện. Từng thử rất nhiều công nghệ làm đẹp trước khi phục vụ khách, Thu Hoài đánh giá cao ưu điểm của phương pháp căng da mới. Theo đó, những sợi chỉ đóng vai trò như giá đỡ, giảm sức nặng lên các bó cơ và làm da phẳng phiu. Với sơ đồ hình mạng nhện, tỷ lệ bao phủ của Spider Web Lift&nbsp;lớn hơn, tạo hiệu quả nâng đỡ tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh đó, vật liệu chỉ Crystal khá&nbsp;đặc biệt, hỗ trợ tăng sinh collagen nhờ cấu trúc tinh tế và nguyên liệu cấu thành&nbsp;tương thích với cơ thể người. Nhờ vậy, ngoài tác dụng giá đỡ cho cơ mặt, giải pháp này&nbsp;còn giúp da đẹp từ sâu bên trong.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Hoa hậu Thu hoài được khen trẻ trung khi đứng lẫn trong team bạn thân ít hơn mình nhiều tuổi.\" src=\"https://i1-suckhoe.vnecdn.net/2020/06/26/image002-6238-1593137205.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=isEGPipnHgr2GfmBxS8LbQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hoa hậu Thu hoài (hàng hai, thứ hai từ phải qua)&nbsp;được khen trẻ trung khi đứng lẫn trong team bạn thân ít hơn mình nhiều tuổi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nhiều khách hàng của tôi gọi công nghệ này là \'kẻ cắp thời gian\', có thể trẻ ra đến 10 tuổi. Đội ngũ bác sĩ tại Khơ Thị từng thực hiện 3.000 ca&nbsp;từng&nbsp;chứng kiến nhiều chị em thậm chí còn trẻ hơn nhiều so với con số đó\", Thu Hoài nói thêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiện Khơ Thị giảm tới 50% (còn 44 triệu) dành cho công nghệ căng chỉ mạng nhện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Liên hệ&nbsp;Viện thẩm mỹ&nbsp;Khơ Thị&nbsp;hoặc gọi hotline 19006717 để biết thêm chi tiết.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Vạn Phát</strong></span></p>', 'c5oz_1.jpg', 1, 1, 34, '2020-06-26 08:43:37', '2020-06-26 08:43:37'),
(41, 'Cách nạp carb cho runner trước giải chạy', 'cach-nap-carb-cho-runner-truoc-giai-chay', 'Nguồn carb (carbohydrate) lành mạnh là một thành phần quan trọng để thúc đẩy hiệu suất tốt của runner trước mỗi cuộc đua.', '<p><span style=\"font-size:18px\">Carb&nbsp;gồm hàm lượng đường, tinh bột, chất xơ có trong trái cây, ngũ cốc, rau và các sản phẩm, chế phẩm từ sữa. Những người chạy bộ đều nắm rõ&nbsp;tầm quan trọng của việc nạp carb&nbsp;trước khi tham gia một cuộc đua marathon nhưng không phải ai cũng nắm được thời điểm \"vàng\" và cách&nbsp;chọn thực phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thời điểm nạp carb</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi bạn ăn một bát mì spaghetti, hầu hết các carb&nbsp;được lưu trữ dưới dạng glycogen trong cơ bắp và gan của bạn. Monique Ryan, R.D., tác giả của \"Sports Nutrition for Endurance Athletes\" (tạm dịch&nbsp;<em>Dinh dưỡng thể thao cho vận động viên sức bền</em>) cho biết, glycogen là dạng năng lượng dễ tiếp cận nhất với cơ thể, nhưng nó không phải là nguồn duy nhất. Trong cuộc đua marathon, runner sẽ đốt cháy cả glycogen và chất béo. Khi hết glycogen, cơ thể sẽ&nbsp;chậm lại vì nó cần biến chất béo thành năng lượng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Carb góp phần thúc đẩy hiệu suất của runner trong cuộc đua. Ảnh:&nbsp;Podiumrunner.\" src=\"https://i1-suckhoe.vnecdn.net/2020/06/22/new-rules-3-5933-1592793274.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mMb0obMqqaQKmA1cJ37DCA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Carb góp phần thúc đẩy hiệu suất của runner trong cuộc đua. Ảnh:&nbsp;<em>Podiumrunner</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các vận động viên&nbsp;có thể lấp đầy cơ bắp bằng glycogen chỉ sau một bữa ăn, đó là lý do họ nên bắt đầu nạp carb hai hoặc ba ngày trước cuộc đua. Nhiều người có thói quen nạp carb trước cả tuần; tuy nhiên nhiều nghiên cứu chứng minh việc nạp carb như vậy không hiệu quả và gây căng thẳng hơn cho người chạy, thậm chí có thể khiến cơ thể trở nên chậm chạp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Do đó, để chuẩn bị tốt cho cuộc đua, runner hãy bắt đầu tăng lượng carbohydrate của cơ thể trước 48 giờ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thực phẩm nên dùng</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài thời điểm nạp carb, những loại thực phẩm được chọn để cũng rất quan trọng. Trong quá trình này, protein và chất béo nên được giảm thiểu và carbohydrate dễ tiêu hóa nên được chú trọng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Carbohydrate phức tạp như ngũ cốc và rau quả rất bổ dưỡng nhưng chứa rất nhiều chất xơ và chất xơ có thể gây ra vấn đề đầy hơi và tiêu hóa. Bên cạnh đó,&nbsp;thực phẩm giàu chất béo&nbsp;như dầu, bơ và phô mai, cũng như những thực phẩm chứa lượng protein cao, làm bạn no nhanh và mất nhiều thời gian để tiêu hóa hơn carbohydrate.&nbsp;Chúng nên được giới hạn trong quá trình tải carb suốt 48 giờ của runner.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Thực phẩm đóng vai trò quan trọng trong quá trình nạp carb của vận động viên.\" src=\"https://i1-suckhoe.vnecdn.net/2020/06/22/spaghetti-and-meatballs-in-red-9777-6145-1592793275.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5YNqesOTDNkyDKc9hc-JoA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thực phẩm đóng vai trò quan trọng trong quá trình nạp carb của vận động viên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các lựa chọn carb phổ biến là&nbsp;bánh rán, bánh sừng bò và pizza. Những thực phẩm hỗn hợp này nên kết hợp với các thực phẩm có tỷ lệ carbohydrate cao hơn như bánh quy, nước trái cây, gạo trắng, chuối, mì ống với sốt cà chua hoặc bánh mì tròn. Bột yến mạch, bánh mì, bánh kếp, bánh quế, sữa chua và nước trái cây cũng dễ tiêu hóa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều loại trái cây chứa nhiều carbs nhưng cũng chứa nhiều chất xơ và quá nhiều có thể gây ra vấn đề về dạ dày. Chuối là một lựa chọn ít chất xơ, theo chuyên gia dinh dưỡng thể thao Ilana Katz, R.D. Với táo, đào và lê, bạn có thể bỏ vỏ trước khi ăn để giảm hàm lượng chất xơ, tốt cho tiêu hóa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong quá trình tập luyện marathon, runner có thể cần 5 đến 7 gram carbohydrate cho mỗi kg trọng lượng cơ thể. Runner cũng có thể chia nhỏ các bữa ăn trong ngày và bổ sung thêm carbs như một ly nước trái cây trong bữa ăn, mật ong hoặc xi-rô trái cây yêu thích hoặc thêm một phần mì ống hoặc gạo...&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu muốn&nbsp;tận dụng tối đa lượng carbohydrate, runner có thể tìm tới các&nbsp;chuyên gia dinh dưỡng thể thao để tư vấn thêm.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>An Nhiên</strong>&nbsp;(<em>tổng hợp</em>)</span></p>', 'yhFU_1.jpg', 1, 0, 35, '2020-06-26 08:46:20', '2020-06-26 08:46:20');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(42, 'Hai từ cất giữ bí mật của cuộc sống hạnh phúc', 'hai-tu-cat-giu-bi-mat-cua-cuoc-song-hanh-phuc', 'Theo các nhà khoa học của Đại học Harvard, bí quyết để cảm thấy hạnh phúc hơn trong cuộc sống chỉ nằm vẻn vẹn trong hai từ: \"Giúp đỡ\".', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Bằng cách này hay cách khác, tất cả chúng ta luôn không ngừng đeo đuổi hạnh phúc, sự hài lòng và sự bình yên trong cuộc sống. Đó là một nhiệm vụ khó nắm bắt, bởi những hoạt động, hay những vật chất mà chúng ta nghĩ sẽ mang lại cho chúng ta hạnh phúc, chẳng hạn như rượu bia, hay những ngày cuối tuần đắm mình với Netflix, hoặc những trò chơi điện tử... thường khiến chúng ta mệt lử nhiều hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Chúng ta luôn không ngừng đeo đuổi hạnh phúc, sự hài lòng và sự bình yên trong cuộc sống nhưng nhiều khi chọn sai con đường. Ảnh minh họa: Shutterstock.\" src=\"https://i1-giadinh.vnecdn.net/2020/06/26/Hanh-phuc-6015-1593133521.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cgC3t_4QBcAqOnHMTVIjKg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chúng ta luôn không ngừng đeo đuổi hạnh phúc, sự hài lòng và sự bình yên trong cuộc sống nhưng nhiều khi chọn sai con đường. Ảnh minh họa:&nbsp;<em>Shutterstock.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vậy đâu là cách chắc chắn để có được một tâm hồn hạnh phúc hơn? Theo một nghiên cứu mới, câu trả lời rất đơn giản: Tình nguyện giúp đỡ người khác. Nghiên cứu, được dẫn đầu thực hiện bởi nhà nghiên cứu Eric S. Kim - tiến sĩ của Đại học Harvard đã chỉ ra rằng, những người&nbsp;thường xuyên tình nguyện giúp đỡ những người khác chính là những người thường xuyên tận hưởng cảm giác hạnh phúc trọn vẹn nhất.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Và kể cả trong trường hợp kết quả đó chưa đủ để thuyết phục bạn, nghiên cứu tiếp tục chỉ ra rằng việc thường xuyên giúp đỡ người khác có sự liên hệ chặt chẽ với việc nguy cơ tử vong thấp hơn, tỷ lệ&nbsp;biến chứng sức khỏe giảm, khả năng hoạt động thể chất, thể dục cao hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo nghiên cứu này, một cá nhân nên tình nguyện giúp đỡ người khác khoảng 100 giờ mỗi năm, tương đương 2 giờ mỗi tuần, để gặt hái được những phần thưởng này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Một cách tự nhiên, con người có xu hướng hướng tới cộng đồng. Đó là lý do tại sao tâm trí và cơ thể chúng ta cảm thấy như được thưởng khi chúng ta chia sẻ, giúp đỡ người khác. Kết quả nghiên cứu của chúng tôi cho thấy rằng các hoạt động tình nguyện&nbsp;không chỉ giúp củng cố cộng đồng, mà còn làm phong phú thêm cuộc sống của chính chúng ta bằng cách củng cố mối liên hệ của chúng ta với mọi người xung quanh, giúp chúng ta cảm nhận được ý thức mục tiêu và hạnh phúc, đồng thời bảo vệ chúng ta khỏi cảm giác cô đơn, trầm cảm và vô vọng\", tiến sĩ Kim, thành viên của khoa Khoa học xã hội và hành vi, cùng Trung tâm Lee Kum Sheung - trung tâm Sức khỏe và Hạnh phúc và Trường Y tế công cộng trực thuộc Đại học Harvard, thực hiện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Từ trước đó, rất nhiều các nghiên cứu từng được thực hiện về lợi ích của việc giúp đỡ người khác, nhưng hầu hết các dự án đều mang lại kết quả không nhất quán. Cho đến nay, chưa từng có một nghiên cứu đủ kết luận để đảm bảo sự phát triển của các chương trình chăm sóc sức khỏe tình nguyện. Vì thế, với kết quả này, các chuyên gia sức khỏe tâm thần có thể sẽ muốn xem xét đề xuất công việc tình nguyện cho bệnh nhân, nhằm tìm kiếm những khả năng tích cực trong cuộc sống của họ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nghiên cứu trên hoàn toàn không phải là một nghiên cứu nhỏ. Dữ liệu, các cuộc phỏng vấn và các câu hỏi khảo sát từ gần 13.000 người trưởng thành ở Mỹ đã được phân tích. Tất cả các ứng viên tham gia đều được theo dõi trong 4 năm, vào khoảng từ 2010-2016. Sau đó, ảnh hưởng của các hoạt động tình nguyện đối với sức khỏe tinh thần, thể chất của mỗi người sẽ được đánh giá thông qua 34 kết quả thể chất và tinh thần khác nhau. Cách tiếp cận này cho phép các tác giả của các nghiên cứu có thể trực tiếp so sánh kết quả giữa các tình nguyện viên và nhận diện các kết quả (hạnh phúc, buồn bã, thư giãn, ung thư, tim mạch... ) mà việc tình nguyện có thể tạo ra.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Điều quan trọng đáng lưu ý là trong khi nghiên cứu đã tiết lộ mối liên hệ giữa việc tình nguyện giúp đỡ người khác và sự giảm nguy cơ tử vong chung, không có mối liên hệ nào được tìm thấy giữa việc tình nguyện giúp đỡ và sự cải thiện cụ thể, hoặc giảm nguy cơ phát triển các bệnh như ung thư, béo phì, đột quỵ, phổi, vấn đề nhận thức, đau mạn tính...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Y học hiện đại, công nghệ tiếp tục phát triển và tiến bộ với tốc độ nhanh chóng, mọi người trên thế giới đang sống lâu nhiều hơn so với trước đây. Với suy nghĩ này, nhóm nghiên cứu nói rằng việc khuyến khích, quảng cáo và tạo nhiều cơ hội tình nguyện hơn cho người cao tuổi là một cách tuyệt vời để vừa giúp đỡ người khác, vừa đồng thời cải thiện xã hội và nuôi dưỡng sức khỏe tinh thần, thể chất, trong bối cảnh dân số già của thế giới ngày càng tăng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đương nhiên, giống như hầu hết các khía cạnh của cuộc sống hiện nay, Covid-19 có thể là một thách thức với nghiên cứu này, một phần vì nghiên cứu được thực hiện trước thời điểm đại dịch&nbsp;xuất hiện trên thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tác giả của nghiên cứu này cảnh báo rằng các tình nguyện viên có thể gặp rủi ro khi tiếp xúc gần gũi với người khác. Điều đó có nghĩa là, nếu bạn có thể giúp đỡ mọi người trong khi bản thân vẫn an toàn, thì đó chính là một nỗ lực tốt đẹp hơn bao giờ hết.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tiến sĩ Kim nhấn mạnh: \"Đây có thể là một thời điểm đặc biệt trong lịch sử, khi xã hội cần đến sự chung tay của bạn nhất. Nếu bạn có thể là tình nguyện viên trong khi tuân thủ đúng theo các nguyên tắc về sức khỏe, bạn không chỉ giúp ích trong việc chữa lành cho thế giới, mà còn là giúp đỡ cho chính mình. Khi cuộc khủng hoảng nCoV lắng xuống, chúng ta có cơ hội tạo ra các chính sách cho phép công dân cống hiến nhiều hơn cho xã hội. Một số thành phố đã đi tiên phong triển khai ý tưởng này trước đại dịch, tôi hy vọng chúng ta có thiện chí và quyết tâm để thực hiện nó trong một xã hội hậu Covid-19\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thùy Linh&nbsp;</strong><em>(Theo Theladders)</em></span></p>', 'FsLw_1.jpg', 1, 1, 39, '2020-06-26 08:49:29', '2020-06-26 08:56:21'),
(43, 'Nội thất trong biệt thự tân cổ điển', 'noi-that-trong-biet-thu-tan-co-dien', 'Ngôi nhà sử dụng tông màu trắng, xám, kem... kết hợp cùng nội thất sang trọng tạo cảm giác rộng rãi, thoáng mát.', '<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/1-1593071718.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=SkvOx6CY6xSzcdwxUsBqpg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo yêu cầu của chủ nhà, Công ty thiết kế nội thất Luxy Design đã chọn gam màu xám kết hợp tông&nbsp;kem làm chủ đạo, tạo cảm giác nhẹ nhàng, thanh thoát,&nbsp;sang trọng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/2-1593071719.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jq1xxPP01bVNYNVFxRbKjQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại phòng&nbsp; khách, một&nbsp;bộ sofa tông màu kem nhẹ nhàng kết hợp với chất liệu vải cao cấp, những chi tiết nội thất không&nbsp;cầu kỳ nhưng vẫn&nbsp;đủ những đường nét cong mềm mại. Kệ để tivi là sự kết hợp giữa các khoang tủ, hộc trang trí&nbsp;tận dụng để đựng đồ.&nbsp;Ngoài tính&nbsp;thẩm mỹ thì yếu tố công năng&nbsp;được chú trọng&nbsp;trong thiết kế.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/3-1593071720.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gM30VWR8yGMBpHIO4b_OPw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khu vực bàn ăn được thiết kế liền kề theo hướng mở, điểm nhấn chính là bộ bàn ăn mang tông màu kem với kiểu dáng mềm mại, chiếc đèn trần Atlantis trải dài theo bàn ăn&nbsp;làm tăng tính thẩm mỹ, sang trọng cho không gian.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/4-1593071721.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v8zLXg7pbMkQR_pTGAUziw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh đó, phần không gian dưới chân cầu thang được tận dụng tối đa làm các ngăn chứa đồ.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thiết kế phòng bếp nhằm phân chia không gian phòng ăn, khu vực nấu nướng. Ánh sáng từ phía khu vườn giúp gia chủ có cảm giác hòa mình vào thiên nhiên khi đứng tại&nbsp;không gian nấu nướng.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/5-1593071722.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1O4zFCuAbKZh3KKtd8rYTg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc thiết kế một cửa sổ lớn kết hợp cùng hệ băng ghế ngồi dài khiến không gian làm việc, học tập thoáng đoãng. Sau những giờ làm việc mệt mỏi, gia chủ có dịpthư giãn&nbsp;ngồi bên khung cửa sổ ngắm nhìn sân vườn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/6-1593071723.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uzpxbNKW7qQIBWniipVhQQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phòng ngủ&nbsp;tô điểm&nbsp;bằng các gờ chỉ cùng với tông màu ghi xám phối kem kết hợp cùng nội thất, cách bài trí tinh tế&nbsp;tạo cảm giác ấm cúng, mềm mại.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/7-1593071724.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ApNcb5sR-TzkyVNlDJw0og\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khu vực thay đồ là một không gian thông thoáng với những hệ tủ để đồ cho gia chủ như túi xách, giầy dép,&nbsp;quần áo.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/8-1593071726.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0Rpb8Un9akDD5VNd5FbbAw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hệ vách tivi được bố trí phân chia giữa giường ngủ, khu vực thay đồ giúp gia chủ linh hoạt trong việc sử dụng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/9-1593071726.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UIk-HCYdDHWCiX_dHnzwGQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không gian sân vườn bên ngoài biệt thự bố trí&nbsp;cây xanh kết hợp với ao&nbsp;cá Koi,&nbsp;gần gũi với thiên nhiên, tạo cảm giác mát mẻ, trong lành vừa giúp căn nhà thêm sinh động.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-giadinh.vnecdn.net/2020/06/25/10-1593071727.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=rsCnWuNMZrQ6Rw86YceCsA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Với thiết kế&nbsp;&nbsp;không gian xanh bao trùm biệt thự, gia chủ có thể ngắm cảnh, tổ chức tiệc ngoài trời, thư giãn sau những giờ làm việc mệt mỏi.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Lê Nguyễn</strong></span></p>', 'JFNL_1.jpg', 1, 1, 40, '2020-06-26 08:55:25', '2020-06-26 08:55:25'),
(44, '8 lỗi trang phục mùa hè nam giới nên tránh', '8-loi-trang-phuc-mua-he-nam-gioi-nen-tranh', 'Mùa hè là thời gian lý tưởng để thử nghiệm các phong cách thời trang mới, nhưng nếu chỉ mắc một lỗi nhỏ sẽ làm hỏng toàn bộ diện mạo.', '<p><span style=\"font-size:18px\">1. Chọn áo phông có ống tay quá ngắn</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi chọn áo phông, nhiều người chỉ quan tâm mình mặc nó vừa vặn hay không, hoàn toàn quên chú ý đến tay áo. Tuy nhiên, thực tế cho thấy, chọn tay áo che khoảng nửa cánh tay, tính từ khuỷu tay trở lên sẽ giúp bạn đẹp hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"8 lỗi trang phục mùa hè nam giới&nbsp;nên tránh\" src=\"https://i1-giadinh.vnecdn.net/2020/06/26/1dd89e59deb8884d29801948a9-7855-1593144188.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sfcbNPbZyDw_XnVgOHCLEg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">2. Hay mặc quần jean</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mặc quần jean khá hoàn hảo với nam giới về mặt thẩm mỹ, nhưng không thiết thực trong mùa hè. Vải denim -chất liệu làm quần jean -&nbsp;không phải chất liệu thoáng khí. Bạn sẽ nóng bức, đổ mồ hôi nhiều khi mặc loại quần này. Tốt nhất, nên chọn quần làm từ loại vải nhẹ hơn như vải lanh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">3. Chỉ mặc quần áo sáng màu</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mùa hè là thời điểm hoàn hảo để bạn thử màu sắc khác nhau, đặc biệt khi bạn chưa từng trải nghiệm điều này. Nếu chưa quen, bạn nên kết hợp các món đồ một màu hoặc kết hợp áo khoác bên ngoài để thử nghiệm. Bạn cũng có thể thử các mẫu khác nhau như in hoa hoặc động vật để thay đổi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"8 lỗi trang phục mùa hè nam giới&nbsp;nên tránh - 2\" src=\"https://i1-giadinh.vnecdn.net/2020/06/26/037ff35e029325249aee0ca460-7739-1593144189.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ImRINiM_ElkSYkPnBJnGOg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">4. Chọn đồ bó sát cơ thể</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Quần áo bó sát khiến bạn đổ mồ hôi nhiều và hạn chế vận động. Vào mùa hè, nên thư&nbsp;giãn và tạm quên phong cách trang trọng, ngột ngạt. Thay vào đó, hãy cung cấp cho làn da một không gian để thở và chọn quần áo rộng hơn một chút. Điều đó không có nghĩa bạn nên mặc quần áo rộng thùng thình, chỉ cần chọn món đồ vẫn làm nổi bật các điểm mạnh mẽ của bạn như cánh tay, vai dưới mà không quá nóng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">5. Chọn quần short sai kích cỡ</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các chuyên gia cho rằng mặc quần short qua đầu gối là xu hướng lỗi thời. Nếu muốn trông hấp dẫn và sang trọng hơn, đừng ngại khoe chân và đùi trước. Lý tưởng nhất là quần trên đầu gối khoảng 8-10cm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">6. Mặc quần short với tất dài</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù chọn&nbsp;tất có màu sắc và hoa văn rực rỡ đang là xu hướng, nhưng nếu mặc quần short thì chỉ nên đi tất đến mắt cá chân hoặc đi tất không cổ hoặc cổ ngắn. Mục đích chính là khoe da chân mà không làm biến dạng tỷ lệ cơ thể bằng tất.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"8 lỗi trang phục mùa hè nam giới&nbsp;nên tránh - 4\" src=\"https://i1-giadinh.vnecdn.net/2020/06/26/fda79058d89959652fa243225d-2152-1593144189.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-o_47xjJ7MTOUH-jzPVD0g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">7. Mặc áo khoác \"mùa đông\"</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vào mùa hè, bạn chỉ mặc áo khoác nếu bắt buộc, ví dụ khi phải tham dự sự kiện sang trọng. Lúc ấy, nên chọn loại áo khoác&nbsp;được may bằng vải nhẹ, tự nhiên và thoáng khí. Bạn cũng có thể thử các màu sắc khác nhau, mặc áo màu sắc tươi mới&nbsp;hơn màu đen truyền thống hay màu xanh hải quân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">8. Không quan tâm giày dép</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều đàn ông dễ dãi trong việc chọn giày dép. Họ đi những đôi quá thoáng như dép xỏ ngón, chỉ phù hợp khi đi biển hoặc chọn những đôi quá bí như giày thể thao, giày da. Từ nay, tốt hơn hết, bạn nên chọn giày, dép nhẹ hoặc giày da lộn để đi vào mùa hè.</span></p>\r\n\r\n<p style=\"text-align: right;\"><strong><span style=\"font-size:18px\">Nhật Minh&nbsp;(Theo&nbsp;Brightside)</span></strong></p>', 'LBqL_1.jpg', 1, 0, 41, '2020-06-26 09:01:16', '2020-06-26 09:01:16'),
(45, 'Điểm hẹn của hướng dẫn viên \'mất việc\'', 'diem-hen-cua-huong-dan-vien-mat-viec', 'TP HCM - Thay vì dậm chân ở nhà khi chưa thể đi tour vì Covid-19, các hướng dẫn viên đã quy tụ tại Điểm hẹn Hướng dẫn mỗi cuối tuần.', '<p><span style=\"font-size:18px\"><img alt=\"hoichoHDV\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2206-jpg-1592818657-2447-1592821143.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dQWnHHXCfmzD3Wf3AZUbHA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hội chợ Điểm hẹn Hướng dẫn, được tổ chức trên&nbsp;tàu du lịch Indochina Queen,&nbsp;là không gian gặp gỡ, giao lưu giữa các hướng dẫn viên (HDV). Từ 6/6, trên dưới 30 gian hàng của các HDV phần nào giúp họ&nbsp;cải thiện thu nhập trong thời gian ngành du lịch chững lại do Covid-19, các tour đón khách nước ngoài vào Việt Nam và đưa khách Việt Nam đi nước ngoài \"đóng băng\"..</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"jk\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-4672-1592821139-6280-1592821144.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hCSfjJxWFxwcAVR7bXR1ZQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">HDV Hoàng Oanh (39 tuổi) có 12 năm trong nghề, chuyên dẫn khách nước ngoài vào Việt Nam lẫn đưa khách Việt đi Australia, Đài Loan, Hàn Quốc, phải tạm ngưng dẫn tour rồi về quê Gia Lai. Sẵn gia đình ở quê trồng bơ, tiêu và hạt mắc-ca, chị Oanh bán hàng online để cải thiện thu nhập. \"Từ khi có hội chợ HDV ở TP HCM, mỗi cuối tuần tôi đóng thùng hàng xuống tham gia, chủ yếu để gặp đồng nghiệp, nói chuyện với khách, cho đỡ nhớ nghề\", chị Oanh hồ hởi tâm sự.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"khjk\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2205-jpg-1592818687-4832-1592821144.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ni8MAv2kOh9P5HPxDVHB4w\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Là HDV tại điểm du lịch ở Bến Tre, Kim Linh (29 tuổi, áo trắng) đã quay trở lại guồng làm việc từ khi du lịch nội địa hồi sinh&nbsp;sau cách ly xã hội. Bên cạnh công việc hướng dẫn, Linh còn làm cho một homestay tại địa phương. \"Thủy hải sản, rau củ mình mang lên đây đều được nuôi trồng ở homestay phục vụ khách trải nghiệm, như cá mương thay vì khách lội bắt thì mình đóng gói cá đã sơ chế mang lên bán\", Kim Linh giới thiệu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"hkgk\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-4699-1592818674-9339-1592821144.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=HuSMaqD3H-bWzqOm6ivJxw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại các gian hàng ẩm thực, HDV trổ tài đầu bếp tự mang nguyên liệu từ quê đến để chế biến đặc sản cho khách thưởng thức với giá phải chăng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"hkj\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2204-jpg-1592818708-1702-1592821145.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AttRTNtpgWtejK6ipCB4jQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các mặt hàng đặc sản địa phương, ẩm thực, món \"nhà làm\" được bày bán nhiều nhất, bên cạnh những sản phẩm khác như đồ thủ công mỹ nghệ, quần áo, sách, dược phẩm...</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"kjlk\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2203-jpg-1592818719-5080-1592821145.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aRfOwTydfwVUqslr-mC2fQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều&nbsp;mặt hàng chỉn chu từ phương thức đóng gói đến cách bày biện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"kjhkl\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2208-jpg-1592818728-2802-1592821145.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=agAcQ_HzAMkS-dU4oHC_RA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bà Jenifer (60 tuổi, ngụ quận 4) hứng thú nghe giới thiệu và thử ăn món bánh mì quết&nbsp;đường thốt nốt ở&nbsp;gian hàng đặc sản của HDV người An Giang. Quen nhiều bạn bè làm du lịch, bà tin tưởng những người trải nghiệm nhiều nơi như HDV có thể gợi ý những món ngon nhất cho khách.<br />\r\nKhách hàng đến hội chợ phần nhiều là bạn bè, đồng nghiệp và khách từng đi tour của HDV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"kjkl\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2200-jpg-1592818750-5171-1592821146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=di-om2Z06Hr6ThLmqZhsvg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hội chợ ngày 20/6 mang đến chủ đề \"Cua Cà Mau - Niềm tự hào Đất Mũi\", du khách thích thú khi được nghe hướng dẫn cách chọn cua ngon, gợi ý điểm mua cua khi du lịch Cà Mau.<br />\r\nChủ đề của các ngày tháng 6 là: \"Marketing Online và Tour Guide\"; \"Bến Tre - Những điều bạn chưa biết\" và từ 16 - 20h thứ bảy 27/6 là \"Trầm hương và quế trong cuộc sống\".<br />\r\nKhông chỉ bán hàng, mỗi buổi hội chợ mang đến những chia sẻ về ngành nghề, kinh nghiệm, trải nghiệm từ chuyên gia và HDV, củng cố kiến thức hỗ trợ HDV theo chủ đề hàng tuần.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"nkjn\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2202-jpg-1592818739-7245-1592821146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=CFB9fYh1hSEF9sujqYr47w\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hội chợ trên tàu Indochina thu hút trung bình 200 người mỗi buổi. Nhà hàng trên tàu phục vụ đồng giá 39.000 đồng một món ăn, gồm bánh mì trứng ốp la, cơm chiên Dương Châu, mì xào hải sản cùng nhiều loại nước giải khát.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"kjlk\" src=\"https://i1-dulich.vnecdn.net/2020/06/22/img-2201-jpg-1592818767-2397-1592821146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m6GFVqwcfkL-ERtQBk3xcw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hội chợ Điểm hẹn Hướng dẫn cũng là dịp để du khách tham quan miễn phí bến cảng du lịch Sài Gòn, khám phá con tàu Indochina Queen, một trong những du&nbsp;thuyền du ngoạn ẩm thực trên sông Sài Gòn về đêm với giá 500.000 đồng một người.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hội chợ mở cửa miễn phí cho mọi người&nbsp;đến tham gia, không cần đăng ký trước.&nbsp;Lịch hoạt động diễn ra vào thứ bảy hàng tuần, đến tháng 8/2020. Giờ cụ thể được thông báo trên fanpage của Chi&nbsp;hội Hướng dẫn viên Du lịch TPHCM.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Địa chỉ: Cảng Sài Gòn,&nbsp;số 5 Nguyễn Tất Thành (quận 4).</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Tâm Linh</strong></span></p>', 'UfSM_1.jpg', 1, 0, 43, '2020-06-26 09:09:53', '2020-06-26 09:09:53'),
(46, 'Tour du lịch dành cho trẻ em', 'tour-du-lich-danh-cho-tre-em', 'Sản phẩm Hè cực chất của Vietravel mang đến nhiều trải nghiệm và kiến thức cho trẻ như trekking giữa rừng, vận động trên biển và từ thiện.', '<p><span style=\"font-size:18px\">Sau khoảng thời gian cách ly xã hội và trở lại học tập, trẻ cần một&nbsp;chuyến đi chơi để&nbsp;thỏa thích khám phá, trau dồi thêm kĩ năng mềm và làm quen thêm nhiều bạn mới. Hiểu được nhu cầu của phụ huynh và các em, công ty đã cho&nbsp;ra mắt dòng sản phẩm Hè cực chất, dành riêng cho các gia đình có con từ độ tuổi mầm non đến cấp 3.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Dành thời gian du lịch cùng con là cách các thành viên hiểu hơn về nhau. Ảnh: Shutterstock.\" src=\"https://i1-dulich.vnecdn.net/2020/06/26/3-29-8202-1593147171.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X1H7bSf-LSmA_CE7arxrHw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dành thời gian du lịch cùng con là cách các thành viên hiểu hơn về nhau. Ảnh:&nbsp;Shutterstock.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công ty&nbsp;lựa chọn những điểm đến gần, ngắn ngày, phù hợp với từng lứa tuổi như Madagui, Bảo Lộc;&nbsp;Lagi, Bình Thuận;&nbsp;Hồ Tràm, Long Hải;&nbsp;Cần Giờ;&nbsp;Mỹ Tho; Đồng Tháp;&nbsp;Vĩnh Long... Dù là những điểm đến quen thuộc, nhưng gia đình sẽ được trải nghiệm những hoạt động mới như&nbsp;trekking xuyên rừng, xem thú đêm, khám phá hồ bơi 3 tầng giữa núi rừng, trượt zipline, lửa trại kết thân. Ngoài ra là&nbsp;hành trình ‘về nguồn’ – tìm hiểu nét đẹp văn hóa dân tộc để yêu thêm đất nước và con người Việt Nam. Đây là những hoạt động dành cho trẻ độ tuổi trung học.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Độ tuổi mầm non và tiểu học là những hoạt động nhẹ nhàng nhưng cũng không kém phần thú vị như: Thử tài họa sĩ, kể chuyện bé nghe, bé nhảy aerobic cùng biển...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đặc biệt, tour sẽ kết hợp&nbsp;với các hoạt động từ thiện nhằm thắp lên tình yêu thương, chia sẻ và đồng cảm với những số phận kém may mắn hơn. Đồng thời khơi dậy&nbsp;nơi các con ý thức trách nhiệm với bản thân, gia đình và xã hội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Trong chuyến đi, trẻ sẽ có thêm nhiều bạn và phát triển thêm các kĩ năng mềm. Ảnh: Shutterstock.\" src=\"https://i1-dulich.vnecdn.net/2020/06/26/5-12-6813-1593147171.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aAaSW5Mrds77FNr0cqfZqg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trong chuyến đi, trẻ sẽ có thêm nhiều bạn và phát triển thêm các kĩ năng mềm. Ảnh:&nbsp;Shutterstock.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công ty chú trọng chọn địa điểm nghỉ dưỡng đầy đủ tiện nghi và phù hợp với hoạt động như Forever Green Resort – Resort đẹp nhất Bến Tre, Resort Cần Giờ, Homestay Út Trinh (Vĩnh Long), resort Đất Lành (Lagi), Quinter Central Nha Trang, Villa Madagui Forest City...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thông qua chuyến đi với những trải nghiệm cực chất, Vietravel mong muốn trẻ sẽ có thêm nhiều tình bạn đẹp,&nbsp;học tập lẫn nhau những kĩ năng sống, chia sẻ cùng nhau những điều thú vị và có một mùa hè với nhiều kỉ niệm đáng nhớ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thông tin tour tham khảo:</span></p>\r\n\r\n<p><span style=\"font-size:18px\">MỸ THO/ĐỒNG THÁP (2N1Đ)</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho bé: từ 695.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho phụ huynh: từ 1.390.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">KH dự kiến: 17,18,25/7/2020</span></p>\r\n\r\n<p><span style=\"font-size:18px\">VĨNH LONG (2N1Đ)</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho bé: từ 745.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho phụ huynh: từ 1.490.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">KH dự kiến: 17, 18, 25, 26/7/2020, 1,8/8/2020</span></p>\r\n\r\n<p><span style=\"font-size:18px\">CẦN GIỜ (2N1Đ)</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho bé: từ 845.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho phụ huynh: từ 1.690.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">KH dự kiến: 17, 18, 25, 26/7/2020, 1, 8/8/2020&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">MADAGUI (2N1Đ)</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho bé: từ 945.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho phụ huynh: từ 1.990.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">KH dự kiến: 17, 18, 25, 26/7/2020; 1, 8/8/2020&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">LAGI (BÌNH THUẬN) (2N1Đ)</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho bé: từ 995.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho phụ huynh: từ 1.990.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">KH dự kiến: 17, 18, 25/7/2020</span></p>\r\n\r\n<p><span style=\"font-size:18px\">HỒ TRÀM (2N1Đ)</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giá dành cho bé: từ 1.395.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">dành cho phụ huynh: từ 2.790.000 đồng/khách</span></p>\r\n\r\n<p><span style=\"font-size:18px\">KH dự kiến: 25,26/7/2020; 1,8/8/2020&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vui lòng liên hệ&nbsp;để biết thêm chi tiết:</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khối Kinh doanh Du lịch Khách đoàn - M.I.C.E</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Công ty Du lịch Vietravel</span></p>\r\n\r\n<p><span style=\"font-size:18px\">190 Pasteur, Phường 6, Quận 3, Tp. HCM</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tel: (028) 3822 8898</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Theo&nbsp;Vietravel</strong></span></p>', 'dOqa_1.jpg', 1, 0, 44, '2020-06-26 09:14:57', '2020-06-26 09:14:57'),
(47, 'Khỉ hoang náo loạn thành phố', 'khi-hoang-nao-loan-thanh-pho', 'THÁI LAN - Lũ khỉ ở Lopburi trước đây thường xuyên được du khách cho ăn nhưng Covid-19 ập tới khiến du khách không còn, khỉ hoang cũng thay đổi hành vi.', '<p style=\"text-align: center;\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/Gb7zlgNMDtk\" width=\"560\"></iframe></p>\r\n\r\n<p style=\"text-align:right\"><strong><span style=\"font-size:18px\"><span style=\"background-color:rgb(252, 250, 246); color:rgb(34, 34, 34); font-family:arial\">Theo&nbsp;</span><em>CGTN</em></span></strong></p>', 'QVaH_1.jpg', 1, 1, 46, '2020-06-26 09:22:07', '2020-06-26 09:22:35'),
(48, 'Tại sao ăn cay khiến ta chảy nước mũi?', 'tai-sao-an-cay-khien-ta-chay-nuoc-mui-', 'Đồ ăn cay chứa các hóa chất gây kích ứng niêm mạc mũi và như một cơ chế bảo vệ, chất nhầy được tiết ra hơn để loại bỏ chúng.', '<p style=\"text-align: center;\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/Xg3W_6D1g-E\" width=\"560\"></iframe></p>\r\n\r\n<p style=\"text-align:right\"><strong><span style=\"font-size:18px\"><span style=\"font-family:arial\">Đồ họa:&nbsp;</span><em>It\'s AumSum Time</em></span></strong></p>', 'dPze_1.jpg', 1, 5, 48, '2020-06-26 09:25:32', '2020-06-26 09:25:32'),
(49, 'Trăn mang thai dài 7m tấn công ôtô', 'tran-mang-thai-dai-7m-tan-cong-oto', 'BRAZIL - Trăn xanh Nam Mỹ nằm bên vệ đường, há miệng lao về phía ôtô chạy qua khiến tài xế bên trong hoảng sợ.', '<p style=\"text-align: center;\"><span style=\"font-size:18px\"><iframe align=\"middle\" frameborder=\"0\" height=\"315\" scrolling=\"no\" src=\"https://www.youtube.com/embed/gz75REiR0iI\" width=\"560\"></iframe></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trăn xanh Nam Mỹ dài khoảng 7 mét nằm phơi nắng ven một con đường đất ở Campo Grande, bang Mato Grosso do Sul, miền tây Brazil,&nbsp;<em>Daily Star</em>&nbsp;hôm 23/6 đưa tin. Con vật khổng lồ thu hút những chiếc xe&nbsp;trên đường. Một số tài xế dừng lại quan sát trong khi hành khách trên xe ghi hình nó. &nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi ôtô&nbsp;đi qua, con trăn ngóc đầu lên với tư thế sẵn sàng chiến đấu. Nó há miệng và phóng về phía chiếc xe khiến tài xế giật mình. Một video quay từ phía sau cho thấy một chiếc xe khác cũng bị tấn công khi chạy ngang qua. &nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một số người địa phương cho biết, họ chưa từng gặp con trăn xanh Nam Mỹ nào có kích thước lớn như vậy. \"Tôi tưởng con trăn sẽ bỏ&nbsp;đi nhưng không phải. Tôi đã hoảng sợ. Nó rất to, tôi chưa từng thấy con trăn nào to như thế\", Eduardo Jacintho, một trong những người lái xe qua đoạn đường đất, chia sẻ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhà sinh vật học địa phương Juliana de Souza Terra cho biết, con trăn đang mang thai. \"Cuộc tấn công nhằm khiến mọi người rời đi và để con trăn lại một mình vì nó đang sợ hãi\", Terra nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Jacintho chưa bao giờ nghĩ đến việc giết chết trăn để đi qua. \"Chúng ta có thể giúp bảo tồn các loài vật và không tương tác với chúng quá nhiều. Chúng có không gian riêng và chúng ta cũng vậy\", anh nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trăn xanh Nam Mỹ (Eunectes murinus) là loài trăn lớn nhất thế giới. Chúng có thể dài 6 - 9 mét và nặng tới 250 kg, theo&nbsp;<em>National Geographic.</em>&nbsp;Ngoài tự nhiên, tuổi thọ trung bình của loài vật này là 10 năm. Chúng thường ăn lợn rừng, hươu, chim, rùa, chuột lang nước, thậm chí cá sấu caiman và báo đốm. Trăn cái đẻ vài chục con non mỗi lứa. Trăn mới sinh dài khoảng 0,6 m, gần như biết bơi và săn mồi ngay lập tức.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thu Thảo</strong>&nbsp;(Theo&nbsp;<em>Daily Star</em>)</span></p>', 'sQ7H_1.jpg', 1, 2, 49, '2020-06-26 09:32:21', '2020-06-26 09:36:07'),
(50, 'Ba vòi rồng xuất hiện cùng lúc trên hồ', 'ba-voi-rong-xuat-hien-cung-luc-tren-ho', 'PHILIPPINES - Một đám mây giông lớn quét qua hồ Laguna de Bay hôm 30/5 đã tạo ra ba vòi rồng cao hàng trăm mét gần đảo Talim.', '<p style=\"text-align: center;\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/Bq3-zukOmCg\" width=\"560\"></iframe></p>\r\n\r\n<p style=\"text-align: right;\"><strong><span style=\"font-size:18px\"><span style=\"font-family:arial\">Video:&nbsp;</span><em>ViralHog/Newsflare</em></span></strong></p>', '6YLr_1.jpg', 1, 2, 50, '2020-06-26 09:43:52', '2020-06-26 09:43:52'),
(51, 'Xiaomi ra điện thoại 4 camera giá dưới 4 triệu đồng', 'xiaomi-ra-dien-thoai-4-camera-gia-duoi-4-trieu-dong', 'Xiaomi Redmi 9 bắt đầu bán tại Việt Nam. Máy được trang bị 4 camera AI, giá từ 3,59 triệu đồng.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Cụ thể, Redmi 9 bản RAM 3 GB bộ nhớ 32 GB có giá 3,59 triệu đồng, trong khi phiên bản RAM 4 GB bộ nhớ 64 GB có giá 3,99 triệu đồng. Người dùng sẽ có các lựa chọn màu sắc là xám, xanh và tím.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Redmi 9. Ảnh: Xiaomi\" src=\"https://i1-sohoa.vnecdn.net/2020/06/26/redmi9-8138-1593147671.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TvLKvpIyJgRZFJkA6noJjw\" style=\"width:100%\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px\">Redmi 9. Ảnh:&nbsp;<em>Xiaomi</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Redmi 9 là smartphone đầu tiên của Xiaomi trong phân khúc giá dưới 4 triệu nhưng được trang bị bốn camera sau, gồm camera chính 13 megapixel, camera siêu rộng 8 megapixel, camera macro 5 megapixel và một cảm biến 2 megapixel hỗ trợ chụp ảnh xóa phông. Camera selfie có độ phân giải 8 \"chấm\", thiết kế dạng \"giọt mưa\". Màn hình của Redmi 9 sử dụng công nghệ IPS LCD với kích thước 6,53 inch và độ phân giải FullHD+ (1.080 x 2.400 pixel).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Redmi 9 được trang bị chip MediaTek Helio G80, cùng hai tùy chọn bộ nhớ RAM 3 GB hoặc 4 GB. Pin của máy dung lượng 5.020 mAh, hỗ trợ sạc nhanh 18W. Tuy nhiên, củ sạc đi kèm máy có chỉ có công suất 10W. Máy được cài sẵn MIUI 11 trên nền tảng Android 10.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tương tự \"tiền nhiệm\" Redmi 8, mẫu máy mới của Xiaomi vẫn sử dụng cảm biến vân tay ở mặt lưng, được thiết kế dính liền cụm camera. Sản phẩm vẫn có cổng tai nghe 3,5mm, cảm biến hồng ngoại.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong tầm giá gần 4 triệu đồng, Redmi 9 sẽ là đối thủ của Vivo U10, Vsmart Active 3, Realme 5i hay Nokia 5.3.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Lưu Quý</strong></span></p>', '1O5K_1.jpg', 1, 2, 53, '2020-06-26 09:45:29', '2020-06-26 09:51:00'),
(52, 'Google tự động xoá dữ liệu người dùng sau 18 tháng', 'google-tu-dong-xoa-du-lieu-nguoi-dung-sau-18-thang', 'Dữ liệu vị trí, lịch sử duyệt web, video YouTube trong Google My Activity được cài sẵn ở chế độ \"Tự động xoá sau 18 tháng\" nhằm bảo mật thông tin.', '<p><span style=\"font-size:18px\">Trước đây,&nbsp;các dữ liệu cá nhân được Google thu thập để theo dõi người dùng trong tính năng My Activity đều được mặc định ở chế độ không tự động xoá. Bắt đầu từ ngày 24/6, dữ liệu cá nhân này sẽ tự động xoá sau 18 tháng được thu thập. Nếu muốn, người dùng có thể đặt tuỳ chọn thời gian xoá sớm hơn, sau 3 tháng hoặc cũng có thể chọn xoá thủ công.&nbsp;Các tài khoản mới khởi tạo sau ngày 24/6 cũng sẽ được đặt sẵn ở chế độ tự động xoá nói trên, thay vì mặc định&nbsp;ở \"Xoá thủ công\" như trước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sự thay đổi của Google được đánh giá tích cực, đảm bảo an toàn cho thông tin cá nhân của người dùng, tránh bị kẻ xấu khai thác khi truy cập trái phép&nbsp;vào tài khoản.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"My Activity là tính năng thu thập dữ liệu vị trí, lướt mạng hay xem video YouTube trực tuyến hàng ngày của người dùng, thông qua tài khoản Gmail và các dịch vụ của Google. Ảnh: pinimg\" src=\"https://i1-sohoa.vnecdn.net/2020/06/25/924fa05f9537ff86718be417e5c9dc-2182-9024-1593073453.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2u1MorFHVgfcwsI_FWUdQQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">My Activity thu thập dữ liệu vị trí, lướt web hay xem video YouTube trực tuyến hàng ngày của người dùng, thông qua tài khoản Gmail và các dịch vụ của Google. Ảnh:&nbsp;<em>Pinimg</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">My Activity&nbsp;hay \"Hoạt động của tôi\" xuất hiện từ giữa năm 2016, thu thập nhiều dữ liệu cá nhân của các tài khoản Google. Nó cho phép người dùng&nbsp;xem lại những hoạt động của mình trên Internet, từ vị trí hay di chuyển trên Google Map, trang web đã truy cập cho tới video YouTube đã xem. Tất cả thông tin được thu thập liên tục hàng giờ, hàng&nbsp;ngày&nbsp;thông qua các dịch vụ của Google. Kết quả trả về chi tiết tới&nbsp;tần suất truy cập một trang web nào đó, thiết bị sử dụng, thời gian truy cập, tìm kiếm những gì hay đã đi đến đâu, bao lâu...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">My Activity tương tự tính năng xem lịch sử duyệt web, nhưng dữ liệu rộng hơn. Nếu có được dữ liệu này, kẻ xấu có thể tạo một bản sao các hoạt động trên mạng, thói quen sinh hoạt hay bản đồ di chuyển của người dùng hàng ngày.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trên trang hỗ trợ, Google giải thích rằng, việc thu thập dữ liệu nhằm cải thiện trải nghiệm người dùng cũng như bảo mật khi lướt web. Tuy nhiên, theo&nbsp;<em>IBTimes</em>, mục đích thực sự của gã khổng lồ tìm kiếm là phân tích thói quen người dùng cho mục đích quảng cáo. Vì thế, nên thường xuyên kiểm tra và xoá dữ liệu đã bị thu thập, hoặc tắt hẳn chế độ này&nbsp;khi không cần thiết để tránh bị theo dõi.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Mỹ Anh<span style=\"font-family:arial\">&nbsp;(theo&nbsp;</span><em>CNBC</em><span style=\"font-family:arial\">)</span></strong></span></p>', 'IFXF_1.jpg', 1, 0, 53, '2020-06-26 09:50:27', '2020-06-26 09:50:27'),
(53, 'Cài đặt iOS 14 và iPadOS 14 Beta thế nào', 'cai-dat-ios-14-va-ipados-14-beta-the-nao', 'Phiên bản Beta có thể cài đặt và sử dụng trên nhiều thiết bị, thông qua một số thao tác cập nhật phần mềm OTA, không cần máy tính.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Đầu tiên, bạn&nbsp;chỉ cần cài đặt một cấu hình Profile dành cho lập trình viên,&nbsp;tải về thông qua trình duyệt trên iPhone và iPad. Sau đó, các bản cập nhật iOS 14 và iPadOS 14 Beta sẽ được tải về&nbsp;như bình thường thông qua chức năng Cập nhật phần mềm trong phần Cài đặt, tương tự các phiên bản phần mềm chính thức.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><iframe frameborder=\"0\" height=\"960px\" scrolling=\"no\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/06/23/thao-tac-cai-dat-ios-14-va-ipados-14-beta-1592885228.mp4\" width=\"480px\"></iframe></span></p>\r\n\r\n<p><span style=\"font-size:18px\">iOS 14 hỗ trợ&nbsp;các mẫu iPhone 6s trở về sau trong khi đó, với iPadOS 14, các thiết bị có thể sử dụng là từ iPad Air 2 trở về sau, toàn bộ các mẫu iPad Pro, iPad 5 và iPad mini trở về sau.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc sử dụng iOS 14 và iPadOS 14 Beta có thể gặp một số lỗi hoặc khiến thiết bị không ổn định, do cả hai mới là phiên bản thử nghiệm đầu tiên. Vì thế, người dùng nên sao lưu dữ liệu trước khi nâng cấp. Trong trường hợp muốn quay trở lại phiên bản cũ, chỉ cần xoá Cấu hình Profile&nbsp;đã được cài đặt, khởi động lại thiết bị và khôi phục lại dữ liệu cũ đã sao lưu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dự kiến tháng 9,&nbsp;các phiên bản chính thức của iOS và iPadOS 14 sẽ được phát hành.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Tuấn Anh</strong></span></p>', 'AIsE_1.jpg', 1, 16, 54, '2020-06-26 09:57:00', '2020-06-28 14:53:48'),
(54, 'Sei Giorni II - xe Vespa dáng lạ giá 139 triệu đồng', 'sei-giorni-ii-xe-vespa-dang-la-gia-139-trieu-dong', 'Mẫu xe ga bản chính hãng thiết kế theo phong cách xế đua cổ điển, phát triển từ dòng GTS, lắp động cơ 300 phân khối.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Tên xe Vespa Sei Giorni&nbsp;(Sáu ngày), bắt nguồn&nbsp;từ cuộc đua&nbsp;\"International Six Days\" năm 1951,&nbsp;dịch ra tiếng Italy là \"Sei Giorni Internazionale\". Trước đó, tháng 6/2018, Vespa từng giới thiệu&nbsp;</span>Sei Giorni<span style=\"font-family:arial\">&nbsp;tại Việt Nam giá 199 triệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Xe ga thể thao Sei Giorni II 2020. Ảnh:&nbsp;Vespa\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/Sei-Giorni-5-edited-Bai-5204-1593077275.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nRRuUrcEPgaODb4r6ARRuw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-size:14px\"><span style=\"font-family:arial\">Xe ga thể thao Sei Giorni II 2020. Ảnh:&nbsp;</span><em>Vespa</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/Sei-Giorni-3-edited-9795-15930-3997-2256-1593077282.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=wkln0LkJseep0Zg4kGMktw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-size:14px\">Sei Giorni sở hữu cụm đèn pha đặt ngay trên chắn bùn trước.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/Sei-Giorni-7-edited-7452-15930-8395-5315-1593077287.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GqfHTuRMLyvG7EKV1Aatuw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bảng đồng hồ thừa hưởng từ nguyên mẫu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Xe sơn màu xám nhám, thân xe thép liền khối, đèn pha tròn đặt trên chắn bùn trước. Bảng đồng hồ kiểu tròn như nguyên mẫu, pha trộn giữa hiện đại và cổ điển. Hốc còi sơn đen, vành trang trí với chi tiết màu đỏ thể thao. Yên xe bọc da, kiểu dáng cafe racer, kết hợp với chỉ trắng tương phản.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phát triển từ GTS, Vespa Sei Giorni II sở hữu cốp&nbsp;rộng. Hộc đồ trước tích hợp cổng sạc USB, đèn định vị LED, trang bị an toàn tiêu chuẩn với hệ thống chống bó cứng phanh ABS hai kênh. Phanh đĩa cả hai bánh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Xe sử dụng động cơ 300 HPE, loại một xi-lanh dung tích thực 278 phân khối, 4 thì, 4 van, làm mát bằng dung dịch, phun xăng điện tử, công suất 23,8 mã lực tại 8.250 vòng/phút, mô-men xoắn cực đại 26 Nm tại 5.250 vòng/phút. So với bản cũ, công suất tăng 2,6 mã lực, mô-men xoắn tăng 4 Nm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vespa Sei Giorni II 2020 bán ra tại thị trường Việt Nam từ ngày 24/6 với giá 139 triệu đồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Primavera Red 125. Ảnh: Vespa\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/GreyBG3-Bai-8196-1593077290.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Pct0JhBElgA9ZSpinCtLyA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Primavera Red 125. Ảnh:&nbsp;<em>Vespa</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh mẫu xe ga thể thao, thương hiệu xe Italy còn giới thiệu Vespa Primavera Red 125. Xe phát triển từ&nbsp;Primavera&nbsp;tiêu chuẩn và thừa hưởng từ bản đặc biệt Vespa 946 Red.&nbsp;Bản đặc biệt sơn màu đỏ, từ toàn bộ thân xe cho đến yên và cặp vành. Phiên bản hợp tác giữa Tập đoàn Piaggio với tổ chức từ thiện RED (tổ chức phòng chống các căn bệnh như HIV/AIDS, lao và sốt rét).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bản đặc biệt Vespa Primavera Red bán ra với giá 82,5 triệu. Trong khi bản tiêu chuẩn giá từ 74,5 triệu.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Minh Vũ</strong></span></p>', 'mCax_1.jpg', 1, 1, 56, '2020-06-26 10:11:33', '2020-06-26 10:11:33'),
(55, 'Suýt tông xe máy dừng giữa đường trong mưa lớn', 'suyt-tong-xe-may-dung-giua-duong-trong-mua-lon', 'HÀ NỘI - Cần gạt nước ôtô hoạt động mức cao nhất, loáng bóng xe máy dừng giữa đường khiến tài xế giật mình tối ngày 23/6 tại đường Lê Đức Thọ.', '<iFrame src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/06/25/oto-suyt-tong-vao-xe-may-dung-giua-duong-1593058857.mp4\" width=\"680\" height=\"480\" allowfullscreen></iFrame>\r\n\r\n<p><span style=\"font-size:14px\">Video: Lê Minh Đức.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lái xe ôtô&nbsp;<em>Lê Minh Đức</em>&nbsp;cho biết: \"May mắn tôi đi tốc độ chưa đến 30 km/h mới có thể tránh được. Trong điều kiện tầm nhìn hạn chế, việc xe máy đừng đột ngột chính giữa đường thật nguy hiểm\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Nguyên Vũ</strong></span></p>', '4V4l_1.jpg', 1, 5, 58, '2020-06-26 10:18:05', '2020-06-26 10:19:28'),
(56, 'Môtô điện một bánh tốc độ hơn 110 km/h', 'moto-dien-mot-banh-toc-do-hon-110-km-h', 'MỸ - Người điều khiển ngồi ngay bên trong bánh xe của chiếc môtô điện EV360 - sản phẩm có thiết kế trông như bước ra từ phim ảnh.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Đội kỹ thuật&nbsp;thuộc Đại học Duke (Durham, bang North Carolina) tạo ra chiếc xe đặc biệt nhằm đạt kỷ lục thế giới vào mùa xuân năm nay. Nhưng ảnh hưởng của dịch Covid-19 khiến dự án phải lùi lại. Đội kỹ thuật gồm cả một số cựu kỹ sư của hãng xe Tesla, đồng thời có một số nhân viên hiện tại của hãng hàng không vũ trụ SpaceX cũng của Elon Musk.</span></span></p>\r\n\r\n<p><iframe height=\"480\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/06/19/moto-dien-mot-banh-toc-do-hon-110-kmh-1592556366.mp4\" width=\"680\"></iframe></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\">Video:&nbsp;<em>Electrek</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mẫu môtô một bánh EV360 chỉ có một bánh xe đường kính lớn, không trục, và tài xế ngồi ngay bên trong để điều khiển. Một động cơ điện công suất 14,75 mã lực, tốc độ tối đa 112 km/h.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Xe vốn được thiết kế để đạt tốc độ cao, không phải chạy đường dài. Gói pin lithium polymer&nbsp;72V, 22Ah với công suất&nbsp;1,58 kWh. Đội thiết kế dự tính pin đủ cho quãng đường 14,5 km khi xe chạy ở tốc độ 32 km/h.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiện kỷ lục thế giới của xe một bánh nhanh nhất thuộc về một chiếc xe chạy xăng, với tốc độ tối đa 117 km/h.</span></p>', 'tpaY_1.jpg', 1, 9, 58, '2020-06-26 10:24:27', '2020-06-26 10:24:41');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(57, 'Phó chi cục hải quan bỏ chạy sau tai nạn', 'pho-chi-cuc-hai-quan-bo-chay-sau-tai-nan', 'BÌNH PHƯỚC - Va chạm xe làm một phụ nữ bị thương, ông Mai Như Vệ, Chi cục phó Hải quan cửa khẩu Hoàng Diệu, lái ôtô bỏ chạy 5 km.', '<p><span style=\"font-size:18px\">Ông Vệ, 54 tuổi, lái ôtô Honda CR-V trên tỉnh lộ 759, hướng từ huyện Bù Đốp đi TP HCM, chiều 26/6.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đến xã Phước Minh, huyện Bù Gia Mập, ông này để ôtô va chạm xe máy của chị Phạm Thị Năm, 36 tuổi, chạy hướng ngược lại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Ông Vệ lúc bị người dân chặn lại. Ảnh: Camera người dân cung cấp\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/27/1-6562-1593259882.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=wbzG4yKuuqqo9w3vWWuOTQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ông Vệ lúc bị người dân chặn lại.&nbsp;<em>Ảnh cắt từ video.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chị Năm ngã, được người dân đưa đến Bệnh viện đa khoa Bình Phước trong tình trạng bị thương nặng. Xe máy hư hỏng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau tai nạn, ông Vệ lái xe khỏi hiện trường, bị người dân đuổi theo. Đến đoạn cách nơi xảy ra tai nạn khoảng 5 km, ôtô của ông bị chặn lại. Công an huyện Bù Gia Mập đã lập biên bản xử lý.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Toàn bộ vụ tai nạn được camera an ninh của người dân và người đi đường ghi lại. Cơ quan chức năng xác định ông Vệ vi phạm nồng độ cồn khi điều khiển ôtô.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Văn Trăm</strong></span></p>', 'EPll_1.jpg', 1, 0, 1, '2020-06-27 18:20:26', '2020-06-27 18:20:26'),
(58, 'Tiến độ 3 dự án cao tốc chuyển sang đầu tư công', 'tien-do-3-du-an-cao-toc-chuyen-sang-dau-tu-cong', '3 dự án cao tốc Bắc Nam được Quốc hội chấp thuận chuyển từ vốn xã hội hóa sang đầu tư công sẽ đấu thầu trong tháng 7.', '<p><img src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/3-du-an-cao-toc-Bac-Nam-PC-1-1593055758.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=u30IGv-atMl-nGmsOqlO7g\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Tiến Thành - Đoàn Loan</strong></span></p>', '2ZnB_1.jpg', 1, 1, 1, '2020-06-27 18:47:54', '2020-06-27 18:47:54'),
(59, 'Phà Rạch Miễu hơn 100 tỷ đồng sẽ xong trước Tết', 'pha-rach-mieu-hon-100-ty-dong-se-xong-truoc-tet', 'BẾN TRE - Phà tạm tổng vốn hơn 100 tỷ đồng, cách cầu Rạch Miễu 10 km, sẽ kịp hoàn thành trước Tết Tân Sửu 2021, để giảm kẹt xe cho cầu hiện hữu.', '<p><span style=\"font-size:18px\">Ngày 27/6, ông Lê Văn Hoàng - Giám đốc Sở Giao thông Vận tải Bến Tre cho biết,&nbsp;sở vừa được UBND tỉnh giao làm chủ đầu tư bến phà Rạch Miễu tạm. Trong đó,&nbsp;các hạng mục chính như đường dẫn xuống bến, bến phà và các hạng mục phụ trở với&nbsp;tổng vốn 70 tỷ&nbsp;đồng sẽ do sở&nbsp;đảm nhận.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Riêng hạng mục đường dẫn ở Bến Tre, dài hơn một km, rộng 9 m, tổng mức đầu tư gần 37 tỷ đồng sẽ do UBND huyện Châu Thành làm chủ đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Sở đang chuẩn bị các thủ tục cần thiết, sẽ sớm khởi công và kịp hoàn thành trước Tết Tân Sửu 2021, nhằm giảm bớt tình trạng kẹt xe trên cầu Rạch Miễu\", ông Hoàng nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo Sở Giao thông Vận tải Bến Tre, trước mắt&nbsp;sẽ điều ba phà từ bến phà Tân Phú (Châu Thành) sang, sau đó sẽ tiếp tục bố trí thêm phà tùy theo lượng xe. Bến phà tạm hoàn thành sẽ miễn phí vé cho&nbsp;xe máy và người đi bộ. Các xe&nbsp;khác sẽ thu&nbsp;như mức phí qua trạm BOT Cầu Rạch Miễu, 20.000-140.000 đồng mỗi lượt.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Kẹt xe ở cầu Rạch Miễu, kéo dài từ trạm BOT sang bên kia cầu, hồi Tết Nguyên đán năm nay. Ảnh: Hoàng Nam.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/27/ket-xe-3997-1580216999-5935-1593231113.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=trJqAb4idc5rI_JjjotOmQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kẹt xe ở cầu Rạch Miễu, kéo dài 10 km từ trạm BOT ở Bến Tre sang bên phía Tiền Giang, hồi Tết Nguyên đán năm nay. Ảnh:&nbsp;<em>Hoàng Nam.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiện mỗi ngày có khoảng 18.000 lượt ôtô qua cầu Rạch Miễu. Riêng thứ bảy, chủ nhật tăng lên khoảng 20.000 xe, vượt gấp ba lần thiết kế cho phép của cầu. Do mặt cầu hẹp, mỗi chiều chỉ có một làn xe bốn bánh, nên giờ cao điểm, chỉ cần một vài xe tải nặng lên cầu sẽ gây ra&nbsp;ùn tắc 3-4 km, không chỉ ngày lễ, Tết mà cả ngày thường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước đó, UBND tỉnh Bến Tre và Bộ Giao thông Vận tải đã lên phương án xây cầu Rạch Miễu 2. Cầu có hai nhịp chính bằng dây văng, rộng bốn làn xe. Tổng mức đầu tư khoảng 4.500 tỷ đồng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Do thời gian dự kiến hoàn thành cầu này ít nhất phải 5 năm, UBND tỉnh Bến Tre đề xuất cần phải có bến phà tạm nhằm chia lửa với đường bộ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phương án khôi phục bến phà Rạch Miễu cũ cũng từng được đề xuất. Tuy nhiên, qua xem xét phương án này không khả thi, do xe sẽ đi vào trung tâm TP Mỹ Tho tiếp tục gây thêm tình trạng ùn ứ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau đó, phương án bến phà tạm tại bến đò Song Thuận (Châu Thành), cách cầu Rạch Miễu 10 km được Bộ Giao thông Vận tải trình Chính phủ phê duyệt.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bến phà này sẽ đấu nối với đường tỉnh 864 phía Tiền Giang và quốc lộ 57B phía Bến Tre, từ nguồn vốn BOT hoặc nguồn vốn khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cầu Rạch Miễu dài hơn 8,3 km bao gồm cả đường dẫn, mặt cầu rộng 12-15 m, khánh thành năm 2009.&nbsp;Tuy nhiên, từ sau khi cầu&nbsp;Cổ Chiên&nbsp;trên quốc lộ 60 nối Bến Tre - Trà Vinh đưa vào sử dụng năm 2015,&nbsp;lượng xe trên tuyến này tăng đột biến, dẫn đến tình trạng kẹt xe tại cầu Rạch Miễu. Nguyên nhân là người dân Trà Vinh, Vĩnh Long... cũng qua cầu này về TP HCM để rút ngắn khoảng cách.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Hoàng Nam</strong></span></p>', 'DTKw_1.jpg', 1, 5, 2, '2020-06-27 19:52:20', '2020-06-27 19:52:20'),
(60, 'Hàng chục người đẩy xe buýt cứu phụ nữ mắc kẹt', 'hang-chuc-nguoi-day-xe-buyt-cuu-phu-nu-mac-ket', 'TP HCM - Thấy một phụ nữ bị xe buýt cuốn vào gầm, hàng chục người đi đường đã cùng hợp sức đẩy và nâng xe lên để kéo nạn nhân ra ngoài.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Sáng 26/6, chị Lê Thị Nguyệt, 29 tuổi, ở huyện Hóc Môn, chạy xe máy trên đường Cộng Hòa, hướng từ Trường Chinh về vòng xoay Lăng Cha Cả, quận Tân Bình. Khi qua cầu vượt Hoàng Hoa Thám khoảng 100 m, chị va chạm với ôtô&nbsp;buýt tuyến Bến xe An Sương - Đại học&nbsp;Nông Lâm, do tài xế Ngô Quốc Cường chạy cùng chiều.</span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px\"><iframe height=\"480\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/06/26/hang-chuc-nguoi-nang-xe-buyt-cuu-co-gai-1593155231.mp4\" width=\"680\"></iframe></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Người phụ nữ và xe máy bị ôtô buýt cuốn vào gầm, kéo lê khoảng 10 m. Khi xe dừng,&nbsp;chị&nbsp;Nguyệt&nbsp;bất tỉnh, bị bánh ôtô cán lên&nbsp;tay trái. Do xe buýt&nbsp;bị kẹt phanh không&nbsp;thể di chuyển, tài xế Cường cùng hàng chục người đã hô hào&nbsp;đẩy ôtô&nbsp;lùi lại để cứu nạn nhân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ít phút sau, họ đã kéo được xe máy ra ngoài. Tiếp đó,&nbsp;mọi người tiếp tục nâng nghiêng xe buýt,&nbsp;kéo chị Nguyệt&nbsp;ra ngoài, chuyển đến bệnh viện.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bác sĩ Hoàng Văn Quang (trực lãnh đạo Bệnh viện Thống Nhất) cho biết, nạn nhân bị&nbsp;đa chấn thương. Đến chiều cùng ngày, các bác sĩ đã&nbsp;phẫu thuật cắt 2/3 cẳng tay trái, xử lý&nbsp;các xương sườn và các tổn thương khác của&nbsp;bệnh nhân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Xe buýt tại hiện trường sau khi nạn nhân được đưa ra ngoài. Ảnh: Đinh Vui.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/26/xe-buyt-9226-1593162146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eshnuyJU3ehFSrjhqzzDlg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xe buýt tại hiện trường sau khi nạn nhân được đưa ra ngoài. Ảnh:&nbsp;<em>Đinh Vui.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một lãnh đạo Trung tâm Quản lý và Điều hành&nbsp;vận tải hành khách công cộng (Sở Giao thông Vận tải TP HCM) cho biết, lúc&nbsp;tai nạn đường đông, xe buýt chạy khoảng 5 km/h.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cảnh sát giao thông quận Tân Bình đã lập biên bản điều tra. Chiếc xe buýt sau đó được kéo khỏi hiện trường, giải tỏa ùn tắc.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Đinh Vui</strong></span></p>', 'Trjg_1.jpg', 1, 2, 1, '2020-06-27 23:45:53', '2020-06-27 23:45:53'),
(61, 'Suarez trách HLV Barca', 'suarez-trach-hlv-barca', 'Tiền đạo Luis Suarez cho rằng anh và đồng đội đã chơi cật lực, nhưng Barca vẫn đánh rơi nhiều điểm trong cuộc đua vô địch La Liga.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Sau trận hòa&nbsp;</span>Celta Vigo<span style=\"font-family:arial\">&nbsp;hôm qua 27/6, đài&nbsp;</span><em>Movistar</em><span style=\"font-family:arial\">&nbsp;hỏi&nbsp;</span>Suarez<span style=\"font-family:arial\">&nbsp;rằng vấn đề của&nbsp;</span>Barca<span style=\"font-family:arial\">&nbsp;hiện tại là gì. Anh đáp: \"Vấn đề này các anh phải hỏi ban huấn luyện&nbsp;Barca. Họ sẽ phân tích để tìm ra vấn đề. Tôi và đồng đội đã làm tất cả những gì có thể trên sân. Cảm giác như Barca mùa này mất rất nhiều điểm trên sân khách, so với những mùa trước đây\".</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Suarez trút giận lên HLV Setien và cộng sự. Ảnh: AFP7.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/suarez-1593330966-4336-1593331005.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sCLTzytNY5zqCT_SZvU7Fg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Suarez trút giận lên HLV Setien và cộng sự. Ảnh:&nbsp;<em>AFP7</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi mất tự tin vì vừa đánh rơi hai điểm ở Vigo. Nếu muốn tranh vô địch, chúng tôi phải thắng các trận còn lại và hy vọng&nbsp;Real Madrid&nbsp;mất điểm ở đâu đó. Thật là bực mình. Barca vẫn còn nhiều trận đấu khó khăn, như làm khách ở&nbsp;Villarreal&nbsp;hay tiếp&nbsp;Atletico Madrid&nbsp;trên sân nhà. Chúng tôi phải sữa chữa sai lầm để tiến bộ\", anh nói thêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Suarez hai lần đưa Barca vượt lên dẫn trước Celta, nhưng hai lần chủ nhà gỡ hòa. Thậm chí nếu Nolito tận dụng tốt hơn cơ hội trước khung thành trống ở phút cuối, Barca đã ra về trắng tay. Sau trận, HLV Quique Setien cũng cho rằng cầu thủ Barca&nbsp;thiếu niềm tin ghi bàn.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px\"><iframe frameborder=\"0\" height=\"480\" scrolling=\"no\" src=\"https://v.vnecdn.net/thethao/video/web/mp4/2020/06/28/celta-vigo-2-2-barcelona-1593280345.mp4\" width=\"680\"></iframe></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trận hòa thứ hai liên tiếp trên sân khách khiến Barca hụt hơi trong cuộc đua vô địch với Real. Họ vượt lên chiếm đỉnh bảng với một điểm nhiều hơn kình địch. Nhưng, Real có thể tái chiếm đỉnh bảng nếu thắng chủ nhà Espanyol trong trận đấu đêm nay 28/6.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Hoàng An</strong>&nbsp;(theo&nbsp;<em>Besoccer</em>)</span></p>', 'dfL6_1.jpg', 1, 1, 24, '2020-06-28 13:54:27', '2020-06-28 13:54:27'),
(62, 'Bản đồ nhiệt đáng quên của Haaland', 'ban-do-nhiet-dang-quen-cua-haaland', 'Tiền đạo trẻ Erling Braut Haaland xuất hiện nhiều nhất ở vòng tròn giao bóng giữa sân, trong trận Dortmund thua Hoffenheim 0-4 ở vòng 34 Bundesliga hôm 27/6', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Trong cả trận, Haaland chỉ chạm bóng 18 lần, bốn lần mất bóng và không dứt điểm lần nào. Dortmund, dù kiểm soát bóng 58%, chỉ dứt điểm sáu lần so với 13 của Hoffenheim. Đội khách dứt điểm trúng hướng cầu môn tám lần, ghi bốn bàn đều do công tiền đạo Andrej Kramaric.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Bản đồ nhiệt của Haaland ở trận gặp Hoffenheim. Ảnh: Sofascore.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/capture-png-1593325928-6484-1593326183.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XM2kOk_MNqSH3HRWg1LhrQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bản đồ nhiệt của Haaland ở trận gặp Hoffenheim.&nbsp;<em>Ảnh: Sofascore.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mùa 2019-2020.&nbsp;Haaland&nbsp;ghi 44 bàn và 10 kiến tạo sau 40 trận cho Salzburg và Dortmund. Từ khi chuyển đến Dortmund hồi tháng 1/2020, chân sút 19 tuổi ghi 13 bàn sau 15 trận. Nhưng ở sáu trận gần nhất, Haaland chỉ ghi hai bàn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Động lực của Haaland cũng như Dortmund mất đi sau khi Bayern sớm&nbsp;vô địch Bundesliga. Ở ba vòng cuối, thầy trò Lucien Favre thua hai trận trước Mainz 05 và Hoffenheim. Thất bại 0-4 hôm qua là trận thua đậm nhất của Dortmund trên sân nhà trong 11 năm qua.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Haaland (ôm mặt) và các đồng đội lần thứ tư thua trắng mùa này. Ảnh: AP.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/dea5a7bf313011adc744eb7dbc4e71-6813-1335-1593326183.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qFmN9y28HbR7PjL46o0c4g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Haaland (ôm mặt) và các đồng đội lần thứ tư thua trắng mùa này.&nbsp;<em>Ảnh: AP.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mùa giải Bundesliga 2019-2020 khép lại với bốn suất dự Champions League dành cho Bayern, Dortmund, Leipzig và Monchengladbach. Hoffenheim, với trận thắng Dortmund, giành suất dự Europa League cùng Leverkusen.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px\"><iframe height=\"480\" src=\"https://v.vnecdn.net/thethao/video/web/mp4/2020/06/28/dortmund-0-4-hoffenheim-1593280674.mp4\" width=\"680\"></iframe></span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Nhân Đạt&nbsp;</strong><span style=\"font-family:arial\">(theo&nbsp;</span><em>Sportbible</em><span style=\"font-family:arial\">)</span></span></p>', 'lTNv_1.jpg', 1, 0, 24, '2020-06-28 13:58:12', '2020-06-28 13:58:12'),
(63, 'Guardiola: \'Man City sẽ xếp hàng đón nhà vô địch\'', 'guardiola-man-city-se-xep-hang-don-nha-vo-dich', 'HLV Pep Guardiola xác nhận sẽ tán thưởng Liverpool khi nhà tân vô địch Ngoại hạng Anh làm khách tại Etihad vào ngày 2/7.', '<p><span style=\"font-size:18px\"><em>*Newcastle - Man City: 0h30 ngày 29/6 theo giờ Hà Nội.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tất nhiên, chúng tôi sẽ làm như vậy. Chúng tôi sẽ xếp hàng chào đón nhà vô địch. Chúng tôi luôn đón tiếp Liverpool một cách trang trọng khi họ đến làm khách. Họ không thể phàn nàn, và dĩ nhiên, chúng tôi làm vì họ xứng đáng\",&nbsp;Guardiola&nbsp;trả lời&nbsp;<em>Sky Sports</em>&nbsp;khi được hỏi liệu&nbsp;Man City&nbsp;sẽ thực hiện nghi thức xếp hàng vỗ tay cho nhà vô địch&nbsp;Liverpool&nbsp;khi hai đội gặp nhau vào ngày 2/7.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Guardiola hứa Man City sẽ vỗ tay tán thưởng Liverpool khi hai đội gặp nhau vào tuần tới. Ảnh: PA.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/untitled-1593306420-5087-1593306526.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vXmOcv7Kbqmh35oBFsmSXw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Guardiola hứa Man City sẽ vỗ tay tán thưởng Liverpool khi hai đội gặp nhau vào tuần tới. Ảnh:&nbsp;<em>PA</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thất bại 1-2 của Man City trước Chelsea hôm 25/6 giúp&nbsp;Liverpool đăng quang Ngoại hạng Anh&nbsp;sớm 7 vòng. Guardiola không thể giúp đội nhà cạnh tranh với đối thủ họ từng đánh bại mùa trước. Hiện, Man City kém Liverpool tới 23 điểm. Tuy nhiên, Guardiola khẳng định khao khát của Man City vẫn rất mạnh mẽ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ông nói: \"Một đội đã giành 8 chiếc Cup trong 3 năm qua luôn có khao khát, trong mọi trận đấu, mọi giải đấu và bất cứ đâu. Không ai trên đất nước này có thể phủ nhận điều đó. Để làm được những điều như chúng tôi ở giải đấu khó chơi nhất hành tinh không phải chuyện đơn giản nhưng không phải lúc nào bạn cũng có thể chiến thắng\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chỉ có một đội đã thi đấu phi thường và có thể nói tôi học được nhiều điều từ họ. Cảm ơn họ. Đơn giản là thế. Đam mê và khao khát của Man City sẽ tiếp tục được thể hiện từ trận này qua trận khác\", Guardiola khẳng định.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tối 28/6, Man City sẽ làm khách của Newcastle trong khuôn khổ tứ kết Cup FA. Ngoài giải đấu này, thầy trò Guardiola cũng đang đá vòng 1/8 Champions League.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Guardiola đặt mục tiêu vào bán kết Cup FA cho đội nhà. Ông nói: \"Đó là ưu tiên lúc này vì chúng tôi có thể đến Wembley lần nữa nếu thắng trận tiếp theo. Tôi đã xem Newcastle thi đấu, và họ có những vũ khí để trừng phạt chúng tôi. Trận đấu này có tính chất quan trọng và chúng tôi cũng phải kết thúc Ngoại hạng Anh với thứ tự tốt nhất. Sau đó, chúng tôi sẽ có thời gian chuẩn bị cho trận đấu với Real\".</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Vĩnh San</strong><span style=\"font-family:arial\">&nbsp;(theo&nbsp;</span><em>Sky Sports</em><span style=\"font-family:arial\">)</span></span></p>', 'Vt1t_1.jpg', 1, 0, 24, '2020-06-28 14:04:18', '2020-06-28 14:04:18'),
(64, 'Lampard: \'Ưu tiên số một là Ngoại hạng Anh\'', 'lampard-uu-tien-so-mot-la-ngoai-hang-anh', 'ANH - HLV Chelsea bóng gió chuyện xoay vòng đội hình trong chuyến làm khách của Leicester ở tứ kết Cup FA tối nay 28/6.', '<p><span style=\"font-size:18px\"><em>* Leicester City - Chelsea: 22h Chủ nhật 28/6.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi phải cân nhắc việc xoay tua đội hình nhằm giữ thể lực cho cầu thủ\", Frank Lampard nói trên trang chủ&nbsp;Chelsea&nbsp;hôm 28/6. \"Chelsea đang trong giai đoạn chuyển giao. Ưu tiên số một của chúng tôi vẫn là Ngoại hạng Anh. Nhưng nếu cơ hội đến, nhất là khi đã vào tứ kết và có thể chơi bóng ở Wembley, bạn cần nắm lấy\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Sau khi mang về Timo Werner và Hakim Ziyech, áp lực giành vé dự Champions League lên Lampard (giữa) là rất lớn. Ảnh: Chelsea FC.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/lampard-drinks-break-man-city-5306-1593333080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o9XFGvZEt-K78VyYEjijfg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi mang về Timo Werner và Hakim Ziyech, áp lực giành vé dự Champions League lên Lampard (giữa) là rất lớn. Ảnh:&nbsp;<em>Chelsea FC.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cơ hội đoạt một danh hiệu mùa này của Chelsea giờ trông cả vào&nbsp;Cup FA, sau khi&nbsp;Liverpool&nbsp;vô địch&nbsp;Ngoại hạng Anh, còn ở vòng 1/8&nbsp;Champions League, thầy trò Frank Lampard thua Bayern Munich 0-3 trên sân nhà lượt đi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại Cup FA, Man Utd là đội đầu tiên vào bán kết. Ngoài cặp đấu Leicester City - Chelsea, hai cái tên đáng gờm khác cũng góp mặt ở tứ kết là&nbsp;Arsenal&nbsp;và&nbsp;Man City. Lampard bày tỏ: \"Không dễ&nbsp;đoạt Cup FA, nhưng với đội hình hiện có cùng tâm lý đang lên, chúng tôi có quyền chọn cách tiếp cận trận đấu một phù hợp\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chelsea hiện đứng thứ tư&nbsp;Ngoại hạng Anh, với lần lượt hai và năm điểm nhiều hơn Wolves, Man Utd. Trong bối cảnh mùa giải còn bảy vòng và phải thi đấu với mật độ ba, bốn ngày một trận, Lampard cần có kế hoạch để giữ suất dự Champions League mùa sau.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Thật tuyệt khi mọi tuyến Chelsea đều không bị quá tải. Chúng tôi có đủ phương án dự phòng, trước khi nghĩ tới việc cải thiện chất lượng vào cuối mùa\", Lampard chia sẻ. \"May mắn là đội hình Chelsea hầu như không bị sứt mẻ, cả trước và sau thời gian giãn cách xã hội. Tôi cần học trò luôn khỏe mạnh và đạt 100% thể lực, nhất là những người đã gặp Man City và Aston Villa. Chúng tôi vẫn trong thời kỳ tái thiết, và sẽ giữ vững tinh thần làm việc chăm chỉ\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thắng Nguyễn</strong>&nbsp;(theo&nbsp;<em>Chelsea FC</em>)</span></p>', 'VX7s_1.jpg', 1, 0, 24, '2020-06-28 14:17:19', '2020-06-28 14:17:19'),
(65, 'Ian Wright: \'Có Sancho, Man Utd đủ sức uy hiếp Liverpool\'', 'ian-wright-co-sancho-man-utd-du-suc-uy-hiep-liverpool', 'Huyền thoại Arsenal tin rằng Jadon Sancho sẽ giúp Man Utd nâng cấp sức mạnh hàng công.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">\"Mọi người đều thấy phấn khích vì sự kết hợp&nbsp;</span>Bruno Fernandes<span style=\"font-family:arial\">&nbsp;và&nbsp;</span>Paul Pogba<span style=\"font-family:arial\">.&nbsp;</span>Anthony Martial<span style=\"font-family:arial\">&nbsp;và&nbsp;</span>Marcus Rashford<span style=\"font-family:arial\">&nbsp;cũng đang ở phong độ tốt, mỗi người đã có 19 bàn từ đầu mùa\", Ian Wright nói với&nbsp;</span><em>BBC One&nbsp;</em><span style=\"font-family:arial\">sau trận Man Utd&nbsp;</span>thắng Norwich 2-1<span style=\"font-family:arial\">&nbsp;ở tứ kết Cup FA. \"Lần gần nhất,&nbsp;</span>Man Utd<span style=\"font-family:arial\">&nbsp;có hai cầu thủ ghi nhiều bàn như vậy là Chicharito và Dimitar Berbatov. Có vẻ họ sắp trở lại cuộc đua vô địch\".</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Solskjaer xuống sân chúc mừng học trò, sau khi hạ Norwich 2-1 tối 27/6. Ảnh: Reuters.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/30130402-8466735-image-a-41-15-1948-5499-1593313617.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=y8nqIoEpuNH3JCP6S1QfGw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Solskjaer xuống sân chúc mừng học trò, sau khi hạ Norwich 2-1 tối 27/6. Ảnh:&nbsp;<em>Reuters.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cựu tiền đạo Alan Shearer đồng tình với ý kiến của Ian Wright, và cho rằng Man Utd cần thêm ba hoặc bốn hợp đồng chất lượng nữa để thách thức&nbsp;Liverpool&nbsp;và&nbsp;Man City&nbsp;ở mùa kế tiếp.&nbsp;Trong đó,&nbsp;Jadon Sancho&nbsp;là cái tên nổi bật. Tuyển thủ Anh có tốc độ, kỹ thuật, và đã được kiểm chứng tài năng ở&nbsp;Borussia Dortmund.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Sancho thuộc nhóm cầu thủ bạn bắt buộc phải ký nếu muốn cạnh tranh chức vô địch. Đó cũng là cách Man City, Liverpool đã làm và thành công\", Wright nhấn mạnh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài Man Utd, Liverpool và Man City cũng quan tâm đến Sancho. Tuy nhiên, HLV&nbsp;Jurgen Klopp&nbsp;vừa tuyên bố rút lui khỏi cuộc đua chữ ký được Dortmund định giá hơn 100 triệu USD. \"Sancho rất cuốn hút. Tôi thực sự quan tâm tới cậu ấy. Nhưng một hợp đồng đắt giá như vậy rất khó xảy ra trong hè này\", nhà cầm quân người Đức nói gần đây.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px\"><iframe height=\"480\" src=\"https://v.vnecdn.net/thethao/video/web/mp4/2020/05/20/nhung-khoanh-khac-dang-nho-cua-jadon-sancho-1589943928.mp4\" width=\"680\"></iframe></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu Man City không kháng cáo thành công án cấm thi đấu Champions League, Man Utd gần như một mình trong cuộc đua giành Sancho. Họ hiện xếp thứ năm Ngoại hạng Anh. Nếu đội bóng cùng thành phố bị phạt, \"Quỷ Đỏ\" đương nhiên có suất dự Champions League.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một khả năng khác đưa Man Utd đến Champions League là vô địch Europa League, giải đấu họ hiện đặt một chân vào tứ kết nhờ thắng LASK 5-0 trên sân khách ở lượt đi vòng 1/8. Bên cạnh đó, Ole Gunnar Solskjaer cũng&nbsp;quyết tâm đoạt Cup FA, sau khi thắng&nbsp;Norwich&nbsp;2-1 ở tứ kết.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thắng Nguyễn</strong>&nbsp;(theo&nbsp;<em>Sports Mail</em>)</span></p>', 'R0OV_1.jpg', 1, 1, 24, '2020-06-28 14:21:47', '2020-06-28 14:21:47'),
(66, 'Stoichkov: \'Van Gaal là gã ngốc nghếch\'', 'stoichkov-van-gaal-la-ga-ngoc-nghech', 'Cựu tiền đạo Barca, Hristo Stoichkov chỉ trích cựu HLV Louis van Gaal về cách dùng sai lầm đối với Juan Roman Riquelme và nhiều vấn đề khác.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">\"Tại sao Riquelme không thành công với&nbsp;</span>Barca<span style=\"font-family:arial\">? Vì cậu ấy phải làm việc với một gã&nbsp;ngốc như&nbsp;</span>Van Gaal<span style=\"font-family:arial\">&nbsp;trong vai trò HLV. Mọi thứ quá rõ ràng, phải không?\", Stoichkov nói hôm 27/6. \"Riquelme đạt được nhiều thành tích được công nhận, nhưng phải chịu đựng vì thứ bóng đá ngớ ngẩn của Van Gaal. Cậu ấy không thành công vì cách dùng của Van Gaal, nhưng mùa duy nhất cậu ấy chơi cho Barca vẫn để lại nhiều dấu ấn. Tôi thích lối chơi, những bàn thắng, đường chuyền và cách di chuyển của cậu ấy\".</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Van Gaal gây điều tiếng ở gần như mọi CLB mà ông dẫn dắt. Ảnh: Reuters.\" src=\"https://i1-thethao.vnecdn.net/2020/06/28/van-5951-1593322049.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dAAaImiqoWxAeU5fIK7Zig\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Van Gaal gây điều tiếng ở gần như mọi CLB mà ông dẫn dắt. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Riquelme, tiền vệ từng bốn lần vô địch Argentina và ba lần giành Copa Libertadores, trở thành cái gai trong mắt Van Gaal trong mùa giải 2002-2003. Trong một lần trả lời phỏng vấn, HLV người Hà Lan gọi Riquelme là \"bản hợp đồng mang tính chất chính trị\". Ông cũng thường cho ngôi sao người Argentina chơi ở vị trí tiền vệ cánh, mà không phải tiền vệ dẫn dắt sở trường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau mùa giải duy nhất khoác áo Barca, Riquelme lấy lại sự tự tin ở Villarreal. Năm 2005, anh thậm chí lọt vào danh sách ứng viên giải Cầu thủ hay nhất năm của FIFA.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Van Gaal từng giành không ít danh hiệu với Ajax, Barca và Bayern, nhưng cũng nổi tiếng vì sự cứng nhắc. Trước&nbsp;Stoichkov, nhiều ngôi sao cũng từng chê và cho rằng, Van Gaal chỉ thích hợp làm việc một mùa giải với mỗi CLB. Stoichkov kể&nbsp;thêm: \"Tôi cũng phải chịu đựng Van Gaal. Ông ấy từng bước vào phòng thay đồ và nói rằng, ở Barca có quá nhiều tư tưởng bóng đá của Cruyff. Và tất nhiên, tôi nghĩ: \'Cruyff ở tầm nào và Van Gaal đứng ở đâu\'. Giữa hai người có sự chênh lệch lớn\".</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thanh Quý&nbsp;</strong>(theo&nbsp;<em>Radio Impacto</em>)</span></p>', 'h02j_1.jpg', 1, 2, 24, '2020-06-28 14:24:44', '2020-06-28 14:24:44'),
(67, 'Laptop có cổng USB ẩn', 'laptop-co-cong-usb-an', 'Dòng laptop tầm trung ThinkBook 14 mới của Lenovo trang bị nhiều cổng kết nối, trong đó có cả cổng USB giấu kín.', '<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE26-1593156244.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KfgLQBdb6AblKM9SW7CWig\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">ThinkBook 14 IML nằm giữa dòng sản phẩm cao cấp ThinkPad và dòng máy tính tầm trung IdeaPad của Lenovo. Tại Việt Nam, sản phẩm có nhiều tuỳ chọn&nbsp;cấu hình, từ Core i3 cho tới Core i7 thế hệ 10 với giá từ 11,9 triệu đến 33,5 triệu đồng. Tuy nhiên, các phiên bản giá trên 20 triệu đồng của ThinkBook được đánh gia là&nbsp;đắt so với hiệu năng và cấu hình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE17-1593156285.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=p8Zowx9G6-e-klKJBz7MFQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">ThinkBook 14 IML cứng cáp và chắc chắn. Vỏ kim loại với tông màu xám mờ, hạn chế xước và&nbsp;chống bám bẩn vân tay. Bốn góc được bo vuông kết hợp các đường vát chéo,&nbsp;thẳng ở viền tạo cho máy vẻ nam tính. Máy có trọng lượng 1,5 kg, thuộc hàng&nbsp;nhẹ trong nhóm laptop 14 inch ở tầm giá dưới 20 triệu đồng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE16.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=gtQxND9CgXrflJIchU-Btg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trái ngược kiểu tối giản của nhiều laptop hiện nay, ThinkBook 14&nbsp;được trang bị rất&nbsp;nhiều cổng kết nối, xen kẽ tiêu chuẩn cũ và mới. Máy vẫn giữ giắc cắm mạng RJ45, cổng HDMI nằm ở cạnh phải,&nbsp;bên cạnh là cổng&nbsp;USB-A 3.1 có thể sạc điện ngay cả khi laptop đã tắt. Ngoài ra, máy cũng có hai cổng chuẩn USB-C&nbsp;hỗ trợ Display Port và sạc Power Delivery. Máy dùng khe đọc thẻ SD thay vì microSD, phù hợp với người thường dùng máy ảnh, máy quay video.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE21-1593156370.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nWxqRNIhlnFxppyfeja4IA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cạnh phải có một cổng USB-A 2.0 ẩn trong thân máy,&nbsp;có thể cất vừa một chiếc USB lưu trữ cỡ nhỏ hay gắn bộ thu phát không dây của chuột máy tính. Thiết kế này khá hữu dụng, giúp khung viền vẫn liền lạc và tránh bị thất lạc phụ kiện.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE32-1593156383.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=i-UpkK7w2Ky370KxfoK63g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giống điện thoại và máy tính bảng, ThinkBook 14 có thể sạc pin thông qua cổng USB-C và pin dự phòng. Máy&nbsp;có dung lượng pin 45 Wh, hỗ trợ sạc nhanh RapidCharge nên&nbsp;có thể nạp được 80% dung lượng pin trong một giờ. Điểm chưa hài lòng là bộ sạc có sẵn theo máy khá nặng, cồng kềnh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE30-1593156406.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zawlJg-OZLcueJb5XDUlJA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bản lề của máy có thể mở phẳng 180 độ, tạo ra khả năng sử dụng linh hoạt. Góc nhìn màn hình và bàn phím&nbsp;thoải mái.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE10.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=i-2x664q-eolDkw4RZ2O4g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Màn hình chưa tốt so với mức giá 20 đến 30 triệu đồng mà chỉ chấp nhận được với các phiên bản giá thấp. Viền bên dưới dày. Chất liệu viền bao quanh là dạng nhựa nhám dễ bám bụi, dấu mồ hôi và lồi lên khá nhiều so với màn hình.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chất lượng hiển thị của màn hình tốt so với giá: độ phân giải Full HD và&nbsp;tấm nền IPS. Góc nhìn rộng, màu sắc sống động phù hợp khi lướt web, xem phim trực tuyến.&nbsp;Độ sáng cần tăng lên mức tối đa khi sử dụng ở môi trường nhiều sáng, ngoài trời.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE4.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=WCgF6L5qI8UlykHEzb1zBQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kế thừa thiết kế trên các dòng ThinkPad, sản phẩm cũng có cảm biến vân tay một chạm, gạt che camera để bảo mật.&nbsp;Cảm biến vân tay kiêm phím nguồn cho thao tác nhận diện nhanh và chính xác, như trên smartphone. Camera trước có độ phân giải HD 720p, đáp ứng tốt nhu cầu họp hay học trực tuyến.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE13.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tHVAuF8cv-etpxrbePiGfg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kích thước phím, trackpad&nbsp;và khoảng cách giữa các phím khá thoải mái để soạn thảo văn bản. Hàng phím chức năng trên cùng có thêm một dãy phím riêng để thao tác cuộc gọi với Skype, tuy nhiên, sự bổ sung này không cần thiết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Toàn bộ bàn phím được làm nổi tạo ra hành trình phím ổn, thể hiện được độ nảy khi gõ, nhưng lại không bị lún phím nhiều khi nhấn mạnh. Lenovo cũng trang bị đèn nền LED với hai độ sáng. So với màn hình, phần viền bao quanh bàn phím bên dưới trông cao cấp hơn với mặt vân phay xước ngang.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE7-1593156601.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fL-xezXqLrkQLFDIt3DY3w\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cấu hình cao nhất của ThinkBook 14 IML&nbsp;cho thị trường Việt Nam dùng chip Core i7-10510U thế hệ 10 đi kèm RAM 16GB DDR4, ổ cứng Intel Optane 512 GB và card đồ hoạ rời AMD Radeon 625. Tốc độ khởi động nhanh, máy đáp ứng tốt nhu cầu làm việc văn phòng, đảm bảo hoạt động mượt với trình duyệt mở 20 trang cùng lúc. Các ứng dụng biên tập hình ảnh như Photoshop và Lightroom hoạt động ổn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù có card đồ hoạ rời AMD Radeon 625, ThinkBook 14 IML vẫn không dành cho nhu cầu giải trí. Với game esport quen thuộc,&nbsp;<em>LOL</em>, máy bị rớt khung hình và không đảm&nbsp;bảo 60 fps với thiết lập đồ hoạ cao nhất. Khi chạy ở chế độ hiệu năng cao hoặc trong một số trường hợp cần ép hiệu năng, tiếng quạt tản nhiệt của máy rít khá lớn, gây khó chịu.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img src=\"https://i1-sohoa.vnecdn.net/2020/06/26/Lenovo-ThinkBook-14-VnE8-1593156612.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=l4QQKVK9Xg2Ha5i-nOBQeg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thời gian sử dụng trung bình khoảng 8 tiếng với nhu cầu sử dụng&nbsp;thông thường - dùng ứng dụng văn phòng và lướt web.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Tuấn Anh</strong></span></p>', '67YO_1.jpg', 1, 1, 53, '2020-06-28 14:40:44', '2020-06-28 14:40:44'),
(68, 'iPhone 12 có giá thấp nhất 549 USD', 'iphone-12-co-gia-thap-nhat-549-usd', 'iPhone 12 được cho là sẽ có một phiên bản sử dụng kết nối 4G/LTE với giá 549 USD, rẻ hơn 100 USD so với bản dùng 5G.', '<p><span style=\"font-size:18px\">Theo&nbsp;<em>GSMArena</em>, Apple sẽ không trang bị công nghệ 5G cho tất cả các mẫu iPhone 12. Hãng vẫn phát triển một model 4G dùng&nbsp;màn hình LCD 5,4 inch để đưa mức giá sản phẩm xuống dưới ngưỡng 600 USD, nhờ đó&nbsp;tiếp cận nhiều người dùng hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuần trước, nhà phân tích Daniel Ives cũng cho rằng iPhone 12 sẽ có bản 4G giá rẻ để thu hút người sử dụng tại Trung Quốc và&nbsp;các thị trường mới nổi.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Như vậy, iPhone 12 nhiều khả năng sẽ có tới năm&nbsp;phiên bản, thay vì bốn như các tin rò rỉ trước đó.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Ý tưởng iPhone 12 không tai thỏ.\" src=\"https://i1-sohoa.vnecdn.net/2020/06/27/iPhone-8792-1593226898.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cYoG4nuG4pUBUHAadzGMNA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ý tưởng iPhone 12 không \"tai thỏ\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hồi tháng 4, chuyên&nbsp;gia Jon Prosser&nbsp;chia sẻ&nbsp;về bốn phiên bản iPhone thế hệ mới, đều dùng màn hình OLED, chip A14 và hỗ trợ 5G. Trong đó, iPhone 12 có giá thấp nhất là 649 USD, dùng&nbsp;màn hình 5,4 inch và&nbsp;cụm camera kép ở mặt sau. Tiếp theo là iPhone 12 Plus (749 USD)&nbsp;sử dụng màn hình 6,1 inch, cụm camera kép và được thiết kế để thay thế cho iPhone 11.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hai mẫu smartphone còn lại thuộc dòng Pro. iPhone 12 Pro có&nbsp;màn hình OLED 6,1 inch như iPhone 12 Plus nhưng điểm khác biệt khiến thiết bị có giá 999 USD nằm ở cụm ba camera phía sau cùng cảm biến LiDar. Còn&nbsp;iPhone 12 Pro Max tích hợp màn hình 6,7 inch, có&nbsp;hệ thống camera tương tự 12 Pro, nhưng kích cỡ lớn hơn khiến giá tăng lên 1.099 USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cùng với iPhone SE, 2020 dự kiến là năm Apple công bố nhiều&nbsp;iPhone nhất với sáu phiên bản. iPhone 12 được cho là sẽ&nbsp;trình làng ngày 25/9, tuy nhiên, thời gian các sản phẩm có mặt trên thị trường còn tuỳ thuộc vào diễn biến của Covid-19.&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Châu An</strong></span></p>', 'YeHk_1.jpg', 1, 0, 53, '2020-06-28 14:44:06', '2020-06-28 14:44:06'),
(69, 'Ý tưởng \'hồi sinh\' Nokia 7610 với 5 camera', 'y-tuong-hoi-sinh-nokia-7610-voi-5-camera', 'Nokia 7610 nếu ra đời năm 2020 sẽ có màn hình OLED 6,9 inch với tần số quét 244Hz cùng cụm 5 camera ở mặt sau.', '<p>Những năm gần đây, HMD đã thiết kế lại và bổ sung các tính năng mới cho một số mẫu điện thoại vang bóng một thời của Nokia để bán ra thị trường như&nbsp;Nokia 5310 XpressMusic, Nokia 8110...&nbsp;Nokia 7610, ra đời năm 2004, cũng là một trong những smartphone&nbsp;rất được ưa chuộng của hãng Phần Lan. Do đó, nhiều người kỳ vọng HMD cũng sẽ sớm tung ra phiên bản làm lại của sản phẩm này.</p>\r\n\r\n<p>Theo hình dung của&nbsp;<em>Peacock Tech,&nbsp;</em>Nokia 7610 của năm 2020 sẽ vẫn giữ nguyên thiết kế bo cong hai góc của bản cũ, nhưng sử dụng khung kim loại và màn hình lớn hơn, lên tới 6,9 inch. Máy hỗ trợ kết nối 5G và bút cảm ứng.</p>\r\n\r\n<p>Mặt sau là cụm 5 camera với camera chính 108 megapixel. Camera selfie phía trước có độ phân giải 44 megapixel, nằm ở phía trên cùng bên trái giống Xiaomi Mi Mix. Máy chạy Android 11, tích hợp pin 6.000 mAH, hỗ trợ sạc nhanh 55W.</p>\r\n\r\n<iFrame src=\"https://v.vnecdn.net/sohoa/video/web/mp4/2020/06/27/y-tuong-hoi-sinh-nokia-7610-voi-5-camera-1593224808.mp4\" width=\"680\" height=\"480\" allowfullscreen></iFrame>\r\n\r\n<p style=\"text-align: right;\"><strong>Minh Minh</strong><span style=\"background-color:rgb(252, 250, 246); color:rgb(34, 34, 34); font-family:arial; font-size:18px\">&nbsp;</span></p>', '15Zo_1.jpg', 1, 0, 53, '2020-06-28 14:46:16', '2020-06-28 14:46:16'),
(70, 'iOS 14 phát hiện TikTok đọc dữ liệu người dùng', 'ios-14-phat-hien-tiktok-doc-du-lieu-nguoi-dung', 'Sau khi nâng cấp điện thoại lên iOS 14, một người dùng liên tục nhận cảnh báo ứng dụng TikTok đang truy cập vào bộ nhớ tạm của thiết bị.', '<p><span style=\"font-size:18px\"><span style=\"font-family:arial\">Ngày 25/6, thành viên&nbsp;Jeremy Burge chia sẻ lên Twitter đoạn video cho thấy cứ vài giây, iOS 14 lại hiện thông báo&nbsp;TikTok truy cập bộ nhớ tạm (clipboard). Video nhanh chóng thu hút gần 400.000 lượt xem với hàng nghìn bình luận, trong đó nhiều người đánh giá cao Apple khi đưa ra tính năng&nbsp;bảo vệ sự riêng tư của người dùng.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"iOS 14 nhắc người dùng khi ứng dụng đọc bộ nhớ tạm trên thiết bị.\" src=\"https://i1-sohoa.vnecdn.net/2020/06/26/TT-3708-1593160435.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=rawNXg_Ar4GzgEMHi5CVMw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">iOS 14 nhắc người dùng khi ứng dụng đọc bộ nhớ tạm trên thiết bị.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo&nbsp;<em>Telegraph</em>, ngay sau khi bị phát hiện, TikTok đã xóa tính năng này trong bản cập nhật ứng dụng mới trên App Store. Hãng giải thích ứng dụng truy cập bộ nhớ tạm để nhận dạng và ngăn chặn hành vi spam lặp đi lặp lại, chứ không xâm phạm sự riêng tư. Tuy nhiên, hãng chưa trả lời việc đã loại bỏ tính năng này&nbsp;trên thiết bị Android hay chưa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trên smartphone, khi người dùng thực hiện lệnh copy, dữ liệu sẽ được lưu vào bộ nhớ tạm và sẽ bị xóa khi người dùng dán dữ liệu vào nơi khác. Từ cuối tháng 3, các chuyên gia bảo mật phát hiện rằng&nbsp;trên cả iPhone và thiết bị Android, một số ứng dụng âm thầm đọc dữ liệu clipboard mà chưa được sự cho phép của người dùng. Những ứng dụng này có thể kể đến TikTok, Weibo, Accuweather, PUBG Mobile...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các ứng dụng chỉ đọc nội dung chứ không thu thập dữ liệu, nhưng các nhà phát triển vẫn lo ngại trong tương lai, hành vi này có thể bị khai thác để đánh cắp thông tin nhạy cảm, bởi người dùng vẫn thường copy mật khẩu, địa chỉ email... từ tài khoản này sang tài khoản khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">TikTok, cũng như một số ứng dụng khác, khẳng định với&nbsp;<em>Telegraph&nbsp;</em>từ tháng 3 rằng họ sẽ loại bỏ tính năng này trong vài&nbsp;tuần.&nbsp;Tuy nhiên, việc Apple bổ sung cảnh&nbsp;báo tới người dùng khi ứng dụng đọc clipboard&nbsp;trên iOS 14 đã cho thấy TikTok vẫn tiếp tục.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Apple cũng cho biết sẽ điều chỉnh phần thông báo chi tiết&nbsp;hơn trong bản điều chỉnh&nbsp;iOS 14 tới, như nói rõ khi nào ứng dụng đọc bộ nhớ tạm, hay sử dụng thông tin đó cho mục đích khác.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Minh Minh</strong>&nbsp;</span></p>', 'VCz6_1.jpg', 1, 0, 53, '2020-06-28 14:48:53', '2020-06-28 14:48:53'),
(71, 'Hiểu lầm thường gặp khi sử dụng điều hòa', 'hieu-lam-thuong-gap-khi-su-dung-dieu-hoa', 'Một số người thường hạ nhiệt độ thấp để nhanh mát phòng, đến khi phòng mát thì tắt điều hòa đi, lúc sau bật tiếp... - cách này chưa chính xác.', '<p><span style=\"font-size:18px\"><strong>Để nhiệt độ thấp nhanh mát</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều người sử dụng khi mới mở điều hòa thường&nbsp;cài đặt mức nhiệt thấp nhất có thể (khoảng 16 độ) vì cho rằng như vậy sẽ nhanh mát hơn. Tuy nhiên, thực tế việc để 16 hay 24 độ ban đầu thì tốc độ làm lạnh là như nhau.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Để nhanh chóng làm mát, người dùng có thể sử dụng chế độ làm mát nhanh (tên gọi Power Full hoặc Hi Power) trên một số dòng điều hòa. Nếu không được hỗ trợ, cách làm tốt nhất là để tốc độ quạt gió lên tối đa, tăng khả năng trao đổi nhiệt với giàn lạnh, giúp phòng nhanh mát hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Bật tắt điều hòa liên tục để tiết kiệm điện</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc tắt điều hòa sau khi phòng đã đủ mát và bật lại để tiết kiệm điện cũng là sai lầm phổ biến của nhiều người. Thực tế, khi khởi động lại và làm lạnh đến nhiệt độ nhất định, điều hòa tốn điện gấp 3 lần so với việc duy trì ở nhiệt độ đó. Ngoài ra, dòng khởi động lớn cũng khiến hệ thống điện gặp nhiều rủi ro hơn nếu điều hòa bị bật/tắt lại liên tục.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, nếu rời phòng trong thời gian dài, người dùng vẫn nên&nbsp;tắt hẳn điều hòa để tiết kiệm điện, tránh bật liên tục cả ngày khiến máy nhanh hỏng hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Điều hòa Inverter tiết kiệm điện nhưng không quá nhiều như quảng cáo.&nbsp;\" src=\"https://i1-sohoa.vnecdn.net/2020/06/11/a1-1240-1591861267.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=95SOPlmynpeSiNeaIVKrJg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điều hòa Inverter tiết kiệm điện nhưng không quá nhiều như quảng cáo.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Điều hòa Inverter luôn tiết kiệm điện</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Điều hòa có công nghệ biến tần Inverter ngày nay rất phổ biến và được quảng&nbsp;cáo là tiết kiệm điện từ 30 đến 60%. Thực tế, điều hòa Inverter tốt nhưng không quá nhiều như quảng cáo, đặc biệt là điều kiện sử dụng tại Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các dòng sản phẩm dạng này được trang bị thêm một bộ biến tần, điều chỉnh dòng điện, giúp giảm công suất khi nhiệt độ ổn định. Tuy nhiên, thường các sản phẩm có Inverter chỉ tiết kiệm điện khi điều hòa bật liên tục trên 8 tiếng. Ngoài ra, những ngày trời luôn duy trì ở mức nhiệt cao, phòng lắp đặt không có khả năng cách nhiệt tốt, điều hòa luôn phải chạy ở mức 100% công suất, máy Inverter không tiết kiệm điện hơn, thậm chí còn tốn hơn, do cần năng lượng để \"nuôi\" bộ biến tần.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Chế độ Dry vừa làm mát, vừa tiết kiệm điện</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mẹo được chia sẻ nhiều trong vài năm gần đây là chế độ Dry có thể làm mát trong khi tiết kiệm điện hơn đáng kể so với thông thường. Thực tế, chế độ làm khô này giúp giảm độ ẩm trong không khí, tăng tốc quá trình bay hơi mồ hôi, tạo cảm giác mát mẻ cho người sử dụng. Tuy nhiên, điều hòa ở chế độ này thường không thể làm giảm quá 2 đến 3 độ C và thường \"vô dụng\" nếu trời quá nắng nóng.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Hoài Anh</strong>&nbsp;</span></p>', 'sgee_1.jpg', 1, 0, 54, '2020-06-28 14:56:10', '2020-06-28 14:56:10'),
(72, 'Sinh viên nên dành bao nhiêu tiền để mua điện thoại mới?', 'sinh-vien-nen-danh-bao-nhieu-tien-de-mua-dien-thoai-moi-', 'Em đang là sinh viên năm thứ 3, em đã dùng Galaxy J7 Prime được 3 năm rồi.', '<p><span style=\"font-size:18px\">Hiện nó bị sọc màn hình, chảy mực ở cạnh màn hình,&nbsp;giắc 3,5mm chập chờn không nghe được bằng tai nghe và bị mờ camera trước. Còn lại nghe gọi và hiệu năng của nó vẫn ổn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Em muốn mua điện thoại mới, tổng số tiền hiện có của em là hơn 15 triệu đồng, nhưng em không muốn dùng hết số tiền đó chỉ để mua điện thoại, nên em dự kiến chỉ chi từ 4 đến&nbsp;gần&nbsp;5 triệu đồng để mua thôi. Nhưng em sợ 4-5 triệu đồng vẫn hơi nhiều quá.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mọi người có kinh nghiệm quản lý tài chính cho em xin ý kiến nên chi bao nhiêu tiền để mua điện thoại mới trong số tiền hiện có? Và mua máy nào là phù hợp nhất? Nhu cầu của em chỉ là chụp ảnh đẹp (có camera góc rộng để chụp công trình) pin khỏe, có jack 3,5mm, các tác vụ dùng hàng ngày mượt và ổn định là được. Em không chơi game nên không cần máy quá mạnh.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Trần Long</strong></span></p>', '4Hpa_1.jpg', 1, 2, 54, '2020-06-28 14:57:52', '2020-06-28 15:01:22'),
(73, 'Mua laptop xịn hay mua cả PC lẫn laptop giá rẻ', 'mua-laptop-xin-hay-mua-ca-pc-lan-laptop-gia-re', 'Em phân vân giữa laptop cấu hình mạnh giá cao hoặc mua một máy để bàn tương đối và laptop giá rẻ.', '<p><span style=\"font-size:18px\">Tình hình là em có để dành và được gia đình cho thêm được tầm gần 20 triệu đồng. Em đang phân vân giữa việc mua một cái laptop xịn hoặc vừa mua một máy bàn cùng với một laptop cỡ 5 triệu đồng. Mục đích em sử dụng là để học và chơi những game offline cần cấu hình mạnh như GTA V.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Em xin cảm ơn.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><em>Độc giả</em><strong>&nbsp;Phát Hồ</strong></span></p>', 'd1B2_1.jpg', 1, 0, 54, '2020-06-28 15:02:30', '2020-06-28 15:19:16');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(74, 'Facebook siết chặt kiểm duyệt sau khi bị tẩy chay', 'facebook-siet-chat-kiem-duyet-sau-khi-bi-tay-chay', 'Mark Zuckerberg hứa sẽ kiểm duyệt nội dung, gắn nhãn bài đăng có nội dung thù địch, phân biệt chủng tộc sau khi bị hàng loạt nhãn hàng tẩy chay.', '<p><span style=\"font-size:18px\">\"Tôi cam kết rằng Facebook vẫn là nơi mọi người có thể sử dụng tiếng nói của mình để thảo luận về các vấn đề quan trọng.&nbsp;Tôi cũng phản đối bất kỳ nội dung nào gây kích động bạo lực, đàn áp. Chúng tôi cam kết xoá những nội dung này, bất kể nó đến từ đâu\",&nbsp;<em>WSJ</em>&nbsp;dẫn lời&nbsp;Mark Zuckerberg.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">CEO của Facebook cũng cam kết thực hiện nhiều biện pháp hơn để bảo vệ người nhập cư, người tị nạn khỏi những bài viết, quảng cáo thể hiện sự khinh miệt hoặc những ngôn từ có thể gây tổn thương.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Làn sóng tẩy chay toàn cầu buộc Facebook phải nhượng bộ, cam kết kiểm duyệt nội dung. Ảnh:&nbsp;Adweek.\" src=\"https://i1-sohoa.vnecdn.net/2020/06/28/Facebook-tay-chay-quang-cao-7848-1593336658.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zrOFeGEf1csdYP9h40D-aw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Làn sóng tẩy chay toàn cầu buộc Facebook phải nhượng bộ, cam kết kiểm duyệt nội dung.&nbsp;Ảnh:&nbsp;<em>Adweek.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những cam kết này được đưa ra sau khi hơn 100 nhà quảng cáo lớn cùng dừng quảng cáo trên mạng xã hội này, buộc Facebook&nbsp;phải nhượng bộ. Ngày 27/6, giá cổ phiếu của Facebook giảm 8,3%.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chiến dịch #StopHateForProfit được khởi xướng từ ngày 17/6, kêu gọi các doanh nghiệp tẩy chay Facebook bằng cách rút quảng cáo trên mạng xã hội này trong tháng 7. Các nhà hoạt động mong muốn&nbsp;Facebook sẽ&nbsp;xây dựng một nền tảng an toàn hơn, đưa ra chính sách quyết liệt hơn để ngăn kẻ xấu phát tán&nbsp;thông tin&nbsp;gây thù ghét, bạo lực, phân biệt chủng tộc... thay vì tiếp tục kiếm tiền từ những nội dung gây phẫn nộ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">#StopHateForProfit cũng thành chủ đề được bàn tán&nbsp;sôi nổi khắp Internet. Trên Weibo, mạng xã hội lớn nhất Trung Quốc, chủ đề này đang trong top thịnh hành với hơn 7 triệu lượt thảo luận. Phần lớn người dùng mạng xã hội ủng hộ chiến dịch của các nhà quảng cáo. \"Facebook nên có trách nhiệm với những nội dung xuất hiện trên nền tảng của mình. Người dùng giúp họ giàu lên và họ phải đặt nặng trách nhiệm với người dùng chứ không thể mãi phát ngôn vô trách nhiệm như cách mà Mark Zuckerberg thể hiện lâu nay\", Jin Shiyu nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bình luận nhận được hàng trăm lượt thích của tài khoảnGuo Wenyu viết: \"Đáng lẽ Facebook phải làm điều này từ nhiều năm trước. Người dùng và&nbsp;các nhà hoạt động đã kêu gọi nền tảng này quản lý chặt hơn những nội dung có thể gây chia rẽ&nbsp;nhưng họ đã làm lơ. Cho đến khi làn sóng tẩy chay quảng cáo nổ ra thì họ mới thay đổi. Rõ ràng họ đặt lợi ích kinh tế quan trọng hơn tiếng nói của cộng đồng\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Người phát ngôn của Facebook nói rằng trong tương lai sẽ có nhiều bài viết vi phạm bị gắn nhãn. Nội dung có tính kích động cũng sẽ bị xoá, dù được viết bởi Tổng thống. \"Chính sách kiểm duyệt nội dung của chúng tôi áp dụng cho mọi người, kể cả các chính trị gia\", ông&nbsp;nói.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thiên An</strong></span></p>', 'rQ0X_1.jpg', 1, 0, 52, '2020-06-28 15:15:19', '2020-06-28 15:15:19'),
(75, 'Vụ án từng gây xáo động cộng đồng người Mỹ gốc Á', 'vu-an-tung-gay-xao-dong-cong-dong-nguoi-my-goc-a', 'Đêm 19/6/1982, thành phố Detroit, một thanh niên Mỹ gốc Hoa đang tiệc tùng chia tay đời độc thân cùng bạn bè thì ẩu đả nổ ra.', '<p><span style=\"font-size:18px\">Vincent Chin, 27 tuổi, làm nghề vẽ phác thảo công nghiệp, chuẩn bị có một đám cưới trong mơ và bắt đầu xây dựng cuộc sống mới. Nhưng đêm đó, anh không thể trở về nhà. Vài ngày sau, 400 khách mời thay vì dự đám cưới lại phải đến dự đám tang của anh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngày 23/6, Chin qua đời tại bệnh viện sau 4 đêm hôn mê vì bị đánh đập dã man bằng gậy bóng chày bên ngoài một cửa hàng McDonald\'s thuộc khu Highland Park, thành phố Detroit, thủ phủ ngành công nghiệp ôtô Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Vincent Chin. Ảnh: Vincent Chin Estate.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/j111-9806-1593076389.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hg4Jg9zf_8MbQOJc1mP9Pg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Vincent Chin. Ảnh:&nbsp;<em>Vincent Chin Estate</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Vì lũ khốn chúng mày mà chúng tao mất việc\", một trong những kẻ tấn công, Ronald Ebens, giám sát viên nhà máy sản xuất ôtô Chrysler, vừa đánh vừa mắng mỏ Chin tại bãi đỗ xe của câu lạc bộ thoát y Fancy Pants. Ebens và con trai riêng của vợ, Michael Nitz, người vừa bị sa thải, nghĩ Chin đến từ Nhật Bản.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 1974, giá dầu đã tăng gấp 4 lần vài tháng sau một quyết định từ Tổ chức các Nước xuất khẩu Dầu mỏ (OPEC), áp lệnh cấm vận đối với dầu mỏ Mỹ. Đây là động thái đáp trả quyết định của chính quyền Mỹ, tái tiếp tế cho quân đội Israel trong cuộc chiến tranh Arab - Israel. Kết quả là ngành công nghiệp xe hơi Mỹ rơi vào khủng hoảng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Doanh số bán xe hơi tiết kiệm nhiên liệu của Nhật Bản tăng vọt, trong khi doanh số xe hơi Mỹ tuột dốc không phanh. Các nhà sản xuất ôtô Mỹ bắt đầu sa thải công nhân, khiến tâm lý giận dữ nhanh chóng bùng lên trong xã hội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Bất kỳ ai lái xe Nhật đều có nguy cơ bị bắn khi đang đi trên đường, bất kể bạn da trắng hay da màu\", nhà hoạt động Helen Zia cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Phương Đông trỗi dậy và phương Tây suy tàn đã trở thành một chủ đề kinh điển\", Frank Wu, hiệu trưởng Trường Queens thuộc Đại học City New York, cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù cuộc tấn công đầu tiên nhằm vào Chin kết thúc bên ngoài câu lạc bộ thoát y Fancy Pants, những kẻ gây gổ sau đó đuổi theo anh tới nhà hàng McDonald\'s. Chúng thậm chí còn trả 20 USD cho một người xa lạ để giúp tìm Chin.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước một đám đông người qua đường, Nitz ghì Chin xuống đất. Ebens dùng gậy bóng chày đập lia lịa vào thân thể và đầu Chin đến khi hộp sọ của anh nứt vỡ. Nhân chứng cho biết trước khi rơi vào hôn mê, Chin đã nói \"thật không công bằng\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hai năm trước vụ sát hại Chin, Zia, hiện 68 tuổi, cũng bị sa thải khỏi ngành công nghiệp ôtô và theo đuổi sự nghiệp viết lách với tư cách một phóng viên tự do cho tạp chí Detroit City rồi sau này là báo Detroit Metro Times.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi đọc về cái chết của Chin, bà bắt đầu đào sâu tìm hiểu câu chuyện. \"Với tôi, câu hỏi lớn đặt ra là: Vì sao một người Mỹ gốc Hoa lại bị sát hại giữa lúc làn sóng căm ghét nhằm vào người Nhật Bản đang dâng cao\", bà nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Lily Chin, mẹ của Vincent Chin, phát biểu tại một cuộc họp mặt đòi công lý cho con trai. Ảnh: Courtesy Renee Tajima-Pena.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/j333-5376-1593076389.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BvIE2y8nfM0xQOO2aWtt_g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lily Chin, mẹ của Vincent Chin, phát biểu tại một cuộc họp mặt đòi công lý cho con trai. Ảnh:&nbsp;<em>Courtesy Renee Tajima-Pena.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tháng 3/1983, sự việc của Chin gây chú ý trở lại khi hai thủ phạm tấn công Chin nhận tội ngộ sát. Cả hai đều không phải đi tù mà chỉ bị phạt 3.000 USD và phải trả án phí 780 USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chin từng làm thêm với công việc bồi bàn tại nhà hàng Trung Quốc Golden Star ở Ferndale, Detroit. Sau khi bản án được tuyên, Lily Chin, mẹ anh, gặp luật sư, đại diện cộng đồng người Hoa trong thành phố và một người Mỹ gốc Nhật tại nơi làm việc cũ của con trai mình để nói về thảm kịch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Lily vô cùng đau đớn\", Zia kể.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cái chết của Chin trở thành động lực khiến cộng đồng người Mỹ gốc châu Á trở nên đoàn kết. \"Vụ Vincent Chin đã khiến các bồi bàn, đầu bếp, công nhân giặt là, công nhân cổ cồn xanh, nhà khoa học, kỹ sư và cả người Mỹ gốc Phi cùng đoàn kết đấu tranh cho công lý\", bà nói thêm. \"Đây là lần đầu tiên người Mỹ gốc Á kề vai với nhau, tự nhận mình là cộng đồng người Mỹ gốc Á, đấu tranh cho quyền lợi dân sự. Trước đây, phong trào chỉ giới hạn trong giới sinh viên và nhà hoạt động\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vicky Wong, hôn thê của Chin, từ chối xuất hiện trước công chúng và không lên tiếng. Tuy nhiên, mẹ anh, Lily Chin tham dự tất cả các cuộc họp của cộng đồng. Bà khóc lóc sụt sùi nhưng luôn lắng nghe, theo lời Zia.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Rồi bà ấy ngừng khóc và nói \'Chúng ta phải chứng minh rằng đây không phải công lý kiểu Mỹ. Chúng ta phải cho họ thấy rằng không một bà mẹ nào đáng phải trải qua những chuyện như vậy\'\", Zia cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Renee Tajima-Pena, 61 tuổi, nhà làm phim kiêm giáo sư nghiên cứu về người Mỹ gốc Á tại Đại học California ở Los Angeles, năm 1983 bắt đầu làm phim tài liệu về vụ án Vincent Chin. Cùng lúc, Zia và các luật sư đại diện gia đình Chin thành lập nhóm dân quyền mang tên Công dân Mỹ vì Công lý, đặt trụ sở ở Detroit. Các luật sư cho rằng luật dân quyền không được xây dựng để bảo vệ người Mỹ gốc Á.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi nhóm này tổ chức các cuộc biểu tình và liên tục gửi đơn lên tòa án liên bang, chính quyền liên bang tiến hành khởi tố vụ án, Zia cho biết. Năm 1987, gia đình Chin chiến thắng một vụ kiện dân sự chống lại những kẻ giết anh. Ebens và Nitz bị yêu cầu bồi thường cho Lily Chin lần lượt 1,5 triệu USD và 50.000 USD vì tổn thất tài chính mà bà phải chịu sau cái chết của con trai.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Kết quả đó đem lại lợi ích cho tất cả người dân Mỹ\", Zia nói. \"Phán quyết từ vụ Vincent Chin có nghĩa là người Mỹ gốc Á giờ đây sẽ được bảo vệ, người nhập cư sẽ được bảo vệ\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Một cuộc biểu tình đòi công lý cho Vincent Chin ở Detroit năm 1983. Ảnh: Lily and Vincent Chin Estate.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/25/j444-8995-1593076389.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZcLj8o73yLRWB9IH683QeA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Một cuộc biểu tình đòi công lý cho Vincent Chin ở Detroit năm 1983. Ảnh:&nbsp;<em>Lily and Vincent Chin Estate.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 1988, bộ phim tài liệu mang tên \"Who Killed Vincent Chin?\" của Tajima-Pena được công chiếu tại một liên hoan phim ở New York. Nó sau đó được đề cử giải Oscar cho hạng mục phim tài liệu hay nhất.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tajima-Pena đánh giá vụ sát hại Chin \"thực sự đã củng cố phong trào đấu tranh cho người châu Á\" tại Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nitz, người ghì Chin xuống đất, đã bồi thường đủ số tiền 50.000 USD, nhưng số nợ của Ebens đã tăng lên 8 triệu USD do tiền lãi tích lũy. Dù không thể làm được gì nhiều từ góc độ pháp lý, Zia cho hay vụ án vẫn được làm mới lại mỗi năm bởi nhóm nhân quyền Công dân Mỹ vì Công lý.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Bài học rút ra là chúng ta phải đoàn kết\", Zia nói. \"Trong vụ Vincent Chin, chúng ta đã làm được điều đó. Nó không thể đưa Vincent trở lại cuộc sống nhưng công lý được hưởng lợi\".</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Vũ Hoàng&nbsp;</strong><span style=\"font-family:arial\">(Theo&nbsp;</span><em>SCMP</em><span style=\"font-family:arial\">)</span></span></p>', 'KoMz_1.jpg', 1, 8, 5, '2020-06-28 15:57:59', '2020-06-28 15:57:59'),
(76, 'Bài học chống Covid-19 từ ngành phim khiêu dâm Mỹ', 'bai-hoc-chong-covid-19-tu-nganh-phim-khieu-dam-my', 'Khi các công ty Mỹ tìm cách bảo vệ nhân viên thời Covid-19, giới chuyên gia gợi ý mô hình ít ai nghĩ đến: ngành công nghiệp phim khiêu dâm.', '<p><span style=\"font-size:18px\">Những người hoạt động trong lĩnh vực đặc biệt này từng vượt qua đại dịch HIV vào cuối những năm 1990, suýt khiến ngành công nghiệp trị giá hàng tỷ USD phải đóng cửa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng ta thực sự có thể học được rất nhiều điều về quy tắc phòng dịch từ các nhà sản xuất phim khiêu dâm\", Perry N. Halkitis, trưởng khoa y tế công cộng tại Đại học Rutgers, nói. \"Hãy nhìn lại khủng hoảng HIV/AIDS, khi ngành công nghiệp phim khiêu dâm đã phải học cách giữ an toàn cho các diễn viên\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Sao khiêu dâm Maitland Ward. Ảnh: NYTimes.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/26/00well-ward-jumbo-v2-3096-1593168743.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pWiv1Z7cb5O18Ld600FpPA\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sao khiêu dâm Maitland Ward. Ảnh:&nbsp;<em>NYTimes</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngành công nghiệp phim khiêu dâm sử dụng một chương trình toàn quốc có tên PASS - Cơ chế Sàng lọc Diễn viên, yêu cầu diễn viên xét nghiệm HIV và các bệnh lây truyền qua đường tình dục khác 14 ngày một lần. Nếu dương tính, người đó sẽ được điều trị và các diễn viên đóng cùng sẽ được truy vết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng ta phải vượt qua ái ngại rằng biện pháp&nbsp;này đến từ ngành phim khiêu dâm\", Ashish Jha, giám đốc Viện Y tế Toàn cầu Harvard nói. \"Khi chúng ta đang cố gắng vực dậy nền kinh tế, những thông tin đó giúp chúng ta thêm tự tin khi tham gia vào các hoạt động thông thường như đến nhà hàng, lên máy bay hoặc đi làm trở lại\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngành công nghiệp phim khiêu dâm, phần lớn đã ngừng hoạt động kể từ ngày 15/3, tuần trước công bố hướng dẫn về cách quay trở lại làm việc. Mike Stabile, giám đốc truyền thông của Liên minh Tự do Ngôn luận, hiệp hội thương mại của ngành này, đã họp với những người trong ngành hai lần một tuần để thảo luận về cách làm việc khi họ được bật đèn xanh hoạt động trở lại. Họ thường xuyên tham khảo ý kiến các chuyên gia y tế.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi xem xét các biện pháp xét nghiệm nCoV, kiểm tra thân nhiệt, đeo khẩu trang trên phim trường khi không quay phim hay cho các cặp diễn viên cố định đóng phim cùng nhau\", ông nói. Hiệp hội đã lập trang web về nguồn lực chống Covid-19 cho ngành công nghiệp phim người lớn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Để phòng chống HIV, chúng tôi sử dụng xét nghiệm PCR-RNA có độ nhạy cao, có thể phát hiện virus trong vòng 7-10 ngày sau khi nhiễm\", ông cho biết. Việc xét nghiệm thường xuyên và các biện pháp kiểm soát khác được các diễn viên áp dụng như không quan hệ tình dục ngoài nhóm diễn viên đã xét nghiệm hoặc sử dụng bao cao su giúp giảm thiểu rủi ro HIV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng Covid-19 lây qua đường giọt bắn khi ho hoặc thậm chí nói chuyện chứ không phải quan hệ tình dục. \"Nguy cơ phơi nhiễm lớn hơn rất nhiều, từ những người bạn làm việc cùng trên phim trường cho đến những người tiếp xúc ở các nơi khác. Nhưng mọi người sẽ đều vui vẻ tuân thủ quy định xét nghiệm vì không ai muốn làm việc ở phim trường có nguy cơ lây&nbsp;nhiễm\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nếu phát hiện ca nhiễm, đội ngũ sản xuất phim đó phải dừng hoàn toàn. Tất cả mọi người trên trường quay, bất kể dương tính hay âm tính với virus, sẽ được cho nghỉ việc. Hầu hết người làm việc trong ngành công nghiệp phim khiêu dâm là lao động độc lập. Nếu họ không làm việc, họ sẽ không được trả lương, vì vậy, họ phải có ý thức tuân thủ quy định để được duy trì việc làm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 1998, khi dịch HIV bùng phát trong ngành công nghiệp phim người lớn, nam diễn viên Marc Wallice nhiễm virus nhưng làm giả kết quả âm tính và lây bệnh cho một số nữ diễn viên. Kết quả xét nghiệm HIV khi đó được in ra trên giấy và có thể dễ dàng làm giả.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sharon Mitchell, cựu nữ diễn viên khiêu dâm có bằng tiến sĩ về bản năng sinh dục, đã thành lập tổ chức Chăm sóc Y tế cho Ngành công nghiệp Phim người lớn vào năm đó, chính thức hóa các quy định cuối cùng trở thành PASS.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi chỉ làm việc với đội ngũ nào áp dụng PASS\", sao khiêu dâm Maitland Ward nói. \"Những người làm việc chuyên nghiệp tuân thủ hướng dẫn rất nghiêm ngặt. Khi chúng tôi hoạt động trở lại, ngoài xét nghiệm nCoV, các biện pháp phòng ngừa bổ sung sẽ được áp dụng trên trường quay, như kiểm tra thân nhiệt và giãn cách xã hội giữa các nhân viên\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Sàng lọc y tế là điều bình thường với chúng tôi. Tôi nghĩ rằng ngành công nghiệp phim khiêu dâm có tâm thế chống dịch tốt hơn nhiều ngành làm phim chính thống hay các ngành khác\", Ward cho biết. \"Thành thật mà nói, tôi nghĩ rằng ngành làm phim chính thống sẽ phải nhìn vào cách chúng tôi xử lý vì chúng tôi là hình mẫu về đảm bảo an toàn và y tế tại các phim trường\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Jha đồng ý với ý kiến này. Ông muốn triển khai cơ sở dữ liệu giống như chương trình của PASS, nơi mọi người có thể báo cáo họ đã xét nghiệm nCoV hay chưa. \"Nó nên được điều hành bởi một doanh nghiệp phi lợi nhuận hoặc tư nhân, không phải chính phủ\", ông nói. \"Tôi không phản đối chính phủ làm điều đó. Nhưng nhiều người sẽ không thấy thoải mái nếu chính phủ quản lý&nbsp;cơ sở dữ liệu\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong ngành công nghiệp phim người lớn, người có kết quả âm tính sẽ có dấu tick xanh lá cây hiện bên cạnh tên. Nếu họ dương tính hoặc không làm xét nghiệm, họ sẽ có dấu X.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các nhà&nbsp;sản xuất dựa vào ký hiệu này để tuyển diễn viên. Theo Stabile, \"mọi người đều muốn làm việc trong một không gian an toàn, vì vậy họ làm xét nghiệm\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Jha hiểu rằng nếu mô hình được sử dụng cho Covid-19, không phải ai cũng muốn xét nghiệm và có thể có những trường hợp âm tính giả vì Covid-19 có thời gian ủ bệnh 5-7 ngày. Tuy nhiên, ông tin rằng hầu hết mọi người sẽ chịu hợp tác vì ai cũng muốn biết liệu nơi làm việc, nhà hàng hoặc máy bay họ đi có an toàn hay không.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nếu Delta hoặc American Airlines nói \'bạn phải làm xét nghiệm để cho chúng tôi thấy bạn không nhiễm nCoV\', bạn sẽ cảm thấy an toàn hơn khi bay trên hãng hàng không đó\", Jha nói. Các hãng hàng không có thể mua hàng nghìn kit xét nghiệm và tính phí khách hàng 5 hoặc 10 USD. Việc biết tất cả mọi người trên máy bay, mọi người trong nhà hàng và tất cả đồng nghiệp của bạn đều không nhiễm virus giúp tăng thêm yên tâm. Ông dẫn chứng công ty cho thuê máy bay Set Jet và Emirates Airline đã thử nghiệm&nbsp;xét nghiệm nhanh cho kết quả trong 10 phút.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Robert Gallo, một trong những người phát hiện ra HIV vào những năm 1980 và là giám đốc Viện Virus trên người tại Đại học Y Maryland, nhấn mạnh rằng \"HIV và nCoV là những virus rất khác nhau và việc so sánh chúng là rất khập khiễng\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên,&nbsp;ông cho rằng \"học theo mô hình của ngành công nghiệp phim khiêu dâm để chống nCoV là điều đáng xem xét\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng ta cần sử dụng bất kỳ công cụ nào sẵn có, ngay cả những công cụ từ ngành công nghiệp phim khiêu dâm, và áp dụng những biện pháp có tác dụng để hạn chế nCoV lây lan\",&nbsp;Halkitis cho biết.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Phương Vũ</strong><span style=\"font-family:arial\">&nbsp;(Theo&nbsp;</span><em>NYTimes</em><span style=\"font-family:arial\">)</span></span></p>', 'kUuz_1.jpg', 1, 1, 5, '2020-06-28 16:02:56', '2020-06-28 16:02:56'),
(77, 'Biden lo giẫm \'vết xe đổ\' của Hillary Clinton', 'biden-lo-giam-vet-xe-do-cua-hillary-clinton', 'MỸ - Kết quả thăm dò cho thấy Biden \"vượt mặt\" Trump, nhưng đảng Dân chủ lo ngại nguy cơ thất bại vì \"mộng du\" trong tự mãn như năm 2016.', '<p><span style=\"font-size:18px\">Một loạt cuộc&nbsp;thăm dò gần đây đều mang tới&nbsp;tín hiệu tích cực cho đảng Dân chủ, khi&nbsp;tỷ lệ ủng hộ của cựu phó tổng thống Joe Biden thậm chí lớn hơn Trump ở mức hai con số. Tại nhiều bang chiến địa, Biden cũng dẫn trước Trump, dù sự cách biệt nhỏ hơn. Biden ngày càng lạc quan hơn về triển vọng đảng Dân chủ của ông sẽ chiếm đa số ghế ở Thượng viện, điều mà trước đây họ thậm chí không nghĩ tới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Từ một người bị Trump bỏ xa về&nbsp;chiến dịch gây quỹ, Biden giờ thường xuyên thu hút hàng triệu đôla.&nbsp;Trong tháng 5, Biden và đảng Dân chủ đã gây quỹ được gần 81 triệu USD, cao hơn Trump 10%, dù chiến dịch tranh cử của Trump đã&nbsp;quyên được tổng số&nbsp;tiền nhiều hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi đó, Trump đang đối mặt với một trong những giai đoạn khủng hoảng chính trị nghiêm trọng nhất mà tổng thống Mỹ&nbsp;từng trải qua. Phản ứng sai lầm trong giai đoạn đầu đại dịch của chính quyền Trump khiến Mỹ trở thành vùng dịch lớn nhất toàn cầu, với hơn 123.000 người chết,&nbsp;đe dọa nỗ lực mở cửa và phục hồi nền kinh tế.&nbsp;Làn sóng biểu tình chống phân biệt chủng tộc và bạo lực cảnh sát sau cái chết của&nbsp;George Floyd, người đàn ông da màu 46 tuổi bị cảnh sát Minneapolis&nbsp;ghì gáy&nbsp;gần 9 phút, khiến Trump lún sâu trong khủng hoảng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tranh cãi xung quanh hồi ký của cựu cố vấn an ninh quốc gia&nbsp;John Bolton và hình ảnh những hàng ghế trống trơn trong buổi vận động tranh cử ở Tulsa, bang Oklahoma tuần trước càng khiến nhiều cố vấn của Trump hoài nghi về triển vọng thắng cử.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Ứng viên đảng Dân chủ Joe Biden phát biểu tại sự kiện vận động tranh cử ở thành phố&nbsp;Darby, bang Pennsylvania, tuần trước. Ảnh: AP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/joe-biden-2-8717-1592997250.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=H31of6tN4eJxmD-8ASVA_Q\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ứng viên đảng Dân chủ Joe Biden phát biểu tại sự kiện vận động tranh cử ở thành phố&nbsp;Darby, bang Pennsylvania, tuần trước. Ảnh:&nbsp;<em>AP.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù có nhiều lợi thế, đảng Dân chủ cho rằng chính sự tự tin rằng họ đang làm tốt lại tiềm ẩn nhiều mối lo ngại.&nbsp;Họ lo rằng đại dịch và các biện pháp hạn chế khiến việc bầu cử trở nên khó khăn hơn. Một số khác e ngại&nbsp;về một chiến dịch thông tin sai lệch trong thời gian tới và nguy cơ đảng Dân chủ đang đánh giá thấp khả năng của Trump. Nhiều người khác thì cho rằng đảng Dân chủ có thể&nbsp;chưa hoàn toàn hiểu lý do&nbsp;khiến cử tri ủng hộ Trump trong cuộc bầu cử năm 2016.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng hơn hết, điều khiến phe&nbsp;Dân chủ lo sợ nhất chính&nbsp;là thái độ&nbsp;tự mãn về kết quả thăm dò khiến họ&nbsp;\"mộng du\" về triển vọng thắng cử và phải trả giá bằng thất bại, giống&nbsp;4 năm trước.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cảm giác hiện giờ rất giống năm 2016.&nbsp;Việc cho rằng bạn đã nắm chắc phần thắng trong tay không phải chuyện hay. Điều này chính xác&nbsp;đã xảy ra trong cuộc bầu cử trước\",&nbsp;Andrew Werthmann, thành viên hội đồng thành phố&nbsp;Eau Claire, bang Wisconsin, nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhóm vận động tranh cử của Biden tin rằng con đường dẫn đến chiến thắng của họ đi qua các bang vùng công nghiệp Trung Tây, như Wisconsin, Michigan và Pennsylvania, ba bang mà bà Hillary Clinton đã thua Trump năm 2016. Nhưng họ cũng ngày càng lạc quan về chiến thắng ở các bang chưa từng được nghĩ tới như Arizona, Texas và Georgia. Mục tiêu kép mà họ đặt ra là vừa thu hút cử tri da màu ở vùng đô thị, vừa lôi kéo được cử tri đại học ở vùng ngoại ô, nhằm tăng tỷ lệ ủng hộ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Bản đồ vận động tranh cử mở rộng mà tôi hình dung gồm cả Texas, thậm chí là Nam Carolina và Georgia. Chúng tôi sẽ không bỏ qua bất kỳ phiếu bầu nào\", nghị sĩ Cedric L. Richmond, đồng chủ tịch chiến dịch tranh cử của Biden, nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng ta phải chạy đua như thể đang bị bỏ lại phía sau, dù thực tế là chúng ta đang dẫn trước. Đừng quan tâm về kết quả khảo sát dù chúng chỉ ra chúng ta đang nhiều hơn 2, 5 hay thậm chí 10 điểm\", ông nói thêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù chiến dịch tranh cử của Biden có vẻ tốt hơn Clinton, gần 5 tháng trước ngày bầu cử vẫn là khoảng thời gian đầy thách thức với ông, theo&nbsp;Matt Viser, biên tập&nbsp;viên của&nbsp;<em>Washington Post</em>. Thực tế cho thấy Biden đã thất bại trong việc lôi kéo ủng hộ của&nbsp;người Mỹ&nbsp;gốc Latinh và nhiều cử tri trẻ người da màu vẫn còn nhiều&nbsp;hoài nghi về ứng viên Dân chủ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi đó, Trump vẫn duy trì được&nbsp;nhóm cử tri&nbsp;ủng hộ vững chắc. Hai tuần trước, báo cáo về tỷ lệ thất nghiệp cải thiện đã cho thấy tín hiệu tích cực về triển vọng khôi phục nền kinh tế, khía cạnh mà Trump luôn nhận được ủng hộ nhiều hơn Biden.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không ít thành viên đảng Dân chủ ở một số bang chủ chốt tin rằng Trump có khả năng phát triển nhóm cử tri ủng hộ không thể lay chuyển. Theo quan điểm của họ, chìa khóa giúp Biden thắng cử nằm ở việc lôi kéo sự ủng hộ của nhóm cử tri Dân chủ không tham gia bỏ phiếu năm 2016.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chiến dịch tranh cử của Biden đang bắt đầu phối hợp với thành viên đảng Dân chủ ở các bang và kỳ vọng có khoảng 600 nhà tổ chức tranh cử làm việc tại các bang chiến địa vào đầu tháng sau. Chỉ trong tuần qua, chiến dịch của Biden đã tổ chức 39 sự kiện ở Wisconsin, Florida, Arizona và Michigan.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một số thành viên Dân chủ vẫn lo ngại liệu việc chỉ trích phong trào \"Cắt ngân sách cảnh sát\" của Biden có khiến ông mất đi thiện cảm của nhóm&nbsp;người biểu tình ở Mỹ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chiến dịch tranh cử của Biden cũng đối mặt với thách thức về việc làm thế nào để không \"mộng du\" trong các tin tức có lợi. Nhiều thành viên thuộc nhóm tranh cử của Biden thậm chí đã kêu gọi người ủng hộ không để tâm quá nhiều đến các thông tin khả quan về triển vọng thắng cử của phe Dân chủ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Plouffe, người phụ trách&nbsp;chiến dịch tranh cử đầu tiên của tổng thống Barack Obama, cũng cảnh báo rằng đảng Dân chủ nên lường trước khả năng Trump sẽ giành lại sự ủng hộ khi cuộc bầu cử bước vào giai đoạn gay cấn hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi có chút lo lắng rằng mọi người đang quá hào hứng về các kết quả thăm dò. Tất nhiên điều này tốt hơn là bạn không có. Nhưng nếu nhìn vào kết quả cuối cùng trong ngày bầu cử, bạn sẽ thấy tỷ lệ rất sát sao\", ông nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Cựu phó tổng thống Joe Biden (giữa) tại một sự kiện ở thành phố&nbsp;Des Moines, bang Iowa, tháng 8/2019. Ảnh: AP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/24/joe-biden-1-4964-1592997250.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LRgZdUwXcx7yQOuG2vOA8g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cựu phó tổng thống Joe Biden (giữa) tại một sự kiện ở thành phố&nbsp;Des Moines, bang Iowa, tháng 8/2019. Ảnh:&nbsp;<em>AP.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nancy Quarles, ủy viên hạt Oakland, bang Michigan, vẫn nhớ cảm giác bất ngờ khi biết kết quả ủng hộ dành cho Trump ở các hạt quan trọng mà bà không ngờ tới. \"Tôi rất lo lắng về cuộc đua này. Tôi thấy đảng Dân chủ hoàn toàn có cơ hội chiến thắng, nhưng tôi cũng đánh giá thấp Tổng thống Trump. Ông ấy rất quyết tâm chiến thắng\", bà nói.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nỗ lực thu hút cử tri trong chiến dịch tranh cử của Biden ở các bang chủ chút cho thấy họ không muốn đi vào \"vết xe đổ\" của Clinton năm 2016. \"Bà Hillary từng vấp chỉ trích vì không đến bang Wisconsin. Nhưng bạn có thể thấy chiến lược của Biden rất khác biệt&nbsp;khi không bỏ qua bang này\",&nbsp;David Bowen, nhà lập pháp bang Wisconsin ở thành phố&nbsp;Milwaukee, cho hay.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi đó, nghị sĩ Dân chủ Ruben Gallego của Arizona bày tỏ lạc quan rằng Biden sẽ trở thành ứng viên Dân chủ đầu tiên giành chiến thắng ở bang của ông, sau Bill Clinton.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Thời gian đang dần cạn. Cuộc bầu cử sẽ không kết thúc vào tháng 11. Nó sẽ kết thúc ngay đầu tháng 10, khi vòng bỏ phiếu sớm bắt đầu ở các bang dao động\", ông nói. \"Nếu Trump không thay đổi bản thân và chiến lược tranh cử&nbsp;trước đầu tháng 10, ông ấy sẽ gặp nhiều vấn đề nghiêm trọng. Trump từng thắng năm 2016, nhưng có thể đó chỉ là may mắn\".&nbsp;</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Thanh Tâm</strong><span style=\"font-family:arial\">&nbsp;(Theo&nbsp;</span><em>Washington Post</em><span style=\"font-family:arial\">)</span></span></p>', 'c8p3_1.jpg', 1, 1, 5, '2020-06-28 16:06:46', '2020-06-28 16:06:46'),
(78, 'Việt Nam đẩy mạnh nghiên cứu vaccine Covid-19', 'viet-nam-day-manh-nghien-cuu-vaccine-covid-19', 'Việt Nam đã phát triển thành công vaccine dự tuyển, bước đầu cho kết quả tích cực khi thử nghiệm trên động vật.', '<p><span style=\"font-size:18px\">Sáng 30/6, Phó Thủ tướng Vũ Đức Đam đã nghe báo cáo về công tác nghiên cứu, sản xuất sinh phẩm xét nghiệm (kit thử) và vaccine phòng chống Covid-19.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo đó, việc phát triển thành công vaccine dự tuyển nêu trên được cho là cơ sở quan trọng để triển khai các bước tiếp theo để phát triển thành vaccine hoàn chỉnh. Thời gian tới, các dự án liên quan lĩnh vực này sẽ tiếp tục được thúc đẩy tích cực với sự tập trung cao độ về nguồn lực, con người.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài phương án nghiên cứu, sản xuất vaccine theo các quy chuẩn bình thường, Bộ Y tế cũng chuẩn bị \"phương án thời chiến\" để đẩy nhanh các bước trong trường hợp diễn biến dịch bệnh Covid-19 xấu đi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh đó, Bộ Y tế cũng sẽ lên phương án tổ chức lại lực lượng nghiên cứu, phối hợp với doanh nghiệp để khai thác lợi thế về nghiên cứu, sản xuất vaccine của Việt Nam từ trước tới nay; đáp ứng yêu cầu phòng bệnh cho người dân, khi tương lai có thể xảy ra những dịch bệnh khác sau Covid-19.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Nhân viên y tế lấy mẫu xét nghiệm nhanh tại quận Đống Đa, Hà Nội tháng 3/2020. Ảnh:Giang Huy\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/xet-nghiem-okok-7507-158597557-8082-9613-1593507193.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=F5gDTLJvpsUx1vn2MEEhew\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhân viên y tế lấy mẫu xét nghiệm nhanh tại quận Đống Đa, Hà Nội tháng 3/2020. Ảnh:<em>Giang Huy</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại cuộc họp, lãnh đạo Bộ Y tế cho biết đến nay Việt Nam đã làm chủ hoàn toàn công nghệ sản xuất kit thử để xét nghiệm kháng thể (xét nghiệm nhanh); xét nghiệm kháng nguyên (xét nghiệm PCR) có độ chính xác cao.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việt Nam đã sản xuất được một loại kit xét nghiệm nhanh không cần dùng máy; một loại sử dụng hệ thống máy xét nghiệm ELISA đã được trang bị từ tuyến Trung ương đến tận y tế tuyến huyện. Tương tự, Việt Nam cũng có 2 loại kit xét nghiệm kháng nguyên (PCR).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thời gian tới, Bộ Y tế sẽ xây dựng kế hoạch điều chỉnh chiến lược xét nghiệm, phương án đặt mua các loại kit thử, hướng dẫn tập huấn, sử dụng các loại kit thử khi nào, ở đâu... Mục tiêu của việc này là vừa bảo đảm phát hiện nhanh, chính xác các ca nhiễm bệnh, vừa đảm bảo sàng lọc trong cộng đồng. Từ đó giúp kiểm soát dịch bệnh nhanh, giảm tải áp lực cơ sở y tế và tập trung nguồn lực vào điều trị những ca thực sự dương tính nCoV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hồi đầu tháng 3/2020, Học viện Quân y và Công ty Cổ phần công nghệ Việt Á đã nghiên cứu và phát triển thành công hai loại&nbsp;kit chẩn đoán nCoV&nbsp;bằng kỹ thuật sinh học phân tử (RT-PCR và realtime RT-PCR), được Bộ Y tế cấp phép đưa vào sản xuất đại trà. Sau đó, hai bộ kit này được Tổ chức Y tế Thế giới (WHO) công nhận và cấp mã số danh sách sử dụng khẩn cấp (EUL).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tháng 4/2020, Viện Vệ sinh Dịch tễ Trung ương đã phối hợp với một trường đại học của Nhật Bản, nghiên cứu và phát triển thành công sinh phẩm xét nghiệm kháng thể có độ nhạy đạt 95%; giá khoảng 5 USD/kit, rẻ hơn so với nhập ngoại.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Viết Tuân</strong></span></p>', 'lnm9_1.jpg', 1, 0, 32, '2020-06-30 11:59:01', '2020-06-30 11:59:01'),
(79, 'TP HCM cần hơn 900.000 tỷ đồng cho giao thông', 'tp-hcm-can-hon-900-000-ty-dong-cho-giao-thong', 'Sở Giao thông Vận tải TP HCM tính toán giai đoạn 2021-2030, thành phố cần hơn 904.000 tỷ đồng để đầu tư hạ tầng giao thông hiện đại, đồng bộ.', '<p><span style=\"font-size:18px\">Dự thảo<em>&nbsp;Đề án phát triển kết cấu hạ tầng giao thông ở TP HCM giai đoạn 2021-2030</em>&nbsp;được Sở Giao thông Vận tải trình UBND thành phố chiều 30/6 đề ra các giải pháp mang tính đột phá nhằm phát triển giao thông, kéo giảm ùn tắc và tai nạn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong đó, Sở Giao thông Vận tải xác định nhu cầu vốn để đầu tư cho hạ tầng giao thông thành phố trong 10 năm tới là 904.293 tỷ đồng, gồm hơn 438.000 tỷ từ vốn ngân sách, còn lại các nguồn vốn khác (Trung ương, xã hội hóa, ODA...).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo đề án, trong 5 năm tới thành phố cần thực hiện các dự án đường bộ gồm 3 tuyến cao tốc:&nbsp;TP HCM - Mộc Bài&nbsp;(xây dựng mới), TP HCM - Long Thành - Dầu Giây và TP HCM - Trung Lương (mở rộng); tập trung các tuyến quốc lộ: 1, 13, 22, 50; tập trung các dự án xây dựng các tuyến&nbsp;vành đai 2&nbsp;và&nbsp;vành đai 3.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Dự án nút giao thông Mỹ Thủy (quận 2) sẽ được ưu tiên nguồn vốn để giải tỏa ùn tắc ở cảng Cát Lái và kết nối giao thông khu vực phía Đông thành phố. Ảnh:Quỳnh Trần.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/nut-giao-thong-my-thuy-7287-15-7371-2689-1593515356.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DiOTjGDo9tJzEYddaozDQw\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dự án nút giao thông Mỹ Thủy (quận 2) sẽ được ưu tiên nguồn vốn để giải tỏa ùn tắc ở cảng Cát Lái và kết nối giao thông khu vực phía Đông thành phố. Ảnh:<em>Quỳnh Trần.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thành phố cũng đầu tư 7 đường trục chính đô thị gồm đoạn ngã tư Bảy Hiền - Âu Cơ (Âu Cơ giao với Thoại Ngọc Hầu); đoạn từ nút giao Thoại Ngọc Hầu - Vành đai trong đến nút giao Vành đai trong - đường số 29; đoạn từ kinh Dương Vương (khu vực đường Đỗ Năng Tế) đến Nguyễn Văn Linh; đường song song quốc lộ 50; đường nối Trần Quốc Hoàn - Cộng Hòa;&nbsp;cầu đường Nguyễn Khoái&nbsp;và cầu đường Bình Tiên...</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sở Giao thông Vận tải cũng đề xuất đầu tư 6 nút giao thông trong giai đoạn 2020-2025 gồm: An Phú, Mỹ Thủy, Gò Dưa, Linh Xuân, ngã tư Bốn xã và nút giao ngã sáu Công trường Dân Chủ. Đến năm 2025-2030 tập trung đầu tư thêm nút giao quốc lộ 1A - đường Vườn Lài.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đồng thời, 4 cây cầu có quy mô lớn vượt sông cũng được xây dựng, gồm&nbsp;cầu Thủ Thiêm 3&nbsp;(nối quận 2 và 4) và&nbsp;Thủ Thiêm 4&nbsp;(nối quận 2 và 7), cầu Cát Lái (nối TP HCM với Đồng Nai) và cầu Cần Giờ (nối huyện Nhà Bè với Cần Giờ).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giai đoạn này, thành phố cần tập trung đầu tư hoàn thành 3 tuyến metro:&nbsp;Số 1&nbsp;(Bến Thành - Suối Tiên) dài 19,7 km (đang thi công);&nbsp;Số 2&nbsp;(Bến Thành - Tham Lương) dài 11,3 km và Số 5 (Ngã tư Bảy Hiền - cầu Sài Gòn) dài 8,9 km.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đến năm 2025-2030 thêm 4 tuyến metro được đầu tư: Số 3 (Bến Thành - Bến xe miền Tây) dài 9,7 km; Số 2 (đoạn Bến Thành - Thủ Thiêm) và đoạn (Tham Lương - bến xe An Sương) dài 9,1 km; Số 4b (Công viên Gia Định - Lăng Cha Cả) dài 3,5 km; Số 5 (Ngã tư Bảy Hiền - Bến xe Cần Giuộc) dài 14,5 km.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cũng trong giai đoạn này, đề án đề xuất đầu tư 5 tuyến đường trên cao gồm: Số 1 dài khoảng 9,5 km, Số 2 dài gần 12 km, Số 3 dài hơn 8 km, Số 4 dài khoảng 7,3 km và Số 5 dài khoảng 34 km từ nút giao Trạm 2 (quận Thủ Đức) đến An Lạc (quận Bình Tân).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiện, tổng chiều dài các tuyến đường và cầu của TP HCM là 4.392 km, mật độ 2,1 km mỗi km2 (theo tiêu chuẩn phải từ 10-13,3 km mỗi km2) và chỉ có 1.800 km có lòng đường rộng hơn 7 m.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tính đến tháng 3 năm nay thành phố đang quản lý hơn 8,1 triệu phương tiện (hơn 763.000 ôtô, còn lại là xe máy). Hệ thống&nbsp;hạ tầng giao thông&nbsp;thành phố được cho là vừa thiếu vừa yếu so với vai trò đầu tàu kinh tế của cả nước.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Hữu Công</strong></span></p>', 'NcmA_1.jpg', 1, 3, 1, '2020-06-30 12:02:23', '2020-06-30 12:02:23'),
(80, 'Cựu cảnh sát từng gieo kinh hoàng ở California', 'cuu-canh-sat-tung-gieo-kinh-hoang-o-california', 'MỸ - Từ năm 1974 đến 1986, một người đàn ông bí ẩn đã thực hiện hơn 100 vụ trộm, hơn 50 vụ hiếp dâm và giết ít nhất 13 người ở California.', '<p><span style=\"font-size:18px\">Cư dân ở Visalia, California sống trong nơm nớp lo sợ từ tháng 4/1974 đến tháng 12/1975, khi một tên trộm đột nhập ít nhất 100 ngôi nhà để lục lọi đồ dùng cá nhân của gia chủ. Y rải đồ lót của những người phụ nữ quanh nhà, lấy trộm những món đồ nhỏ làm quà lưu niệm nhưng không động đến những vật có giá trị lớn. Y được đặt biệt danh là \"kẻ lục lọi Visalia\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Sát nhân Joseph James DeAngelo tại tòa ở California ngày 29/6, Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/12405038-3x2-xlarge-1802-1593532587.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bogi9asTBokP0ht_G_RlRw\" style=\"width:616px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Joseph James DeAngelo tại tòa ở California ngày 29/6. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng các vụ đột nhập nhà dần không đủ để thỏa mãn y. Từ năm 1976, kẻ này chuyển đến Sacramento, bắc California, tiến hành một loạt vụ cưỡng hiếp tại phía đông thành phố, khiến y được đặt biệt danh \"Kẻ hiếp dâm ở khu vực phía đông\". Y thường chọn những căn nhà một tầng, gần những nơi dễ trốn thoát như rừng cây hay cánh đồng lớn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi chọn mục tiêu, y thường lẻn vào nhà họ trước, theo dõi hoạt động của các nạn nhân để ghi nhớ thói quen. Y tháo đạn khỏi bất kỳ khẩu súng nào tìm thấy trong nhà, mở khóa cửa sổ và giấu trước dây để trói các nạn nhân sau đó.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ban đầu kẻ hiếp dâm tấn công những phụ nữ độc thân hoặc bà mẹ đơn thân, nhưng sau đó chuyển mục tiêu sang các cặp vợ chồng. Khi các nạn nhân đang ngủ, y bịt mặt, cầm súng, chĩa đèn pin để đánh thức họ dậy. Sau khi trói người đàn ông, y cưỡng hiếp người phụ nữ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đôi lúc y xếp bát đĩa lên lưng người đàn ông và dọa giết hai người nếu làm đĩa rơi trong quá trình y hãm hiếp người phụ nữ. Thỉnh thoảng y còn ung dung ăn và uống bia trong bếp của nạn nhân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đôi khi hàng xóm hoặc cảnh sát bắt gặp tên \"yêu râu xanh\" nhưng y trốn thoát bằng cách trèo qua hàng rào hoặc đạp xe bỏ chạy. Người đàn ông này rõ ràng có thể lực tốt.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đêm 2/2/1978, cặp vợ chồng trẻ Mather và Katie Maggiore ở Sacramento đang dắt chó đi dạo trong khu vực Rancho Cordova, gần nơi từng xảy ra 5 vụ hiếp dâm, thì bị bắn chết. Giới chức nghi ngờ hai người đã vô tình nhìn thấy kẻ hiếp dâm và bị y giết người diệt khẩu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Từ năm 1979, y chuyển địa bàn hoạt động từ bắc California sang nam California nhưng không chỉ dừng lại ở hiếp dâm. Tên tội phạm giết một loạt người bằng cách nã súng hoặc đánh đập nạn nhân bằng gậy. Truyền thông đặt biệt danh cho tên sát nhân là \"Gã lén lút trong đêm\" vì giới chức ban đầu không nhận ra kẻ gây án chính là \"Kẻ hiếp dâm khu vực phía đông\" do địa bàn gây án thay đổi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Với các bằng chứng tại hiện trường và mô tả của nạn nhân, cảnh sát xác định nghi phạm là người đàn ông da trắng cao 1m75 hoặc 1m78, có vóc người vạm vỡ. Các nhà tội phạm học tin rằng y hiểu các phương pháp điều tra của cảnh sát, là một tên trộm lão luyện và ghét phụ nữ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, cảnh sát đã theo đuổi nhiều manh mối sai, dẫn họ vào ngõ cụt. Họ đưa một số người vào danh sách nghi phạm nhưng những người này sau đó được chứng minh vô tội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tên sát nhân còn nhiều lần trêu chọc cảnh sát. Một bài thơ được gửi đến báo Sacramento Bee vào tháng 12/1977, tự xưng là của \"Kẻ hiếp dâm khu vực phía đông\". Ngày 10/12/1977, y gọi cho cảnh sát, thông báo y sẽ phạm tội tại Đại lộ Watt tối hôm đó. Cảnh sát suýt bắt được một người đàn ông chạy trốn bằng xe đạp vào rạng sáng 11/12 nhưng y đã trốn được.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Từ năm 1977 đến 2001, y vài lần gọi điện cho các nhà điều tra để chế giễu. \"Các người sẽ không bao giờ bắt được tôi\", y nói. Y thậm chí còn gọi điện cho các nạn nhân sống sót, chúc họ Giáng sinh vui vẻ, gọi họ là gái điếm và dọa giết họ. Năm 2001, tên sát nhân gọi điện cho một nạn nhân bị y tấn công 24 năm trước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Vào năm này, nhờ tiến bộ công nghệ, các bằng chứng ADN giúp cảnh sát liên kết các vụ án ở nhiều khu vực khác nhau của bang California. Cuối cùng, vài thập kỷ sau khi hung thủ gây án, họ kết luận \"Kẻ hiếp dâm khu vực phía đông\" và \"Gã lén lút trong đêm\" là một.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đầu năm 2013, tác gia chuyên viết về tội phạm Michelle McNamara đặt cho y biệt danh \"Sát nhân bang vàng\". (Bang vàng là biệt danh của bang California vì sự phát triển của bang này liên quan đến việc phát hiện ra vàng năm 1848 và các cánh đồng hoa anh túc vàng nở rộ trên khắp bang vào mỗi mùa xuân).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 2016, FBI công khai một loạt dữ liệu về \"Sát nhân bang vàng\", bao gồm các bản phác thảo và các chi tiết về tội ác của y, treo thưởng 50.000 USD cho người cung cấp thông tin.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nếu ông ta còn sống, kẻ giết người giờ khoảng 60-75 tuổi, được mô tả là một người đàn ông da trắng, cao gần 1m8, tóc vàng hoặc nâu, dáng người lực lưỡng. Ông ta có thể có hứng thú hoặc đã được đào tạo về các kỹ thuật quân sự hay hành pháp, sử dụng súng thành thạo\", thông báo của FBI có đoạn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngày 24/4/2018, Joseph James DeAngelo, sinh năm 1945, bị bắt ở California. Y là cựu binh hải quân Mỹ và từng làm cảnh sát từ năm 1973 đến 1979, chuyên điều tra các vụ trộm. Tháng 7/1979, DeAngelo bị bắt vì trộm đồ ở siêu thị, bị kết án 6 tháng quản chế và bị sa thải vào tháng 10 năm đó.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Quá trình nhận dạng DeAngelo bắt đầu từ khoảng cuối năm 2017, khi giới chức tải hồ sơ ADN của kẻ giết người lên cơ sở dữ liệu GEDmatch. Trang web giúp xác định 10-20 người có chung tổ tiên với tên sát nhân. Các nhà điều tra xây dựng được một sơ đồ phả hệ và xác định DeAngelo là nghi phạm chính.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngày 18/4/2018, giới chức lén thu thập mẫu ADN từ tay nắm cửa xe của DeAngelo và sau đó thu được giấy ăn từ trong thùng rác của ông ta. Cả hai đều khớp với mẫu ADN liên quan đến tên sát nhân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi bị bắt, DeAngelo nói rằng có nhân cách bên trong tên là \"Jerry\" đã thôi thúc ông ta làm điều ác. Theo công tố viên hạt Sacramento Thien Ho, DeAngelo nói một mình trong phòng thẩm vấn rằng: \"Tôi không đủ sức để đẩy anh ta ra. Anh ta ở trong đầu tôi, khiến tôi hành động như vậy, anh ta là một phần của tôi. Tôi không muốn làm những điều đó. Cuối cùng, tôi đã đẩy được Jerry ra và có một cuộc sống hạnh phúc. Nhưng tôi đã gây ra tất cả những điều đó. Tôi đã phá hủy tất cả cuộc sống của họ. Vì vậy, bây giờ tôi phải trả giá\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, giới chức nghi ngờ DeAngelo giả vờ bị rối loạn đa nhân cách. Công tố viên Thien Ho cho biết DeAngelo từng giả điên khi bị bắt vì tội ăn trộm năm 1979 để tránh bị truy tố.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">DeAngelo không thể bị truy tố tội hãm hiếp hoặc trộm cắp, vì thời hiệu đã hết cho những tội danh đó, nhưng ông ta bị truy tố 13 tội danh giết người và 13 tội bắt cóc liên quan đến các vụ hiếp dâm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tháng 4/2019, các công tố viên cho biết họ sẽ đề nghị án tử hình cho DeAngelo. Tháng 3/2020, DeAngelo, 74 tuổi, đưa ra thỏa thuận sẽ nhận tội nếu tránh được án tử hình. Ngày 29/6, ông ta thừa nhận 161 tội ác liên quan đến 48 người, trong đó có giết người và bắt cóc, lĩnh án tù chung thân không ân xá.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">James Alan Fox, nhà tội phạm học tại Đại học Đông Bắc, cho biết hầu hết những kẻ giết người hàng loạt không có một nhân cách khác hoặc tiếng nói nội tâm thôi thúc họ gây án, mặc dù các bộ phim thường miêu tả theo cách đó.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ông bình luận những kẻ giết người hàng loạt trốn tránh được công lý trong nhiều năm thường là kẻ xảo quyệt và tính toán kỹ càng trong khi người mắc bệnh tâm thần nghiêm trọng không có khả năng đó. Những kẻ giết người hàng loạt đổ lỗi cho một nhân cách khác thường là nói dối, ông cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Họ chỉ cố gắng chối tội khi nói rằng họ gây ra tất cả tội lỗi do một giọng nói trong đầu thôi thúc họ. \'Đừng đổ lỗi cho tôi, hãy đổ lỗi cho giọng nói đó\', những tên sát nhân muốn nói vậy\", Fox cho biết.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Phương Vũ</strong>&nbsp;(Theo&nbsp;<em>AP, ATI</em>)</span></p>', 'CMtd_1.jpg', 1, 0, 5, '2020-07-01 03:47:48', '2020-07-01 03:47:48');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(81, 'Mức lương phi công trên thế giới', 'muc-luong-phi-cong-tren-the-gioi', 'Phi công là một trong những ngành có thu nhập tốt nhất thế giới, khiến nhiều người lựa chọn nghề này.', '<p><span style=\"font-size:18px\">Nhiệm vụ của một phi công bắt đầu từ trước khi máy bay cất cánh. Họ phải tiến hành một số quy trình kiểm tra quan trọng trước bất cứ chuyến bay nào, như thời tiết trên hành trình sắp tới, tình trạng phi cơ, nhiên liệu cần thiết cho chuyến đi, trọng lượng hành khách và hành lý. Phi công còn phải nộp kế hoạch bay trước khi máy bay di chuyển.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong chuyến bay, phi công phải giám sát các thiết bị của máy bay và tất cả liên lạc vô tuyến, sử dụng dữ liệu nhận được để đánh giá và phát hiện bất cứ vấn đề nào có khả năng ảnh hưởng đến phi cơ. Thêm vào đó, họ cần duy trì liên lạc với đài kiểm soát không lưu để tiếp nhận và tuân theo những chỉ dẫn về cất hạ cánh an toàn trên đường băng được chỉ định. Phi công cũng giám sát tất cả nhân viên trong khoang máy bay và buồng lái.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khác với hầu hết chuyên ngành khác,<strong>&nbsp;phi công thường không nhận lương theo tháng, mà thường được trả theo giờ</strong>, dựa trên thời gian bay, và theo ngày. Nhằm đảm bảo thu nhập tối thiểu hàng tháng, hầu hết hãng hàng không lớn trên thế giới đều cố gắng sắp xếp sao cho đủ số giờ bay tối thiểu mỗi tháng cho phi công. Bên cạnh khoản lương theo giờ này, phi công còn có thể nhận được tiền trợ cấp ăn ở khi phải xa nhà qua đêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tương tự tất cả ngành nghề khác, phi công càng nhiều kinh nghiệm thì mức lương theo giờ càng cao. Những phi công thu nhập hàng đầu là những cơ trưởng làm việc cho các hãng hàng không quốc tế.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"Các phi công ngồi trong buồng lái một chiếc máy bay của hãng Ethiopian Airlines chuẩn bị khởi hành từ Addis Ababa đến Eritrea hồi tháng 7/2018. Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/29/resized-7a124-2018-07-18t16141-6166-1741-1593432363.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6hlWHpDYGHXz-Kr1k7xGLQ\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Các phi công ngồi trong buồng lái một chiếc máy bay của hãng Ethiopian Airlines chuẩn bị khởi hành từ Addis Ababa đến Eritrea hồi tháng 7/2018. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thông thường, mức lương khởi điểm dành cho phi công mới vào nghề của các hãng hàng không dao động từ 40 đến 70 USD mỗi giờ. Các phi công trung bình mỗi tháng bay 75 giờ. Tính thêm phụ cấp mỗi ngày, một cơ phó mới có thể kiếm gần 7.000 USD mỗi tháng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo báo cáo hồi tháng một của&nbsp;US News,&nbsp;<strong>phi công đứng thứ 17 trong số những nghề thu nhập cao nhất thế giới</strong>, với mức lương trung bình hồi năm 2018 là 115.670 USD mỗi năm. 25% số phi công kiếm nhiều tiền nhất được trả trung bình 197.540 USD, trong khi 25% lương thấp nhất nhận được trung bình 77.910 USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thu nhập của phi công hiện nay bị ảnh hưởng đáng kể bởi Covid-19, đại dịch khiến hàng loạt hãng hàng không phải ngừng bay và nhiều phi công không có việc làm. Tuy nhiên, khi đại dịch qua đi và kinh tế các nước cũng như hoạt động hàng không được khôi phục, lương phi công có thể tiếp tục tăng lên do tình trạng thiếu nhân lực và nhu cầu tuyển phi công đủ kinh nghiệm ngày càng nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trước Covid-19, quốc gia trả lương cao nhất cho phi công là Trung Quốc, theo Naibuzz. Một phi công tại Trung Quốc có thể kiếm 25.000 USD mỗi tháng, tức là khoảng 300.000 USD một năm, chưa kể khoản tiền thưởng cuối năm có thể lên tới 80.000 USD. Tốc độ tăng trưởng kinh tế nhanh chóng của Trung Quốc khiến nhu cầu di chuyển xa ngày càng lớn, dẫn tới thiếu hụt phi công.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một số hãng hàng không Trung Quốc thậm chí tuyên bố nộp thuế cho phi công để thu hút nhân lực. \"Không có đủ phi công trên thế giới để đáp ứng nhu cầu. Đó là lý do mức lương tiếp tục tăng\", Dave Ross, giám đốc điều hành Wasinc International, công ty chuyên tuyển dụng phi công cho các hãng hàng không Trung Quốc, cho biết. Theo ước tính của các nhà phân tích, nước này cần 4.000 – 5.000 phi công mới mỗi năm trong 20 năm tới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cũng theo xếp hạng của Naibuzz, quốc gia trả lương phi công cao thứ hai là Hà Lan. Mức lương khởi điểm tại KLM, hãng hàng không lớn nhất nước này, là hơn 56.000 USD cho cơ phó và khoảng 289.000 USD cho cơ trưởng nhiều kinh nghiệm. Lương cơ bản cho một cơ trưởng là hơn 150.000 USD. Lương trung bình của toàn bộ phi công khoảng 245.000 USD mỗi năm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đứng thứ ba trong bảng xếp hạng này là Pháp. Hãng hàng không quốc gia Air France trả khoảng 235.000 USD mỗi năm cho các phi công giàu kinh nghiệm. Vị trí tiếp theo là Ireland, với Ryanair, hãng hàng không lớn nhất đất nước, trả cho các phi công tại thủ đô Dublin tối đa 225.000 USD mỗi năm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo trang web cung cấp dữ liệu việc làm Salary Expert, mức lương cơ bản trung bình mỗi năm của phi công Pakistan là hơn 14.000 USD. Ngành nghề này tại Pakistan gần đây gây chú ý, sau khi Bộ trưởng Hàng không Ghulam Sarwar Khan tiết lộ hơn 260 phi công Pakistan dùng bằng giả hoặc gian lận thi bằng, khiến nhiều hãng hàng không trên thế giới quyết định đình chỉ các phi công và kỹ thuật viên người Pakistan của họ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên,&nbsp;<strong>có rất nhiều yếu tố khác nhau ảnh hưởng tới mức lương của phi công</strong>, bên cạnh những điều khoản trong hợp đồng. Đầu tiên là loại máy bay mà họ điều khiển. \"Nếu lái một chiếc máy bay kích cỡ lớn hơn, chở nhiều hành khách hơn, bạn sẽ được trả nhiều hơn\", Brian Sumers, chuyên gia hàng không tại công ty truyền thông Skift, cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Để được giao nhiệm vụ điều khiển máy bay kích thước lớn hơn, phi công cần có thâm niên. \"Càng làm việc lâu cho một hãng hàng không, bạn càng có thâm niên. Do đó, hầu hết phi công có xu hướng gắn bó với một hãng hàng không sau khi vào làm việc cho họ\", Sumers giải thích.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một yếu tố khác tác động tới mức lương của phi công là phạm vi hoạt động của hãng hàng không. Những hãng trong phạm vi khu vực thường trả lương thấp hơn so với các hãng lớn thực hiện các chặng bay quốc tế đường dài. Đối với hầu hết phi công, làm việc cho một hãng hàng không khu vực chỉ là bước đệm cho công việc tiếp theo của họ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những phi công sẵn sàng ra nước ngoài làm việc thậm chí có mức thu nhập cao hơn. Một số quốc gia tại Trung Đông và châu Á không đào tạo đủ phi công, buộc phải tuyển nhân lực từ các nước khác. \"Họ tuyển phi công nhằm phục vụ cho những hãng hàng không Trung Đông như Emirates hay Qatar Airways\", Sumers cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hãng hàng không Emirates, trụ sở ở Dubai, hồi năm 2018 đề nghị mức lương mỗi tháng cho các cơ trưởng ít nhất là 16.000 USD, thêm vào đó là khoản trợ cấp nhà ở 4.377 USD mỗi tháng, hoặc được công ty cung cấp chỗ ở. Phi công của hãng này còn được nghỉ tối thiểu 8 ngày mỗi tháng.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Phương Vũ</strong>&nbsp;(Theo&nbsp;<em>AP, ATI</em>)</span></p>\r\n\r\n<p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>\r\n</p>', 'bLZs_1.jpg', 1, 2, 5, '2020-07-01 03:50:29', '2020-07-01 03:50:29'),
(82, 'Lý do Iran phát lệnh truy nã Trump', 'ly-do-iran-phat-lenh-truy-na-trump', 'Quyết định truy nã Trump của Iran được cho là chỉ mang tính biểu tượng, nhưng mục tiêu chính dường như nhằm châm ngòi phản ứng quốc tế với Mỹ.', '<p><span style=\"font-size:18px\">Iran hôm qua phát lệnh&nbsp;truy nã&nbsp;Tổng thống Mỹ Donald Trump cùng 35 người khác mà họ cáo buộc liên quan đến vụ không kích hạ sát tướng Qassem Soleimani, tư lệnh đặc nhiệm Quds thuộc Vệ binh Cách mạng Hồi giáo Iran, vào ngày 3/1 tại Baghdad, Iraq.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ali al-Qasimehr, công tố viên hàng đầu của Tehran, cho biết họ có ý định theo đuổi việc truy tố Trump với cáo buộc giết người và hoạt động khủng bố ngay cả sau khi nhiệm kỳ của ông kết thúc, đồng thời kêu gọi Tổ chức Cảnh sát Hình sự Quốc tế (Interpol) phát lệnh truy nã đỏ với ông chủ Nhà Trắng cùng 35 người trên.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"Tổng thống Mỹ Donald Trump phát biểu tại thành phố Marinette, bang Wisconsin, hôm 25/6. Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/5414f75387605d45882d-jpeg-6051-1593493140.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pZdhRRBhV-iQ27VgD8nJog\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Tổng thống Mỹ Donald Trump phát biểu tại thành phố Marinette, bang Wisconsin, hôm 25/6. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, trong một thông báo gửi đi sau đó, Interpol cho biết theo hiến chương của họ, tổ chức \"nghiêm cấm thực hiện bất cứ hành vi can thiệp nào, hoặc việc liên quan tới người hoạt động chính trị, quân sự, tôn giáo, chủng tộc\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Do đó, theo những quy định trong hiến chương và quy tắc của chúng tôi, trong trường hợp những yêu cầu có tính chất như vậy được gửi tới Tổng thư ký của tổ chức, Interpol sẽ không xem xét chúng\", thông báo có đoạn, nhưng không đề cập cụ thể tới lời kêu gọi của Iran.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Interpol chịu trách nhiệm điều phối các nỗ lực của cảnh sát quốc tế, nhưng không có thẩm quyền bắt hoặc buộc các quốc gia thay mặt chính phủ nước khác bắt ai đó. Lệnh truy nã đỏ của tổ chức không phải lệnh bắt, mà đóng vai trò như một lời đề nghị \"lực lượng thực thi pháp luật toàn cầu xác định vị trí và tạm thời bắt một người đang chờ dẫn độ, ra đầu thú, hoặc hành động pháp lý khác\", Interpol cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo bình luận viên Negar Mortazavi của Independent, lệnh truy nã Trump của Iran được coi là một động thái chính trị khá táo bạo, nhưng không có khả năng gây ra bất cứ hệ quả pháp lý nào với Tổng thống Mỹ. Tuy nhiên, hành động của họ được cho là&nbsp;<strong>nhằm thu hút sự chú ý của dư luận và làm dấy lên câu hỏi về vai trò của các tổ chức quốc tế</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Iran đang sử dụng hệ thống quốc tế như cách Mỹ vẫn làm. Washington rõ ràng phớt lờ những nghĩa vụ và luật pháp quốc tế mỗi khi thấy thích hợp, nhưng lại dùng hệ thống toàn cầu đó để hợp pháp hóa những hành động chống lại \'đối thủ\' của họ\", Assal Rad, chuyên gia cấp cao tại Hội đồng Người Mỹ gốc Iran Quốc gia, tổ chức phi chính phủ ở Washington, nêu ý kiến.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Do đó, mục tiêu thật sự của Iran trong lệnh truy nã này dường như là&nbsp;<strong>thúc đẩy các phản ứng và làm dấy lên làn sóng chỉ trích \"đạo đức giả\" của Mỹ</strong>, ngay cả khi bản thân họ cũng bị hoài nghi, Rad nói thêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sanam Vakil, chuyên gia về Iran tại viện nghiên cứu Chatham House ở Anh, đánh giá việc Iran chọn động thái mang tính biểu tượng này là quyết định không bất ngờ, nói thêm rằng vụ hạ sát Soleimani đã trở thành một trong những bằng chứng giúp Tehran chứng minh Washington \"bất chấp công lý\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Iran vẫn chưa quên mối thù ngày 3/1. Soleimani là người thực sự quan trọng đối với chính quyền và có sức ảnh hưởng rộng lớn trong công chúng. Vì vậy, việc họ sử dụng các cơ quan quốc tế nhằm phản kháng một cách tượng trưng có thể&nbsp;<strong>ẩn chứa thông điệp rằng mọi chuyện vẫn chưa chấm dứt</strong>\", Vakil nhận định.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"Tướng Iran Qassem Soleimani trả lời phỏng vấn tại thủ đô Tehran năm ngoái. Ảnh: AFP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/2020-1-largeimg-515575257-6214-1593493140.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_N4F01Tm3_ItNNq9YWCi5w\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Tướng Iran Qassem Soleimani trả lời phỏng vấn tại thủ đô Tehran năm ngoái. Ảnh:&nbsp;<em>AFP</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc Trump ra lệnh hạ sát Soleimani được cho là bước leo thang nghiêm trọng, thậm chí đưa Washington và Tehran đến bờ bực chiến tranh, và căng thẳng vẫn chưa hạ nhiệt suốt những tháng qua. Phần lớn xung đột hiện nay bắt nguồn từ việc Trump hồi năm 2018 rút khỏi thỏa thuận hạt nhân giữa 6 cường quốc với Iran, đồng thời tăng cường trừng phạt với Tehran, bất chấp sự phản đối của những nước còn lại trong thỏa thuận.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"<strong>Bên trong nội bộ, Iran có lẽ ngày càng chịu nhiều sức ép</strong>&nbsp;trong nhiệm vụ duy trì phản ứng đối với cái chết của Soleimani\", Vakil cho hay, nói thêm rằng dù đã tiến hành những vụ tấn công nhằm vào các căn cứ của Mỹ tại Iraq ngay sau vụ hạ sát, \"Iran vẫn tìm kiếm một số hình thức trả đũa\" khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc phát lệnh truy nã Trump do nhánh tư pháp của Iran, nơi những quan chức bảo thủ kiểm soát, dẫn đầu. Trong khi đó, chính phủ Iran, dưới sự lãnh đạo của Tổng thống ôn hòa Hassan Rouhani, vẫn giữ im lặng. Tình hình nội bộ Iran hiện vẫn chưa được làm rõ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các nhóm hoạt động và chuyên gia luật quốc tế đặt ra câu hỏi liệu vụ hạ sát Soleimani, cùng một số cuộc không kích khác của Mỹ, có hợp pháp hay không. Trump giải thích rằng vụ không kích giết Soleimani nhằm ngăn chặn \"cuộc tấn công tiềm tàng sắp xảy ra\" của Tehran. Ngoại trưởng Mỹ Mike Pompeo cũng cáo buộc tướng Iran đang chuẩn bị một kế hoạch hành động đe dọa tính mạng công dân Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Từ góc độ pháp lý, các nước có thể tiêu diệt mục tiêu nếu mối đe dọa là hiển hiện và không còn lựa chọn nào khác\", Sina Azodi, chuyên gia tại Hội đồng Atlantic ở Washington, nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, chính quyền Trump tới nay vẫn chưa cung cấp bất cứ bằng chứng đáng tin cậy nào cho thấy tướng Soleimani trên thực tế đang chuẩn bị \"một cuộc tấn công tiềm tàng\" và rằng hạ sát ông là lựa chọn duy nhất của Mỹ, Azodi nói thêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Mỹ đã ám sát vị tướng vĩ đại của chúng ta. Chúng ta sẽ không bỏ qua việc này\", Tổng thống Iran Rouhani tuyên bố hồi tháng 3.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Ánh Ngọc</strong>&nbsp;(Theo&nbsp;<em>Independent, NY Times</em>)</span></p>\r\n\r\n<p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>\r\n</p>', 'pa2w_1.jpg', 1, 1, 6, '2020-07-01 03:54:04', '2020-07-01 03:54:04'),
(83, 'Trung Quốc \'quyến rũ\' quốc đảo Thái Bình Dương', 'trung-quoc-quyen-ru-quoc-dao-thai-binh-duong', 'Khi Covid-19 vẫn hoành hành khắp thế giới, cờ Trung Quốc được kéo lên tại sứ quán ở Kiribati, quốc gia chỉ có 116.000 dân, cách xa Bắc Kinh hàng nghìn km.', '<p><span style=\"font-size:18px\">Việc Trung Quốc hồi tháng 5 mở sứ quán tại Kiribati, quốc gia gồm 33 đảo san hô ở trung tâm Thái Bình Dương, có vẻ kỳ lạ, đặc biệt là trong bối cảnh đại dịch đang diễn biến phức tạp. Chỉ ba quốc gia khác có đại sứ quán ở quốc đảo này: Australia, New Zealand và Cuba.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"Chủ tịch Trung Quốc Tập Cận Bình (phải) và Tổng thống Kiribati Taneti Maamau tại Bắc Kinh hồi tháng một. Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/1592551362-2020-06-19t065822z-5809-5005-1593494279.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=x8-vsfzriHBYiRZ7J2ZWaQ\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Chủ tịch Trung Quốc Tập Cận Bình (phải) và Tổng thống Kiribati Taneti Maamau tại Bắc Kinh hồi tháng một. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kiribati ngày càng trở thành sân khấu cạnh tranh địa chính trị. Tháng 9 năm ngoái, họ chấm dứt quan hệ ngoại giao với Đài Loan và thiết lập quan hệ với Trung Quốc đại lục. Tuần này, Tổng thống thân Trung Quốc Kiribati Taneti Maamau tái đắc cử sau khi hứa hẹn sẽ thúc đẩy quan hệ gần gũi hơn với Bắc Kinh, đánh bại đối thủ có thiện cảm với Đài Loan hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kiribati là minh chứng mới nhất về ảnh hưởng ngày càng tăng của Bắc Kinh ở Thái Bình Dương, bao gồm một loạt các quốc đảo giàu tài nguyên, nằm trên các tuyến đường thủy quan trọng giữa châu Á và Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các quốc đảo Thái Bình Dương từ lâu đã duy trì quan hệ tốt với Mỹ, quốc gia có sự hiện diện quân sự lớn ở Thái Bình Dương và các đồng minh như Australia - nhà tài trợ và đối tác an ninh lớn nhất trong khu vực. Nhưng nhiều nước gần đây quay sang củng cố quan hệ với Trung Quốc, khi Bắc Kinh thúc đẩy chiến lược \"quyến rũ\" bằng ngoại giao và kinh tế.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Năm 2006, Thủ tướng Ôn Gia Bảo trở thành quan chức cao cấp nhất của Trung Quốc đến thăm các quốc đảo Thái Bình Dương. Ông cam kết cung cấp khoản vay ưu đãi ba tỷ NDT (424 triệu USD) để đầu tư vào phát triển tài nguyên, nông nghiệp, thủy sản và các ngành công nghiệp quan trọng khác, gia tăng lợi ích của Bắc Kinh trong khu vực.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bắc Kinh hiện là nhà tài trợ lớn thứ hai cho các quốc đảo Thái Bình Dương, chỉ sau Australia, theo dữ liệu từ Viện Lowy, nhóm chuyên gia Australia. Các chuyên gia y tế Trung Quốc đã đưa ra lời khuyên về cách chống Covid-19 qua các cuộc họp trực tuyến với đối tác ở 10 quốc đảo Thái Bình Dương có quan hệ ngoại giao với Bắc Kinh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hồi tháng ba, Trung Quốc tuyên bố ủng hộ 1,9 triệu USD tiền mặt và vật tư y tế để giúp các nước chiến đấu với Covid-19. Họ cũng gửi vật tư y tế, đồ bảo hộ và kit xét nghiệm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các nhóm cố vấn y tế Trung Quốc đến các quốc đảo như Samoa, giúp giới chức y tế địa phương soạn thảo chỉ dẫn về cách kiểm soát Covid-19. Ở Fiji, Trung Quốc cung cấp xe quân sự chuyên dụng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo WHO, các quốc đảo Thái Bình Dương báo cáo 312 ca nhiễm và 7 ca tử vong do Covid-19, phần lớn tại đảo Guam, lãnh thổ hải ngoại của Mỹ. Các hòn đảo đã tránh được Covid-19 bùng phát mạnh nhờ nằm ở vị trí xa xôi, cách biệt và các biện pháp phong tỏa sớm. Người dân tại đây có thể đối mặt với hậu quả tàn khốc nếu Covid-19 hoành hành mạnh, vì bộ máy y tế không đủ sức chống chịu và năng lực xét nghiệm hạn chế.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chủ nghĩa cơ hội là yếu tố thúc đẩy Trung Quốc tăng cường quan hệ với các quốc đảo Thái Bình Dương. Họ đang cố giành được nhiều ảnh hưởng nhất có thể\", Jonathan Pryke, chuyên gia từ Viện Lowy, cho biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bộ Ngoại giao Trung Quốc bác bỏ điều này, nói rằng sự hỗ trợ của Trung Quốc cho các quốc đảo Thái Bình Dương là \"chân thành\" và không có \"bất kỳ sự ràng buộc chính trị nào\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng mối quan hệ thân thiết có thể có ích trong những lúc cần thiết. Hồi tháng 5, khi Trung Quốc bị nhiều nước chỉ trích vì xử lý sai lầm trong giai đoạn đầu Covid-19 bùng phát, họ đã trông cậy vào sự hỗ trợ của các quốc đảo Thái Bình Dương. Vài ngày trước cuộc họp của Đại hội đồng Y tế Thế giới vào tháng 5, các bộ trưởng từ 10 quốc đảo Thái Bình Dương tham gia một cuộc họp trực tuyến về Covid-19 do Trung Quốc chủ trì.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuộc họp kết thúc với lời khẳng định rằng Trung Quốc đã xử lý dịch tốt. Trong thông cáo báo chí chung sau sự kiện này, các quốc đảo Thái Bình Dương ca ngợi Trung Quốc vì \"cách tiếp cận cởi mở, minh bạch và có trách nhiệm trong việc áp dụng các biện pháp ứng phó kịp thời, mạnh mẽ và chia sẻ kinh nghiệm kiềm chế dịch\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Đây là những gì chính phủ Trung Quốc cần\", Denghua Zhang, từ Đại học Quốc gia Australia ở Canberra, nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi đó, chính quyền Tổng thống Mỹ Donald Trump nhiều lần đổ lỗi cho Trung Quốc về đại dịch. Canberra khiến Bắc Kinh tức giận khi kêu gọi một cuộc điều tra độc lập về nguồn gốc virus.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"14 quốc đảo Thái Bình Dương có dân số chưa đến 10 triệu. Đồ họa: CNN.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/cadts-5364-1593494279.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fLovuZ1DjkNUdRXoz25S8g\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">14 quốc đảo Thái Bình Dương có dân số chưa đến 10 triệu. Đồ họa:&nbsp;<em>CNN</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, khoản hỗ trợ của Trung Quốc cho các quốc đảo Thái Bình Dương vẫn khiêm tốn so với Australia. Tháng trước, Canberra cho biết họ đã chi 69 triệu USD để \"hỗ trợ tài chính nhanh chóng\" cho 10 quốc gia trong khu vực, một phần trong số đó được tái phân bổ từ các chương trình viện trợ hiện có.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Australia gần đây thông báo rằng họ sẽ phát các chương trình truyền hình nổi tiếng trong nước như \" Neighbours\" và \"Masterchef\" tại 7 quốc đảo Thái Bình Dương - động thái được coi là nhằm thúc đẩy \"quyền lực mềm\" để đối trọng với ảnh hưởng đang gia tăng của Trung Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chính phủ Australia rõ ràng nhận ra không thể để khoảng trống cho Trung Quốc chen chân, cả về quyền lực cứng, quyền lực mềm, viện trợ hay y tế tại khu vực này\", Pryke nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Australia đã chú ý đến vấn đề này từ trước đại dịch. Sau khi nhậm chức năm 2018, Thủ tướng Scott Morrison đưa ra sáng kiến \"Bước tiến Thái Bình Dương\", bao gồm tăng viện trợ nước ngoài và thành lập quỹ cơ sở hạ tầng trị giá 1,5 tỷ USD cho khu vực.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cạnh tranh địa chính trị ở Thái Bình Dương còn có thể tác động đến vấn đề đi lại. Australia và New Zealand đã làm phẳng được đường cong dịch, các chính trị gia hai bên thảo luận về việc mở biên giới với nhau, tạo ra một hành lang đi lại giữa hai quốc gia. Các quốc đảo Thái Bình Dương bao gồm Fiji, Samoa và Quần đảo Solomon đã đề nghị tham gia kế hoạch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chưa có thông tin nào về kế hoạch tương tự giữa các quốc đảo Thái Bình Dương và Trung Quốc, quốc gia có vẻ đang tập trung vào các vùng lãnh thổ lân cận. Tỉnh Quảng Đông ở miền nam đất nước đang thảo luận với Hong Kong và Macau về mở hành lang đi lại an toàn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Covid-19 đã gây áp lực rất lớn đối với nền kinh tế phụ thuộc vào du lịch của các quốc đảo Thái Bình Dương. Australia và New Zealand là nguồn khách du lịch chính của họ. Năm 2018, hai nước đóng góp hơn một triệu lượt khách nước ngoài đến các quốc đảo Thái Bình Dương, chiếm 51% tổng lượng khách. Trong khi đó, 124.939 du khách Trung Quốc đến các quần đảo Thái Bình Dương năm 2018, giảm 10,9% so với năm trước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Một số chính trị gia Australia cũng mong muốn có hành lang đi lại xuyên Thái Bình Dương. Dave Sharma, nghị sĩ của đảng Tự do, viết hồi tháng trước rằng hành lang sẽ giúp các quốc đảo láng giềng về mặt kinh tế và đảm bảo \"họ tiếp tục coi Australia là đối tác hàng đầu\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cạnh tranh chiến lược ở Thái Bình Dương đang diễn ra quyết liệt, Trung Quốc và các quốc gia khác đang tìm cách đóng vai trò lớn hơn. Điều quan trọng là giữ cho ảnh hưởng và dấu chân của chúng ta trong khu vực lân cận trở nên rõ ràng\", ông viết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Australia và New Zealand sẽ trở thành những người gác cổng cho khu vực Thái Bình Dương khi đại dịch đang tiếp diễn trên khắp thế giới. Vì vậy, điều đó tất nhiên sẽ mang lại cho Australia và New Zealand thêm nhiều lợi thế địa chính trị\", ông nói.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Phương Vũ</strong>&nbsp;(Theo&nbsp;<em>CNN</em>)</span></p>\r\n\r\n<p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>\r\n</p>', 'ZORq_1.jpg', 1, 0, 6, '2020-07-01 03:57:08', '2020-07-01 03:57:08'),
(84, 'Biểu tình sắc tộc Mỹ đối mặt xung đột đảng phái', 'bieu-tinh-sac-toc-my-doi-mat-xung-dot-dang-phai', 'Làn sóng biểu tình đòi bình đẳng sắc tộc và cải cách cảnh sát có thể không tạo ra thay đổi lớn vì mâu thuẫn đảng phái ở Mỹ.', '<p><span style=\"font-size:18px\">Cơn thịnh nộ của người dân bắt nguồn từ vụ George Floyd, người đàn ông da màu tại thành phố Minneapolis, bang Minnesota, bị cảnh sát ghì chết hồi cuối tháng 5. Làn sóng biểu tình nhanh chóng lan rộng khắp nước Mỹ và nhiều quốc gia khác, với đám đông hô vang khẩu hiệu \"Mạng sống người da màu quan trọng\", \"Không công lý, không hòa bình\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phong trào biểu tình rầm rộ nhằm chống lại nạn phân biệt chủng tộc và bạo lực cảnh sát cuối cùng cũng được các chính trị gia lắng nghe. Tuy nhiên, quá trình cải cách lực lượng cảnh sát theo yêu cầu của họ được cho là sẽ không đạt được bước tiến đáng kể nào,&nbsp;<strong>do những xung đột đảng phái giữa phe Dân chủ và Cộng hòa</strong>.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"Người biểu tình tập trung tại một công viên ở thành phố New York, Mỹ, hôm 24/6 nhằm kêu gọi cắt ngân sách cảnh sát. Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/29/2020-06-25T001011Z-1333556634-6078-6810-1593410854.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6R5obVHomeWsHSdKDXLfoA\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Người biểu tình tập trung tại một công viên ở thành phố New York, Mỹ, hôm 24/6 nhằm kêu gọi cắt ngân sách cảnh sát. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Tại bang Minnesota</strong>, cái chết của Floyd đã dẫn đến một số thay đổi. Đáp lại nguyện vọng của người biểu tình, Thống đốc Tim Walz, thành viên đảng Dân chủ, kêu gọi cơ quan lập pháp bang tổ chức một phiên họp đặc biệt nhằm xem xét những biện pháp cải cách cảnh sát khẩn cấp. Ông cho biết các biện pháp này nhắm tới việc giải quyết tình trạng bạo lực của cảnh sát, trợ cấp để tái xây dựng cơ sở hạ tầng địa phương cũng như cơ chế trách nhiệm và minh bạch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, các nhà lập pháp bang không thể hiện nhiều nỗ lực, với bầu không khí đậm tính đảng phái bao trùm. Thượng viện bang do phe Cộng hòa kiểm soát và hạ viện do phe Dân chủ chiếm đa số bất đồng trong gần 20 biện pháp cải tổ lực lượng cảnh sát Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phe Dân chủ hướng tới mục tiêu chấm dứt huấn luyện cảnh sát theo kiểu thiên về vũ lực cũng như cấm các chiến thuật khống chế nghi phạm bằng cách kẹp và ghì cổ. Trong khi đó, phe Cộng hòa ở thượng viện bang Minnesota đưa ra những cải cách quy mô hẹp hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kết quả là phiên họp đặc biệt do Thống đốc bang Minnesota kêu gọi \"hoàn toàn công cốc\". \"Người dân Minnesota hẳn sẽ thất vọng sâu sắc. Đây là một thất bại trong việc thay đổi mọi thứ, một sự thất hứa. Dường như các cơ quan lập pháp có xu hướng đổ lỗi cho người khác\", Walz nói trong buồn rầu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kế hoạch cải cách cảnh sát tại Minneapolis cũng mù mờ, chưa có những chi tiết cụ thể và được cho là khó có thể ban hành sớm, dù hội đồng thành phố này hôm 26/6 bỏ phiếu nhất trí thay thế sở cảnh sát bằng một cơ quan mới \"tập trung vào an toàn cộng đồng và tránh bạo lực\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi cam kết giải tán cảnh sát tại thành phố Minneapolis và tái xây dựng cộng đồng, một mô hình an ninh công cộng mới thực sự giữ an toàn cho người dân\", Lisa Bender, chủ tịch hội đồng thành phố Minneapolis, phát biểu hồi đầu tháng. Tuy nhiên, kế hoạch này vẫn cần sự đồng ý từ các lãnh đạo thành phố khác, cũng như phụ thuộc vào lựa chọn của cử tri trong tháng 11.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tình trạng mâu thuẫn đảng phái ảnh hưởng tới quá trình thông qua các biện pháp cải tổ cảnh sát&nbsp;<strong>không chỉ diễn ra tại Minnesota, mà còn trên quy mô liên bang</strong>. Tuần trước, phe Dân chủ tại Thượng viện Mỹ đã ngăn chặn một dự luật cải cách của phe Cộng hòa, cho rằng nó không thỏa đáng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phe Dân chủ muốn có điều khoản cấm cảnh sát ghì và kẹp cổ nghi phạm, biện pháp mà một số thành phố Mỹ đã đơn phương áp dụng, cùng vài đề xuất khác, nhưng chúng không xuất hiện trong dự luật của đảng Cộng hòa. \"Dự luật nửa vời đó không bao gồm những điều chúng ta nên làm, đấy mới là cải cách cảnh sát thực sự\", thượng nghị sĩ Dân chủ Mazie Hirono cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tại Hạ viện Mỹ, phe Dân chủ hôm 25/6 thông qua những biện pháp cải cách cảnh sát trên diện rộng của riêng họ, bao gồm kêu gọi hạn chế quyền miễn trừ cho cảnh sát, cấm chiến thuật ghì cổ và loại bỏ phần khai về sắc tộc trên giấy tờ, hồ sơ. Tuy nhiên, Thượng viện được cho là sẽ không phê chuẩn dự luật này. Tổng thống Donald Trump cũng không có khả năng ký thông qua nó.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi đó, Nhà Trắng cũng nỗ lực thể hiện vai trò lãnh đạo giữa cuộc tranh luận. Tuy nhiên, sắc lệnh hành pháp về cải cách cảnh sát mà Trump ký hôm 16/6 bị nhiều thành viên đảng Dân chủ chỉ trích. Thượng nghị sĩ California Kamala Harris chỉ ra rằng&nbsp;<strong>sắc lệnh khuyến khích cải cách, nhưng không có cơ chế thực thi rõ ràng</strong>.</span></p>\r\n\r\n<div><span style=\"font-size:18px\"><img alt=\"Tổng thống Mỹ Donald Trump ký sắc lệnh cải cách cảnh sát tại Nhà Trắng hôm 16/6. Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/29/EardbfWWsAAMQfn-2543-1593410854.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2KJN9pfEgyFotFzCmZak7g\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></span></div>\r\n\r\n<p><span style=\"font-size:14px\">Tổng thống Mỹ Donald Trump ký sắc lệnh cải cách cảnh sát tại Nhà Trắng hôm 16/6. Ảnh:&nbsp;<em>Reuters</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ví dụ, sắc lệnh kêu gọi cấm các nhân viên thực thi pháp luật ghì cổ nghi phạm, nhưng ngoại trừ \"những trường hợp việc sử dụng vũ lực gây chết người được pháp luật cho phép\". Đây có thể là kẽ hở giúp cảnh sát tiếp tục sử dụng biện pháp ghì cổ nếu họ lo sợ tính mạng bị đe dọa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Việc cấm cảnh sát ghì cổ nghi phạm được cho là còn gây tranh cãi. Nhiều chuyên gia cho rằng trong một số tình huống nguy hiểm nhất định, khi cảnh sát phải chiến đấu vì chính mạng sống của họ, bất cứ điều gì cũng có thể xảy ra.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Thêm vào đó, mặc dù sắc lệnh của Trump nhắm tới những sĩ quan \"lạm dụng\" thẩm quyền, bản thân ông lại từng ca ngợi sự mạnh tay của lực lượng phụ trách vấn đề nhập cư. \"Khi chứng kiến những tên côn đồ đó bị ném vào trong xe cảnh sát, tôi đã nói rằng \'Làm ơn đừng quá tuyệt như vậy\'\", ông chủ Nhà Trắng từng nói trước đám đông cảnh sát ở bang New York, trong tiếng vỗ tay vang dội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhiều nhà hoạt động kêu gọi Mỹ ngay lập tức cải cách cảnh sát, nhưng vẫn có&nbsp;<strong>một số nhóm dường như bằng lòng với sự trì hoãn</strong>. Hôm 22/6, Bob Kroll, chủ tịch công đoàn cảnh sát Minneapolis, cho biết ông chưa đọc hết những dự luật mà cơ quan lập pháp bang đang xem xét. Ông tỏ ra thận trọng trước việc cải cách quá vội vàng. \"Mọi người đều phải dừng lại một lúc đã\", Kroll nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khi công đoàn cảnh sát Minneapolis kêu gọi thêm thời gian để thay đổi, những người đòi cải cách tư pháp hình sự cho rằng nhiều mạng sống vẫn gặp nguy hiểm mỗi ngày, chừng nào những quy định mới hạn chế cảnh sát lạm dụng vũ lực chưa được thông qua.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong cuộc biểu tình gần đây bên ngoài nơi ở của Thống đốc Minnesota, một bà mẹ tên Del Shea Perry cho biết con trai bà thiệt mạng hồi năm 2018, khi đang ngồi tù vì bị tình nghi có hành vi bạo lực gia đình.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Perry nói rằng bà cùng các gia đình khác vẫn cố gắng hết sức để gây chú ý với giới lãnh đạo, thúc đẩy họ thực hiện hành động cụ thể để loại bỏ những cảnh sát tồi tệ. \"Tôi là nhà truyền giáo, không phải nhà hoạt động, nhưng đã bị đẩy vào vai trò này\", bà cho hay.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi được hỏi bà sẽ tiếp tục làm gương mặt đại diện cho công chúng nhằm kêu gọi cải cách cảnh sát đến khi nào, Perry trả lời: \"Cho đến khi chúng tôi đòi được công lý\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nếu không giành được điều đó ngay bây giờ, chúng ta sẽ không bao giờ có được nó\", người phụ nữ nói thêm.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Ánh Ngọc</strong>&nbsp;(Theo&nbsp;<em>CNN</em>)</span></p>\r\n\r\n<p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>\r\n</p>', 'JvCP_1.jpg', 1, 0, 6, '2020-07-01 03:59:11', '2020-07-01 03:59:11'),
(85, 'Đầu bếp Mỹ dọa kiện vì bị bắt lỗi từ \'bánh mỳ\'', 'dau-bep-my-doa-kien-vi-bi-bat-loi-tu-banh-my', 'Đầu bếp Peja Krstic bị chỉ trích vì dọa kiện người phụ nữ Mỹ gốc Việt Tiffany Tran, sau khi bị cô chỉnh lỗi chính tả từ \"bánh mì\".', '<p><span style=\"font-size:18px\">Hôm 16/6, Peja Krstic, chủ của nhà hàng Việt Nam \"Một Hai Ba\" ở thành phố Dallas, bang Texas, thông báo trên Instagram về việc mở cửa cả hai chi nhánh ở khu dân cư Lakewood và Công viên Victory Park. Anh đính kèm hai bức ảnh gồm món thịt viên Việt Nam và bánh mì.</span></p>\r\n\r\n<p><img alt=\"Món bánh mì của nhà hàng Một Hai Ba ở Dallas, Texas. Ảnh: Facebook/Một Hai Ba\" src=\"https://i1-vnexpress.vnecdn.net/2020/07/01/10632833-895932827139863-50991-5115-8575-1593569003.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=IVwHlABuEXuQniyZiugOrw\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Món bánh mì của nhà hàng Một Hai Ba ở Dallas, Texas. Ảnh:<em>&nbsp;Facebook/Một Hai Ba</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tiffany Tran, một phụ nữ gốc Việt, chủ của tiệm bánh SneakerBaby Macarons, sau đó chỉ ra lỗi chính tả trong bài viết là từ \"bánh mì\" không có dấu. Điều này khiến đầu bếp người Serbia phản ứng gay gắt. Khi Tiffany đùa rằng viết sai chính tả gây hại cho mắt người đọc, câu chuyện rẽ sang một hướng khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Krstic đã nhắn nhiều tin nhắn riêng cho Tiffany, một khách hàng yêu mến Một Hai Ba, dọa sẽ \"cấm cửa\" cô và khởi kiện. Tiffany nói với tờ Eater Dallas rằng đầu bếp này đã tìm được số điện thoại của cô qua hệ thống lưu trữ của nhà hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong các tin nhắn, Krstic nói rằng mình \"bị bôi nhọ công khai\" và ngoài việc \"bị xúc phạm\", anh này còn cảm thấy \"bị phân biệt chủng tộc\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cô chỉ trích tôi về một lỗi chính tả như thế là sai trên mọi cấp độ\", Krstic viết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tiffany cho biết nam đầu bếp thậm chí còn liên lạc với bạn trai cô, do họ từng làm việc với nhau ở một số sự kiện, để nói rằng cô \"đã đi quá xa\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi hy vọng ngày hôm sau anh ta sẽ hạ hỏa và xin lỗi\", Tiffany nói. \"Có thể anh ta đã có một buổi tối không vui và trút giận lên tôi. Tôi đã cho anh ta 5 ngày để liên lạc với tôi và xin lỗi, và cả 5 ngày đó tôi khổ sở vì sự việc đó\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Krstic cuối cùng đã xin lỗi về cuộc trao đổi với Tiffany, đăng video giải thích sự việc \"leo thang\" vì anh quá bận rộn với việc mở nhà hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi xin lỗi vì đã không đọc hết tin nhắn. Tôi đã đưa ra kết luận dựa trên đoạn đầu tiên, điều đó là sai lầm\", anh nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, Tiffany không chấp nhận lời xin lỗi của Krstic và phản hồi bằng một bài viết dài trên Instagram. Cô cho rằng video dài 8 phút của Krstic không phải là lời xin lỗi mà cô cần sau khi nam đầu bếp nhắn tin cáo buộc cô phân biệt chủng tộc và cấm đến nhà hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cô cáo buộc Krstic chỉ cố \"giữ thể diện\" và \"giảm thiệt hại\" cho mình. \"Anh ta chưa bao giờ thừa nhận là đã làm điều gì sai ngoài việc quá bận nên không đọc hết tin nhắn của tôi\", cô viết.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Reyna Duong, đầu bếp gốc Việt của nhà hàng Sandwich Hag, cũng lên tiếng chỉ trích Krstic không chỉ không thích ứng về mặt văn hóa mà còn \"xóa sổ\" ẩm thực Việt Nam khi đưa ra thực đơn không bao gồm các thành phần nguyên liệu của người Việt và đôi co với Tiffany.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Anh viết sai từ bánh mì và khi một số phụ nữ người Việt đề nghị anh sửa lỗi chính tả, phản ứng của anh là lên án họ phân biệt chủng tộc và anh, một đầu bếp da trắng, đang bị phân biệt đối xử, tôi nghĩ đã đến lúc anh nên nhìn lại mình\", Duong viết trên Instagram.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đây không phải là lần đầu tiên Krstic bị cáo buộc không tôn trọng người gốc Á. Donny Sirisavath, ông chủ kiêm đầu bếp của nhà hàng Khao Noodle Shop cho hay mình cũng từng tranh cãi nảy lửa với Krstic tại một quán bar ở Dallas hồi tháng hai sau khi anh yêu cầu Krstic ngừng \"săn trộm\" nhân viên.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Anh ta nói mình không biết gì về chuyện đó và bắt đầu chửi bới tôi, cố gắng đánh tôi\", Sirisavath kể. \"Anh ấy bảo tôi không là gì trong ngành công nghiệp này và không có quyền làm đầu bếp\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sirisavath cho hay khi rắc rối xảy ra với Tiffany, anh không thể im lặng và phải lên tiếng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo Eater Dallas, Krstic chưa bình luận thêm về sự việc.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Anh Ngọc</strong>&nbsp;(Theo&nbsp;<em>Next Shark</em>)</span></p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>', '4eNd_1.jpg', 1, 4, 7, '2020-07-01 04:01:06', '2020-07-01 04:03:52'),
(86, 'Chủ tiệm nail gốc Việt so sánh George Floyd với nCoV', 'chu-tiem-nail-goc-viet-so-sanh-george-floyd-voi-ncov', 'MỸ - Tiệm nail của Hanh Phan ở bang California bị phản đối và kêu gọi đóng cửa, sau khi bà so sánh người da màu George Floyd với nCoV.', '<p><span style=\"font-size:18px\">Người biểu tình da màu tập trung bên ngoài tiệm nail \"Tips&amp;Toes\" của bà&nbsp;Hanh (Hannah) Phan ở thành phố&nbsp;Carson,&nbsp;quận Los Angeles, bang California, hôm 27/6 với các biểu ngữ kêu gọi đóng cửa và \"đã tới&nbsp;lúc để thay đổi\" bằng tiếng Việt.&nbsp;</span></p>\r\n\r\n<p><img alt=\"Người biểu tình da màu tập trung bên ngoài tiệm nail Tips&amp;Toes của bà&nbsp;Hanh (Hannah) Phan ở thành phố&nbsp;Carson, bang California, hôm 27/6. Ảnh: CBS Los Angeles\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/29/BB1631MY-9000-1593394521.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n1W4YpBaUST57p_uDUditQ\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Người biểu tình da màu tập trung bên ngoài tiệm nail \"Tips&amp;Toes\" của bà&nbsp;Hanh (Hannah) Phan ở thành phố&nbsp;Carson, bang California, hôm 27/6. Ảnh:&nbsp;</span><em>CBS Los Angeles</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Phản đối diễn ra sau khi bà Phan, chủ tiệm nail trên, so sánh George Floyd, người đàn ông da màu bị cảnh sát ghì chết ở thành phố&nbsp;Minneapolis hồi tháng 5, với nCoV và nói anh đã nhận \"quả báo\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cái chết của Floyd đã làm dấy lên phong trào biểu tình phản đối phân biệt chủng tộc và bạo lực của cảnh sát hơn một tháng qua ở khắp nước Mỹ và trên thế giới. Một số cuộc biểu tình biến thành bạo loạn, cướp bóc, gây thiệt hại cho nhiều doanh nghiệp, trong đó có&nbsp;<a href=\"https://vnexpress.net/nha-hang-viet-o-my-bi-thieu-rui-trong-bieu-tinh-4108462.html\" rel=\"dofollow\">các doanh nghiệp của người gốc Việt</a>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Salon của bà Phan ở Carson hiện trưng biển&nbsp;cho thuê và người biểu tình nói rằng họ dự định tiếp tục phản đối tại một tiệm khác của bà ở thành phố&nbsp;Anaheim.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng tôi không dừng lại ở đây. Chúng tôi sẽ tiếp tục công việc của mình, nhưng chúng tôi chắc chắn muốn bà ấy phải biến mất khỏi cộng đồng Carson\",&nbsp;Tarsha Rodgers, một người biểu tình, nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bà Phan hiện chưa đưa ra bình luận gì.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hôm 19/6, các tiệm nail ở bang California đã được phép tái mở sau gần 100 ngày ngừng hoạt động&nbsp;tránh Covid-19.&nbsp;California đang là một trong những bang của Mỹ gây lo ngại vì số ca nhiễm mới nCoV tăng mạnh khi các biện pháp phong tỏa được dần nới lỏng. Tổng số ca nhiễm&nbsp;ở California hiện là gần 210.000, trong đó gần 5.900 ca tử vong.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Toàn nước Mỹ, vùng dịch lớn nhất thế giới, báo cáo hơn 2,6 triệu ca nhiễm, hơn 128.000&nbsp;ca tử vong.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Anh Ngọc</strong>&nbsp;(Theo<em>&nbsp;CBS Los Angeles</em>)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>', '0yG7_1.jpg', 1, 4, 7, '2020-07-01 04:02:44', '2020-07-01 04:05:13'),
(87, 'Thêm bị cáo nhận tội vụ 39 người Việt chết trong container', 'them-bi-cao-nhan-toi-vu-39-nguoi-viet-chet-trong-container', 'ANH - Alexandru Hanga, 28 tuổi, thừa nhận vi phạm luật nhập cư do liên quan đến cái chết của 39 người Việt trong xe container hồi năm ngoái.', '<p><span style=\"font-size:18px\">Alexandru Hanga, ở thị trấn&nbsp;Tilbury, hạt Essex, Anh, hôm 26/6 bị xét xử qua video trực tiếp&nbsp;tại Toà án Hình sự trung tâm London&nbsp;và nhận tội âm mưu hỗ trợ nhập cư trái phép. Bị cáo&nbsp;sẽ bị tuyên án trong phiên toà tiếp theo.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hanga bị cảnh sát Anh bắt hồi đầu tháng 3 và bị truy tố với tội danh liên quan đến vi phạm quy định nhập cư. Hành vi phạm tội của Hanga được cho là diễn ra từ ngày 1/5/2018 đến 24/10/2019.</span></p>\r\n\r\n<p><img alt=\"Cảnh sát Anh đưa xe container chở 39 thi thể được phát hiện ở hạt Essex&nbsp;rời khỏi hiện trường hôm 23/10. Ảnh:&nbsp;Reuters\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/27/UK-2-6964-1572651108-2973-1572-1448-5661-1593222264.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tHsOdGOqNUGRrNJwzDKJEw\" style=\"width:100%\" /></p>\r\n\r\n<div><span style=\"font-size:18px\"><span style=\"font-size:14px\">Cảnh sát Anh đưa xe container chở 39 thi thể được phát hiện ở hạt Essex&nbsp;rời khỏi hiện trường hôm 23/10. Ảnh:&nbsp;<em>Reuters</em></span></span></div>\r\n\r\n<p><span style=\"font-size:18px\">Thi thể 39 người Việt được phát hiện trong xe container đông lạnh tại khu công nghiệp Waterglade, hạt Essex vào&nbsp;sáng 23/10/2019. Container trước đó được chuyển bằng phà từ cảng Zeebrugge ở Bỉ tới cảng Purfleet, Anh.&nbsp;Giới chức Anh hồi tháng hai kết luận&nbsp;<a href=\"https://vnexpress.net/39-nguoi-viet-chet-trong-container-do-ngat-khi-4053749.html\" rel=\"dofollow\">nguyên nhân</a>&nbsp;khiến họ thiệt mạng là thiếu oxy và quá nóng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cảnh sát Anh đã phối hợp với cảnh sát Việt Nam và các nước châu Âu bắt nhiều nghi phạm, bao gồm các tài xế, trong vụ án. Maurice Robinson, 25 tuổi, tài xế điều khiển xe container, hồi tháng 4 đã thừa&nbsp;nhận 39 tội ngộ sát. Hồi tháng 11/2019, anh ta nhận tội hỗ trợ nhập cư trái phép.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tháng trước, cảnh sát Pháp và Bỉ cũng&nbsp;<a href=\"https://vnexpress.net/phap-bi-bat-26-nghi-pham-vu-39-nguoi-viet-chet-trong-container-4106295.html\" rel=\"dofollow\">đột kích bắt 26 người</a>, gồm&nbsp;nhiều người Việt, liên quan đến đường dây buôn người trên.&nbsp;Mỗi người nhập cư trái phép phải nộp 20.000 euro cho những kẻ này để đi từ Pháp sang Anh.&nbsp;Tất cả các nghi phạm bị Pháp và Bỉ&nbsp;buộc tội buôn người, tội phạm có tổ chức và lừa đảo.</span></p>\r\n\r\n<p style=\"text-align: right;\"><strong style=\"font-size:18px\">Anh Ngọc</strong><span style=\"font-size:18px\">&nbsp;(Theo</span><em style=\"font-size:18px\">&nbsp;Star Tribune</em><span style=\"font-size:18px\">)</span></p>\r\n\r\n<p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>\r\n</p>', 'odgK_1.jpg', 1, 0, 7, '2020-07-01 04:07:19', '2020-07-01 04:07:19');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(88, 'Sợ nhiễm Covid-19 qua giao hàng ở Bắc Kinh', 'so-nhiem-covid-19-qua-giao-hang-o-bac-kinh', 'Từ khi Covid-19 bùng phát ở Trung Quốc, Wu Jun, một người làm nghề chuyển phát nhanh, càng cẩn thận hơn khi đi làm.', '<p><span style=\"font-size:18px\">Wu luôn mang theo một chai nước khử trùng trên chiếc xe máy điện của mình, vệ sinh hai bàn tay sau mỗi lần giao hàng và cố hết sức không chạm tay lên mặt. Nhưng khi tin tức về một người đồng nghiệp giao hàng cho nền tảng gọi đồ ăn trực tuyến Ele.me nhiễm Covid-19 được công bố hôm 23/6, Wu \"cảm thấy vô cùng hoang mang\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Dù chúng tôi không phải giao nhiều đơn như những người làm nghề giao đồ ăn, nhưng chúng tôi vẫn phải tiếp xúc với rất nhiều người\", Wu nói.</span></p>\r\n\r\n<p><img alt=\"Người giao hàng và người dân trong một khu chung cư ở quận Tây Thành, Bắc Kinh, hôm 17/6. Ảnh: Xinhua.\" src=\"https://i1-vnexpress.vnecdn.net/2020/07/01/139146685-15924375791811n-1117-1593571700.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=HnqIS9GqCwJrAKkTuEh-_w\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Người giao hàng và người dân trong một khu chung cư ở quận Tây Thành, Bắc Kinh, hôm 17/6. Ảnh:&nbsp;</span><em style=\"font-size:14px\">Xinhua.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Anh là một trong 100.000 người làm nghề giao hàng, từ giao đồ ăn, tạp hóa, tới những người giao hàng hóa đặt mua trên các nền tảng trực tuyến, phải xét nghiệm axit nucleic kiểm tra nCoV. Đây là cách mà Kong, người giao đồ ăn trên nền tảng Ele.me, bị phát hiện nhiễm nCoV.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kong, 47 tuổi, giao tới 50 đơn hàng mỗi ngày trong quận Phong Đài ở Bắc Kinh. Phong Đài là tâm điểm chú ý khi đợt bùng phát thứ hai đều liên quan tới chợ đầu mối nông sản&nbsp;Tân Phát Địa&nbsp;ở quận này.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau khi ca nhiễm đầu tiên được phát hiện hôm 11/6, Bắc Kinh ghi nhận hơn 300 ca nữa liên quan tới Tân Phát Địa và hàng chục ca liên quan ở những tỉnh khác, từ tỉnh lân cận Hà Bắc tới tỉnh Liêu Ninh phía đông bắc, cách Bắc Kinh hơn 400 km.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chính quyền Bắc Kinh nỗ lực để thành phố không bị phong tỏa toàn diện, nhưng đã cho cách ly một số quận nội thành, đồng thời yêu cầu một số nhóm người có nguy cơ lây nhiễm cao phải xét nghiệm, bao gồm nhân viên chợ, người xử lý thực phẩm và giao hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Giới chức hôm 28/6 cho hay đã lấy mẫu của 8,2 triệu người dân và xử lý được 7,6 triệu mẫu. Một lần nữa bị yêu cầu phải ở yên trong nhà, người tiêu dùng Trung Quốc chuyển sang Internet để đáp ứng nhu cầu mua sắm và ăn uống, dựa vào đội quân giao hàng tận nhà.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trường hợp của Kong gây hoang mang khi nhiều người tự hỏi liệu mình có thể bị lây qua tiếp xúc với gói hàng không. Chủ đề này cũng liên tục gây sốt trên mạng truyền thông xã hội.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng nhiều bình luận nhanh chóng bị dập tắt, giống như những vấn đề nhạy cảm từng bị đưa ra thảo luận trước đây. Trong lúc Trung Quốc cố phục hồi ảnh hưởng của Covid-19, chính phủ đang khuyến khích người tiêu dùng trong nước hỗ trợ thúc đẩy nền kinh tế và trọng tâm là thương mại điện tử.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tuy nhiên, đa phần những gì người dùng mạng thảo luận xoay quanh chuyện Kong phải làm việc 16 giờ một ngày và vất vả mà ông đã trải qua.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Làm việc 16 tiếng một ngày, kiếm sống thật không dễ dàng\", một người bình luận trên Weibo. \"Mong ông chóng khỏe\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngoài ra, còn có những trấn an từ quan chức y tế rằng việc đặt mua đồ ăn mang đi và mua sắm trực tuyến vẫn tương đối ăn toàn, nhắc nhở người dân Bắ Kinh tiếp tục giữ gìn vệ sinh tốt, lựa chọn giao hàng không tiếp xúc và rửa tay ngay sau khi mở gói giao hàng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Trong khi chính phủ đang kêu gọi thực hiện giao hàng không tiếp xúc, đa số mọi người lại muốn chúng tôi giao đồ ăn tới tận cửa từ khi các khu dân cư mở cửa lại\", Feng Yisheng, một nhân viên giao hàng làm việc cho một nền tảng lớn, nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nếu chúng tôi không đồng ý, sẽ bị đánh giá kém và ảnh hưởng tới công việc\", ông nói thêm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Không giống Wu, người giao những mặt hàng như tài liệu, hàng hóa đóng gói trên phạm vi toàn thành phố, Feng chủ yếu giao các đơn đồ ăn và tạp hóa trong phạm vi nhỏ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Nhận 50 đơn một ngày là chuyện bình thường vì chúng tôi cần làm đủ chỉ tiêu. Tôi không nói dối, công việc này rất vất vả. Nếu thạo nghề khác, tôi đã không làm việc này\", Feng bày tỏ.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Hồng Hạnh</strong>&nbsp;(Theo<em>&nbsp;Straits Times</em>)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>', '3RMl_1.jpg', 1, 3, 8, '2020-07-01 04:11:31', '2020-07-01 04:12:56'),
(89, 'Những người mẹ đẻ thuê ở Ukraine', 'nhung-nguoi-me-de-thue-o-ukraine', 'Nước mắt vui sướng chảy trên gương mặt Andrea Viez khi bế con trai bé bỏng trên tay, đứa trẻ sinh ra từ một người mẹ đẻ thuê ở Ukraine.', '<p><span style=\"font-size:18px\">\"Thằng bé thật giỏi\", Viez, người phụ nữ Argentina gần 50 tuổi mới được làm mẹ, run giọng nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sau 9 năm cố gắng sinh con, Viez cuối cùng cũng có thể bế con trai trên tay, nhờ ngành công nghiệp đẻ thuê đang bùng nổ tại Ukraine, ngành mang lại hy vọng cho hàng nghìn cặp bố mẹ đang vất vả trong đường con cái.</span></p>\r\n\r\n<p><img alt=\"Vợ chồng Viez đón con trai trong khách sạn Venice tại Kiev hôm 10/6. Ảnh: AFP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/1-4222-1593505213.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UXRRhiWBsoyqI3NxnRYQhw\" style=\"border:0px; font-size:18px; line-height:0; max-height:700px; width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Vợ chồng Viez đón con trai trong khách sạn Venice tại Kiev hôm 10/6. Ảnh:</span><em style=\"font-size:14px\">&nbsp;AFP.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Nhưng đằng sau giấc mơ trở thành hiện thực của họ là một ngành kinh doanh lợi nhuận cao và nhiều mặt tối, khi người ta lo ngại nó đang lợi dụng những phụ nữ trẻ tuyệt vọng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Ukraine đang trở thành một cửa hàng bán trẻ sơ sinh trực tuyến quốc tế\", ủy viên quốc gia về quyền trẻ em Mykola Kuleba cảnh báo hồi tháng trước, lên án việc \"bóc lột\" phụ nữ Ukraine và kêu gọi cấm dịch vụ đẻ thuê.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ukraine là một trong số ít quốc gia cho phép thương mại hóa dịch vụ đẻ thuê với người nước ngoài, bất ngờ gây chú ý khi Covid-19 bùng phát.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Khi chính phủ các nước cấm đi lại để ngăn ngừa đại dịch, hàng tá phụ huynh cũng không thể tới Ukraine đón những đứa con chào đời nhờ dịch vụ đẻ thuê. Một công ty dịch vụ đẻ thuê tại địa phương đã đăng video lên mạng, cho thấy hàng chục em bé nằm trong cũi tại một khách sạn ở ven thủ đô Kiev.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phòng khám BioTexCom hy vọng thu hút sự chú ý của dư luận đến hoàn cảnh những em bé mắc kẹt. Video tỏ ra hữu hiệu, chính phủ đã can thiệp để giúp đỡ các gia đình như Viez xin được giấy phép đặc biệt tới Ukraine đón con sau vài tuần.</span></p>\r\n\r\n<p><img alt=\"Y tá chăm sóc các bé sơ sinh trong Khách sạn Venice tại Kiev hôm 15/5. Ảnh: AFP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/2-9149-1593505214.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=f8uqUS5t4zaeX01cqlFsPA\" style=\"border:0px; font-size:18px; line-height:0; max-height:700px; width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Y tá chăm sóc các bé sơ sinh trong Khách sạn Venice tại Kiev hôm 15/5. Ảnh:&nbsp;</span><em style=\"font-size:14px\">AFP.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Dù ngành này tồn tại từ đầu thập niên 2000, nó mới bùng nổ tại Ukraine khi Ấn Độ và Thái Lan bắt đầu cấm người nước ngoài sử dụng dịch vụ đẻ thuê từ 5 năm trước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ukraine là một trong những quốc gia nghèo nhất châu Âu, trở nên hấp dẫn bởi mức giá đẻ thuê chỉ 42.000 USD, bằng một nửa so với Mỹ. Tuy không có dữ liệu chính thức, các chuyên gia ước tính khoảng 2.500-3.000 trẻ em được sinh ra mỗi năm ở Ukraine nhờ các cặp bố mẹ nước ngoài thuê đẻ. Khoảng một phần ba khách hàng là người Trung Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngành này thiếu sự quản lý nhà nước, đầy rẫy lạm dụng và tham nhũng, theo Sergiy Antonov, người điều hành một công ty luật chuyên tư vấn các vấn đề sinh sản.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những người mẹ đẻ thuê đôi khi không được trả đúng số tiền được hứa hoặc bị bố trí ăn nghỉ trong điều kiện tồi tệ suốt giai đoạn cuối thai kỳ. Trong vài trường hợp, các cặp bố mẹ còn phát hiện ADN đứa con mình thuê đẻ không trùng khớp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các nhà chức trách nghi ngờ một số phòng khám lợi dụng việc thuê đẻ làm vỏ bọc dịch vụ mua bán con nuôi trái phép. \"Tình hình rất loạn\", Antonov nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Olga Korsunova, 27 tuổi, đang mang thai lần thứ tư, cho biết phụ nữ \"thường xuyên\" gặp khó khăn khi đòi số tiền được hứa hẹn. Họ thường được thuê đẻ qua môi giới, những người sẽ giữ lại một phần hoa hồng. Korsunova được trả 400 USD một tháng trong suốt thai kỳ và nhận 1.500 USD sau sinh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi sẽ không gọi việc này là bóc lột bởi không ai ép buộc chúng tôi cả\", cô nói khi ở trong căn hộ nhỏ thuê tại Kiev cùng con trai 8 tuổi.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Korsunova mơ ước trở thành bác sĩ, nhưng bắt đầu đẻ thuê sau khi cô và con trai chạy trốn khỏi miền đông Ukraine năm 2014. Cô thừa nhận vì khó khăn tài chính nên phụ nữ Ukraine \"phải đánh đổi một phần sức khỏe để lấy tiền\".</span></p>\r\n\r\n<p><img alt=\"Olga Korsunova, 27 tuổi, đang mang thai đứa trẻ đẻ thuê thứ 4, trong căn hộ nhỏ thuê ở Kiev hôm 4/6. Ảnh: AFP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/30/000-1TV9RX-1277-1593505214.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Rznji-bP3tzpPXOrp4xZwg\" style=\"border:0px; line-height:0; max-height:700px; width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Olga Korsunova, 27 tuổi, đang mang thai đứa trẻ đẻ thuê thứ 4, trong căn hộ nhỏ thuê ở Kiev hôm 4/6. Ảnh:</span><em style=\"font-size:14px\">&nbsp;AFP.</em></p>\r\n\r\n<p><span style=\"font-size:18px\">Olga, 26 tuổi, một người đẻ thuê khác, cho biết rất vui khi giúp đỡ được người khác có con.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chúng sẽ được bố mẹ yêu thương tới hết cuộc đời\", cô nói, cho biết đang mang thai song sinh cho một cặp vợ chồng Trung Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Olga bình thường làm nghề bồi bàn, thu nhập 135 USD một tháng. Đây là lần đẻ thuê thứ hai của cô. Olga hy vọng sẽ mở được quán cà phê của riêng mình khi nhận số tiền 15.000 USD sau sinh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tôi rất tự hào có thể cung cấp em bé cho những người không thể sinh con theo một cách khác\", cô nói, nhưng thừa nhận \"nếu tôi có một công việc bình thường, dĩ nhiên là tôi sẽ không làm việc này\".</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Hồng Hạnh&nbsp;</strong>(Theo&nbsp;<em>AFP</em>)</span></p>\r\n\r\n<p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>\r\n</p>', 'SkEw_1.jpg', 1, 1, 8, '2020-07-01 04:16:29', '2020-07-01 04:16:29'),
(90, 'Biên phòng Trung Quốc diễn tập chống khủng bố', 'bien-phong-trung-quoc-dien-tap-chong-khung-bo', 'Hơn 2.100 công an và lính biên phòng Trung Quốc diễn tập chống khủng bố và các mối đe dọa an ninh phi truyền thống tại 4 địa phương.', '<p><span style=\"font-size:18px\">Cục Quản lý Xuất nhập cảnh Quốc gia Trung Quốc cho biết cuộc diễn tập diễn ra hôm qua tại bốn địa điểm gồm thành phố Thượng Hải ở phía đông, tỉnh Cát Lâm ở đông bắc, tỉnh Vân Nam ở tây nam và khu tự trị Tân Cương ở phía tây. Máy bay không người lái (UAV) và chó nghiệp vụ được sử dụng trong diễn tập.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các cuộc diễn tập nhằm kiểm tra khả năng đối phó với hành vi vượt biên, tấn công khủng bố, tội phạm xuyên biên giới, tụ tập phá hoại quy mô lớn cùng cấp cứu tại các khu vực biên giới và điểm kiểm soát xuất nhập cảnh. Cục Quản lý Xuất nhập cảnh Quốc gia Trung Quốc cho biết đây là cuộc diễn tập lớn nhất từ khi cơ quan này được thành lập năm 2018.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Video cuộc diễn tập cho thấy lực lượng biên phòng đối phó với những kẻ tấn công mang bom xăng tại Tân Cương, ứng phó với khủng hoảng con tin tại Vân Nam, khống chế và bắt một kẻ tấn công ẩn náu trong nhà tại Cát Lâm và xử lý ẩu đả tại trạm kiểm soát xuất nhập cảnh ở Thượng Hải.</span></p>\r\n\r\n<p style=\"text-align: center;\"><iframe height=\"480\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/07/01/hang-nghin-canh-sat-binh-si-trung-quoc-dien-tap-chong-khung--1593573632.mp4\" width=\"680\"></iframe></p>\r\n\r\n<p><span style=\"font-size:14px\">Công an và lính biên phòng Trung Quốc tham gia diễn tập chống khủng bố tại Tân Cương, Vân Nam, Cát Lâm và Thượng Hải. Video:&nbsp;<em>CNIA</em>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Wang Chuan, chuyên gia thuộc Viện nghiên cứu Chiến lược và Quốc phòng Knowfar tại Trung Quốc, cho biết địa điểm diễn ra các cuộc diễn tập phù hợp với các mối đe dọa an ninh phi truyền thống mà Trung Quốc phải đối mặt trong những năm gần đây, như nguy cơ tấn công của các lực lượng ly khai ở Tân Cương.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Những mối đe dọa an ninh phi truyền thống có thể bao gồm nguy cơ bùng phát hoặc leo thang xung đột ở khu vực dọc theo biên giới Trung Quốc, người nhập cư vượt biên quy mô lớn và khủng hoảng tị nạn\", Wang Chuan nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Các cuộc diễn tập diễn ra sau khi Bộ Công an Trung Quốc ban hành hướng dẫn yêu cầu lực lượng công an tổ chức diễn tập cải thiện khả năng duy trì trị an và ổn định.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Nguyễn Tiến</strong>&nbsp;(Theo&nbsp;<em>SCMP, CNIA</em>)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>', 'RKS9_1.jpg', 1, 1, 9, '2020-07-01 04:20:08', '2020-07-01 04:20:08'),
(91, 'Lính Trung Quốc tại Hong Kong diễn tập truy bắt người đào tẩu', 'linh-trung-quoc-tai-hong-kong-dien-tap-truy-bat-nguoi-dao-tau', 'Truyền thông Trung Quốc công bố video binh sĩ đồn trú tại Hong Kong diễn tập lục soát tàu, bắt người chạy trốn vào ngày thông qua luật an ninh.', '<p><span style=\"font-size:18px\">Video dài hai phút được CCTV công bố hôm qua, cùng ngày Chủ tịch Trung Quốc Tập Cận Bình ký phê chuẩn&nbsp;luật an ninh&nbsp;quốc gia, quay cảnh các binh sĩ đồn trú tại Hong Kong dùng trực thăng tìm kiếm cứu nạn trên biển, điều xuồng cao tốc chặn và lục soát một con tàu, đổ bộ xuống đảo để truy lùng và bắt người đào tẩu lẩn trốn trên đó.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuối video là hình ảnh các binh sĩ Trung Quốc sử dụng tàu chiến, trực thăng tuần tra trên biển và trên không tại khu vực cảng Victoria của Hong Kong sau cuộc diễn tập.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cuộc diễn tập nhằm \"kiểm tra toàn diện khả năng phòng thủ của lực lượng đồn trú tại Hong Kong\", CCTV đưa tin. \"Mục tiêu của chúng tôi là phát hiện kẻ chạy trốn ngay từ đầu và chặn bắt nghi phạm\", một binh sĩ Trung Quốc nói trong video.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">CCTV cho biết các binh sĩ Trung Quốc đồn trú tại Hong Kong cam kết \"bảo vệ \'một quốc gia, hai chế độ\'\", hệ thống nhằm \"đảm bảo an ninh và hòa bình tại Hong Kong\".</span></p>\r\n\r\n<p style=\"text-align:center\"><iframe height=\"480\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/07/01/linh-trung-quoc-dien-tap-o-hong-kong-1593566519.mp4\" width=\"680\"></iframe></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px\">Binh sĩ thuộc lực lượng đồn trú tại Hong Kong của quân đội Trung Quốc diễn tập. Video:&nbsp;</span><em>CCTV</em><span style=\"font-size:14px\">.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lực lượng quân đội Trung Quốc đồn trú tại Hong Kong tổ chức nhiều cuộc diễn tập từ khi biểu tình bùng phát tại đặc khu giữa năm 2019. Các cuộc diễn tập này được coi là lời cảnh báo dành cho \"những kẻ có ý đồ ly khai\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tờ PLA Daily của quân đội Trung Quốc ngày 28/6 cũng công bố video lính bắn tỉa tham gia&nbsp;diễn tập&nbsp;bắn đạn thật và huấn luyện tại một thao trường ở Hong Kong. Buổi diễn tập có hình thức giống cuộc thi bắn súng, các binh sĩ tập bắn mục tiêu cố định và di chuyển với các loại súng trường bắn tỉa khác nhau. Một sĩ quan chỉ huy cho biết buổi tập nhằm chuẩn bị cho tình huống \"thực chiến\" của các xạ thủ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Luật an ninh Hong Kong&nbsp;có hiệu lực từ 23 giờ đêm qua, hình sự hóa 4 tội danh gồm ly khai, lật đổ, khủng bố và cấu kết với nước ngoài đe dọa an ninh quốc gia. Người vi phạm có thể bị kết án chung thân, các vụ án \"phức tạp\" sẽ do cơ quan an ninh quốc gia của đại lục tiếp quản và tiến hành quy trình tố tụng.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Nguyễn Tiến</strong>&nbsp;(Theo&nbsp;<em>SCMP</em>)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n<script type=\"text/javascript\">document.oncontextmenu=null;document.onselectstart=null;document.ondragstart=null;document.onmousedown=null;document.body.oncontextmenu=null;document.body.onselectstart=null;document.body.ondragstart=null;document.body.onmousedown=null;document.body.oncut=null;document.body.oncopy=null;document.body.onpaste=null;</script>', 'HFGE_1.jpg', 1, 3, 9, '2020-07-01 04:22:58', '2020-07-01 04:23:47'),
(92, 'Dời dân để bảo tồn cây da hơn 100 tuổi', 'doi-dan-de-bao-ton-cay-da-hon-100-tuoi', 'ĐỒNG THÁP - TP Sa Đéc sẽ đổi đất cho các hộ dân quanh cây da hơn 100 tuổi bị nghiêng để bảo tồn cổ thụ gắn với quá trình phát triển đô thị xưa.', '<p><span style=\"font-size:18px\"><img alt=\"Phần thân cây da từ mái nhà ông Sa trở lên, cách mặt đất khoãng 4 m. Ảnh: Cửu Long\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/04/than-cay-da-5m-1801-1591260341.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AKu2VLoQ1oofW5M6TGPMRg\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần thân cây da từ mái nhà ông Sa trở lên, cách mặt đất khoảng 4 m. Ảnh:&nbsp;<em>Cửu Long.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Trong khuôn viên 86 m2 đất thổ cư của gia đình ông Nguyễn Hoàng Sa, 47 tuổi, ở góc đường Phan Chu Trinh - Hùng Vương (phường 1, TP Sa Đéc), có 19 m2 không thể sử dụng được vì gốc da án ngữ. Cây có vòng tròn gốc 6 m,&nbsp;cao 25 m, bị nghiêng vào bên trong. Gốc và thân cây đã mục ruỗng nhiều lỗ rất to, người lớn có thể chui vào...&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Căn nhà cấp bốn ọp ẹp cũng là nơi bán quán cơm nuôi sống tám người trong gia đình ông Sa. \"Mỗi khi mưa gió là mọi người không thể ngủ yên vì dột nước và sợ cây ngã đè\", ông Sa nói&nbsp;và cho biết nhà đã xuống cấp, hư hỏng nhiều nơi nhưng không thể&nbsp;sửa, xây mới&nbsp;được. Gia đình cũng phải đóng phần thuế cho diện tích bị cây da&nbsp;\"lấn chiếm\".</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Chủ nhân thửa đất cho rằng cha mẹ mình sống ở đây từ năm 1960, khi đó đã có cây da. Đến năm 1985, cơn bão lớn quét qua khiến cây bị nghiêng và đè lên căn nhà. Đầu tháng 8/2019, trận mưa giông kèm lốc xoáy làm cổ thụ bật gốc, phần thân đè sập mảng tường của phòng thờ trên gác và gian bếp bên dưới với tổng diện tích gần 20 m2. Các căn nhà lân cận cũng bị thủng tôn, nứt tường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Gốc cây da phía bên trong nhà ông Hoàng Sa. Ảnh: Cửu Long\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/04/goc-da-ben-trong-1-9311-1591261682.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4ViM1DGdxZ7IvcWkuRYd7g\" style=\"width:100%\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Gốc cây da phía bên trong nhà ông Hoàng Sa. Ảnh:&nbsp;<em>Cửu Long</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Theo ông Nguyễn Văn Của Anh, 62 tuổi, nhà liền kề với ông Hoàng Sa, cây da được cắt tỉa nhánh nhưng vẫn còn rất cao, tán rộng. \"Chúng tôi rất lo sợ cây bật gốc khi đang vào mùa mưa\", ông nói.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Lo sợ nguy hiểm, gia đình ông Sa cùng 5 hộ lân cận làm đơn đề nghị chính quyền địa phương đốn hạ hoặc di dời cây nhưng không được chấp nhận vì đây là một trong hai cổ thụ trăm tuổi thuộc diện bảo tồn của TP Sa Đéc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ông Võ Thanh Tùng, Chủ tịch TP Sa Đéc cho biết chính quyền địa phương chia sẻ trước lo lắng và những khó khăn của các hộ dân sống cạnh cây da.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Cây da này gắn với sự hình thành và phát triển của Sa Đéc, vì vậy việc bảo tồn cổ thụ này như giữ cái hồn của đô thị xưa\", ông Tùng nói và cho biết tháng 8/2019, cây bị bật gốc, nghiêng 10-15 độ nhưng được hai lần cắt tỉa nhiều cành, nhánh to, nên khó xảy ra đổ ngã.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"Đầu tháng 8/2019, cây da bậc gốc, nghiêng đè sập bức tường nhà dân. Ảnh: Cửu Long\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/04/sap-buc-tuong-SAP-4469-1591261682.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JfWa2i1g9fEhL7DalU89qQ\" style=\"width:100%\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\">Đầu tháng 8/2019, cây da bật&nbsp;gốc, nghiêng đè sập bức tường nhà dân. Ảnh:&nbsp;<em style=\"font-size:18px\">Cửu Long.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Do đây là cây bảo tồn nên UBND TP Sa Đéc quyết tâm giữ lại và đã có giải pháp đảm bảo sự an toàn và quyền lợi của người dân.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Chính quyền sẽ đối thoại với các hộ dân bị ảnh hưởng bởi cây da để đổi bằng phần đất có giá trị tương đương và sớm hỗ trợ các hộ này di dời đến nơi ở mới, ổn định cuộc sống\", ông Tùng nói và cho biết đồng thời, thành phố sẽ triển khai phương án bảo vệ, chăm sóc cổ thụ thật tốt, không để đổ ngã.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sa Đéc trở thành thành phố năm 1993, là trung tâm kinh tế, tài chính, giáo dục, văn hóa và du&nbsp;lịch ở phía Nam Đồng Tháp.&nbsp;Vùng đất Sa Đéc xưa có tên Khmer là Phsar Dek,&nbsp;nghĩa là Chợ Sắt. Giai đoạn 1976-1994, tỉnh lỵ của Đồng Tháp đặt ở&nbsp;Sa Đéc,&nbsp;sau đó dời về Cao Lãnh (nay là&nbsp;TP Cao Lãnh).&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Sa Đéc nổi tiếng với làng hoa được hình thành hơn 100 năm trước. Đến nay có hơn 2.300 hộ dân trồng 2.500 loài hoa kiểng khác nhau, với diện tích hơn 500 ha, trở thành một trong những vựa hoa kiểng lớn nhất miền Tây.</span></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:18px\"><strong>Cửu Long</strong></span></p>', '44QL_1.jpg', 1, 0, 2, '2020-07-07 12:55:27', '2020-07-07 12:55:27'),
(93, 'Chủ vườn Nhật Tân \'tái sinh\' đào sau Tết', 'chu-vuon-nhat-tan-tai-sinh-dao-sau-tet', 'Thời tiết sau Tết mưa, rét đậm nhưng các chủ vườn Nhật Tân vẫn tất bật xới đất, làm cỏ, tỉa cành, trồng lại đào chuẩn bị vụ sau.', '<p><span style=\"font-size:18px\">Vụ đào năm nay được ông Nguyễn Văn Nam - chủ vườn tại Nhật Tân đánh giá là thất thu vì nắng nóng, nồm ẩm dài ngày trước Tết khiến hoa đào nở dồn dập. Nhiều chủ vườn chấp nhận bán tháo, cho thuê giá rẻ nhằm vớt vát chi phí.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Thông thường, thời tiết thuận lợi, khách sẽ thuê đào đến hết Rằm tháng Giêng. Nhưng năm nay hoa nở sớm, từ mùng 7 Tết nhiều khách đã gọi trả. Việc thu gom đào sớm tuy không ảnh hưởng đến chất lượng cây nhưng chúng tôi không thu được nhiều tiền thuê như trước\", ông Nam cho biết.</span></p>\r\n\r\n<p><img alt=\"Các chủ vườn Nhật Tân tái sinh đào sau Tết\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image001-1580981961-3889-1580983351.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Những gốc đào được đưa trở lại vườn sau khi thuê.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">\"Tuy giá đào giảm, chi phí vận chuyển cao nhưng là nghề truyền thống của gia đình nên không thể bỏ\", ông tâm sự.Tết vừa rồi, ông Nguyễn Văn Nam, cho thuê hơn 200 gốc đào với giá từ 1 đến 5 triệu đồng. Ông cho biết, mức giá này giảm gần một nửa so với các năm trước. Cho thuê nhiều, cây lại lớn vì vậy chủ vườn phải thuê thêm 10 nhân công và 3 xe tải để vận chuyển.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ông Nam cũng tiết lộ, để \"hồi sinh\" đào chuẩn bị cho vụ sau cần nhiều công đoạn. Cây được đưa về là phải cắt, tỉa cành ngay. Đất trồng phải là đất mới, chủ yếu là đất sét pha đất thịt thì cây mới có thể sinh trưởng tốt. Ngoài ra, ông còn tưới nước và theo dõi cây hàng ngày, sau khi cây có dấu hiệu bắn mầm mới tiến hành ghép mắt.</span></p>\r\n\r\n<p><img alt=\"Các chủ vườn Nhật Tân tái sinh đào sau Tết  - 1\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image002-1580981971-3584-1580983351.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Ông Nam tỉ mỉ cắt từng cành dăm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Rút kinh nghiệm, năm nay, khi cho thuê, ông Long dặn kỹ khách cách chăm sóc. Ra Tết, ông cùng nhiều nhà vườn chủ động thu gom đào sớm, nên không gặp tình trạng cây chết hay sâu bệnh nhiều như năm ngoái. Tuy nhiên, những ngày gần đây, trời rét đậm, mưa lạnh nên cây nảy&nbsp;mầm chậm hơn mọi năm.Một số chủ vườn tại Nhật Tân cho hay, việc cho thuê đào gặp không ít rủi ro. \"Năm ngoái, 11 cây đào cổ thụ của nhà bị chết, một phần do khách không chăm sóc trong lúc thuê, một phần do không kịp đưa về vườn trồng lại\", ông Long chia sẻ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bên cạnh việc thu gom và trồng lại những gốc đào cổ thụ, các chủ vườn còn trồng thêm nhiều cây giống để mở rộng quy mô và thiết kế thế đào mới phục vụ cho mùa Tết sau.</span><br />\r\n<img alt=\"Các chủ vườn Nhật Tân tái sinh đào sau Tết  - 2\" src=\"https://i-vnexpress.vnecdn.net/2020/02/06/image003-1580981978-9399-1580983352.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Những cây đào giống bắt đầu đâm chồi, nảy lộc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những gốc đào tại Nhật Tân lại bước vào chu trình \"tái sinh\", nảy mầm xanh mới mang theo hy vọng của người dân nơi đây về một năm thời tiết thuận lợi, một vụ mùa mới bội thu.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong>Thu Hiền</strong></span></p>', 'jNJG_1.jpg', 1, 0, 3, '2020-07-07 13:00:09', '2020-07-07 13:00:09'),
(94, 'Bánh tráng phơi sương Trảng Bàng', 'banh-trang-phoi-suong-trang-bang', 'Những hạt gạo ngon được xay thành bột, tráng hai lớp rồi nướng trên than củi trước khi đem phơi sương, có thể dùng cuốn cùng thịt heo, rau.', '<p style=\"text-align: center;\"><iframe height=\"480\" src=\"https://v.vnecdn.net/vnexpress/video/web/mp4/2020/02/07/banh-trang-phoi-suong-trang-bang-1581038358.mp4\" width=\"680\"></iframe></p>\r\n\r\n<p style=\"text-align: right;\"><span style=\"font-size:14px\"><span style=\"font-family:arial,sans-serif\">(Nguồn:&nbsp;</span><em>Nông nghiệp sạch</em><span style=\"font-family:arial,sans-serif\">)</span></span></p>', 'GU53_1.jpg', 1, 1, 3, '2020-07-07 13:02:57', '2020-07-07 13:02:57');

